import React, { useState } from "react";
import { Eye, EyeOff, Shield, Zap, Activity, ChevronRight, Mail, Lock, User, Sparkles, Loader2, ArrowUpRight } from "lucide-react";
import { Link, useNavigate } from "react-router-dom";
import { authService } from "../../services/authService";
import { motion, AnimatePresence } from "framer-motion";

const RegisterPage: React.FC = () => {
    const [showPassword, setShowPassword] = useState(false);
    const [showConfirmPassword, setShowConfirmPassword] = useState(false);
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        password: "",
        confirmPassword: "",
    });
    const [isLoading, setIsLoading] = useState(false);
    const navigate = useNavigate();

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        if (formData.password !== formData.confirmPassword) {
            alert("PASSWORDS DO NOT MATCH");
            return;
        }
        setIsLoading(true);
        try {
            await authService.register({
                email: formData.email,
                name: formData.name,
                password: formData.password
            });
            navigate("/login");
        } catch (error: any) {
            console.error("Registration failed:", error);
            alert(error.response?.data?.detail || "REGISTRATION FAILED");
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-white flex flex-col items-center justify-center p-10 font-sans relative overflow-hidden">

            {/* Structural Elements */}
            <div className="absolute inset-0 pointer-events-none opacity-[0.03]">
                <div className="absolute top-0 left-1/2 -translate-x-1/2 w-px h-full bg-black" />
                <div className="absolute top-1/2 left-0 -translate-y-1/2 w-full h-px bg-black" />
            </div>

            <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="w-full max-w-xl relative z-10"
            >
                {/* Logo Area */}
                <div className="flex flex-col items-center mb-16">
                    <div className="w-16 h-16 bg-black flex items-center justify-center mb-10">
                        <Sparkles size={32} className="text-white fill-white" />
                    </div>
                    <p className="text-[10px] font-black tracking-[0.5em] text-zinc-400 mb-4 uppercase">COLLABRYTA / INDUCTION</p>
                    <h1 className="text-6xl font-black tracking-tighter leading-none uppercase text-center">REQUEST<br />ACCESS</h1>
                </div>

                {/* Register Form */}
                <div className="bg-white border border-zinc-100 p-12 lg:p-16 shadow-2xl relative">
                    <div className="absolute -top-px -left-px w-10 h-10 border-t border-l border-black" />
                    <div className="absolute -bottom-px -right-px w-10 h-10 border-b border-r border-black" />

                    <form onSubmit={handleSubmit} className="space-y-10">
                        <div className="space-y-4">
                            <label className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.3em] ml-1">FULL APPELLATION</label>
                            <div className="relative group border-b border-zinc-200 focus-within:border-black transition-all">
                                <User className="absolute left-0 top-1/2 -translate-y-1/2 text-zinc-200 group-focus-within:text-black transition-colors" size={16} />
                                <input
                                    name="name"
                                    type="text"
                                    value={formData.name}
                                    onChange={handleChange}
                                    className="w-full bg-transparent py-5 pl-8 pr-4 text-[11px] font-bold tracking-widest text-black outline-none transition-all placeholder:text-zinc-100 uppercase"
                                    placeholder="OPERATIVE FULL NAME"
                                    required
                                />
                            </div>
                        </div>

                        <div className="space-y-4">
                            <label className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.3em] ml-1">SIGNAL ENDPOINT</label>
                            <div className="relative group border-b border-zinc-200 focus-within:border-black transition-all">
                                <Mail className="absolute left-0 top-1/2 -translate-y-1/2 text-zinc-200 group-focus-within:text-black transition-colors" size={16} />
                                <input
                                    name="email"
                                    type="email"
                                    value={formData.email}
                                    onChange={handleChange}
                                    className="w-full bg-transparent py-5 pl-8 pr-4 text-[11px] font-bold tracking-widest text-black outline-none transition-all placeholder:text-zinc-100 uppercase"
                                    placeholder="NAME@COMPANY.IO"
                                    required
                                />
                            </div>
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-10">
                            <div className="space-y-4">
                                <label className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.3em] ml-1">ENCRYPTION KEY</label>
                                <div className="relative group border-b border-zinc-200 focus-within:border-black transition-all">
                                    <Lock className="absolute left-0 top-1/2 -translate-y-1/2 text-zinc-200 group-focus-within:text-black transition-colors" size={16} />
                                    <input
                                        name="password"
                                        type={showPassword ? "text" : "password"}
                                        value={formData.password}
                                        onChange={handleChange}
                                        className="w-full bg-transparent py-5 pl-8 pr-12 text-[11px] font-bold tracking-widest text-black outline-none transition-all placeholder:text-zinc-100 uppercase"
                                        placeholder="••••••••••••"
                                        required
                                    />
                                    <button
                                        type="button"
                                        onClick={() => setShowPassword(!showPassword)}
                                        className="absolute right-0 top-1/2 -translate-y-1/2 text-zinc-200 hover:text-black transition-colors"
                                    >
                                        {showPassword ? <EyeOff size={16} /> : <Eye size={16} />}
                                    </button>
                                </div>
                            </div>
                            <div className="space-y-4">
                                <label className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.3em] ml-1">VALIDATE KEY</label>
                                <div className="relative group border-b border-zinc-200 focus-within:border-black transition-all">
                                    <Lock className="absolute left-0 top-1/2 -translate-y-1/2 text-zinc-200 group-focus-within:text-black transition-colors" size={16} />
                                    <input
                                        name="confirmPassword"
                                        type={showConfirmPassword ? "text" : "password"}
                                        value={formData.confirmPassword}
                                        onChange={handleChange}
                                        className="w-full bg-transparent py-5 pl-8 pr-12 text-[11px] font-bold tracking-widest text-black outline-none transition-all placeholder:text-zinc-100 uppercase"
                                        placeholder="••••••••••••"
                                        required
                                    />
                                    <button
                                        type="button"
                                        onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                                        className="absolute right-0 top-1/2 -translate-y-1/2 text-zinc-200 hover:text-black transition-colors"
                                    >
                                        {showConfirmPassword ? <EyeOff size={16} /> : <Eye size={16} />}
                                    </button>
                                </div>
                            </div>
                        </div>

                        <div className="pt-6">
                            <button
                                disabled={isLoading}
                                type="submit"
                                className="w-full bg-black hover:bg-zinc-800 text-white py-6 font-black text-[11px] tracking-[0.4em] uppercase transition-all flex items-center justify-center gap-6 active:scale-[0.98]"
                            >
                                {isLoading ? <Loader2 className="animate-spin" size={20} /> : (
                                    <>
                                        ESTABLISH IDENTITY
                                        <ArrowUpRight size={18} />
                                    </>
                                )}
                            </button>
                        </div>
                    </form>

                    <div className="mt-16 pt-10 border-t border-zinc-50 text-center">
                        <p className="text-[10px] font-black text-zinc-300 uppercase tracking-[0.2em]">
                            AUTHORIZED OPERATIVE?{" "}
                            <Link to="/login" className="text-black hover:underline font-black ml-4 decoration-2 underline-offset-8">
                                ACCESS PORTAL
                            </Link>
                        </p>
                    </div>
                </div>

                <div className="mt-12 flex items-center justify-center gap-8 text-[9px] font-black text-zinc-200 uppercase tracking-[0.4em]">
                    <div className="flex items-center gap-2">
                        <Shield size={12} />
                        <span>SECURE INDUCTION</span>
                    </div>
                    <div className="w-1 h-1 bg-zinc-100" />
                    <span>NODE 01 ACTIVE</span>
                </div>
            </motion.div>
        </div>
    );
};

export default RegisterPage;
