import React, { useState, useEffect, useRef, useCallback } from "react";
import {
    Search, Users, Paperclip, Smile, Send,
    Check, ArrowLeft, X, Sparkles, Zap, Shield, Plus, ArrowUpRight, ChevronRight, MessageSquare as MessageIcon
} from "lucide-react";
import { messageService, Chat, Message } from "../../services/messageService";
import { userService, User } from "../../services/userService";
import { authService } from "../../services/authService";

import { motion, AnimatePresence } from "framer-motion";

const CreateGroupModal = ({ isOpen, onClose, users, onCreate }: any) => {
    const [groupName, setGroupName] = useState("");
    const [selectedIds, setSelectedIds] = useState<number[]>([]);

    const toggleUser = (id: number) => {
        setSelectedIds(prev => prev.includes(id) ? prev.filter(x => x !== id) : [...prev, id]);
    };

    const handleSubmit = () => {
        if (!groupName || selectedIds.length === 0) return;
        onCreate(groupName, selectedIds);
        onClose();
        setGroupName("");
        setSelectedIds([]);
    };

    return (
        <AnimatePresence>
            {isOpen && (
                <div className="fixed inset-0 z-[100] flex items-center justify-center p-4">
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        className="absolute inset-0 bg-slate-900/10 backdrop-blur-sm"
                        onClick={onClose}
                    />
                    <motion.div
                        initial={{ opacity: 0, scale: 0.95, y: 10 }}
                        animate={{ opacity: 1, scale: 1, y: 0 }}
                        exit={{ opacity: 0, scale: 0.95, y: 10 }}
                        className="bg-white rounded-[32px] border border-slate-100 w-full max-w-md p-8 relative z-10 shadow-2xl"
                    >
                        <div className="flex justify-between items-center mb-8">
                            <div>
                                <h3 className="text-2xl font-bold tracking-tight text-slate-900">Create Group</h3>
                                <p className="text-sm text-slate-500 font-medium">Start a new team conversation</p>
                            </div>
                            <button
                                onClick={onClose}
                                className="w-10 h-10 flex items-center justify-center bg-slate-50 text-slate-400 hover:bg-slate-100 hover:text-slate-900 rounded-xl transition-all"
                            >
                                <X size={20} />
                            </button>
                        </div>

                        <div className="space-y-6">
                            <div className="space-y-2">
                                <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Group Name</label>
                                <input
                                    className="input-field"
                                    value={groupName}
                                    onChange={e => setGroupName(e.target.value)}
                                    placeholder="Enter group name..."
                                />
                            </div>

                            <div className="space-y-2">
                                <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Select Members</label>
                                <div className="max-h-[250px] overflow-y-auto space-y-1 pr-2 custom-scrollbar">
                                    {users.map((u: User) => (
                                        <button
                                            key={u.id}
                                            onClick={() => toggleUser(u.id)}
                                            className={`w-full flex items-center gap-4 p-3 rounded-2xl transition-all ${selectedIds.includes(u.id) ? 'bg-indigo-50 border border-indigo-100' : 'bg-white border border-transparent hover:bg-slate-50'
                                                }`}
                                        >
                                            <div className={`w-10 h-10 rounded-xl flex items-center justify-center text-xs font-bold transition-all shadow-sm ${selectedIds.includes(u.id) ? 'bg-indigo-600 text-white' : 'bg-slate-100 text-slate-500'
                                                }`}>
                                                {u.name.substring(0, 2).toUpperCase()}
                                            </div>
                                            <span className={`text-sm font-semibold ${selectedIds.includes(u.id) ? 'text-indigo-600' : 'text-slate-700'}`}>{u.name}</span>
                                            {selectedIds.includes(u.id) && (
                                                <div className="ml-auto p-1 bg-indigo-600 rounded-lg text-white">
                                                    <Check size={14} />
                                                </div>
                                            )}
                                        </button>
                                    ))}
                                </div>
                            </div>

                            <button
                                onClick={handleSubmit}
                                disabled={!groupName || selectedIds.length === 0}
                                className="btn-primary w-full shadow-lg shadow-indigo-100 disabled:opacity-50 disabled:shadow-none mt-4"
                            >
                                <Plus size={18} />
                                Create Group
                            </button>
                        </div>
                    </motion.div>
                </div>
            )}
        </AnimatePresence>
    );
};

const MessagesPage: React.FC = () => {
    const [chats, setChats] = useState<Chat[]>([]);
    const [messages, setMessages] = useState<Message[]>([]);
    const [currentUser, setCurrentUser] = useState<any>(null);
    const [activeChatId, setActiveChatId] = useState<number | null>(null);
    const [searchQuery, setSearchQuery] = useState("");
    const [allUsers, setAllUsers] = useState<User[]>([]);
    const [inputText, setInputText] = useState("");
    const [isMobileChatOpen, setIsMobileChatOpen] = useState(false);
    const [isGroupModalOpen, setIsGroupModalOpen] = useState(false);

    const messagesContainerRef = useRef<HTMLDivElement>(null);

    const loadChats = useCallback(async () => {
        try {
            const data = await messageService.getChats();
            setChats(data);
        } catch (e) {
            console.error("Failed to load chats:", e);
        }
    }, []);

    const loadUserAndData = useCallback(async () => {
        try {
            const user = await authService.getCurrentUser();
            setCurrentUser(user);
            await loadChats();
            const users = await userService.getAllUsers();
            setAllUsers(users.filter(u => u.id !== user.id));
        } catch (e) {
            console.error("Failed to load user data:", e);
        }
    }, [loadChats]);

    const loadMessages = useCallback(async (chatId: number) => {
        try {
            const msgs = await messageService.getMessages(chatId);
            setMessages(msgs);
            await messageService.markAsRead(chatId);
            loadChats();
        } catch (e) {
            console.error("Failed to load messages:", e);
        }
    }, [loadChats]);

    const scrollToBottom = useCallback((smooth = true) => {
        if (messagesContainerRef.current) {
            const { scrollHeight, clientHeight } = messagesContainerRef.current;
            messagesContainerRef.current.scrollTo({
                top: scrollHeight - clientHeight,
                behavior: smooth ? 'smooth' : 'auto'
            });
        }
    }, []);

    useEffect(() => {
        loadUserAndData();
    }, [loadUserAndData]);

    useEffect(() => {
        const pollInterval = setInterval(() => {
            loadChats();
            if (activeChatId) {
                loadMessages(activeChatId);
            }
        }, 10000);

        return () => {
            clearInterval(pollInterval);
        };
    }, [activeChatId, loadChats, loadMessages]);

    useEffect(() => {
        if (activeChatId) {
            loadMessages(activeChatId);
            setTimeout(() => scrollToBottom(false), 100);
        }
    }, [activeChatId, loadMessages, scrollToBottom]);

    useEffect(() => {
        scrollToBottom();
    }, [messages, scrollToBottom]);

    const handleSendMessage = async () => {
        if (!inputText.trim() || !activeChatId) return;
        try {
            await messageService.sendMessage(activeChatId, inputText);
            setInputText("");
            loadMessages(activeChatId);
            loadChats();
            setTimeout(() => scrollToBottom(), 100);
        } catch (e) {
            console.error("Failed to send message:", e);
        }
    };

    const handleCreateP2PChat = async (userId: number) => {
        try {
            const chat = await messageService.createChat([userId], undefined, false);
            setChats(prev => prev.find(c => c.id === chat.id) ? prev : [chat, ...prev]);
            setActiveChatId(chat.id);
            setIsMobileChatOpen(true);
            setSearchQuery("");
        } catch (e) {
            console.error("Failed to create P2P chat:", e);
        }
    };

    const handleCreateGroup = async (name: string, userIds: number[]) => {
        try {
            const chat = await messageService.createChat(userIds, name, true);
            setChats(prev => [chat, ...prev]);
            setActiveChatId(chat.id);
            setIsMobileChatOpen(true);
        } catch (e) {
            console.error("Failed to create group chat:", e);
        }
    };

    const filteredChats = chats.filter(c => c.name?.toLowerCase().includes(searchQuery.toLowerCase()));
    const suggestedUsers = searchQuery ? allUsers.filter(u => u.name.toLowerCase().includes(searchQuery.toLowerCase())) : [];
    const activeChat = chats.find(c => c.id === activeChatId);

    return (
        <div className="flex h-screen bg-[#fcfdfe] overflow-hidden animate-fade-in">
            <CreateGroupModal
                isOpen={isGroupModalOpen}
                onClose={() => setIsGroupModalOpen(false)}
                users={allUsers}
                onCreate={handleCreateGroup}
            />

            {/* Sidebar (Chat List) */}
            <div className={`w-full md:w-[350px] lg:w-[400px] bg-white border-r border-slate-100 flex flex-col ${isMobileChatOpen ? 'hidden md:flex' : 'flex'}`}>
                <div className="p-8 pb-6 border-b border-slate-50 flex items-center justify-between">
                    <div>
                        <h1 className="text-2xl font-bold tracking-tight text-slate-900 leading-none mb-1">Messages</h1>
                        <p className="text-xs font-semibold text-indigo-500 uppercase tracking-wider">Internal Chat</p>
                    </div>
                    <button
                        onClick={() => setIsGroupModalOpen(true)}
                        className="w-10 h-10 flex items-center justify-center bg-slate-50 text-slate-500 hover:bg-slate-100 hover:text-indigo-600 rounded-xl transition-all"
                        title="New Group"
                    >
                        <Plus size={20} />
                    </button>
                </div>

                <div className="p-6">
                    <div className="relative group">
                        <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 group-focus-within:text-indigo-600 transition-colors" size={16} />
                        <input
                            className="w-full bg-slate-50 border border-slate-100 py-2.5 pl-11 pr-4 text-sm font-medium text-slate-900 rounded-2xl focus:bg-white focus:border-indigo-500 focus:ring-4 focus:ring-indigo-500/5 transition-all outline-none placeholder:text-slate-400"
                            placeholder="Search messages..."
                            value={searchQuery}
                            onChange={e => setSearchQuery(e.target.value)}
                        />
                    </div>
                </div>

                <div className="flex-1 overflow-y-auto custom-scrollbar px-3 space-y-1">
                    {filteredChats.map(chat => (
                        <button
                            key={chat.id}
                            onClick={() => { setActiveChatId(chat.id); setIsMobileChatOpen(true); }}
                            className={`w-full p-4 flex items-center gap-4 rounded-[24px] transition-all ${activeChatId === chat.id ? 'bg-indigo-50 shadow-sm' : 'hover:bg-slate-50'
                                }`}
                        >
                            <div className={`w-12 h-12 rounded-[18px] flex items-center justify-center font-bold text-sm shrink-0 transition-all ${activeChatId === chat.id ? 'bg-indigo-600 text-white shadow-lg shadow-indigo-100' : 'bg-slate-100 text-slate-500'
                                }`}>
                                {chat.is_group ? <Users size={20} /> : (chat.name?.substring(0, 2).toUpperCase())}
                            </div>

                            <div className="flex-1 min-w-0 text-left">
                                <div className="flex justify-between items-center mb-0.5">
                                    <h3 className={`text-sm font-bold truncate ${activeChatId === chat.id ? 'text-indigo-600' : 'text-slate-900'}`}>{chat.name}</h3>
                                    <span className="text-[10px] font-semibold text-slate-400 shrink-0 ml-2">
                                        {chat.last_message_time ? new Date(chat.last_message_time).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }) : ''}
                                    </span>
                                </div>
                                <div className="flex justify-between items-center">
                                    <p className="text-xs font-medium text-slate-500 truncate mr-4">{chat.last_message || "New message..."}</p>
                                    {chat.unread_count && chat.unread_count > 0 && (
                                        <div className="bg-indigo-600 text-white min-w-[18px] h-[18px] px-1 rounded-full flex items-center justify-center text-[10px] font-bold shadow-lg shadow-indigo-100">
                                            {chat.unread_count}
                                        </div>
                                    )}
                                </div>
                            </div>
                        </button>
                    ))}

                    {suggestedUsers.length > 0 && (
                        <div className="mt-6">
                            <h4 className="px-5 mb-2 text-[10px] font-bold text-slate-400 uppercase tracking-[0.2em]">Team Members</h4>
                            {suggestedUsers.map(user => (
                                <button
                                    key={user.id}
                                    onClick={() => handleCreateP2PChat(user.id)}
                                    className="w-full p-4 rounded-[24px] hover:bg-slate-50 transition-all flex items-center justify-between group"
                                >
                                    <div className="flex items-center gap-4">
                                        <div className="w-10 h-10 bg-slate-100 rounded-[14px] flex items-center justify-center text-slate-500 font-bold text-xs group-hover:bg-indigo-600 group-hover:text-white transition-all shadow-sm">
                                            {user.name.substring(0, 2).toUpperCase()}
                                        </div>
                                        <span className="text-sm font-semibold text-slate-700">{user.name}</span>
                                    </div>
                                    <ChevronRight size={16} className="text-slate-300 group-hover:text-indigo-600 transition-all" />
                                </button>
                            ))}
                        </div>
                    )}
                </div>
            </div>

            {/* Chat Area */}
            {activeChat ? (
                <div className={`flex-1 flex flex-col bg-white overflow-hidden ${isMobileChatOpen ? 'flex' : 'hidden md:flex'}`}>
                    {/* Chat Header */}
                    <div className="h-20 bg-white/80 backdrop-blur-md border-b border-slate-50 flex items-center justify-between px-8 shrink-0 relative z-10">
                        <div className="flex items-center gap-4">
                            <button onClick={() => setIsMobileChatOpen(false)} className="md:hidden p-2 text-slate-400 hover:text-slate-900 transition-all">
                                <ArrowLeft size={20} />
                            </button>
                            <div className="relative">
                                <div className={`w-11 h-11 rounded-2xl flex items-center justify-center font-bold text-sm shrink-0 shadow-sm ${activeChat.is_group ? 'bg-slate-900 text-white' : 'bg-indigo-600 text-white'
                                    }`}>
                                    {activeChat.is_group ? <Users size={18} /> : activeChat.name?.substring(0, 2).toUpperCase()}
                                </div>
                                <div className="absolute -bottom-0.5 -right-0.5 w-3.5 h-3.5 bg-green-500 border-2 border-white rounded-full shadow-sm" />
                            </div>
                            <div>
                                <h2 className="text-base font-bold text-slate-900 leading-tight">{activeChat.name}</h2>
                                <p className="text-[10px] font-bold text-indigo-500 uppercase tracking-widest flex items-center gap-1.5 mt-0.5">
                                    <span className="animate-pulse">Active Now</span>
                                </p>
                            </div>
                        </div>
                        <div className="flex items-center gap-4">
                            <button className="hidden sm:flex items-center gap-2 px-4 py-2 bg-slate-50 text-slate-500 border border-slate-100 hover:bg-slate-100 hover:text-slate-900 transition-all rounded-xl text-[11px] font-bold">
                                <Shield size={14} />
                                Secure Channel
                            </button>
                        </div>
                    </div>

                    {/* Chat Messages */}
                    <div className="flex-1 relative flex flex-col overflow-hidden bg-[#fbfcfd]">
                        <div ref={messagesContainerRef} className="flex-1 overflow-y-auto p-8 space-y-8 custom-scrollbar">
                            <AnimatePresence mode="popLayout">
                                {messages.map((msg, idx) => {
                                    const isMe = msg.sender_id === currentUser?.id;
                                    const showSender = !isMe && activeChat.is_group && (idx === 0 || messages[idx - 1].sender_id !== msg.sender_id);

                                    return (
                                        <motion.div
                                            initial={{ opacity: 0, y: 10, scale: 0.95 }}
                                            animate={{ opacity: 1, y: 0, scale: 1 }}
                                            key={msg.id}
                                            className={`flex w-full ${isMe ? 'justify-end' : 'justify-start'}`}
                                        >
                                            <div className={`flex flex-col max-w-[70%] ${isMe ? 'items-end' : 'items-start'}`}>
                                                {showSender && <span className="text-[10px] font-bold text-slate-400 mb-1.5 px-3 uppercase tracking-wider">{msg.sender?.name}</span>}
                                                <div className={`p-4 rounded-2xl text-[13px] font-medium leading-relaxed shadow-sm ${isMe
                                                        ? 'bg-indigo-600 text-white rounded-tr-none shadow-indigo-100'
                                                        : 'bg-white text-slate-700 border border-slate-100 rounded-tl-none'
                                                    }`}>
                                                    <p>{msg.content}</p>
                                                </div>
                                                <div className="flex items-center gap-2 mt-2 px-1">
                                                    <span className="text-[10px] font-semibold text-slate-400">
                                                        {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                                                    </span>
                                                    {isMe && <Check size={12} className="text-indigo-400" />}
                                                </div>
                                            </div>
                                        </motion.div>
                                    );
                                })}
                            </AnimatePresence>
                        </div>
                    </div>

                    {/* Chat Input */}
                    <div className="p-6 bg-white border-t border-slate-50">
                        <div className="flex items-center gap-4 max-w-5xl mx-auto">
                            <button className="w-11 h-11 flex items-center justify-center bg-slate-50 text-slate-400 hover:bg-slate-100 hover:text-indigo-600 border border-slate-100 rounded-2xl transition-all">
                                <Paperclip size={20} />
                            </button>
                            <div className="flex-1 relative group">
                                <input
                                    className="w-full py-3 px-5 bg-slate-50 border border-slate-50 rounded-2xl text-sm font-medium text-slate-900 focus:bg-white focus:border-indigo-500 focus:ring-4 focus:ring-indigo-500/5 transition-all outline-none placeholder:text-slate-400 shadow-sm"
                                    placeholder="Type a message..."
                                    value={inputText}
                                    onChange={e => setInputText(e.target.value)}
                                    onKeyDown={e => e.key === 'Enter' && handleSendMessage()}
                                />
                                <button className="absolute right-3 top-1/2 -translate-y-1/2 text-slate-300 hover:text-indigo-500 p-1.5 transition-colors">
                                    <Smile size={20} />
                                </button>
                            </div>
                            <button
                                onClick={handleSendMessage}
                                disabled={!inputText.trim()}
                                className="w-11 h-11 flex items-center justify-center bg-indigo-600 text-white rounded-2xl hover:bg-indigo-700 transition-all shadow-lg shadow-indigo-100 disabled:opacity-50 disabled:shadow-none hover:scale-105 active:scale-95"
                            >
                                <Send size={20} className="ml-0.5" />
                            </button>
                        </div>
                    </div>
                </div>
            ) : (
                <div className="hidden md:flex flex-1 flex-col items-center justify-center bg-[#fcfdfe] p-10">
                    <div className="w-24 h-24 bg-white border border-slate-100 rounded-[32px] shadow-sm flex items-center justify-center mb-6">
                        <MessageIcon size={40} className="text-slate-200" />
                    </div>
                    <h3 className="text-2xl font-bold text-slate-900 tracking-tight">Select a conversation</h3>
                    <p className="text-sm text-slate-500 font-medium mt-2 max-w-xs text-center">Choose a chat from the left or search for team members to start messaging.</p>
                </div>
            )}
        </div>
    );
};

export default MessagesPage;
