import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {
  Calendar,
  Clock,
  Plus,
  Trash2,
  ExternalLink,
  Users,
  Video,
  History,
  AlertCircle
} from "lucide-react";
import { meetingService, Meeting } from "../../services/meetingService";
import { webSocketService, WebSocketMessage } from "../../services/websocketService";

const MeetingsPage: React.FC = () => {
  const navigate = useNavigate();
  const [meetings, setMeetings] = useState<Meeting[]>([]);
  const [loading, setLoading] = useState(true);
  const [currentTime, setCurrentTime] = useState(new Date());

  const fetchMeetings = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const data = await meetingService.getAllMeetings();
      setMeetings(data);
    } catch (error) {
      console.error("Failed to fetch meetings", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000);
    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    fetchMeetings();

    const unsubscribe = webSocketService.subscribe((message: WebSocketMessage) => {
      if (message.type === 'meeting') {
        fetchMeetings(false);
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);

  const handleDelete = async (id: number) => {
    if (window.confirm("Delete this meeting?")) {
      try {
        await meetingService.deleteMeeting(id);
        setMeetings(prev => prev.filter(m => m.id !== id));
      } catch (error) {
        console.error("Failed to delete meeting", error);
      }
    }
  };

  const getStatus = (start: string, end: string) => {
    const now = currentTime;
    const startTime = new Date(start);
    const endTime = new Date(end);
    if (now >= startTime && now <= endTime) return 'present';
    if (now > endTime) return 'past';
    return 'upcoming';
  };

  const formatTime = (dateString: string) => {
    return new Date(dateString).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString([], { weekday: 'short', month: 'short', day: 'numeric' });
  };

  const isToday = (dateString: string) => {
    const d = new Date(dateString);
    const today = new Date();
    return d.getFullYear() === today.getFullYear() &&
      d.getMonth() === today.getMonth() &&
      d.getDate() === today.getDate();
  };

  // Filter and Sort based on strict requirements:
  // 1. Present: Currently running
  // 2. Upcoming: Only for TODAY
  // 3. Past: Only within 48 hours
  const presentMeetings = meetings.filter(m => getStatus(m.start_time, m.end_time) === 'present');

  const upcomingMeetings = meetings.filter(m => {
    return getStatus(m.start_time, m.end_time) === 'upcoming' && isToday(m.start_time);
  }).sort((a, b) => new Date(a.start_time).getTime() - new Date(b.start_time).getTime());

  const pastMeetings = meetings.filter(m => {
    if (getStatus(m.start_time, m.end_time) !== 'past') return false;
    const endTime = new Date(m.end_time);
    const fortyEightHoursAgo = new Date(currentTime.getTime() - (48 * 60 * 60 * 1000));
    return endTime > fortyEightHoursAgo;
  }).sort((a, b) => new Date(b.start_time).getTime() - new Date(a.start_time).getTime());

  const MeetingRow = ({ meeting, type }: { meeting: Meeting, type: 'present' | 'upcoming' | 'past' }) => (
    <div className={`p-4 bg-white border border-gray-200 rounded-xl hover:shadow-sm transition-all flex flex-col md:flex-row md:items-center justify-between gap-4 ${type === 'past' ? 'opacity-70' : ''}`}>
      <div className="flex items-start gap-4 flex-1">
        <div className={`mt-1 p-2 rounded-lg ${type === 'present' ? 'bg-green-100 text-green-700' :
          type === 'past' ? 'bg-gray-100 text-gray-500' : 'bg-blue-100 text-blue-700'
          }`}>
          {type === 'present' ? <Video size={20} /> : type === 'past' ? <History size={20} /> : <Calendar size={20} />}
        </div>
        <div>
          <div className="flex items-center gap-2 flex-wrap">
            <h3 className="font-semibold text-gray-900">{meeting.title}</h3>
            {type === 'present' && <span className="px-2 py-0.5 bg-green-100 text-green-700 text-[10px] font-bold rounded uppercase tracking-wider">Live Now</span>}
            {type === 'past' && <span className="px-2 py-0.5 bg-gray-100 text-gray-500 text-[10px] font-bold rounded uppercase tracking-wider">Completed</span>}
          </div>
          <div className="flex flex-wrap items-center gap-x-4 gap-y-1 mt-1 text-sm text-gray-500">
            <span className="flex items-center gap-1.5"><Clock size={14} /> {formatTime(meeting.start_time)} - {formatTime(meeting.end_time)}</span>
            <span className="flex items-center gap-1.5"><Calendar size={14} /> {formatDate(meeting.start_time)}</span>
            <span className="flex items-center gap-1.5"><Users size={14} /> {meeting.participants?.length || 0} participants</span>
          </div>
        </div>
      </div>

      <div className="flex items-center gap-3">
        {meeting.meeting_link && type !== 'past' && (
          <button
            onClick={() => window.open(meeting.meeting_link, '_blank')}
            className={`px-4 py-2 rounded-lg text-sm font-semibold transition-colors flex items-center gap-2 ${type === 'present' ? 'bg-green-600 text-white hover:bg-green-700' : 'bg-gray-900 text-white hover:bg-black'
              }`}
          >
            Join <ExternalLink size={14} />
          </button>
        )}
        <button
          onClick={() => handleDelete(meeting.id)}
          className="p-2 text-gray-400 hover:text-red-600 hover:bg-red-50 rounded-lg transition-colors"
          title="Delete Meeting"
        >
          <Trash2 size={18} />
        </button>
      </div>
    </div>
  );

  return (
    <div className="max-w-6xl mx-auto p-4 md:p-8">
      {/* Header */}
      <div className="flex flex-col md:flex-row md:items-center justify-between gap-4 mb-8">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Meetings</h1>
          <p className="text-sm text-gray-500 mt-1">Manage your team syncs and today's schedule.</p>
        </div>
        <div className="flex items-center gap-3">
          <button
            onClick={() => navigate("/calendar")}
            className="px-4 py-2 border border-gray-300 rounded-lg bg-white hover:bg-gray-50 text-sm font-semibold text-gray-700 transition"
          >
            Calendar
          </button>
          <button
            onClick={() => navigate("/meetings/schedule")}
            className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg text-sm font-semibold flex items-center gap-2 transition"
          >
            <Plus size={18} /> Schedule Meeting
          </button>
        </div>
      </div>

      {loading ? (
        <div className="py-20 text-center">
          <div className="inline-block w-8 h-8 border-4 border-gray-200 border-t-blue-600 rounded-full animate-spin mb-4"></div>
          <p className="text-gray-400 text-sm font-medium">Loading your schedule...</p>
        </div>
      ) : (
        <div className="space-y-10">

          {/* Present */}
          {presentMeetings.length > 0 && (
            <section>
              <h2 className="text-xs font-bold text-green-600 uppercase tracking-widest mb-4 flex items-center gap-2">
                <span className="w-2 h-2 bg-green-600 rounded-full animate-pulse"></span>
                Active Right Now
              </h2>
              <div className="space-y-3">
                {presentMeetings.map(m => <MeetingRow key={m.id} meeting={m} type="present" />)}
              </div>
            </section>
          )}

          {/* Upcoming */}
          <section>
            <h2 className="text-xs font-bold text-gray-400 uppercase tracking-widest mb-4">Upcoming Today</h2>
            {upcomingMeetings.length > 0 ? (
              <div className="space-y-3">
                {upcomingMeetings.map(m => <MeetingRow key={m.id} meeting={m} type="upcoming" />)}
              </div>
            ) : (
              <div className="p-8 border-2 border-dashed border-gray-200 rounded-xl text-center text-gray-400">
                <AlertCircle size={32} className="mx-auto mb-2 opacity-50" />
                <p className="text-sm">No more meetings scheduled for today.</p>
              </div>
            )}
          </section>

          {/* Past */}
          {pastMeetings.length > 0 && (
            <section>
              <h2 className="text-xs font-bold text-gray-400 uppercase tracking-widest mb-4">Past 48 Hours</h2>
              <div className="space-y-2">
                {pastMeetings.map(m => <MeetingRow key={m.id} meeting={m} type="past" />)}
              </div>
            </section>
          )}

        </div>
      )}
    </div>
  );
};

export default MeetingsPage;
