import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { ChevronDown, ArrowLeft } from "lucide-react";

const AddNewWorkflow: React.FC = () => {
  const navigate = useNavigate();

  const [workflow, setWorkflow] = useState({
    name: "",
    trigger: "When a task is completed",
    actions: ["Notify manager", "Update task status"],
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setWorkflow({ ...workflow, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    console.log("✅ New Workflow Created:", workflow);
    navigate("/workflows");
  };

  return (
    <div className="flex h-screen bg-gray-50 text-gray-800">
      <main className="flex-1 flex flex-col p-10">
        {/* Header */}
        <div className="flex justify-between items-center mb-10">
          <div className="flex items-center space-x-3">
            <button
              onClick={() => navigate("/workflows")}
              className="flex items-center text-sm text-gray-600 hover:text-blue-600 transition"
            >
              <ArrowLeft className="w-4 h-4 mr-1" />
              Back to Workflows
            </button>
            <h2 className="text-2xl font-bold text-gray-800">Add New Workflow</h2>
          </div>
        </div>

        {/* Form */}
        <form
          onSubmit={handleSubmit}
          className="bg-white rounded-2xl shadow-md border border-gray-100 p-8 max-w-lg mx-auto space-y-6"
        >
          {/* Workflow Name */}
          <div>
            <label className="block text-sm font-semibold mb-2">Workflow Name</label>
            <input
              type="text"
              name="name"
              value={workflow.name}
              onChange={handleChange}
              placeholder="e.g. Task Completion Alert"
              className="w-full border border-gray-200 rounded-lg px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          {/* Trigger */}
          <div>
            <label className="block text-sm font-semibold mb-2">Trigger</label>
            <div className="relative">
              <select
                name="trigger"
                value={workflow.trigger}
                onChange={(e) =>
                  setWorkflow({ ...workflow, trigger: e.target.value })
                }
                className="w-full appearance-none border border-gray-200 rounded-lg px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option>When a task is completed</option>
                <option>When a file is uploaded</option>
                <option>When a meeting starts</option>
                <option>When a new message arrives</option>
              </select>
              <ChevronDown className="absolute right-3 top-3 w-4 h-4 text-gray-500" />
            </div>
          </div>

          {/* Actions */}
          <div>
            <label className="block text-sm font-semibold mb-2">Actions</label>
            {workflow.actions.map((action, index) => (
              <input
                key={index}
                type="text"
                value={action}
                onChange={(e) => {
                  const updated = [...workflow.actions];
                  updated[index] = e.target.value;
                  setWorkflow({ ...workflow, actions: updated });
                }}
                className="w-full border border-gray-200 rounded-lg px-3 py-2 text-sm mb-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            ))}
          </div>

          {/* Save Button */}
          <div className="flex justify-end">
            <button
              type="submit"
              className="bg-blue-600 text-white px-6 py-2 rounded-lg text-sm font-medium hover:bg-blue-700 transition"
            >
              Save Workflow
            </button>
          </div>
        </form>
      </main>
    </div>
  );
};

export default AddNewWorkflow;
