import React, { useState, DragEvent, ChangeEvent } from "react";
import { Upload, FileText, ArrowLeft, Activity, Shield, Zap, Sparkles, CheckCircle2 } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { fileService } from "../../services/fileService";
import { motion, AnimatePresence } from "framer-motion";

const UploadFilePage: React.FC = () => {
  const navigate = useNavigate();
  const [dragActive, setDragActive] = useState(false);
  const [fileName, setFileName] = useState<string | null>(null);
  const [file, setFile] = useState<File | null>(null);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [uploading, setUploading] = useState(false);

  const handleDrag = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e: DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      setFile(e.dataTransfer.files[0]);
      setFileName(e.dataTransfer.files[0].name);
    }
  };

  const handleFileChange = (e: ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      setFile(e.target.files[0]);
      setFileName(e.target.files[0].name);
    }
  };

  const handleSubmit = async () => {
    if (!file || !title) return;
    setUploading(true);
    try {
      await fileService.uploadFile(file, title, description);
      navigate("/files");
    } catch (error) {
      console.error("Upload failed", error);
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="min-h-screen bg-[#f8fafc] text-slate-900 p-8 lg:p-12 overflow-hidden relative font-sans">
      {/* Background Orbs */}
      <div className="fixed top-0 right-0 w-[700px] h-[700px] bg-indigo-50/50 rounded-full blur-[150px] -mr-64 -mt-64 animate-pulse" />
      <div className="fixed bottom-0 left-0 w-[500px] h-[500px] bg-sky-50/50 rounded-full blur-[120px] -ml-48 -mb-48" />

      <div className="max-w-4xl mx-auto relative z-10">
        {/* Nav Header */}
        <div className="flex items-center justify-between mb-16">
          <button
            onClick={() => navigate("/files")}
            className="group flex items-center gap-4 px-6 py-3 bg-white rounded-2xl hover:bg-slate-900 hover:text-white transition-all shadow-sm border border-slate-50"
          >
            <ArrowLeft size={18} className="group-hover:-translate-x-1 transition-transform" />
            <span className="text-[10px] font-black uppercase tracking-widest">Repository Archive</span>
          </button>
          <div className="flex items-center gap-3 px-5 py-2.5 bg-indigo-50 text-indigo-700 rounded-full text-[10px] font-black uppercase tracking-[0.3em] border border-indigo-100 shadow-sm">
            <Shield size={14} className="fill-indigo-600" /> Data Uplink
          </div>
        </div>

        <div className="flex flex-col lg:flex-row gap-16">
          {/* Left - Hero Copy */}
          <div className="lg:w-1/3 space-y-8">
            <h1 className="text-6xl font-[1000] tracking-tighter leading-[0.95] text-slate-900">
              Data <br />
              <span className="gradient-text">Injection.</span>
            </h1>
            <p className="text-slate-500 font-medium text-xl leading-relaxed">
              Securely transmit your assets into the encrypted workspace repository.
            </p>
            <div className="pt-8 space-y-6">
              <div className="flex items-start gap-4 p-5 bg-white rounded-[32px] border border-slate-50 shadow-sm">
                <div className="w-10 h-10 bg-indigo-600 rounded-2xl flex items-center justify-center text-white shrink-0"><Zap size={20} /></div>
                <div>
                  <p className="text-[10px] font-black uppercase tracking-widest text-slate-400">Elastic</p>
                  <p className="text-sm font-bold text-slate-700">Multi-format asset support.</p>
                </div>
              </div>
              <div className="flex items-start gap-4 p-5 bg-white rounded-[32px] border border-slate-50 shadow-sm">
                <div className="w-10 h-10 bg-emerald-500 rounded-2xl flex items-center justify-center text-white shrink-0"><Sparkles size={20} /></div>
                <div>
                  <p className="text-[10px] font-black uppercase tracking-widest text-slate-400">Precision</p>
                  <p className="text-sm font-bold text-slate-700">Encrypted transmission active.</p>
                </div>
              </div>
            </div>
          </div>

          {/* Right - Form Module */}
          <div className="lg:w-2/3">
            <motion.div
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              className="bg-white rounded-[56px] border-2 border-slate-50 p-12 shadow-2xl shadow-indigo-500/5"
            >
              {/* Drag & Drop Zone */}
              <div
                className={`relative border-4 border-dashed rounded-[48px] p-16 text-center transition-all group overflow-hidden ${dragActive ? "border-indigo-600 bg-indigo-50" : "border-slate-100 bg-slate-50/50 hover:border-indigo-100 hover:bg-white"
                  }`}
                onDragEnter={handleDrag}
                onDragOver={handleDrag}
                onDragLeave={handleDrag}
                onDrop={handleDrop}
              >
                <div className={`w-20 h-20 rounded-[28px] flex items-center justify-center mx-auto mb-8 shadow-2xl transition-all ${fileName ? 'bg-indigo-600 text-white animate-bounce' : 'bg-white text-indigo-400 group-hover:scale-110'
                  }`}>
                  <Upload size={32} />
                </div>

                <h3 className="text-2xl font-[1000] text-slate-900 tracking-tight leading-none mb-3 uppercase">
                  {fileName ? "Payload Synchronized" : "Manifest Data Drop"}
                </h3>
                <p className="text-sm font-bold text-slate-400 max-w-xs mx-auto">
                  {fileName ? fileName : "Drag and drop your mission assets or browse the local nexus."}
                </p>

                {!fileName && (
                  <div className="mt-10">
                    <label
                      htmlFor="fileUpload"
                      className="h-14 px-10 bg-white border-2 border-slate-100 text-slate-900 rounded-[20px] text-[10px] font-black uppercase tracking-widest hover:border-indigo-600 hover:text-indigo-600 cursor-pointer shadow-sm transition-all inline-flex items-center"
                    >
                      Browse Nexus
                    </label>
                    <input id="fileUpload" type="file" className="hidden" onChange={handleFileChange} />
                  </div>
                )}
              </div>

              {/* Inputs */}
              <div className="mt-12 space-y-10">
                <div className="space-y-3">
                  <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Asset Label (Required)</label>
                  <input
                    type="text"
                    placeholder="e.g. Q4 Performance Ledger"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    className="w-full px-8 py-5 bg-slate-50/50 border-2 border-slate-50 rounded-[28px] text-slate-900 font-bold text-sm focus:bg-white focus:border-indigo-600/20 focus:outline-none transition-all placeholder:text-slate-200"
                  />
                </div>

                <div className="space-y-3">
                  <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Transmission Context (Description)</label>
                  <textarea
                    placeholder="Enter additional briefing data..."
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    rows={4}
                    className="w-full px-8 py-5 bg-slate-50/50 border-2 border-slate-50 rounded-[32px] text-slate-900 font-bold text-sm focus:bg-white focus:border-indigo-600/20 focus:outline-none transition-all placeholder:text-slate-200 resize-none"
                  />
                </div>

                <div className="pt-8 flex items-center justify-end gap-10">
                  <button
                    onClick={() => navigate("/files")}
                    className="text-[10px] font-black uppercase tracking-widest text-slate-400 hover:text-slate-900 transition-colors"
                  >
                    Cancel Uplink
                  </button>
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={handleSubmit}
                    disabled={!fileName || !title || uploading}
                    className="h-16 px-12 bg-slate-900 text-white rounded-[28px] font-black uppercase tracking-widest text-[10px] shadow-2xl shadow-indigo-500/10 hover:bg-indigo-600 transition-all flex items-center gap-4 disabled:opacity-20 disabled:cursor-not-allowed"
                  >
                    {uploading ? <Activity size={20} className="animate-spin" /> : <CheckCircle2 size={20} />}
                    Establish Uplink
                  </motion.button>
                </div>
              </div>
            </motion.div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UploadFilePage;
