import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import {
    User,
    Lock,
    Bell,
    ShieldCheck,
    Save,
    Camera,
    Globe,
    HardDrive,
    Zap,
    Loader2,
    CheckCircle2,
    Eye,
    Settings as SettingsIcon,
    ChevronRight,
    HelpCircle,
    CreditCard
} from "lucide-react";
import { authService } from "../../services/authService";

const SettingsPage: React.FC = () => {
    const [loading, setLoading] = useState(true);
    const [saving, setSaving] = useState(false);
    const [success, setSuccess] = useState(false);
    const [settings, setSettings] = useState<any>(null);
    const [activeTab, setActiveTab] = useState("profile");

    useEffect(() => {
        const fetchSettings = async () => {
            try {
                const data = await authService.getCurrentUser();
                setSettings(data);
            } catch (error) {
                console.error("Failed to load settings", error);
            } finally {
                setLoading(false);
            }
        };
        fetchSettings();
    }, []);

    const handleUpdate = (field: string, value: any) => {
        setSettings((prev: any) => ({ ...prev, [field]: value }));
    };

    const handleSave = async () => {
        try {
            setSaving(true);
            setSuccess(false);
            const { id, email, hashed_password, last_seen, status, ...updateData } = settings;
            await authService.updateProfile(updateData);
            setSuccess(true);
            setTimeout(() => setSuccess(false), 3000);
        } catch (error) {
            console.error("Failed to save settings", error);
        } finally {
            setSaving(false);
        }
    };

    if (loading || !settings) {
        return (
            <div className="min-h-screen flex items-center justify-center bg-slate-50/50">
                <Loader2 className="w-10 h-10 text-indigo-600 animate-spin" />
            </div>
        );
    }

    const navigation = [
        { id: "profile", label: "Public Profile", icon: User },
        { id: "localization", label: "Localization", icon: Globe },
        { id: "privacy", label: "Privacy & Status", icon: Eye },
        { id: "notifications", label: "Notifications", icon: Bell },
        { id: "security", label: "Security & Auth", icon: ShieldCheck },
        { id: "storage", label: "Storage & Cache", icon: HardDrive },
        { id: "beta", label: "Beta Program", icon: Zap },
    ];

    const Toggle = ({ active, label, field }: any) => (
        <div className="flex items-center justify-between py-4 first:pt-0 last:pb-0">
            <span className="text-sm font-semibold text-slate-700">{label}</span>
            <button
                onClick={() => handleUpdate(field, !active)}
                className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors ${active ? 'bg-indigo-600' : 'bg-slate-200'}`}
            >
                <span className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform ${active ? 'translate-x-6' : 'translate-x-1'}`} />
            </button>
        </div>
    );

    return (
        <div className="max-w-6xl mx-auto p-6 space-y-8">
            {/* Header */}
            <header className="flex flex-col md:flex-row md:items-center justify-between gap-4 border-b border-slate-100 pb-8">
                <div>
                    <h1 className="text-3xl font-black text-slate-900 tracking-tight">System Settings</h1>
                    <p className="text-slate-500 font-medium">Manage your global workspace preferences and security.</p>
                </div>
                <button
                    onClick={handleSave}
                    disabled={saving}
                    className={`flex items-center justify-center gap-2 px-6 py-3 rounded-xl font-bold text-sm transition-all shadow-sm ${success ? 'bg-green-500 text-white' : 'bg-indigo-600 text-white hover:bg-indigo-700'
                        }`}
                >
                    {saving ? <Loader2 size={18} className="animate-spin" /> : success ? <CheckCircle2 size={18} /> : <Save size={18} />}
                    {saving ? 'Saving...' : success ? 'Settings Saved' : 'Save All Changes'}
                </button>
            </header>

            <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
                {/* Internal Sidebar */}
                <aside className="lg:col-span-1 space-y-1">
                    {navigation.map((item) => (
                        <button
                            key={item.id}
                            onClick={() => setActiveTab(item.id)}
                            className={`w-full flex items-center justify-between p-3.5 rounded-xl transition-all font-bold text-sm ${activeTab === item.id
                                ? "bg-indigo-50 text-indigo-700 shadow-sm"
                                : "text-slate-500 hover:bg-slate-50 hover:text-slate-900"
                                }`}
                        >
                            <div className="flex items-center gap-3">
                                <item.icon size={18} />
                                {item.label}
                            </div>
                            {activeTab === item.id && <ChevronRight size={14} />}
                        </button>
                    ))}

                    <div className="pt-8 space-y-1 opacity-60">
                        <button className="w-full flex items-center gap-3 p-3.5 rounded-xl text-slate-500 text-sm font-bold hover:bg-slate-50">
                            <CreditCard size={18} /> Billing
                        </button>
                        <button className="w-full flex items-center gap-3 p-3.5 rounded-xl text-slate-500 text-sm font-bold hover:bg-slate-50">
                            <HelpCircle size={18} /> Support
                        </button>
                    </div>
                </aside>

                {/* Content Area */}
                <main className="lg:col-span-3">
                    <AnimatePresence mode="wait">
                        <motion.div
                            key={activeTab}
                            initial={{ opacity: 0, x: 10 }}
                            animate={{ opacity: 1, x: 0 }}
                            exit={{ opacity: 0, x: -10 }}
                            className="bg-white rounded-3xl border border-slate-100 shadow-sm p-8"
                        >
                            {activeTab === "profile" && (
                                <div className="space-y-8">
                                    <h2 className="text-xl font-black text-slate-900">Public Profile</h2>
                                    <div className="flex items-center gap-6">
                                        <div className="relative">
                                            <div className="w-24 h-24 rounded-2xl bg-indigo-100 border-4 border-white shadow-md overflow-hidden flex items-center justify-center text-indigo-700 text-3xl font-black">
                                                {settings.avatar ? <img src={settings.avatar} className="w-full h-full object-cover" /> : settings.name?.charAt(0)}
                                            </div>
                                            <button className="absolute -bottom-2 -right-2 p-2 bg-indigo-600 text-white rounded-lg shadow-lg border-2 border-white"><Camera size={14} /></button>
                                        </div>
                                        <div>
                                            <p className="text-sm font-bold text-slate-700">Display Photo</p>
                                            <p className="text-xs text-slate-400 mt-1">Updates your visibility across the workspace.</p>
                                        </div>
                                    </div>
                                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                        <div className="space-y-2">
                                            <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-1">Full Name</label>
                                            <input type="text" value={settings.name || ''} onChange={(e) => handleUpdate('name', e.target.value)} className="w-full px-4 py-3 bg-slate-50 border border-slate-200 rounded-xl focus:ring-2 focus:ring-indigo-500/10 focus:border-indigo-500 outline-none font-bold text-slate-800" />
                                        </div>
                                        <div className="space-y-2">
                                            <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-1">Professional Role</label>
                                            <input type="text" value={settings.job_title || ''} onChange={(e) => handleUpdate('job_title', e.target.value)} className="w-full px-4 py-3 bg-slate-50 border border-slate-200 rounded-xl focus:ring-2 focus:ring-indigo-500/10 focus:border-indigo-500 outline-none font-bold text-slate-800" />
                                        </div>
                                    </div>
                                </div>
                            )}

                            {activeTab === "localization" && (
                                <div className="space-y-8">
                                    <h2 className="text-xl font-black text-slate-900">Localization</h2>
                                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                        <div className="space-y-2">
                                            <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-1">Primary Language</label>
                                            <select value={settings.language} onChange={(e) => handleUpdate('language', e.target.value)} className="w-full px-4 py-3 bg-slate-50 border border-slate-200 rounded-xl font-bold text-slate-800 outline-none cursor-pointer">
                                                <option>English (US)</option>
                                                <option>Hindi (HI)</option>
                                                <option>Spanish (ES)</option>
                                            </select>
                                        </div>
                                        <div className="space-y-2">
                                            <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-1">Regional Timezone</label>
                                            <select value={settings.timezone} onChange={(e) => handleUpdate('timezone', e.target.value)} className="w-full px-4 py-3 bg-slate-50 border border-slate-200 rounded-xl font-bold text-slate-800 outline-none cursor-pointer">
                                                <option>(GMT+05:30) Mumbai, New Delhi</option>
                                                <option>(GMT-08:00) Pacific Time</option>
                                            </select>
                                        </div>
                                    </div>
                                </div>
                            )}

                            {activeTab === "privacy" && (
                                <div className="space-y-6">
                                    <h2 className="text-xl font-black text-slate-900">Privacy & Status</h2>
                                    <div className="divide-y divide-slate-50">
                                        <Toggle active={settings.show_online_status} label="Show Online Status" field="show_online_status" />
                                        <Toggle active={settings.share_local_time} label="Share Local Timezone" field="share_local_time" />
                                        <Toggle active={settings.anonymous_reporting} label="Anonymous Usage Data" field="anonymous_reporting" />
                                    </div>
                                </div>
                            )}

                            {activeTab === "notifications" && (
                                <div className="space-y-8">
                                    <div className="space-y-4">
                                        <h3 className="text-sm font-black text-indigo-600 uppercase tracking-widest">Email Alerts</h3>
                                        <div className="divide-y divide-slate-50">
                                            <Toggle active={settings.email_tasks} label="New Task Assignments" field="email_tasks" />
                                            <Toggle active={settings.email_summary} label="Weekly Digest" field="email_summary" />
                                        </div>
                                    </div>
                                    <div className="space-y-4">
                                        <h3 className="text-sm font-black text-indigo-600 uppercase tracking-widest">Push Notifications</h3>
                                        <div className="divide-y divide-slate-50">
                                            <Toggle active={settings.push_meetings} label="Meeting Reminders" field="push_meetings" />
                                            <Toggle active={settings.push_chat} label="New Messages" field="push_chat" />
                                        </div>
                                    </div>
                                </div>
                            )}

                            {activeTab === "security" && (
                                <div className="space-y-8">
                                    <h2 className="text-xl font-black text-slate-900">Security & Auth</h2>
                                    <div className="p-6 bg-slate-50 border border-slate-100 rounded-2xl flex items-center justify-between">
                                        <div>
                                            <p className="font-bold text-slate-900">Two-Factor Authentication</p>
                                            <p className="text-xs text-slate-500 mt-1">{settings.two_factor_enabled ? 'Protected with 2FA' : 'Highly recommended for safety'}</p>
                                        </div>
                                        <button onClick={() => handleUpdate('two_factor_enabled', !settings.two_factor_enabled)} className={`px-4 py-2 rounded-lg text-xs font-black uppercase tracking-widest transition-all ${settings.two_factor_enabled ? 'bg-white text-indigo-600 border border-indigo-100' : 'bg-indigo-600 text-white shadow-lg shadow-indigo-500/20'}`}>
                                            {settings.two_factor_enabled ? 'Disable' : 'Enable'}
                                        </button>
                                    </div>
                                    <div className="flex items-center justify-between px-2">
                                        <span className="text-sm font-bold text-slate-600">Last Password Reset</span>
                                        <span className="text-sm font-black text-slate-400">90 DAYS AGO</span>
                                    </div>
                                </div>
                            )}

                            {activeTab === "storage" && (
                                <div className="space-y-8">
                                    <h2 className="text-xl font-black text-slate-900">Storage Management</h2>
                                    <div className="p-6 bg-slate-50 border border-slate-100 rounded-2xl">
                                        <div className="flex justify-between mb-2">
                                            <span className="text-xs font-black text-slate-500 tracking-widest">CLOUD USAGE</span>
                                            <span className="text-xs font-black text-indigo-600">4.2 GB / 10 GB</span>
                                        </div>
                                        <div className="w-full h-2 bg-slate-200 rounded-full overflow-hidden">
                                            <div className="h-full bg-indigo-600 rounded-full" style={{ width: '42%' }} />
                                        </div>
                                    </div>
                                    <button className="flex items-center gap-2 text-sm font-bold text-slate-400 hover:text-indigo-600 transition-colors">
                                        <HardDrive size={16} /> Data Export (JSON)
                                    </button>
                                </div>
                            )}

                            {activeTab === "beta" && (
                                <div className="space-y-6">
                                    <h2 className="text-xl font-black text-slate-900">Beta Features</h2>
                                    <div className="divide-y divide-slate-50">
                                        <Toggle active={settings.beta_ai_summaries} label="AI Task Summaries" field="beta_ai_summaries" />
                                        <Toggle active={settings.beta_voice_commands} label="Voice Control Beta" field="beta_voice_commands" />
                                        <Toggle active={settings.beta_collab_editing} label="Real-time Collab (BETA)" field="beta_collab_editing" />
                                    </div>
                                </div>
                            )}
                        </motion.div>
                    </AnimatePresence>
                </main>
            </div>
        </div>
    );
};

export default SettingsPage;
