import React from "react";
import {
  LayoutDashboard,
  MessageSquare,
  CalendarDays,
  FileUp,
  Workflow,
  ClipboardList,
  LogOut,
  UsersRound,
  X,
  ChevronRight,
} from "lucide-react";
import { useNavigate, useLocation } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";

interface SidebarProps {
  isOpen: boolean;
  onClose: () => void;
}

const Sidebar: React.FC<SidebarProps> = ({ isOpen, onClose }) => {
  const navigate = useNavigate();
  const location = useLocation();

  const navItems = [
    { label: "Dashboard", icon: LayoutDashboard, path: "/dashboard" },
    { label: "Chat", icon: MessageSquare, path: "/chat" },
    { label: "Tasks", icon: ClipboardList, path: "/tasks" },
    { label: "Meetings", icon: UsersRound, path: "/meetings" },
    { label: "Calendar", icon: CalendarDays, path: "/calendar" },
    { label: "Files", icon: FileUp, path: "/files" },
    { label: "Workflows", icon: Workflow, path: "/workflows" },
  ];

  return (
    <>
      {/* Overlay (mobile) */}
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-slate-900/40 backdrop-blur-sm z-40 lg:hidden"
            onClick={onClose}
          />
        )}
      </AnimatePresence>

      <aside
        className={`
          fixed top-0 left-0 z-50 h-screen w-64 bg-white border-r border-slate-200
          transform transition-all duration-300 ease-in-out
          ${isOpen ? "translate-x-0" : "-translate-x-full"}
          lg:translate-x-0 shadow-xl shadow-slate-200/50 lg:shadow-none
        `}
      >
        {/* Mobile Close Button */}
        <div className="lg:hidden flex justify-end p-4">
          <motion.button
            whileTap={{ scale: 0.9 }}
            onClick={onClose}
            className="p-2 hover:bg-slate-100 rounded-xl"
          >
            <X size={20} className="text-slate-500" />
          </motion.button>
        </div>

        {/* Logo Section */}
        <div
          className="flex flex-col items-center py-10 cursor-pointer group"
          onClick={() => {
            navigate("/dashboard");
            onClose();
          }}
        >
          <div className="relative">
            <div className="absolute inset-0 bg-blue-500 blur-2xl opacity-20 group-hover:opacity-40 transition-opacity rounded-full" />
            <img
              src="/logo1.png"
              alt="Collabryta logo"
              className="w-16 h-16 relative z-10 transition-transform duration-500 group-hover:scale-110"
            />
          </div>
          <h1 className="mt-4 text-xl font-bold tracking-tight bg-gradient-to-r from-slate-900 to-slate-700 bg-clip-text text-transparent">
            Collabryta
          </h1>
          <p className="text-[10px] uppercase tracking-[0.2em] font-semibold text-slate-400 mt-1">Enterprise UI</p>
        </div>

        {/* Navigation Section */}
        <nav className="px-4 mt-2 space-y-1.5 overflow-y-auto max-h-[calc(100vh-280px)] custom-scrollbar">
          {navItems.map(({ label, icon: Icon, path }) => {
            const active = location.pathname === path;
            return (
              <motion.button
                key={path}
                whileHover={{ x: 4 }}
                whileTap={{ scale: 0.98 }}
                onClick={() => {
                  navigate(path);
                  onClose();
                }}
                className={`w-full flex items-center justify-between px-4 py-2.5 rounded-xl text-sm font-medium transition-all group
                  ${active
                    ? "bg-blue-600 text-white shadow-lg shadow-blue-200"
                    : "text-slate-600 hover:bg-slate-50 hover:text-blue-600"
                  }`}
              >
                <div className="flex items-center gap-3">
                  <Icon size={18} className={`${active ? "text-white" : "group-hover:text-blue-600"}`} />
                  {label}
                </div>
                {active && (
                  <motion.div
                    layoutId="activeIndicator"
                    className="w-1.5 h-1.5 rounded-full bg-white ml-2"
                  />
                )}
                {!active && <ChevronRight size={14} className="opacity-0 group-hover:opacity-40 transition-opacity" />}
              </motion.button>
            );
          })}
        </nav>

        {/* Logout Section */}
        <div className="absolute bottom-8 w-full px-6">
          <motion.button
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            onClick={() => navigate("/login")}
            className="w-full flex items-center justify-center gap-2 py-3 bg-rose-50 border border-rose-100 text-rose-600 rounded-xl text-sm font-semibold hover:bg-rose-100 transition-all shadow-sm"
          >
            <LogOut size={18} />
            Sign Out
          </motion.button>
        </div>
      </aside>
    </>
  );
};

export default Sidebar;
