import React, { useState, useEffect } from "react";
import { Plus, ChevronLeft, ChevronRight, Calendar, User, AlignLeft, CheckCircle, Clock, AlertCircle } from "lucide-react";
import { taskService, Task } from "../../services/taskService";
import { userService } from "../../services/userService";
import { User as ApiUser } from "../../services/authService";

const statusOrder: ("Todo" | "In Progress" | "Completed")[] = [
  "Todo",
  "In Progress",
  "Completed",
];

const TasksPage: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedTask, setSelectedTask] = useState<Task | null>(null);
  const [addingTask, setAddingTask] = useState(false);
  const [users, setUsers] = useState<ApiUser[]>([]);

  const [newTask, setNewTask] = useState<{
    title: string;
    description: string;
    assigned_to: number | "";
    due_date: string;
  }>({
    title: "",
    description: "",
    assigned_to: "",
    due_date: "",
  });

  // Fetch logic
  useEffect(() => {
    loadTasks();
    loadUsers();
  }, []);

  const loadTasks = async () => {
    try {
      const data = await taskService.getTasks();
      setTasks(data);
    } catch (e) {
      console.error("Failed to load tasks", e);
    } finally {
      setLoading(false);
    }
  };

  const loadUsers = async () => {
    try {
      const data = await userService.getAllUsers();
      setUsers(data);
    } catch (e) {
      console.error("Failed to load users", e);
    }
  };


  const groupedTasks = {
    Todo: tasks.filter((t) => t.status === "Todo"),
    "In Progress": tasks.filter((t) => t.status === "In Progress"),
    Completed: tasks.filter((t) => t.status === "Completed"),
  };

  const handleStatusChange = async (task: Task, newStatus: "Todo" | "In Progress" | "Completed") => {
    // Optimistic update
    const updatedTask = { ...task, status: newStatus };
    setTasks(prev => prev.map(t => t.id === task.id ? updatedTask : t));
    setSelectedTask(updatedTask);

    try {
      await taskService.updateTask(task.id, { status: newStatus });
    } catch (error) {
      console.error("Failed to update status", error);
      // Revert on failure
      setTasks(prev => prev.map(t => t.id === task.id ? task : t));
      setSelectedTask(task);
      alert("Failed to update task status");
    }
  };

  const moveForward = (task: Task) => {
    const currentIndex = statusOrder.indexOf(task.status);
    if (currentIndex < statusOrder.length - 1) {
      handleStatusChange(task, statusOrder[currentIndex + 1]);
    }
  };

  const moveBackward = (task: Task) => {
    const currentIndex = statusOrder.indexOf(task.status);
    if (currentIndex > 0) {
      handleStatusChange(task, statusOrder[currentIndex - 1]);
    }
  };

  const handleAddTask = async () => {
    if (!newTask.title.trim()) return;

    try {
      const payload = {
        title: newTask.title,
        description: newTask.description,
        assigned_to: newTask.assigned_to ? Number(newTask.assigned_to) : null,
        due_date: newTask.due_date || null
      };
      const created = await taskService.createTask(payload);
      setTasks([...tasks, created]);
      setNewTask({ title: "", description: "", assigned_to: "", due_date: "" });
      setAddingTask(false);
    } catch (error) {
      console.error("Failed to create task", error);
      alert("Failed to create task");
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "Todo": return "bg-slate-100 text-slate-600 border-slate-200";
      case "In Progress": return "bg-blue-50 text-blue-600 border-blue-100";
      case "Completed": return "bg-green-50 text-green-600 border-green-100";
      default: return "bg-gray-100";
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case "Todo": return <AlertCircle size={14} />;
      case "In Progress": return <Clock size={14} />;
      case "Completed": return <CheckCircle size={14} />;
      default: return null;
    }
  };

  return (
    <div className="min-h-screen bg-slate-50/50 p-8 font-sans">
      <div className="max-w-7xl mx-auto">

        {/* Header */}
        <div className="flex flex-col md:flex-row md:items-center justify-between gap-4 mb-8">
          <div>
            <h1 className="text-3xl font-bold text-slate-900 tracking-tight">Project Tasks</h1>
            <p className="text-slate-500 mt-1">Manage, track, and collaborate on your team's objectives.</p>
          </div>
          <button
            onClick={() => setAddingTask(true)}
            className="flex items-center gap-2 bg-blue-600 hover:bg-blue-700 text-white px-5 py-2.5 rounded-xl font-medium shadow-sm hover:shadow transition-all active:scale-95"
          >
            <Plus size={20} /> New Task
          </button>
        </div>

        {/* Board */}
        {loading ? (
          <div className="flex items-center justify-center h-64">
            <div className="w-8 h-8 border-4 border-blue-600 border-t-transparent rounded-full animate-spin"></div>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {(["Todo", "In Progress", "Completed"] as const).map((status) => (
              <div key={status} className="flex flex-col h-full">
                <div className="flex items-center justify-between mb-4 px-1">
                  <div className="flex items-center gap-2">
                    <span className={`p-1.5 rounded-md ${getStatusColor(status)}`}>
                      {getStatusIcon(status)}
                    </span>
                    <h2 className="font-bold text-slate-700">{status}</h2>
                  </div>
                  <span className="bg-white px-2.5 py-0.5 rounded-full text-xs font-semibold text-slate-400 border border-slate-100 shadow-sm">
                    {groupedTasks[status].length}
                  </span>
                </div>

                <div className="bg-slate-100/50 p-2 rounded-2xl flex-1 border border-slate-200/60 min-h-[500px]">
                  {groupedTasks[status].length === 0 ? (
                    <div className="h-32 flex flex-col items-center justify-center text-slate-400 border-2 border-dashed border-slate-200 rounded-xl m-2">
                      <p className="text-xs font-medium">No tasks yet</p>
                    </div>
                  ) : (
                    groupedTasks[status].map((task) => (
                      <div
                        key={task.id}
                        onClick={() => setSelectedTask(task)}
                        className="group bg-white p-4 rounded-xl shadow-sm border border-slate-100 cursor-pointer hover:shadow-md hover:border-blue-100 transition-all mb-3 relative overflow-hidden"
                      >
                        <div className="absolute left-0 top-0 bottom-0 w-1 bg-transparent group-hover:bg-blue-500 transition-colors"></div>

                        <h3 className="font-semibold text-slate-800 mb-2 group-hover:text-blue-700 transition-colors line-clamp-2">{task.title}</h3>

                        <div className="flex items-center justify-between mt-3 pt-3 border-t border-slate-50">
                          <div className="flex items-center gap-2">
                            {task.assigned_to_user ? (
                              <div className="flex items-center gap-1.5 bg-slate-50 px-2 py-1 rounded-md border border-slate-100">
                                <div className="w-5 h-5 rounded-full bg-blue-100 text-blue-600 flex items-center justify-center text-[10px] font-bold">
                                  {task.assigned_to_user.full_name.charAt(0)}
                                </div>
                                <span className="text-xs font-medium text-slate-600 truncate max-w-[80px]">
                                  {task.assigned_to_user.full_name}
                                </span>
                              </div>
                            ) : (
                              <span className="text-xs text-slate-400 italic">Unassigned</span>
                            )}
                          </div>

                          {task.due_date && (
                            <div className={`flex items-center gap-1 text-xs font-medium ${new Date(task.due_date) < new Date() && status !== 'Completed' ? 'text-red-500' : 'text-slate-400'}`}>
                              <Calendar size={12} />
                              {new Date(task.due_date).toLocaleDateString(undefined, { month: 'short', day: 'numeric' })}
                            </div>
                          )}
                        </div>
                      </div>
                    ))
                  )}
                </div>
              </div>
            ))}
          </div>
        )}

      </div>

      {/* Task Details Modal */}
      {selectedTask && (
        <div className="fixed inset-0 bg-slate-900/40 backdrop-blur-sm flex items-center justify-center z-50 p-4 transition-all">
          <div className="bg-white rounded-2xl w-full max-w-lg shadow-2xl overflow-hidden border border-slate-100 transform transition-all scale-100">
            {/* Modal Header */}
            <div className="px-6 py-4 border-b border-slate-100 flex justify-between items-start bg-slate-50/50">
              <div>
                <span className={`inline-flex items-center gap-1.5 px-2.5 py-1 rounded-full text-xs font-bold uppercase tracking-wider mb-2 ${getStatusColor(selectedTask.status)}`}>
                  {getStatusIcon(selectedTask.status)}
                  {selectedTask.status}
                </span>
                <h2 className="text-xl font-bold text-slate-800 leading-snug">{selectedTask.title}</h2>
              </div>
              <button
                onClick={() => setSelectedTask(null)}
                className="p-1 text-slate-400 hover:text-slate-600 hover:bg-slate-100 rounded-full transition"
              >
                <span className="text-xl px-1">Ã—</span>
              </button>
            </div>

            {/* Modal Body */}
            <div className="p-6 space-y-6">
              <div className="space-y-2">
                <label className="flex items-center gap-2 text-xs font-bold text-slate-400 uppercase tracking-wide">
                  <AlignLeft size={14} /> Description
                </label>
                <p className="text-slate-600 text-sm leading-relaxed whitespace-pre-wrap bg-slate-50 p-3 rounded-xl border border-slate-100">
                  {selectedTask.description || <span className="italic text-slate-400">No description provided.</span>}
                </p>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="flex items-center gap-2 text-xs font-bold text-slate-400 uppercase tracking-wide mb-2">
                    <User size={14} /> Assignee
                  </label>
                  <div className="flex items-center gap-3">
                    <div className="w-8 h-8 rounded-full bg-blue-100 text-blue-600 flex items-center justify-center font-bold text-sm">
                      {selectedTask.assigned_to_user?.full_name.charAt(0) || "?"}
                    </div>
                    <div>
                      <p className="text-sm font-semibold text-slate-700">{selectedTask.assigned_to_user?.full_name || "Unassigned"}</p>
                      <p className="text-xs text-slate-400">{selectedTask.assigned_to_user?.email || "No email"}</p>
                    </div>
                  </div>
                </div>
                <div>
                  <label className="flex items-center gap-2 text-xs font-bold text-slate-400 uppercase tracking-wide mb-2">
                    <Calendar size={14} /> Due Date
                  </label>
                  <p className="text-sm font-medium text-slate-700">
                    {selectedTask.due_date ? new Date(selectedTask.due_date).toLocaleDateString(undefined, { weekday: 'short', year: 'numeric', month: 'long', day: 'numeric' }) : "No due date"}
                  </p>
                </div>
              </div>
            </div>

            {/* Modal Footer (Actions) */}
            <div className="px-6 py-4 bg-slate-50 border-t border-slate-100 flex justify-between items-center">
              <button
                disabled={selectedTask.status === "Todo"}
                onClick={() => moveBackward(selectedTask)}
                className={`flex items-center gap-2 px-4 py-2 rounded-xl text-sm font-medium transition-all ${selectedTask.status === "Todo"
                    ? "text-slate-300 cursor-not-allowed"
                    : "text-slate-600 hover:bg-white hover:shadow-sm hover:text-slate-900 border border-transparent hover:border-slate-200"
                  }`}
              >
                <ChevronLeft size={16} /> Move Back
              </button>

              <button
                onClick={() => {
                  if (selectedTask.status === "Completed") {
                    setSelectedTask(null);
                  } else {
                    moveForward(selectedTask);
                  }
                }}
                className={`flex items-center gap-2 px-5 py-2 text-white text-sm font-semibold rounded-xl shadow-sm transition-all transform active:scale-95 ${selectedTask.status === "Completed" ? "bg-slate-800 hover:bg-slate-900" : "bg-blue-600 hover:bg-blue-700"
                  }`}
              >
                {selectedTask.status === "Completed" ? "Close" : "Move Next"}
                {selectedTask.status !== "Completed" && <ChevronRight size={16} />}
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Add New Task Modal */}
      {addingTask && (
        <div className="fixed inset-0 bg-slate-900/40 backdrop-blur-sm flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-2xl w-full max-w-md shadow-2xl overflow-hidden animate-in fade-in zoom-in duration-200">
            <div className="px-6 py-4 border-b border-gray-100 flex justify-between items-center bg-gray-50/50">
              <h2 className="text-lg font-bold text-gray-800">New Task</h2>
              <button
                onClick={() => setAddingTask(false)}
                className="text-gray-400 hover:text-gray-600 p-1 rounded-full hover:bg-gray-100 transition"
              >
                <span className="text-xl">Ã—</span>
              </button>
            </div>

            <div className="p-6 space-y-5">
              <div className="space-y-1.5">
                <label className="text-xs font-semibold text-gray-500 uppercase">Title</label>
                <input
                  type="text"
                  placeholder="What needs to be done?"
                  value={newTask.title}
                  onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
                  className="w-full px-4 py-2.5 bg-gray-50 border border-gray-200 rounded-xl focus:bg-white focus:border-blue-500 focus:ring-4 focus:ring-blue-500/10 transition-all outline-none text-sm"
                />
              </div>

              <div className="space-y-1.5">
                <label className="text-xs font-semibold text-gray-500 uppercase">Description</label>
                <textarea
                  placeholder="Add details..."
                  rows={3}
                  value={newTask.description}
                  onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
                  className="w-full px-4 py-2.5 bg-gray-50 border border-gray-200 rounded-xl focus:bg-white focus:border-blue-500 focus:ring-4 focus:ring-blue-500/10 transition-all outline-none text-sm resize-none"
                />
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-1.5">
                  <label className="text-xs font-semibold text-gray-500 uppercase">Assignee</label>
                  <select
                    value={newTask.assigned_to}
                    onChange={(e) => setNewTask({ ...newTask, assigned_to: e.target.value })}
                    className="w-full px-3 py-2.5 bg-gray-50 border border-gray-200 rounded-xl focus:bg-white focus:border-blue-500 focus:ring-4 focus:ring-blue-500/10 transition-all outline-none text-sm appearance-none"
                  >
                    <option value="">Select User</option>
                    {users.map(u => (
                      <option key={u.id} value={u.id}>{u.full_name}</option>
                    ))}
                  </select>
                </div>
                <div className="space-y-1.5">
                  <label className="text-xs font-semibold text-gray-500 uppercase">Due Date</label>
                  <input
                    type="date"
                    value={newTask.due_date}
                    onChange={(e) => setNewTask({ ...newTask, due_date: e.target.value })}
                    className="w-full px-3 py-2.5 bg-gray-50 border border-gray-200 rounded-xl focus:bg-white focus:border-blue-500 focus:ring-4 focus:ring-blue-500/10 transition-all outline-none text-sm text-gray-600"
                  />
                </div>
              </div>
            </div>

            <div className="px-6 py-4 bg-gray-50 border-t border-gray-100 flex justify-end gap-3">
              <button
                onClick={() => setAddingTask(false)}
                className="px-4 py-2 text-gray-600 font-medium hover:bg-gray-100 rounded-lg transition-colors text-sm"
              >
                Cancel
              </button>
              <button
                onClick={handleAddTask}
                disabled={!newTask.title}
                className="px-5 py-2 bg-blue-600 hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed text-white rounded-lg font-medium shadow-sm hover:shadow transition-all text-sm"
              >
                Create Task
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TasksPage;
