import React, { useState, useEffect } from "react";
import { Outlet } from "react-router-dom";
import Sidebar from "../components/Sidebar";
import Topbar from "../components/Topbar";
import Footer from "../components/Footer";

const MainLayout: React.FC = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState<boolean>(false);
  const [isMobile, setIsMobile] = useState<boolean>(false);
  const [scrolled, setScrolled] = useState<boolean>(false);

  // Handle responsive behavior
  useEffect(() => {
    const handleResize = () => {
      const mobile = window.innerWidth < 1024;
      setIsMobile(mobile);

      // Auto-close sidebar on mobile when resizing
      if (mobile) {
        setIsSidebarOpen(false);
      }
    };

    // Initial check
    handleResize();

    // Add event listener
    window.addEventListener("resize", handleResize);

    // Cleanup
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Handle scroll effect for topbar
  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 10);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // Toggle sidebar
  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  // Close sidebar when clicking outside on mobile
  const handleOverlayClick = () => {
    if (isMobile && isSidebarOpen) {
      setIsSidebarOpen(false);
    }
  };

  return (
    <div className="flex min-h-screen bg-slate-50">
      {/* Mobile Overlay */}
      {isMobile && isSidebarOpen && (
        <div
          className="fixed inset-0 bg-slate-900/50 backdrop-blur-sm z-40 lg:hidden transition-opacity duration-300 animate-in fade-in"
          onClick={handleOverlayClick}
          aria-hidden="true"
        />
      )}

      {/* Sidebar */}
      <aside
        className={`
          fixed top-0 left-0 h-full w-64 
          bg-white border-r border-slate-200 shadow-xl
          z-50 transition-transform duration-300 ease-in-out
          ${isMobile ? (isSidebarOpen ? "translate-x-0" : "-translate-x-full") : "translate-x-0"}
        `}
        aria-label="Main navigation"
      >
        <Sidebar />
      </aside>

      {/* Main Content Area */}
      <div
        className={`
          flex flex-col flex-1 min-h-screen transition-all duration-300
          ${isMobile ? "ml-0" : "ml-64"}
        `}
      >
        {/* Topbar */}
        <header
          className={`
            sticky top-0 z-30 transition-all duration-300
            ${scrolled
              ? "bg-white/95 backdrop-blur-xl shadow-md border-b border-slate-200"
              : "bg-white/80 backdrop-blur-md border-b border-slate-200/60"
            }
          `}
          role="banner"
        >
          <Topbar onMenuClick={toggleSidebar} isSidebarOpen={isSidebarOpen} />
        </header>

        {/* Page Content */}
        <main
          className="flex-1 overflow-y-auto"
          role="main"
          id="main-content"
        >
          <div className="container mx-auto p-4 sm:p-6 lg:p-8 max-w-7xl">
            {/* Content wrapper with animation */}
            <div className="animate-in fade-in slide-in-from-bottom-4 duration-500">
              <Outlet />
            </div>
          </div>
        </main>

        {/* Footer */}
        <footer
          className="mt-auto"
          role="contentinfo"
        >
          <Footer />
        </footer>
      </div>
    </div>
  );
};

export default MainLayout;
