import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import {
    ChevronLeft,
    Save,
    Loader2,
    Calendar,
    User,
    AlignLeft,
    Type,
    AlertCircle,
    Activity,
    Shield,
    Zap,
    Sparkles,
    CheckCircle2
} from "lucide-react";
import { taskService } from "../../services/taskService";
import { userService } from "../../services/userService";
import { motion, AnimatePresence } from "framer-motion";

const AddTaskPage: React.FC = () => {
    const navigate = useNavigate();
    const { id } = useParams<{ id: string }>();
    const isEditing = Boolean(id);
    const today = new Date().toISOString().split('T')[0];

    const [users, setUsers] = useState<any[]>([]);
    const [submitting, setSubmitting] = useState(false);
    const [loading, setLoading] = useState(isEditing);
    const [error, setError] = useState<string | null>(null);

    const [formData, setFormData] = useState({
        title: "",
        description: "",
        status: "Upcoming",
        priority: "Normal",
        assigned_to: "",
        due_date: today
    });

    useEffect(() => {
        const loadData = async () => {
            try {
                const [usersData, taskData] = await Promise.all([
                    userService.getAllUsers(),
                    isEditing && id ? taskService.getTaskById(Number(id)) : Promise.resolve(null)
                ]);
                setUsers(usersData);
                if (taskData) {
                    setFormData({
                        title: taskData.title,
                        description: taskData.description || "",
                        status: taskData.status === "todo" ? "Upcoming" : taskData.status === "in_progress" ? "In Progress" : taskData.status === "done" ? "Completed" : taskData.status,
                        priority: taskData.priority || "Normal",
                        assigned_to: taskData.assignee_id ? String(taskData.assignee_id) : "",
                        due_date: taskData.due_date ? taskData.due_date.split("T")[0] : today
                    });
                }
            } catch (e) {
                console.error("Failed to load data", e);
            } finally {
                setLoading(false);
            }
        };
        loadData();
    }, [id, isEditing, today]);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setError(null);
        if (formData.due_date < today) {
            setError("Temporal breach: Due date cannot be in the past.");
            return;
        }

        setSubmitting(true);
        try {
            const payload = {
                ...formData,
                status: (formData.status === "Upcoming" ? "todo" : formData.status === "In Progress" ? "in_progress" : "done") as any,
                priority: formData.priority as any,
                assigned_to: formData.assigned_to ? Number(formData.assigned_to) : undefined
            };

            if (isEditing && id) {
                await taskService.updateTask(Number(id), payload);
            } else {
                await taskService.createTask(payload);
            }
            navigate("/tasks");
        } catch (err: any) {
            console.error(err);
            setError(err.response?.data?.detail?.[0]?.msg || err.response?.data?.detail || "Sequence failure. Please retry protocol.");
            setSubmitting(false);
        }
    };

    if (loading) {
        return (
            <div className="min-h-screen flex items-center justify-center bg-[#f8fafc]">
                <div className="w-16 h-16 bg-indigo-600 rounded-3xl flex items-center justify-center animate-bounce shadow-2xl">
                    <Activity size={32} className="text-white" />
                </div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-[#f8fafc] text-slate-900 p-8 lg:p-12 overflow-hidden relative font-sans">
            {/* Background Orbs */}
            <div className="fixed top-0 right-0 w-[700px] h-[700px] bg-indigo-50/50 rounded-full blur-[150px] -mr-64 -mt-64 animate-pulse" />
            <div className="fixed bottom-0 left-0 w-[500px] h-[500px] bg-sky-50/50 rounded-full blur-[120px] -ml-48 -mb-48" />

            <div className="max-w-4xl mx-auto relative z-10">
                {/* Nav Header */}
                <div className="flex items-center justify-between mb-16">
                    <button
                        onClick={() => navigate(-1)}
                        className="group flex items-center gap-4 px-6 py-3 bg-white rounded-2xl hover:bg-slate-900 hover:text-white transition-all shadow-sm border border-slate-50"
                    >
                        <ChevronLeft size={18} className="group-hover:-translate-x-1 transition-transform" />
                        <span className="text-[10px] font-black uppercase tracking-widest">Abort Sequence</span>
                    </button>
                    <div className="flex items-center gap-3 px-5 py-2.5 bg-indigo-50 text-indigo-700 rounded-full text-[10px] font-black uppercase tracking-[0.3em] border border-indigo-100 shadow-sm">
                        <Shield size={14} className="fill-indigo-600" /> Operational Core
                    </div>
                </div>

                <div className="flex flex-col lg:flex-row gap-16">
                    {/* Left - Hero Copy */}
                    <div className="lg:w-1/3 space-y-8">
                        <h1 className="text-6xl font-[1000] tracking-tighter leading-[0.95] text-slate-900">
                            {isEditing ? 'Modify' : 'Initialize'} <br />
                            <span className="gradient-text">Unit.</span>
                        </h1>
                        <p className="text-slate-500 font-medium text-xl leading-relaxed">
                            {isEditing ? 'Recalibrate the parameters of this operational objective.' : 'Define a new mission objective and integrate it into the squad matrix.'}
                        </p>
                        <div className="pt-8 space-y-6">
                            <div className="flex items-start gap-4 p-5 bg-white rounded-[32px] border border-slate-50 shadow-sm">
                                <div className="w-10 h-10 bg-indigo-600 rounded-2xl flex items-center justify-center text-white shrink-0"><Zap size={20} /></div>
                                <div>
                                    <p className="text-[10px] font-black uppercase tracking-widest text-slate-400">Intelligence</p>
                                    <p className="text-sm font-bold text-slate-700">Real-time task synchronization.</p>
                                </div>
                            </div>
                            <div className="flex items-start gap-4 p-5 bg-white rounded-[32px] border border-slate-50 shadow-sm">
                                <div className="w-10 h-10 bg-emerald-500 rounded-2xl flex items-center justify-center text-white shrink-0"><Sparkles size={20} /></div>
                                <div>
                                    <p className="text-[10px] font-black uppercase tracking-widest text-slate-400">Velocity</p>
                                    <p className="text-sm font-bold text-slate-700">Automated tracking & alerts.</p>
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Right - Form Module */}
                    <div className="lg:w-2/3">
                        <AnimatePresence>
                            {error && (
                                <motion.div
                                    initial={{ opacity: 0, y: -20 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    exit={{ opacity: 0, y: -20 }}
                                    className="mb-8 p-6 bg-rose-50 border-2 border-rose-100 rounded-[32px] flex items-center gap-4 text-rose-600 shadow-xl shadow-rose-100/20"
                                >
                                    <AlertCircle size={24} />
                                    <p className="text-sm font-black uppercase tracking-tight">{error}</p>
                                </motion.div>
                            )}
                        </AnimatePresence>

                        <motion.div
                            initial={{ opacity: 0, x: 20 }}
                            animate={{ opacity: 1, x: 0 }}
                            className="bg-white rounded-[56px] border-2 border-slate-50 p-12 shadow-2xl shadow-indigo-500/5"
                        >
                            <form onSubmit={handleSubmit} className="space-y-10">
                                <div className="space-y-3">
                                    <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Unit Identification (Title)</label>
                                    <div className="relative group">
                                        <input
                                            type="text"
                                            value={formData.title}
                                            onChange={e => setFormData({ ...formData, title: e.target.value })}
                                            placeholder="Enter task manifest..."
                                            required
                                            className="w-full px-8 py-5 bg-slate-50/50 border-2 border-slate-50 rounded-[28px] text-slate-900 font-bold text-sm focus:bg-white focus:border-indigo-600/20 focus:outline-none transition-all placeholder:text-slate-300"
                                        />
                                        <Type size={18} className="absolute right-8 top-1/2 -translate-y-1/2 text-slate-200" />
                                    </div>
                                </div>

                                <div className="space-y-3">
                                    <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Tactical Briefing (Description)</label>
                                    <div className="relative group">
                                        <textarea
                                            value={formData.description}
                                            onChange={e => setFormData({ ...formData, description: e.target.value })}
                                            placeholder="Outline the operational goals..."
                                            rows={5}
                                            className="w-full px-8 py-5 bg-slate-50/50 border-2 border-slate-50 rounded-[32px] text-slate-900 font-bold text-sm focus:bg-white focus:border-indigo-600/20 focus:outline-none transition-all placeholder:text-slate-300 resize-none leading-relaxed"
                                        />
                                        <AlignLeft size={18} className="absolute right-8 top-8 text-slate-200" />
                                    </div>
                                </div>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                                    <div className="space-y-3">
                                        <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Assigned Operative</label>
                                        <div className="relative">
                                            <select
                                                value={formData.assigned_to}
                                                onChange={e => setFormData({ ...formData, assigned_to: e.target.value })}
                                                className="w-full px-8 py-5 bg-slate-50/50 border-2 border-slate-50 rounded-[24px] text-slate-900 font-bold text-sm focus:bg-white focus:border-indigo-600/20 focus:outline-none transition-all appearance-none cursor-pointer"
                                            >
                                                <option value="">Select Personnel</option>
                                                {users.map(u => (
                                                    <option key={u.id} value={u.id}>{u.name || u.email}</option>
                                                ))}
                                            </select>
                                            <User size={18} className="absolute right-8 top-1/2 -translate-y-1/2 text-slate-200 pointer-events-none" />
                                        </div>
                                    </div>

                                    <div className="space-y-3">
                                        <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Temporal Deadline</label>
                                        <div className="relative">
                                            <input
                                                type="date"
                                                min={today}
                                                value={formData.due_date}
                                                onChange={e => setFormData({ ...formData, due_date: e.target.value })}
                                                className="w-full px-8 py-5 bg-slate-50/50 border-2 border-slate-50 rounded-[24px] text-slate-900 font-bold text-sm focus:bg-white focus:border-indigo-600/20 focus:outline-none transition-all"
                                            />
                                            <Calendar size={18} className="absolute right-8 top-1/2 -translate-y-1/2 text-slate-200 pointer-events-none" />
                                        </div>
                                    </div>
                                </div>

                                <div className="space-y-4">
                                    <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Development Protocol</label>
                                    <div className="flex flex-wrap gap-4">
                                        {["Upcoming", "In Progress", "Completed"].map(s => (
                                            <button
                                                key={s}
                                                type="button"
                                                onClick={() => setFormData({ ...formData, status: s })}
                                                className={`px-8 py-4 rounded-2xl text-[10px] font-black uppercase tracking-widest border-2 transition-all ${formData.status === s
                                                        ? 'bg-slate-900 border-slate-900 text-white shadow-xl shadow-slate-200'
                                                        : 'bg-white border-slate-50 text-slate-400 hover:border-indigo-100 hover:text-indigo-600 shadow-sm'
                                                    }`}
                                            >
                                                {s}
                                            </button>
                                        ))}
                                    </div>
                                </div>

                                <div className="pt-8 flex items-center justify-end gap-10">
                                    <button
                                        type="button"
                                        onClick={() => navigate(-1)}
                                        className="text-[10px] font-black uppercase tracking-widest text-slate-400 hover:text-slate-900 transition-colors"
                                    >
                                        Cancel Phase
                                    </button>
                                    <motion.button
                                        whileHover={{ scale: 1.05 }}
                                        whileTap={{ scale: 0.95 }}
                                        type="submit"
                                        disabled={submitting}
                                        className="h-16 px-12 bg-indigo-600 text-white rounded-[28px] font-black uppercase tracking-widest text-[10px] shadow-2xl shadow-indigo-200 hover:bg-indigo-700 transition-all flex items-center gap-4"
                                    >
                                        {submitting ? <Loader2 size={18} className="animate-spin" /> : <Save size={18} />}
                                        {isEditing ? 'Synch Changes' : 'Commit Objective'}
                                    </motion.button>
                                </div>
                            </form>
                        </motion.div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AddTaskPage;
