import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {
  FileText,
  FileSpreadsheet,
  Presentation,
  Image as ImageIcon,
  File,
  Video,
  Upload,
  Search,
  MoreHorizontal,
  Download,
  Trash2,
  Clock,
  ChevronRight,
  HardDrive
} from "lucide-react";
import { fileService, FileData } from "../../services/fileService";

const FilesPage: React.FC = () => {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState("");
  const [recentFiles, setRecentFiles] = useState<FileData[]>([]);
  const [loading, setLoading] = useState(true);

  const getFileCounts = (files: FileData[]) => {
    const counts = { word: 0, pdf: 0, spread: 0, image: 0, other: 0 };
    files.forEach(f => {
      const ext = f.filename.split('.').pop()?.toLowerCase() || '';
      if (['doc', 'docx'].includes(ext)) counts.word++;
      else if (['pdf'].includes(ext)) counts.pdf++;
      else if (['xls', 'xlsx', 'csv'].includes(ext)) counts.spread++;
      else if (['jpg', 'jpeg', 'png', 'svg', 'gif'].includes(ext)) counts.image++;
      else counts.other++;
    });
    return counts;
  };

  const counts = getFileCounts(recentFiles);

  const fileCategories = [
    { name: "Word", count: counts.word, icon: <FileText size={20} />, color: "bg-blue-100 text-blue-600" },
    { name: "PDFs", count: counts.pdf, icon: <FileText size={20} />, color: "bg-rose-100 text-rose-600" },
    { name: "Spreadsheets", count: counts.spread, icon: <FileSpreadsheet size={20} />, color: "bg-emerald-100 text-emerald-600" },
    { name: "Images", count: counts.image, icon: <ImageIcon size={20} />, color: "bg-purple-100 text-purple-600" },
    { name: "Others", count: counts.other, icon: <File size={20} />, color: "bg-slate-100 text-slate-600" },
  ];

  useEffect(() => {
    const fetchFiles = async () => {
      try {
        const files = await fileService.getAllFiles();
        setRecentFiles(files);
      } catch (error) {
        console.error("Failed to load files", error);
      } finally {
        setLoading(false);
      }
    };
    fetchFiles();
  }, []);

  const formatSize = (bytes: number) => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB', 'GB', 'TB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + ' ' + sizes[i];
  };

  const mapFileType = (ftype: string | undefined, filename: string) => {
    const ext = filename.split('.').pop()?.toLowerCase() || '';
    if (['doc', 'docx'].includes(ext)) return <FileText size={20} className="text-blue-600" />;
    if (['pdf'].includes(ext)) return <FileText size={20} className="text-rose-600" />;
    if (['xls', 'xlsx', 'csv'].includes(ext)) return <FileSpreadsheet size={20} className="text-emerald-600" />;
    if (['jpg', 'jpeg', 'png', 'svg', 'gif'].includes(ext)) return <ImageIcon size={20} className="text-purple-600" />;
    if (['mp4', 'mov', 'avi'].includes(ext)) return <Video size={20} className="text-indigo-600" />;
    return <File size={20} className="text-slate-400" />;
  };

  return (
    <div className="max-w-7xl mx-auto space-y-8 animate-in fade-in duration-500">

      {/* Header */}
      <div className="flex flex-col md:flex-row md:items-center justify-between gap-6">
        <div>
          <h1 className="text-2xl font-bold text-slate-900 flex items-center gap-2">
            <HardDrive className="text-slate-400" size={28} /> Files
          </h1>
          <p className="text-slate-500 mt-1">Manage, share, and track your team's assets.</p>
        </div>

        <div className="flex items-center gap-3">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400" size={18} />
            <input
              type="text"
              placeholder="Search files..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10 pr-4 py-2 border border-slate-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent w-64"
            />
          </div>
          <button
            onClick={() => navigate("/files/upload")}
            className="flex items-center gap-2 bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition-colors shadow-sm"
          >
            <Upload size={18} />
            <span>Upload</span>
          </button>
        </div>
      </div>

      {/* Categories */}
      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-6 gap-4">
        {fileCategories.map((cat, i) => (
          <div
            key={i}
            className="bg-white p-4 rounded-xl border border-slate-200 shadow-sm hover:border-blue-300 hover:shadow-md transition-all cursor-pointer group"
          >
            <div className="flex justify-between items-start mb-3">
              <div className={`p-2 rounded-lg ${cat.color} group-hover:scale-110 transition-transform duration-300`}>
                {cat.icon}
              </div>
              <span className="text-slate-400 group-hover:text-blue-500 transition-colors">
                <ChevronRight size={16} />
              </span>
            </div>
            <div>
              <h3 className="font-semibold text-slate-700 group-hover:text-blue-600 transition-colors">{cat.name}</h3>
              <p className="text-xs text-slate-500 mt-1">{cat.count} files</p>
            </div>
          </div>
        ))}
      </div>

      {/* Recent Files List */}
      <div className="bg-white rounded-xl border border-slate-200 shadow-sm overflow-hidden">
        <div className="px-6 py-4 border-b border-slate-100 flex justify-between items-center bg-slate-50/50">
          <h2 className="font-semibold text-slate-800 flex items-center gap-2">
            <Clock size={18} className="text-slate-400" /> Recent Files
          </h2>
        </div>

        <div className="divide-y divide-slate-100">
          {loading ? (
            <div className="p-8 text-center text-slate-400 text-sm">Loading files...</div>
          ) : recentFiles.length === 0 ? (
            <div className="p-8 text-center text-slate-400 text-sm">No files uploaded yet.</div>
          ) : (
            recentFiles.map((file) => (
              <div
                key={file.id}
                className="p-4 flex items-center gap-4 hover:bg-slate-50 transition-colors group cursor-pointer"
              >
                {/* Icon */}
                <div className="w-10 h-10 rounded-lg bg-slate-100 flex items-center justify-center text-slate-500 group-hover:text-blue-600 group-hover:bg-blue-50 transition-colors">
                  {mapFileType(file.file_type, file.filename)}
                </div>

                {/* Info */}
                <div className="flex-1 min-w-0">
                  <h4 className="font-medium text-slate-800 truncate group-hover:text-blue-600 transition-colors">{file.filename}</h4>
                  <div className="flex items-center gap-2 text-xs text-slate-500 mt-0.5">
                    <span>{file.title || "Untitled"}</span>
                    <span>•</span>
                    <span>{formatSize(file.file_size_bytes)}</span>
                    <span>•</span>
                    <span>{new Date(file.uploaded_at).toLocaleDateString()}</span>
                  </div>
                </div>

                {/* Actions */}
                <div className="flex items-center gap-2 opacity-0 group-hover:opacity-100 transition-opacity">
                  <button className="p-2 text-slate-400 hover:text-blue-600 hover:bg-blue-50 rounded-lg transition-colors" title="Download">
                    <Download size={18} />
                  </button>
                  <button className="p-2 text-slate-400 hover:text-red-600 hover:bg-red-50 rounded-lg transition-colors" title="Delete">
                    <Trash2 size={18} />
                  </button>
                  <button className="p-2 text-slate-400 hover:text-slate-600 hover:bg-slate-100 rounded-lg transition-colors">
                    <MoreHorizontal size={18} />
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default FilesPage;
