import React, { useState } from "react";
import { Eye, EyeOff, Shield, Zap, Activity, ChevronRight, Mail, Lock, User, Sparkles, Loader2, ArrowUpRight, UserPlus, CheckCircle2 } from "lucide-react";
import { Link, useNavigate } from "react-router-dom";
import { authService } from "../../services/authService";
import { motion, AnimatePresence } from "framer-motion";

const RegisterPage: React.FC = () => {
    const [showPassword, setShowPassword] = useState(false);
    const [showConfirmPassword, setShowConfirmPassword] = useState(false);
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        password: "",
        confirmPassword: "",
    });
    const [isLoading, setIsLoading] = useState(false);
    const navigate = useNavigate();

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        if (formData.password !== formData.confirmPassword) {
            alert("Passwords do not match. Please verify your encryption keys.");
            return;
        }
        setIsLoading(true);
        try {
            await authService.register({
                email: formData.email,
                name: formData.name,
                password: formData.password
            });
            navigate("/login");
        } catch (error: any) {
            console.error("Registration failed:", error);
            alert(error.response?.data?.detail || "Registration failed. Node rejection.");
        } finally {
            setIsLoading(false);
        }
    };

    <div className="min-h-screen bg-slate-50 relative flex flex-col items-center justify-center p-6 font-sans overflow-hidden">

        {/* Dynamic Background */}
        <div className="absolute inset-0 overflow-hidden pointer-events-none">
            <div className="absolute top-[-10%] left-[-10%] w-[50%] h-[50%] bg-gradient-to-tr from-indigo-500/20 to-purple-500/20 rounded-full blur-[120px] animate-float" />
            <div className="absolute bottom-[-10%] right-[-10%] w-[50%] h-[50%] bg-gradient-to-bl from-blue-500/20 to-cyan-500/20 rounded-full blur-[120px]" />
        </div>

        <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, ease: "easeOut" }}
            className="w-full max-w-[500px] relative z-10"
        >
            <div className="text-center mb-10">
                <div className="inline-flex items-center justify-center w-16 h-16 rounded-[24px] bg-gradient-to-tr from-indigo-600 to-violet-600 shadow-xl shadow-indigo-200 mb-6 group hover:scale-110 transition-transform duration-500">
                    <UserPlus className="text-white w-8 h-8" />
                </div>
                <h1 className="text-3xl font-bold tracking-tight text-slate-900 mb-2">Create Account</h1>
                <p className="text-slate-500 font-medium">Join the workspace and start collaborating today.</p>
            </div>

            <div className="glass-card p-10 rounded-[32px] shadow-2xl shadow-indigo-900/10">
                <form onSubmit={handleSubmit} className="space-y-5">
                    <div className="space-y-2">
                        <label className="text-xs font-bold text-slate-500 uppercase tracking-wider ml-1">Full Name</label>
                        <div className="relative group">
                            <User className="absolute left-5 top-1/2 -translate-y-1/2 text-slate-400 group-focus-within:text-indigo-600 transition-colors" size={18} />
                            <input
                                name="name"
                                type="text"
                                value={formData.name}
                                onChange={handleChange}
                                className="input-premium pl-12"
                                placeholder="John Doe"
                                required
                            />
                        </div>
                    </div>

                    <div className="space-y-2">
                        <label className="text-xs font-bold text-slate-500 uppercase tracking-wider ml-1">Email</label>
                        <div className="relative group">
                            <Mail className="absolute left-5 top-1/2 -translate-y-1/2 text-slate-400 group-focus-within:text-indigo-600 transition-colors" size={18} />
                            <input
                                name="email"
                                type="email"
                                value={formData.email}
                                onChange={handleChange}
                                className="input-premium pl-12"
                                placeholder="name@work.com"
                                required
                            />
                        </div>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-5">
                        <div className="space-y-2">
                            <label className="text-xs font-bold text-slate-500 uppercase tracking-wider ml-1">Password</label>
                            <div className="relative group">
                                <Lock className="absolute left-5 top-1/2 -translate-y-1/2 text-slate-400 group-focus-within:text-indigo-600 transition-colors" size={18} />
                                <input
                                    name="password"
                                    type={showPassword ? "text" : "password"}
                                    value={formData.password}
                                    onChange={handleChange}
                                    className="input-premium pl-12 pr-10"
                                    placeholder="••••••••"
                                    required
                                />
                            </div>
                        </div>
                        <div className="space-y-2">
                            <label className="text-xs font-bold text-slate-500 uppercase tracking-wider ml-1">Confirm</label>
                            <div className="relative group">
                                <Lock className="absolute left-5 top-1/2 -translate-y-1/2 text-slate-400 group-focus-within:text-indigo-600 transition-colors" size={18} />
                                <input
                                    name="confirmPassword"
                                    type={showConfirmPassword ? "text" : "password"}
                                    value={formData.confirmPassword}
                                    onChange={handleChange}
                                    className="input-premium pl-12 pr-10"
                                    placeholder="••••••••"
                                    required
                                />
                            </div>
                        </div>
                    </div>

                    <button
                        disabled={isLoading}
                        type="submit"
                        className="w-full btn-premium h-14 text-sm font-bold shadow-lg shadow-indigo-200 hover:shadow-indigo-300 group mt-4"
                    >
                        {isLoading ? (
                            <Loader2 className="animate-spin" size={20} />
                        ) : (
                            <>
                                Create Account
                                <ArrowUpRight size={18} className="group-hover:translate-x-0.5 group-hover:-translate-y-0.5 transition-transform" />
                            </>
                        )}
                    </button>
                </form>

                <div className="mt-8 pt-6 border-t border-slate-100 text-center">
                    <p className="text-sm font-medium text-slate-500">
                        Already have an account?{" "}
                        <Link to="/login" className="text-indigo-600 font-bold hover:text-indigo-700 hover:underline">
                            Sign in
                        </Link>
                    </p>
                </div>
            </div>

            <div className="absolute bottom-[-60px] left-0 w-full text-center">
                <p className="text-xs font-semibold text-slate-400">© 2024 Collabryta Workspace.</p>
            </div>
        </motion.div>
    </div>

};

export default RegisterPage;
