import React, { useEffect, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Users, Mail, Shield, MoreVertical, RefreshCw, Activity, Zap, Star, ShieldCheck, Plus, Search } from "lucide-react";
import { userService, User } from "../../services/userService";

const TeamPage: React.FC = () => {
    const [members, setMembers] = useState<User[]>([]);
    const [loading, setLoading] = useState(true);
    const [searchQuery, setSearchQuery] = useState("");

    const fetchTeam = async () => {
        try {
            setLoading(true);
            const data = await userService.getAllUsers();
            setMembers(data);
        } catch (error) {
            console.error("Failed to fetch team", error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchTeam();
        const interval = setInterval(fetchTeam, 60000);
        return () => clearInterval(interval);
    }, []);

    const onlineCount = members.filter(m => m.status === 'Online').length;
    const adminCount = members.filter(m => m.role === 'Admin').length;

    const getInitials = (name: string) => {
        return name?.split(" ").map(n => n[0]).join("").toUpperCase().substring(0, 2) || "U";
    };

    const containerVariants = {
        hidden: { opacity: 0 },
        show: {
            opacity: 1,
            transition: { staggerChildren: 0.1 }
        }
    };

    const itemVariants = {
        hidden: { opacity: 0, y: 20 },
        show: { opacity: 1, y: 0 }
    };

    const filteredMembers = members.filter(m =>
        m.name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        m.email?.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <div className="min-h-screen bg-[#f8fafc] text-slate-900 p-8 lg:p-12 overflow-hidden relative font-sans">
            {/* Background Orbs */}
            <div className="fixed top-0 right-0 w-[700px] h-[700px] bg-indigo-50/50 rounded-full blur-[150px] -mr-64 -mt-64 animate-pulse" />
            <div className="fixed bottom-0 left-0 w-[500px] h-[500px] bg-sky-50/50 rounded-full blur-[120px] -ml-48 -mb-48" />

            <div className="max-w-[1400px] mx-auto relative z-10">

                {/* Elite Header */}
                <div className="flex flex-col lg:flex-row lg:items-end justify-between gap-12 mb-20">
                    <div className="space-y-6">
                        <motion.div
                            initial={{ opacity: 0, x: -20 }}
                            animate={{ opacity: 1, x: 0 }}
                            className="inline-flex items-center gap-2 px-4 py-2 bg-indigo-50 text-indigo-700 rounded-full text-[10px] font-black uppercase tracking-[0.3em] border border-indigo-100 shadow-sm"
                        >
                            <ShieldCheck size={14} className="fill-indigo-600" /> Authorized Personnel
                        </motion.div>
                        <h1 className="text-7xl font-[1000] tracking-tight leading-[0.95] text-slate-900">
                            Squad <span className="gradient-text">Intelligence.</span>
                        </h1>
                        <p className="text-slate-500 font-medium text-xl max-w-xl leading-relaxed">
                            Orchestrate and manage the high-performance operatives powering your project matrix.
                        </p>
                    </div>

                    <div className="flex items-center gap-6">
                        <div className="relative group">
                            <Search className="absolute left-6 top-1/2 -translate-y-1/2 text-slate-400 group-focus-within:text-indigo-600 transition-colors" size={18} />
                            <input
                                className="w-72 bg-white border-2 border-slate-100 rounded-[24px] py-4 pl-14 pr-8 text-sm font-bold text-slate-900 focus:bg-white focus:border-indigo-600/20 focus:outline-none transition-all placeholder:text-slate-300 shadow-sm"
                                placeholder="Locate Operative..."
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                            />
                        </div>
                        <motion.button
                            whileHover={{ scale: 1.05 }}
                            whileTap={{ scale: 0.95 }}
                            onClick={fetchTeam}
                            className="h-16 w-16 bg-white border-2 border-slate-100 text-slate-400 rounded-[24px] flex items-center justify-center hover:border-indigo-600 hover:text-indigo-600 transition-all shadow-sm"
                        >
                            <RefreshCw size={20} className={loading ? "animate-spin" : ""} />
                        </motion.button>
                        <motion.button
                            whileHover={{ scale: 1.05 }}
                            whileTap={{ scale: 0.95 }}
                            className="h-16 px-12 bg-slate-900 text-white rounded-[28px] font-black uppercase tracking-widest text-[10px] shadow-2xl shadow-slate-200 hover:bg-indigo-600 transition-all flex items-center gap-4"
                        >
                            <Plus size={20} className="stroke-[3]" /> Induct Member
                        </motion.button>
                    </div>
                </div>

                {/* Status Summary Cards */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-16">
                    <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ delay: 0.1 }} className="bg-white p-10 rounded-[48px] border-2 border-slate-50 shadow-xl shadow-slate-200/50 group">
                        <div className="flex items-center justify-between mb-8">
                            <div className="w-14 h-14 bg-indigo-50 text-indigo-600 rounded-2xl flex items-center justify-center group-hover:scale-110 group-hover:rotate-6 transition-transform">
                                <Users size={24} />
                            </div>
                            <span className="text-[10px] font-black text-slate-300 uppercase tracking-widest">Total Squad</span>
                        </div>
                        <p className="text-5xl font-[1000] text-slate-900 tracking-tighter">{members.length}</p>
                        <p className="text-[10px] font-black text-slate-400 uppercase tracking-widest mt-4">Registered Operatives</p>
                    </motion.div>

                    <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ delay: 0.2 }} className="bg-slate-900 p-10 rounded-[48px] shadow-2xl shadow-indigo-500/10 group relative overflow-hidden">
                        <div className="absolute top-0 right-0 w-32 h-32 bg-indigo-500/10 rounded-full blur-3xl -mr-16 -mt-16" />
                        <div className="flex items-center justify-between mb-8 relative z-10">
                            <div className="w-14 h-14 bg-white/10 text-indigo-400 rounded-2xl flex items-center justify-center group-hover:scale-110 group-hover:rotate-6 transition-transform">
                                <Activity size={24} />
                            </div>
                            <span className="text-[10px] font-black text-white/30 uppercase tracking-widest">Live Pulse</span>
                        </div>
                        <p className="text-5xl font-[1000] text-white tracking-tighter">{onlineCount}</p>
                        <p className="text-[10px] font-black text-indigo-400 uppercase tracking-widest mt-4">Online Now</p>
                    </motion.div>

                    <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ delay: 0.3 }} className="bg-white p-10 rounded-[48px] border-2 border-slate-50 shadow-xl shadow-slate-200/50 group">
                        <div className="flex items-center justify-between mb-8">
                            <div className="w-14 h-14 bg-amber-50 text-amber-600 rounded-2xl flex items-center justify-center group-hover:scale-110 group-hover:rotate-6 transition-transform">
                                <Shield size={24} />
                            </div>
                            <span className="text-[10px] font-black text-slate-300 uppercase tracking-widest">Matrix Admins</span>
                        </div>
                        <p className="text-5xl font-[1000] text-slate-900 tracking-tighter">{adminCount}</p>
                        <p className="text-[10px] font-black text-slate-400 uppercase tracking-widest mt-4">Authorized Controllers</p>
                    </motion.div>
                </div>

                {/* Team Grid */}
                <div className="space-y-8">
                    <div className="flex items-center justify-between border-b border-slate-100 pb-8">
                        <h2 className="text-xs font-black uppercase tracking-[0.4em] text-slate-400">Personnel Manifest</h2>
                        <div className="flex items-center gap-2">
                            <div className="w-3 h-3 bg-indigo-600 rounded-full animate-ping" />
                            <span className="text-[10px] font-black text-indigo-600 uppercase tracking-widest">Scanning Network</span>
                        </div>
                    </div>

                    {loading && members.length === 0 ? (
                        <div className="py-40 flex flex-col items-center justify-center gap-8 text-center">
                            <div className="w-20 h-20 bg-indigo-600 rounded-[30px] flex items-center justify-center animate-bounce shadow-2xl">
                                <Activity size={32} className="text-white" />
                            </div>
                            <p className="text-[10px] font-black uppercase tracking-[0.5em] text-slate-400">Parsing Team Matrix</p>
                        </div>
                    ) : (
                        <motion.div
                            variants={containerVariants}
                            initial="hidden"
                            animate="show"
                            className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8"
                        >
                            <AnimatePresence mode="popLayout">
                                {filteredMembers.map((member) => (
                                    <motion.div
                                        layout
                                        key={member.id}
                                        variants={itemVariants}
                                        whileHover={{ y: -8, scale: 1.02 }}
                                        className="bg-white p-8 rounded-[48px] border-2 border-slate-50 shadow-xl shadow-slate-200/40 group relative overflow-hidden"
                                    >
                                        <div className="absolute top-0 right-0 p-8 opacity-0 group-hover:opacity-100 transition-opacity">
                                            <button className="p-3 bg-slate-50 text-slate-400 hover:text-indigo-600 hover:bg-white hover:shadow-xl rounded-2xl transition-all">
                                                <MoreVertical size={18} />
                                            </button>
                                        </div>

                                        <div className="flex flex-col items-center text-center space-y-6">
                                            <div className="relative">
                                                <div className={`w-24 h-24 rounded-[32px] overflow-hidden border-4 border-white shadow-2xl transition-transform group-hover:rotate-6 ${!member.avatar && 'bg-gradient-to-tr from-indigo-600 to-violet-700 flex items-center justify-center text-white text-3xl font-black'}`}>
                                                    {member.avatar ? (
                                                        <img src={member.avatar} alt={member.name} className="w-full h-full object-cover" />
                                                    ) : getInitials(member.name)}
                                                </div>
                                                <div className={`absolute -bottom-2 -right-2 w-8 h-8 rounded-full border-4 border-white shadow-lg ${member.status === 'Online' ? 'bg-emerald-500' :
                                                        member.status === 'Away' ? 'bg-amber-500' : 'bg-slate-300'
                                                    }`} />
                                            </div>

                                            <div>
                                                <h3 className="text-xl font-[1000] text-slate-900 tracking-tight leading-none group-hover:text-indigo-600 transition-colors">{member.name || 'Anonymous Operative'}</h3>
                                                <p className="text-[10px] font-black text-slate-400 uppercase tracking-widest mt-2">{member.email}</p>
                                            </div>

                                            <div className="flex items-center gap-3">
                                                <div className={`px-4 py-2 rounded-2xl border flex items-center gap-2 ${member.role === 'Admin' ? 'bg-indigo-50 border-indigo-100 text-indigo-700' : 'bg-slate-50 border-slate-100 text-slate-600'
                                                    }`}>
                                                    <Shield size={12} className={member.role === 'Admin' ? 'fill-indigo-600' : ''} />
                                                    <span className="text-[10px] font-black uppercase tracking-widest">{member.role || 'Member'}</span>
                                                </div>
                                            </div>

                                            <div className="w-full pt-6 border-t border-slate-50 flex justify-between items-center">
                                                <span className={`text-[10px] font-black uppercase tracking-widest ${member.status === 'Online' ? 'text-emerald-600' : 'text-slate-400'}`}>
                                                    {member.status || 'Offline'}
                                                </span>
                                                {member.last_seen && member.status !== 'Online' && (
                                                    <span className="text-[8px] font-black text-slate-300 uppercase tracking-tighter">
                                                        SYNC: {new Date(member.last_seen).toLocaleDateString()}
                                                    </span>
                                                )}
                                            </div>
                                        </div>
                                    </motion.div>
                                ))}
                            </AnimatePresence>
                        </motion.div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default TeamPage;
