import React from "react";
import { Bell, Menu, X, Search, Settings } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";

interface TopbarProps {
  onMenuClick: () => void;
  isSidebarOpen: boolean;
}

const Topbar: React.FC<TopbarProps> = ({ onMenuClick, isSidebarOpen }) => {
  const navigate = useNavigate();

  return (
    <header className="z-30 w-full backdrop-blur-xl bg-[#0a0f1d]/80 border border-slate-800/80 px-4 sm:px-8 py-3.5 flex justify-between items-center transition-all duration-300 rounded-[2rem] shadow-2xl">
      <div className="flex items-center gap-4 flex-1">
        <motion.button
          whileTap={{ scale: 0.95 }}
          onClick={onMenuClick}
          className="lg:hidden p-2.5 bg-slate-800/40 hover:bg-slate-700/40 text-slate-400 rounded-xl transition-colors border border-slate-700/50"
        >
          {isSidebarOpen ? <X size={18} /> : <Menu size={18} />}
        </motion.button>

        {/* üîç Midnight Search */}
        <div className="hidden md:flex items-center bg-slate-900/60 border border-slate-800 focus-within:border-blue-500/50 focus-within:bg-slate-900 px-4 py-2 rounded-2xl w-80 transition-all shadow-inner shadow-black/20">
          <Search size={16} className="text-slate-500 group-focus-within:text-blue-400 mr-2" />
          <input
            type="text"
            placeholder="Search commands (‚åòK)"
            className="bg-transparent border-none outline-none text-sm w-full text-slate-200 placeholder:text-slate-600"
          />
        </div>
      </div>

      <div className="flex items-center gap-3 sm:gap-6">
        <h1 className="hidden sm:block text-sm font-semibold text-slate-400">
          Operator: <span className="text-white bg-blue-500/20 px-2 py-0.5 rounded-lg border border-blue-500/30">Ashok-01</span>
        </h1>

        <div className="flex items-center gap-2">
          <motion.button
            whileHover={{ scale: 1.05, y: -2 }}
            whileTap={{ scale: 0.95 }}
            className="p-2.5 text-slate-400 hover:text-blue-400 hover:bg-blue-500/10 rounded-xl transition-all relative border border-transparent hover:border-blue-500/20"
          >
            <Bell size={20} />
            <span className="absolute top-2.5 right-3 w-2 h-2 bg-blue-500 rounded-full border-2 border-[#0a0f1d] shadow-[0_0_10px_rgba(37,99,235,0.8)]" />
          </motion.button>

          <motion.button
            whileHover={{ scale: 1.05, y: -2 }}
            whileTap={{ scale: 0.95 }}
            className="p-2.5 text-slate-400 hover:text-blue-400 hover:bg-blue-500/10 rounded-xl transition-all border border-transparent hover:border-blue-500/20"
          >
            <Settings size={20} />
          </motion.button>
        </div>

        <div className="h-8 w-[1px] bg-slate-800 mx-1 hidden sm:block" />

        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={() => navigate("/profile")}
          className="flex items-center gap-3 group pl-2"
        >
          <div className="w-10 h-10 rounded-2xl bg-gradient-to-br from-blue-600 to-indigo-700 flex items-center justify-center text-white font-bold shadow-lg shadow-blue-900/40 group-hover:shadow-blue-600/50 transition-all border border-white/10 overflow-hidden relative">
            <div className="absolute inset-0 bg-white/10 opacity-0 group-hover:opacity-100 transition-opacity" />
            A
          </div>
        </motion.button>
      </div>
    </header>
  );
};

export default Topbar;
