import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import {
    ChevronLeft,
    Save,
    Loader2,
    Calendar,
    User as UserIcon, // Renamed to avoid conflict with User component if any
    Type,
    AlertCircle,
    Zap,
    Sparkles,
    CheckCircle2,
    Target,
    ArrowUpRight,
    Flag,
    ChevronDown
} from "lucide-react";
import { taskService } from "../../services/taskService";
import { userService } from "../../services/userService";
import { motion, AnimatePresence } from "framer-motion";

const AddTaskPage: React.FC = () => {
    const navigate = useNavigate();
    const { id } = useParams<{ id: string }>();
    const isEditing = Boolean(id);
    const today = new Date().toISOString().split('T')[0];

    const [users, setUsers] = useState<any[]>([]);
    const [submitting, setSubmitting] = useState(false);
    const [loading, setLoading] = useState(isEditing);
    const [error, setError] = useState<string | null>(null);

    const [formData, setFormData] = useState({
        title: "",
        description: "",
        status: "Upcoming",
        priority: "Normal",
        assigned_to: "",
        due_date: today
    });

    useEffect(() => {
        const loadData = async () => {
            try {
                const [usersData, taskData] = await Promise.all([
                    userService.getAllUsers(),
                    isEditing && id ? taskService.getTaskById(Number(id)) : Promise.resolve(null)
                ]);
                setUsers(usersData);
                if (taskData) {
                    setFormData({
                        title: taskData.title,
                        description: taskData.description || "",
                        status: taskData.status === "todo" ? "Upcoming" : taskData.status === "in_progress" ? "In Progress" : taskData.status === "done" ? "Completed" : "Upcoming",
                        priority: taskData.priority || "Normal",
                        assigned_to: taskData.assignee_id ? String(taskData.assignee_id) : "",
                        due_date: taskData.due_date ? taskData.due_date.split("T")[0] : today
                    });
                }
            } catch (e) {
                console.error("Failed to load data", e);
                setError("Failed to load task data. Please try again.");
            } finally {
                setLoading(false);
            }
        };
        loadData();
    }, [id, isEditing, today]);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setError(null);
        if (formData.due_date < today) {
            setError("Due date cannot be in the past.");
            return;
        }

        setSubmitting(true);
        try {
            const payload = {
                ...formData,
                status: (formData.status === "Upcoming" ? "todo" : formData.status === "In Progress" ? "in_progress" : "done") as any,
                priority: formData.priority as any,
                assigned_to: formData.assigned_to ? Number(formData.assigned_to) : undefined
            };

            if (isEditing && id) {
                await taskService.updateTask(Number(id), payload);
            } else {
                await taskService.createTask(payload);
            }
            navigate("/tasks");
        } catch (err: any) {
            console.error(err);
            setError(err.response?.data?.detail?.[0]?.msg || err.response?.data?.detail || "Something went wrong. Please try again.");
            setSubmitting(false);
        }
    };

    const updateField = (field: string, value: any) => {
        setFormData(prev => ({ ...prev, [field]: value }));
    };

    if (loading) {
        return (
            <div className="min-h-screen flex flex-col items-center justify-center bg-[#fcfdfe]">
                <div className="w-12 h-12 border-4 border-slate-100 border-t-blue-600 rounded-full animate-spin mb-6" />
                <p className="text-sm font-semibold text-slate-400">Loading task data...</p>
            </div>
        );
    }

    return (
        <div className="min-h-screen text-slate-900 pb-20 animate-fade-in relative bg-slate-50/50">
            <div className="max-w-3xl mx-auto relative z-10 px-6 py-8">

                {/* Header Navigation */}
                <div className="flex items-center justify-between mb-8">
                    <button
                        onClick={() => navigate(-1)}
                        className="flex items-center gap-3 text-slate-500 hover:text-slate-900 transition-all font-bold text-sm group"
                    >
                        <div className="w-10 h-10 rounded-full bg-white border border-slate-200 flex items-center justify-center shadow-sm group-hover:border-slate-300 transition-all">
                            <ChevronLeft size={18} className="group-hover:-translate-x-0.5 transition-transform" />
                        </div>
                        <span className="hidden sm:inline">Back to Board</span>
                    </button>
                </div>

                {/* Title Section */}
                <div className="text-center mb-10 space-y-2">
                    <h1 className="text-4xl font-black tracking-tight text-slate-900">
                        {isEditing ? 'Edit Task' : 'New Task'}
                    </h1>
                    <p className="text-slate-500 font-medium text-lg">
                        {isEditing ? 'Update your task details below.' : 'Create a new task to track progress.'}
                    </p>
                </div>

                {/* Main Form Card */}
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ type: "spring", damping: 25 }}
                    className="bg-white rounded-[40px] shadow-xl shadow-slate-200/50 border border-slate-100 overflow-hidden"
                >
                    <AnimatePresence>
                        {error && (
                            <motion.div
                                initial={{ opacity: 0, height: 0 }}
                                animate={{ opacity: 1, height: 'auto' }}
                                exit={{ opacity: 0, height: 0 }}
                                className="bg-rose-50 border-b border-rose-100 px-8 py-4 flex items-center gap-3 text-rose-600"
                            >
                                <AlertCircle size={18} />
                                <p className="text-sm font-bold">{error}</p>
                            </motion.div>
                        )}
                    </AnimatePresence>

                    <form onSubmit={handleSubmit} className="p-8 md:p-12 space-y-8">
                        {/* Title Input */}
                        <div className="space-y-3">
                            <label htmlFor="task-title" className="text-sm font-bold text-slate-900 uppercase tracking-wider">Task Title</label>
                            <div className="relative group">
                                <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none">
                                    <Type className="text-slate-400 group-focus-within:text-blue-500 transition-colors" size={20} />
                                </div>
                                <input
                                    id="task-title"
                                    className="w-full pl-12 pr-4 py-4 bg-slate-50 border border-slate-200 rounded-2xl focus:bg-white focus:border-blue-500 focus:ring-4 focus:ring-blue-500/10 outline-none transition-all font-bold text-lg text-slate-900 placeholder:text-slate-400"
                                    value={formData.title}
                                    onChange={e => updateField('title', e.target.value)}
                                    placeholder="e.g. Redesign Homepage"
                                    required
                                    autoFocus
                                />
                            </div>
                        </div>

                        {/* Description Input */}
                        <div className="space-y-3">
                            <label htmlFor="task-description" className="text-sm font-bold text-slate-900 uppercase tracking-wider">Description</label>
                            <textarea
                                id="task-description"
                                className="w-full p-5 bg-slate-50 border border-slate-200 rounded-2xl focus:bg-white focus:border-blue-500 focus:ring-4 focus:ring-blue-500/10 outline-none transition-all font-medium text-slate-700 min-h-[140px] resize-y placeholder:text-slate-400 leading-relaxed"
                                value={formData.description}
                                onChange={e => updateField('description', e.target.value)}
                                placeholder="Add detailed requirements or notes..."
                            />
                        </div>

                        {/* Metadata Grid */}
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 pt-4">
                            <div className="space-y-3">
                                <label htmlFor="assigned-to" className="text-sm font-bold text-slate-900 uppercase tracking-wider">Assignee</label>
                                <div className="relative group">
                                    <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none">
                                        <UserIcon className="text-slate-400 group-focus-within:text-blue-500 transition-colors" size={18} />
                                    </div>
                                    <select
                                        id="assigned-to"
                                        className="w-full pl-12 pr-10 py-3.5 bg-slate-50 border border-slate-200 rounded-2xl focus:bg-white focus:border-blue-500 focus:ring-4 focus:ring-blue-500/10 outline-none appearance-none font-bold text-slate-700 cursor-pointer transition-all"
                                        value={formData.assigned_to || ''}
                                        onChange={e => updateField('assigned_to', e.target.value)}
                                    >
                                        <option value="">Unassigned</option>
                                        {users.map(u => (
                                            <option key={u.id} value={u.id}>{u.name || u.email}</option>
                                        ))}
                                    </select>
                                    <ChevronDown className="absolute right-4 top-1/2 -translate-y-1/2 text-slate-400 pointer-events-none" size={16} />
                                </div>
                            </div>

                            <div className="space-y-3">
                                <label htmlFor="due-date" className="text-sm font-bold text-slate-900 uppercase tracking-wider">Due Date</label>
                                <div className="relative group">
                                    <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none">
                                        <Calendar className="text-slate-400 group-focus-within:text-blue-500 transition-colors" size={18} />
                                    </div>
                                    <input
                                        id="due-date"
                                        type="date"
                                        min={today}
                                        className="w-full pl-12 pr-4 py-3.5 bg-slate-50 border border-slate-200 rounded-2xl focus:bg-white focus:border-blue-500 focus:ring-4 focus:ring-blue-500/10 outline-none font-bold text-slate-700 transition-all cursor-pointer"
                                        value={formData.due_date}
                                        onChange={e => updateField('due_date', e.target.value)}
                                    />
                                </div>
                            </div>
                        </div>

                        {/* Actions */}
                        <div className="pt-8 mt-4 border-t border-slate-100 flex items-center justify-between">
                            <button
                                type="button"
                                onClick={() => navigate('/tasks')}
                                className="px-6 py-3 rounded-2xl font-bold text-slate-500 hover:text-slate-900 hover:bg-slate-100 transition-all text-sm"
                            >
                                Cancel
                            </button>
                            <button
                                type="submit"
                                disabled={submitting}
                                className="btn-premium px-8 py-4 bg-slate-900 text-white rounded-2xl font-bold hover:bg-slate-800 hover:-translate-y-1 hover:shadow-lg transition-all flex items-center gap-3 disabled:opacity-70 disabled:hover:translate-y-0 text-sm"
                            >
                                {submitting ? (
                                    <Loader2 size={18} className="animate-spin" />
                                ) : (
                                    <>
                                        <Save size={18} />
                                        {isEditing ? 'Save Changes' : 'Create Task'}
                                    </>
                                )}
                            </button>
                        </div>
                    </form>
                </motion.div>
            </div>
        </div>
    );
};

export default AddTaskPage;
