import React, { useEffect, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Users, Mail, Shield, MoreVertical, RefreshCw, Activity, Zap, Star, ShieldCheck, Plus, Search, Loader2 } from "lucide-react";
import { userService, User } from "../../services/userService";

const TeamPage: React.FC = () => {
    const [members, setMembers] = useState<User[]>([]);
    const [loading, setLoading] = useState(true);
    const [searchQuery, setSearchQuery] = useState("");

    const fetchTeam = async () => {
        try {
            setLoading(true);
            const data = await userService.getAllUsers();
            setMembers(data);
        } catch (error) {
            console.error("Failed to fetch team", error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchTeam();
        const interval = setInterval(fetchTeam, 60000);
        return () => clearInterval(interval);
    }, []);

    const onlineCount = members.filter(m => m.status === 'Online').length;
    const adminCount = members.filter(m => m.role === 'Admin').length;

    const getInitials = (name: string) => {
        return name?.split(" ").map(n => n[0]).join("").toUpperCase().substring(0, 2) || "U";
    };

    const containerVariants = {
        hidden: { opacity: 0 },
        show: { opacity: 1, transition: { staggerChildren: 0.1 } }
    };

    const itemVariants = {
        hidden: { opacity: 0, y: 15 },
        show: { opacity: 1, y: 0 }
    };

    const filteredMembers = members.filter(m =>
        m.name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        m.email?.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <div className="min-h-screen bg-[#fcfdfe] text-slate-900 font-sans">
            <div className="max-w-[1200px] mx-auto px-6 py-10 lg:py-12 relative z-10">

                {/* Header */}
                <div className="flex flex-col md:flex-row md:items-center justify-between gap-6 mb-12">
                    <div>
                        <h1 className="text-3xl font-bold tracking-tight text-slate-900 mb-1">Team</h1>
                        <p className="text-slate-500 font-medium">Manage and collaborate with your team operatives.</p>
                    </div>

                    <div className="flex items-center gap-3">
                        <div className="relative">
                            <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={16} />
                            <input
                                className="w-64 bg-white border border-slate-100 rounded-xl py-2.5 pl-11 pr-4 text-sm font-medium focus:ring-1 focus:ring-indigo-100 outline-none shadow-sm"
                                placeholder="Search members..."
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                            />
                        </div>
                        <button
                            onClick={fetchTeam}
                            disabled={loading}
                            className="p-2.5 bg-white border border-slate-100 text-slate-400 hover:text-indigo-600 rounded-xl transition-all shadow-sm"
                        >
                            <RefreshCw size={18} className={loading ? "animate-spin" : ""} />
                        </button>
                        <button
                            className="flex items-center justify-center gap-2 bg-indigo-600 hover:bg-indigo-700 text-white px-6 py-2.5 rounded-xl font-semibold transition-all shadow-lg shadow-indigo-100"
                        >
                            <Plus size={18} />
                            <span>Invite</span>
                        </button>
                    </div>
                </div>

                {/* Stats */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-12">
                    <div className="bg-white p-8 rounded-3xl border border-slate-100 shadow-sm">
                        <div className="w-10 h-10 bg-indigo-50 text-indigo-600 rounded-xl flex items-center justify-center mb-4">
                            <Users size={20} />
                        </div>
                        <p className="text-2xl font-bold text-slate-900">{members.length}</p>
                        <p className="text-xs font-bold text-slate-400 uppercase tracking-widest mt-1">Total Members</p>
                    </div>

                    <div className="bg-slate-900 p-8 rounded-3xl shadow-xl shadow-slate-200">
                        <div className="w-10 h-10 bg-white/10 text-indigo-400 rounded-xl flex items-center justify-center mb-4">
                            <Activity size={20} />
                        </div>
                        <p className="text-2xl font-bold text-white">{onlineCount}</p>
                        <p className="text-xs font-bold text-slate-500 uppercase tracking-widest mt-1">Currently Online</p>
                    </div>

                    <div className="bg-white p-8 rounded-3xl border border-slate-100 shadow-sm">
                        <div className="w-10 h-10 bg-emerald-50 text-emerald-600 rounded-xl flex items-center justify-center mb-4">
                            <ShieldCheck size={20} />
                        </div>
                        <p className="text-2xl font-bold text-slate-900">{adminCount}</p>
                        <p className="text-xs font-bold text-slate-400 uppercase tracking-widest mt-1">Administrators</p>
                    </div>
                </div>

                {/* Team Grid */}
                <div className="space-y-6">
                    <h3 className="text-lg font-bold text-slate-900 border-b border-slate-50 pb-4">Personnel Manifest</h3>

                    {loading && members.length === 0 ? (
                        <div className="py-20 text-center">
                            <Loader2 className="w-8 h-8 text-indigo-600 animate-spin mx-auto mb-4" />
                            <p className="text-sm font-medium text-slate-400">Loading team data...</p>
                        </div>
                    ) : (
                        <motion.div
                            variants={containerVariants}
                            initial="hidden"
                            animate="show"
                            className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6"
                        >
                            <AnimatePresence mode="popLayout">
                                {filteredMembers.map((member) => (
                                    <div
                                        key={member.id}
                                        className="bg-white p-6 rounded-3xl border border-slate-100 hover:border-indigo-100 hover:shadow-sm transition-all group relative"
                                    >
                                        <button className="absolute top-4 right-4 p-2 text-slate-300 hover:text-slate-600 opacity-0 group-hover:opacity-100 transition-all">
                                            <MoreVertical size={16} />
                                        </button>

                                        <div className="flex items-center gap-5 mb-6">
                                            <div className="relative shrink-0">
                                                <div className={`w-16 h-16 rounded-2xl overflow-hidden border-4 border-slate-50 shadow-sm ${!member.avatar && 'bg-slate-100 flex items-center justify-center text-slate-400 text-xl font-bold uppercase'}`}>
                                                    {member.avatar ? (
                                                        <img src={member.avatar} alt={member.name} className="w-full h-full object-cover" />
                                                    ) : getInitials(member.name)}
                                                </div>
                                                <div className={`absolute -bottom-1 -right-1 w-4 h-4 rounded-full border-2 border-white ${member.status === 'Online' ? 'bg-emerald-500' : 'bg-slate-300'}`} />
                                            </div>
                                            <div className="min-w-0">
                                                <h3 className="text-base font-bold text-slate-900 truncate group-hover:text-indigo-600 transition-colors leading-tight mb-1">{member.name || 'Operative'}</h3>
                                                <p className="text-xs font-medium text-slate-400 truncate">{member.email}</p>
                                            </div>
                                        </div>

                                        <div className="flex items-center gap-3 mb-6">
                                            <div className={`px-3 py-1 rounded-lg text-[10px] font-bold uppercase tracking-widest border ${member.role === 'Admin' ? 'bg-indigo-50 border-indigo-100 text-indigo-600' : 'bg-slate-50 border-slate-100 text-slate-500'}`}>
                                                {member.role || 'Member'}
                                            </div>
                                            <span className={`text-[10px] font-bold uppercase tracking-widest ${member.status === 'Online' ? 'text-emerald-600' : 'text-slate-400'}`}>
                                                {member.status || 'Offline'}
                                            </span>
                                        </div>

                                        <div className="pt-4 border-t border-slate-50 flex items-center justify-between">
                                            <button className="text-[11px] font-bold text-indigo-600 hover:underline">View Profile</button>
                                            <button className="p-2 text-slate-400 hover:text-indigo-600 transition-colors"><Mail size={14} /></button>
                                        </div>
                                    </div>
                                ))}
                            </AnimatePresence>
                        </motion.div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default TeamPage;
