import React, { useState, useEffect } from "react";
import {
  Plus,
  Search,
  Loader2,
  Trash2,
  Edit3,
  Calendar,
  User as UserIcon,
  Filter,
  AlertCircle,
  X,
  Clock,
  CheckCircle2
} from "lucide-react";
import { useNavigate } from "react-router-dom";
import { taskService, Task } from "../../services/taskService";
import { motion, AnimatePresence } from "framer-motion";

const TasksPage: React.FC = () => {
  const navigate = useNavigate();
  const [tasks, setTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState<string>("All");
  const [selectedTask, setSelectedTask] = useState<Task | null>(null);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    setLoading(true);
    try {
      const data = await taskService.getTasks();
      setTasks(data);
    } catch (error) {
      console.error("Failed to fetch tasks", error);
    } finally {
      setLoading(false);
    }
  };

  const normalizeStatus = (status: string) => {
    const s = status ? status.toLowerCase() : "";
    if (s === "todo" || s === "upcoming") return "Upcoming";
    if (s === "in_progress" || s === "in progress" || s === "inprogress") return "In Progress";
    if (s === "completed" || s === "done") return "Completed";
    return "Upcoming";
  };

  const getTimeStatus = (dueDate: string | null | undefined, status: string) => {
    if (normalizeStatus(status) === "Completed") return { label: "Completed", color: "text-emerald-600 bg-emerald-50 border-emerald-100" };
    if (!dueDate) return { label: "Scheduled", color: "text-slate-600 bg-slate-50 border-slate-100" };

    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const due = new Date(dueDate);
    due.setHours(0, 0, 0, 0);

    if (due < today) return { label: "Overdue", color: "text-rose-600 bg-rose-50 border-rose-100 font-bold" };
    if (due.getTime() === today.getTime()) return { label: "Due Today", color: "text-amber-600 bg-amber-50 border-amber-100 font-bold" };

    return { label: "On Track", color: "text-indigo-600 bg-indigo-50 border-indigo-100" };
  };

  const handleDelete = async (e: React.MouseEvent, id: number) => {
    e.stopPropagation();
    if (!window.confirm("Delete this task?")) return;
    try {
      await taskService.deleteTask(id);
      setTasks(prev => prev.filter(t => t.id !== id));
      if (selectedTask?.id === id) setSelectedTask(null);
    } catch (error) {
      console.error("Failed to delete task");
    }
  };

  const filteredTasks = tasks.filter(task => {
    const matchesSearch = task.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (task.description && task.description.toLowerCase().includes(searchQuery.toLowerCase()));
    const matchesStatus = statusFilter === "All" || normalizeStatus(task.status) === statusFilter;
    return matchesSearch && matchesStatus;
  });

  return (
    <div className="min-h-screen bg-slate-50 relative font-sans">

      {/* ðŸ“‹ Main Content Area */}
      <div className={`p-4 md:p-8 max-w-6xl mx-auto space-y-6 transition-all duration-300 ${selectedTask ? 'lg:mr-[420px]' : ''}`}>

        {/* Header */}
        <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
          <div>
            <h1 className="text-2xl font-bold text-slate-900 tracking-tight">Tasks</h1>
            <p className="text-slate-500 text-sm">Monitor and manage project milestones. <span className="text-indigo-400 font-medium ml-1">Note: Tasks with past due dates are automatically hidden after 24h.</span></p>
          </div>
          <button
            onClick={() => navigate("/tasks/new")}
            className="flex items-center gap-2 bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-lg font-semibold transition-all active:scale-95 shadow-sm"
          >
            <Plus size={18} /> Add Task
          </button>
        </div>

        {/* Toolbar */}
        <div className="bg-white p-3 rounded-xl border border-slate-200 shadow-sm flex flex-col md:flex-row items-center gap-3">
          <div className="relative flex-1 w-full">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400" size={18} />
            <input
              type="text"
              placeholder="Search tasks..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-10 pr-4 py-2 bg-slate-50 border border-slate-100 rounded-lg text-sm text-slate-900 focus:outline-none focus:ring-2 focus:ring-indigo-500/20"
            />
          </div>
          <div className="flex items-center gap-2 w-full md:w-auto">
            <Filter size={16} className="text-slate-400" />
            <select
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
              className="flex-1 md:flex-none bg-slate-50 border border-slate-100 rounded-lg px-3 py-2 text-sm font-medium text-slate-700 outline-none cursor-pointer hover:border-slate-300"
            >
              <option value="All">All Statuses</option>
              <option value="Upcoming">Upcoming</option>
              <option value="In Progress">In Progress</option>
              <option value="Completed">Completed</option>
            </select>
          </div>
        </div>

        {/* Task List */}
        <div className="space-y-3">
          {loading ? (
            <div className="py-20 flex flex-col items-center justify-center gap-2">
              <Loader2 className="animate-spin text-indigo-600" size={24} />
              <p className="text-slate-400 text-sm font-medium">Loading workspace...</p>
            </div>
          ) : (
            <AnimatePresence mode="popLayout">
              {filteredTasks.length === 0 ? (
                <motion.div
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  className="py-16 bg-white rounded-xl border border-slate-200 border-dashed text-center"
                >
                  <AlertCircle size={32} className="mx-auto text-slate-300 mb-2" />
                  <h3 className="text-slate-900 font-semibold">No results found</h3>
                  <p className="text-slate-500 text-sm">Try changing your search or filter settings.</p>
                </motion.div>
              ) : (
                filteredTasks.map((task, idx) => {
                  const timeStatus = getTimeStatus(task.due_date, task.status);
                  const isSelected = selectedTask?.id === task.id;
                  return (
                    <motion.div
                      key={task.id}
                      layout
                      initial={{ opacity: 0, y: 10 }}
                      animate={{ opacity: 1, y: 0 }}
                      exit={{ opacity: 0, scale: 0.98 }}
                      transition={{ delay: idx * 0.03 }}
                      onClick={() => setSelectedTask(task)}
                      className={`p-4 rounded-xl border transition-all flex flex-col md:flex-row items-center gap-4 group cursor-pointer ${isSelected ? 'bg-indigo-50 border-indigo-200 shadow-sm' : 'bg-white border-slate-200 hover:border-indigo-200 hover:shadow-sm'}`}
                    >
                      {/* Status Badge */}
                      <div className="w-full md:w-32 flex-shrink-0">
                        <span className={`px-2.5 py-1 rounded-md text-[10px] font-bold uppercase tracking-wider border block text-center ${timeStatus.color}`}>
                          {timeStatus.label}
                        </span>
                      </div>

                      {/* Info */}
                      <div className="flex-1 min-w-0 w-full">
                        <h3 className={`font-bold truncate transition-colors uppercase text-sm ${isSelected ? 'text-indigo-700' : 'text-slate-900 group-hover:text-indigo-600'}`}>
                          {task.title}
                        </h3>
                        <div className="flex items-center gap-2 mt-0.5 text-xs text-slate-500 font-medium">
                          <span className="truncate max-w-[300px]">{task.description || "No description provided."}</span>
                        </div>
                      </div>

                      {/* Attributes */}
                      <div className="flex items-center gap-6 w-full md:w-auto px-2">
                        <div className="flex items-center gap-2 text-slate-600">
                          <div className="w-6 h-6 rounded-full bg-slate-100 flex items-center justify-center text-[10px] font-bold text-slate-600 border border-white shadow-sm">
                            {task.assigned_to_user ? task.assigned_to_user.name.charAt(0) : <UserIcon size={12} />}
                          </div>
                        </div>
                        <div className="flex items-center gap-2 text-slate-500">
                          <Calendar size={14} />
                          <span className="text-xs font-semibold">{task.due_date ? new Date(task.due_date).toLocaleDateString() : "--/--"}</span>
                        </div>
                      </div>

                      {/* Quick Actions (Prevent Propagation) */}
                      <div className="flex items-center gap-1 w-full md:w-auto justify-end border-t md:border-t-0 pt-2 md:pt-0 border-slate-50">
                        <button
                          onClick={(e) => { e.stopPropagation(); navigate(`/tasks/edit/${task.id}`); }}
                          className="p-2 text-slate-400 hover:text-indigo-600 hover:bg-slate-50 rounded-lg transition-all"
                          title="Edit"
                        >
                          <Edit3 size={16} />
                        </button>
                        <button
                          onClick={(e) => handleDelete(e, task.id)}
                          className="p-2 text-slate-400 hover:text-red-600 hover:bg-rose-50 rounded-lg transition-all"
                          title="Delete"
                        >
                          <Trash2 size={16} />
                        </button>
                      </div>
                    </motion.div>
                  );
                })
              )}
            </AnimatePresence>
          )}
        </div>
      </div>

      {/* ðŸ“‘ Fixed Slide-Over Panel */}
      <AnimatePresence>
        {selectedTask && (
          <motion.div
            initial={{ x: "100%" }}
            animate={{ x: 0 }}
            exit={{ x: "100%" }}
            transition={{ type: "spring", damping: 25, stiffness: 200 }}
            className="fixed top-[80px] right-0 w-full lg:w-[400px] h-[calc(100vh-80px)] bg-white border-l border-slate-200 shadow-2xl z-40 flex flex-col"
          >
            {/* Header */}
            <div className="p-5 border-b border-slate-100 flex items-center justify-between bg-white">
              <span className="text-xs font-bold uppercase text-slate-400 tracking-wider">Details</span>
              <button
                onClick={() => setSelectedTask(null)}
                className="p-2 -mr-2 text-slate-400 hover:text-slate-600 hover:bg-slate-50 rounded-lg transition-all"
              >
                <X size={20} />
              </button>
            </div>

            {/* Scrollable Content */}
            <div className="flex-1 overflow-y-auto p-6 space-y-6">

              {/* Title Block */}
              <div>
                <h2 className="text-xl font-bold text-slate-900 leading-snug mb-3">{selectedTask.title}</h2>
                <span className={`inline-block px-3 py-1 rounded-md text-[10px] font-bold uppercase tracking-wider border ${getTimeStatus(selectedTask.due_date, selectedTask.status).color}`}>
                  {getTimeStatus(selectedTask.due_date, selectedTask.status).label}
                </span>
              </div>

              {/* Info Grid */}
              <div className="grid grid-cols-1 gap-4 bg-slate-50 p-4 rounded-xl border border-slate-100">
                <div className="flex items-center justify-between">
                  <span className="text-xs font-semibold text-slate-500 flex items-center gap-2"><UserIcon size={14} /> Assignee</span>
                  <span className="text-sm font-bold text-slate-900">{selectedTask.assigned_to_user?.name || "Unassigned"}</span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-xs font-semibold text-slate-500 flex items-center gap-2"><Clock size={14} /> Due Date</span>
                  <span className="text-sm font-bold text-slate-900">{selectedTask.due_date ? new Date(selectedTask.due_date).toLocaleDateString() : "None"}</span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-xs font-semibold text-slate-500 flex items-center gap-2"><CheckCircle2 size={14} /> Status</span>
                  <span className="text-sm font-bold text-slate-900">{normalizeStatus(selectedTask.status)}</span>
                </div>
              </div>

              {/* Description */}
              <div className="space-y-2">
                <h3 className="text-xs font-bold text-slate-400 uppercase tracking-wide">About Task</h3>
                <div className="p-4 rounded-xl border border-slate-100 text-sm text-slate-600 leading-relaxed font-medium bg-white">
                  {selectedTask.description || "No description provided."}
                </div>
              </div>

            </div>

            {/* Footer Actions */}
            <div className="p-5 border-t border-slate-100 bg-slate-50 flex flex-col gap-3">
              <button
                onClick={() => navigate(`/tasks/edit/${selectedTask.id}`)}
                className="w-full py-3 bg-white border border-slate-200 text-slate-700 font-bold text-sm rounded-xl hover:border-indigo-300 hover:text-indigo-600 transition-all shadow-sm"
              >
                Edit Task
              </button>
              <button
                onClick={(e) => handleDelete(e, selectedTask.id)}
                className="w-full py-3 bg-red-50 border border-red-100 text-red-600 font-bold text-sm rounded-xl hover:bg-red-100 transition-all shadow-sm"
              >
                Delete Task
              </button>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default TasksPage;
