import React, { useState, useEffect, useRef, useCallback } from "react";
import {
    Search, Users, Paperclip, Smile, Send,
    Check, ArrowLeft, X, Sparkles, Zap, Shield, Plus, ArrowUpRight
} from "lucide-react";
import { messageService, Chat, Message } from "../../services/messageService";
import { userService, User } from "../../services/userService";
import { authService } from "../../services/authService";

import { motion, AnimatePresence } from "framer-motion";

const CreateGroupModal = ({ isOpen, onClose, users, onCreate }: any) => {
    const [groupName, setGroupName] = useState("");
    const [selectedIds, setSelectedIds] = useState<number[]>([]);

    const toggleUser = (id: number) => {
        setSelectedIds(prev => prev.includes(id) ? prev.filter(x => x !== id) : [...prev, id]);
    };

    const handleSubmit = () => {
        if (!groupName || selectedIds.length === 0) return;
        onCreate(groupName, selectedIds);
        onClose();
        setGroupName("");
        setSelectedIds([]);
    };

    return (
        <AnimatePresence>
            {isOpen && (
                <div className="fixed inset-0 z-[100] flex items-center justify-center p-4 h-screen w-screen overflow-hidden">
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        className="absolute inset-0 bg-black/5 backdrop-blur-sm"
                        onClick={onClose}
                    />
                    <motion.div
                        initial={{ opacity: 0, y: 10 }}
                        animate={{ opacity: 1, y: 0 }}
                        exit={{ opacity: 0, y: 10 }}
                        className="bg-white border border-zinc-100 w-full max-w-sm p-10 relative z-10 animate-fade-in"
                    >
                        <div className="flex justify-between items-center mb-10">
                            <div>
                                <p className="text-[10px] font-black tracking-[0.3em] text-zinc-400 mb-1 uppercase">ORCHESTRATE</p>
                                <h3 className="text-2xl font-bold tracking-tighter uppercase leading-none">NEW SQUAD</h3>
                            </div>
                            <button onClick={onClose} className="p-2 text-zinc-300 hover:text-black transition-all">
                                <X size={24} />
                            </button>
                        </div>

                        <div className="space-y-8">
                            <div className="space-y-3">
                                <label className="text-[10px] font-black text-zinc-400 uppercase tracking-widest">SQUAD IDENTIFIER</label>
                                <input
                                    className="w-full px-4 py-4 bg-zinc-50 border border-zinc-100 text-[11px] font-bold tracking-widest uppercase text-black outline-none focus:border-black transition-all placeholder:text-zinc-200"
                                    value={groupName}
                                    onChange={e => setGroupName(e.target.value)}
                                    placeholder="e.g. ALPHA SPRINT"
                                />
                            </div>

                            <div className="space-y-3">
                                <label className="text-[10px] font-black text-zinc-400 uppercase tracking-widest">PERSONNEL ASSIGNMENT</label>
                                <div className="max-h-[300px] overflow-y-auto space-y-px bg-zinc-100 border border-zinc-100 custom-scrollbar">
                                    {users.map((u: User) => (
                                        <button
                                            key={u.id}
                                            onClick={() => toggleUser(u.id)}
                                            className={`w-full flex items-center gap-4 p-4 transition-all bg-white hover:bg-zinc-50 ${selectedIds.includes(u.id) ? 'bg-zinc-50' : ''
                                                }`}
                                        >
                                            <div className={`w-8 h-8 flex items-center justify-center text-[9px] font-black tracking-tighter transition-all ${selectedIds.includes(u.id) ? 'bg-black text-white' : 'bg-zinc-100 text-black'
                                                }`}>
                                                {u.name.substring(0, 2).toUpperCase()}
                                            </div>
                                            <span className={`text-[11px] font-bold tracking-wider uppercase ${selectedIds.includes(u.id) ? 'text-black' : 'text-zinc-400'}`}>{u.name}</span>
                                            {selectedIds.includes(u.id) && <Check size={14} className="ml-auto text-black" />}
                                        </button>
                                    ))}
                                </div>
                            </div>

                            <div className="flex gap-px bg-zinc-100 border border-zinc-100 mt-10">
                                <button onClick={onClose} className="flex-1 py-5 bg-white text-[10px] font-black uppercase tracking-widest text-zinc-400 hover:text-black transition-all">ABORT</button>
                                <button
                                    onClick={handleSubmit}
                                    disabled={!groupName || selectedIds.length === 0}
                                    className="flex-1 py-5 bg-black text-white text-[10px] font-black uppercase tracking-widest disabled:opacity-20 hover:bg-zinc-800 transition-all"
                                >
                                    ESTABLISH
                                </button>
                            </div>
                        </div>
                    </motion.div>
                </div>
            )}
        </AnimatePresence>
    );
};

const MessagesPage: React.FC = () => {
    const [chats, setChats] = useState<Chat[]>([]);
    const [messages, setMessages] = useState<Message[]>([]);
    const [currentUser, setCurrentUser] = useState<any>(null);
    const [activeChatId, setActiveChatId] = useState<number | null>(null);
    const [searchQuery, setSearchQuery] = useState("");
    const [allUsers, setAllUsers] = useState<User[]>([]);
    const [inputText, setInputText] = useState("");
    const [isMobileChatOpen, setIsMobileChatOpen] = useState(false);
    const [isGroupModalOpen, setIsGroupModalOpen] = useState(false);

    const messagesContainerRef = useRef<HTMLDivElement>(null);

    const loadChats = useCallback(async () => {
        try {
            const data = await messageService.getChats();
            setChats(data);
        } catch (e) {
            console.error("Failed to load chats:", e);
        }
    }, []);

    const loadUserAndData = useCallback(async () => {
        try {
            const user = await authService.getCurrentUser();
            setCurrentUser(user);
            await loadChats();
            const users = await userService.getAllUsers();
            setAllUsers(users.filter(u => u.id !== user.id));
        } catch (e) {
            console.error("Failed to load user data:", e);
        }
    }, [loadChats]);

    const loadMessages = useCallback(async (chatId: number) => {
        try {
            const msgs = await messageService.getMessages(chatId);
            setMessages(msgs);
            await messageService.markAsRead(chatId);
            loadChats();
        } catch (e) {
            console.error("Failed to load messages:", e);
        }
    }, [loadChats]);

    const scrollToBottom = useCallback((smooth = true) => {
        if (messagesContainerRef.current) {
            const { scrollHeight, clientHeight } = messagesContainerRef.current;
            messagesContainerRef.current.scrollTo({
                top: scrollHeight - clientHeight,
                behavior: smooth ? 'smooth' : 'auto'
            });
        }
    }, []);

    useEffect(() => {
        loadUserAndData();
    }, [loadUserAndData]);

    useEffect(() => {
        const pollInterval = setInterval(() => {
            loadChats();
            if (activeChatId) {
                loadMessages(activeChatId);
            }
        }, 10000); // Poll every 10 seconds for messages

        return () => {
            clearInterval(pollInterval);
        };
    }, [activeChatId, loadChats, loadMessages]);

    useEffect(() => {
        if (activeChatId) {
            loadMessages(activeChatId);
            setTimeout(() => scrollToBottom(false), 100);
        }
    }, [activeChatId, loadMessages, scrollToBottom]);

    useEffect(() => {
        scrollToBottom();
    }, [messages, scrollToBottom]);

    const handleSendMessage = async () => {
        if (!inputText.trim() || !activeChatId) return;
        try {
            await messageService.sendMessage(activeChatId, inputText);
            setInputText("");
            loadMessages(activeChatId);
            loadChats();
            setTimeout(() => scrollToBottom(), 100);
        } catch (e) {
            console.error("Failed to send message:", e);
        }
    };

    const handleCreateP2PChat = async (userId: number) => {
        try {
            const chat = await messageService.createChat([userId], undefined, false);
            setChats(prev => prev.find(c => c.id === chat.id) ? prev : [chat, ...prev]);
            setActiveChatId(chat.id);
            setIsMobileChatOpen(true);
            setSearchQuery("");
        } catch (e) {
            console.error("Failed to create P2P chat:", e);
        }
    };

    const handleCreateGroup = async (name: string, userIds: number[]) => {
        try {
            const chat = await messageService.createChat(userIds, name, true);
            setChats(prev => [chat, ...prev]);
            setActiveChatId(chat.id);
            setIsMobileChatOpen(true);
        } catch (e) {
            console.error("Failed to create group chat:", e);
        }
    };

    const filteredChats = chats.filter(c => c.name?.toLowerCase().includes(searchQuery.toLowerCase()));
    const suggestedUsers = searchQuery ? allUsers.filter(u => u.name.toLowerCase().includes(searchQuery.toLowerCase())) : [];
    const activeChat = chats.find(c => c.id === activeChatId);

    return (
        <div className="flex h-screen bg-white overflow-hidden animate-fade-in">
            <CreateGroupModal
                isOpen={isGroupModalOpen}
                onClose={() => setIsGroupModalOpen(false)}
                users={allUsers}
                onCreate={handleCreateGroup}
            />

            {/* Sidebar */}
            <div className={`w-full md:w-[350px] lg:w-[450px] bg-white border-r border-zinc-100 flex flex-col ${isMobileChatOpen ? 'hidden md:flex' : 'flex'}`}>
                <div className="p-10 lg:p-12 border-b border-zinc-100 flex justify-between items-end">
                    <div>
                        <p className="text-[10px] font-black tracking-[0.3em] text-zinc-400 mb-2 uppercase">INTEL / ARCHIVE</p>
                        <h1 className="text-4xl font-bold tracking-tighter uppercase leading-none">MESSAGES</h1>
                    </div>
                    <button
                        onClick={() => setIsGroupModalOpen(true)}
                        className="p-3 bg-black text-white hover:bg-zinc-800 transition-all active:scale-95"
                        title="New Squad"
                    >
                        <Plus size={20} />
                    </button>
                </div>

                <div className="px-10 lg:px-12 py-8 border-b border-zinc-100">
                    <div className="relative">
                        <Search className="absolute left-0 top-1/2 -translate-y-1/2 text-zinc-300" size={14} />
                        <input
                            className="w-full bg-transparent border-none py-3 pl-8 pr-4 text-[11px] font-bold tracking-widest text-black focus:ring-0 outline-none transition-all placeholder:text-zinc-200 uppercase"
                            placeholder="SEARCH CHANNELS..."
                            value={searchQuery}
                            onChange={e => setSearchQuery(e.target.value)}
                        />
                    </div>
                </div>

                <div className="flex-1 overflow-y-auto space-y-px bg-zinc-100 divide-y divide-zinc-100 custom-scrollbar">
                    {filteredChats.map(chat => (
                        <div
                            key={chat.id}
                            onClick={() => { setActiveChatId(chat.id); setIsMobileChatOpen(true); }}
                            className={`p-10 lg:p-12 flex items-center gap-8 cursor-pointer transition-all bg-white hover:bg-zinc-50 ${activeChatId === chat.id ? 'bg-zinc-50' : ''
                                }`}
                        >
                            <div className={`w-14 h-14 flex items-center justify-center font-black text-[13px] tracking-tighter shrink-0 transition-all ${activeChatId === chat.id ? 'bg-black text-white px-2' : 'bg-zinc-100 text-black px-2'
                                }`}>
                                {chat.is_group ? <Users size={20} /> : (chat.name?.substring(0, 2).toUpperCase())}
                            </div>

                            <div className="flex-1 min-w-0">
                                <div className="flex justify-between items-end mb-2">
                                    <h3 className={`text-[13px] font-bold tracking-wider uppercase truncate ${activeChatId === chat.id ? 'text-black' : 'text-black'}`}>{chat.name}</h3>
                                    <span className="text-[9px] font-black tracking-widest text-zinc-300 uppercase shrink-0 ml-4">
                                        {chat.last_message_time ? new Date(chat.last_message_time).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }) : ''}
                                    </span>
                                </div>
                                <div className="flex justify-between items-center">
                                    <p className="text-[11px] font-medium text-zinc-400 uppercase tracking-tight truncate mr-6">{chat.last_message || "CHANNEL INITIALIZED"}</p>
                                    {chat.unread_count && chat.unread_count > 0 && (
                                        <div className="bg-black text-white px-2 py-0.5 text-[9px] font-black tracking-widest">
                                            {chat.unread_count} LUX
                                        </div>
                                    )}
                                </div>
                            </div>
                        </div>
                    ))}

                    {suggestedUsers.length > 0 && (
                        <div className="bg-zinc-100 p-px">
                            <div className="bg-white px-10 pt-10 pb-4">
                                <h4 className="text-[9px] font-black text-zinc-300 uppercase tracking-[0.4em]">NEURAL COORDINATES</h4>
                            </div>
                            <div className="divide-y divide-zinc-50">
                                {suggestedUsers.map(user => (
                                    <div
                                        key={user.id}
                                        onClick={() => handleCreateP2PChat(user.id)}
                                        className="p-10 bg-white hover:bg-zinc-50 transition-all flex items-center justify-between cursor-pointer group"
                                    >
                                        <div className="flex items-center gap-6">
                                            <div className="w-10 h-10 bg-zinc-100 flex items-center justify-center text-black font-black text-[10px] tracking-tighter group-hover:bg-black group-hover:text-white transition-all">
                                                {user.name.substring(0, 2).toUpperCase()}
                                            </div>
                                            <span className="text-[11px] font-bold text-black uppercase tracking-widest">{user.name}</span>
                                        </div>
                                        <ArrowUpRight size={16} className="text-zinc-200 group-hover:text-black transition-all" />
                                    </div>
                                ))}
                            </div>
                        </div>
                    )}
                </div>
            </div>

            {/* Chat Area */}
            {activeChat ? (
                <div className={`flex-1 flex flex-col bg-white ${isMobileChatOpen ? 'flex' : 'hidden md:flex'}`}>
                    {/* Chat Header */}
                    <div className="h-24 bg-white border-b border-zinc-100 flex items-center justify-between px-10 lg:px-12 shrink-0">
                        <div className="flex items-center gap-8">
                            <button onClick={() => setIsMobileChatOpen(false)} className="md:hidden p-2 text-zinc-300 hover:text-black transition-all">
                                <ArrowLeft size={24} />
                            </button>
                            <div className="relative">
                                <div className={`w-14 h-14 flex items-center justify-center font-black text-[13px] tracking-tighter shrink-0 transition-all px-2 ${activeChat.is_group ? 'bg-black text-white' : 'bg-zinc-100 text-black'}`}>
                                    {activeChat.is_group ? <Users size={24} /> : activeChat.name?.substring(0, 2).toUpperCase()}
                                </div>
                            </div>
                            <div>
                                <h2 className="text-xl font-bold text-black tracking-tighter uppercase leading-none mb-1.5">{activeChat.name}</h2>
                                <p className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.2em] flex items-center gap-2">
                                    <span className="w-1.5 h-1.5 bg-black rounded-none"></span> ACTIVE TRANSMISSION
                                </p>
                            </div>
                        </div>
                        <div className="flex items-center gap-6">
                            <button className="hidden sm:flex items-center gap-2 text-[9px] font-black tracking-widest uppercase text-zinc-300 hover:text-black transition-all border border-zinc-100 px-4 py-2">
                                <Shield size={12} />
                                SECURE
                            </button>
                        </div>
                    </div>

                    {/* Chat Messages */}
                    <div className="flex-1 relative flex flex-col overflow-hidden">
                        <div ref={messagesContainerRef} className="flex-1 overflow-y-auto p-10 lg:p-12 space-y-12 custom-scrollbar">
                            <AnimatePresence mode="popLayout">
                                {messages.map((msg, idx) => {
                                    const isMe = msg.sender_id === currentUser?.id;
                                    const showSender = !isMe && activeChat.is_group && (idx === 0 || messages[idx - 1].sender_id !== msg.sender_id);

                                    return (
                                        <motion.div
                                            initial={{ opacity: 0, y: 5 }}
                                            animate={{ opacity: 1, y: 0 }}
                                            key={msg.id}
                                            className={`flex w-full ${isMe ? 'justify-end' : 'justify-start'}`}
                                        >
                                            <div className={`flex flex-col max-w-[80%] ${isMe ? 'items-end' : 'items-start'}`}>
                                                {showSender && <span className="text-[9px] font-black text-zinc-300 uppercase tracking-[0.2em] mb-2 px-1">{msg.sender?.name}</span>}
                                                <div className={`p-6 text-[13px] font-medium leading-relaxed tracking-tight uppercase ${isMe
                                                    ? 'bg-black text-white'
                                                    : 'bg-zinc-50 text-black'
                                                    }`}>
                                                    <p>{msg.content}</p>
                                                </div>
                                                <div className="flex items-center gap-3 mt-3 px-1">
                                                    <span className="text-[9px] font-black text-zinc-300 uppercase tracking-[0.2em]">
                                                        {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                                                    </span>
                                                    {isMe && <Check size={12} className="text-zinc-200" />}
                                                </div>
                                            </div>
                                        </motion.div>
                                    );
                                })}
                            </AnimatePresence>
                        </div>
                    </div>

                    {/* Chat Input */}
                    <div className="p-10 lg:p-12 bg-white border-t border-zinc-100">
                        <div className="flex items-center gap-6 max-w-6xl mx-auto">
                            <button className="p-4 text-zinc-300 hover:text-black transition-all border border-zinc-100"><Paperclip size={20} /></button>
                            <div className="flex-1 relative border-b border-zinc-200 focus-within:border-black transition-all">
                                <input
                                    className="w-full py-4 bg-transparent border-none text-[11px] font-bold tracking-widest text-black focus:ring-0 outline-none transition-all placeholder:text-zinc-200 uppercase"
                                    placeholder="TYPE TRANSMISSION..."
                                    value={inputText}
                                    onChange={e => setInputText(e.target.value)}
                                    onKeyDown={e => e.key === 'Enter' && handleSendMessage()}
                                />
                            </div>
                            <button
                                onClick={handleSendMessage}
                                disabled={!inputText.trim()}
                                className="p-5 bg-black text-white hover:bg-zinc-800 transition-all disabled:opacity-20 active:scale-95"
                            >
                                <Send size={20} />
                            </button>
                        </div>
                    </div>
                </div>
            ) : (
                <div className="hidden md:flex flex-1 flex-col items-center justify-center bg-zinc-50/30">
                    <div className="w-20 h-20 bg-white border border-zinc-100 flex items-center justify-center mb-8">
                        <MessageSquare size={32} className="text-zinc-100" />
                    </div>
                    <h3 className="text-xl font-bold text-black tracking-tighter uppercase mb-2">INITIALIZE ARCHIVE</h3>
                    <p className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.3em]">SELECT A CHANNEL TO BEGIN TRANSMISSION</p>
                </div>
            )}
        </div>
    );
};

const MessageSquare = ({ size, className }: any) => <svg width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M21 15a2 2 0 0 1-2 2H7l-4 4V5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2z" /></svg>;

export default MessagesPage;
