// Mock data for now
export interface Notification {
    id: number;
    title: string;
    message: string;
    timestamp: string;
    read: boolean;
    type: 'info' | 'success' | 'warning' | 'error';
}

export const notificationService = {
    getNotifications: async (): Promise<Notification[]> => {
        return [
            { id: 1, title: "New Task Assigned", message: "You have been assigned to 'Update Homepage'", timestamp: new Date().toISOString(), read: false, type: 'info' },
            { id: 2, title: "System Update", message: "Maintenance scheduled for tonight", timestamp: new Date(Date.now() - 86400000).toISOString(), read: true, type: 'warning' }
        ];
    },

    markAsRead: async (id: number) => {
        console.log(`Notification ${id} marked as read`);
        return true;
    }
};
