import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Calendar as CalendarIcon,
  MessageSquare,
  CheckCircle,
  Clock,
  ArrowRight,
  Activity,
  Zap,
  Star,
  Shield,
  Layers
} from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import { taskService, Task } from "../../services/taskService";
import { meetingService } from "../../services/meetingService";
import { messageService, Chat } from "../../services/messageService";
import { userService } from "../../services/userService";
import { authService } from "../../services/authService";
import { webSocketService, WebSocketMessage } from "../../services/websocketService";

interface DashboardEvent {
  id: string;
  title: string;
  time: string;
  type: "meeting" | "task";
  status?: string;
  details?: string;
  timestamp: Date;
}

const DashboardPage: React.FC = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState<any>(null);
  const [stats, setStats] = useState({
    tasksPending: 0,
    meetingsToday: 0,
    unreadMessages: 0,
    teamMembers: 0,
    tasksCompleted: 0
  });
  const [todaysEvents, setTodaysEvents] = useState<DashboardEvent[]>([]);
  const [recentTasks, setRecentTasks] = useState<Task[]>([]);

  const fetchData = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const [user, tasks, meetings, chats, users] = await Promise.all([
        authService.getCurrentUser(),
        taskService.getTasks(),
        meetingService.getAllMeetings(),
        messageService.getChats(),
        userService.getAllUsers()
      ]);

      setCurrentUser(user);

      const pendingTasks = tasks.filter(t => t.status !== "Completed");
      const completedTasks = tasks.filter(t => t.status === "Completed");
      const unreadCount = chats.reduce((acc: number, curr: Chat) => acc + (curr.unread_count || 0), 0);
      const todayStr = new Date().toLocaleDateString("en-CA");
      const todayMeetings = meetings.filter(m => new Date(m.start_time).toLocaleDateString("en-CA") === todayStr);

      setStats({
        tasksPending: pendingTasks.length,
        meetingsToday: todayMeetings.length,
        unreadMessages: unreadCount,
        teamMembers: users.length,
        tasksCompleted: completedTasks.length
      });

      const taskEvents: DashboardEvent[] = tasks
        .filter(t => t.due_date && new Date(t.due_date).toLocaleDateString("en-CA") === todayStr && t.status !== "Completed")
        .map(t => ({
          id: `task-${t.id}`,
          title: t.title,
          time: "Due Today",
          type: "task",
          status: t.status,
          details: t.priority,
          timestamp: new Date(t.created_at || new Date())
        }));

      const meetingEvents: DashboardEvent[] = todayMeetings.map(m => {
        const startDate = new Date(m.start_time);
        return {
          id: `meeting-${m.id}`,
          title: m.title,
          time: startDate.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', hour12: true }).toUpperCase(),
          type: "meeting",
          details: m.location || "Virtual HQ",
          timestamp: startDate
        };
      });

      const allEvents = [...taskEvents, ...meetingEvents].sort((a, b) => a.timestamp.getTime() - b.timestamp.getTime());
      setTodaysEvents(allEvents);
      setRecentTasks(tasks.slice(0, 5));

    } catch (error) {
      console.error("Failed to load dashboard data", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
    const unsubscribe = webSocketService.subscribe((message: WebSocketMessage) => {
      if (['task', 'meeting', 'message', 'notification'].includes(message.type)) {
        fetchData(false);
      }
    });

    return () => unsubscribe();
  }, []);

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return "Rise and Shine";
    if (hour < 18) return "Focused Afternoon";
    return "Steady Evening";
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: { y: 0, opacity: 1 }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center bg-[#f8fafc]">
        <div className="w-16 h-1- bg-indigo-600 rounded-full animate-bounce mb-4 flex items-center justify-center">
          <Zap className="text-white" size={32} />
        </div>
        <p className="text-[10px] font-black uppercase tracking-[0.3em] text-slate-400">Personalizing Space</p>
      </div>
    );
  }

  return (
    <motion.div
      initial="hidden"
      animate="visible"
      variants={containerVariants}
      className="min-h-screen bg-[#f8fafc] text-slate-900 p-6 lg:p-12 space-y-12"
    >
      {/* Header with Glass Card */}
      <motion.div variants={itemVariants} className="flex flex-col lg:flex-row lg:items-center justify-between gap-10">
        <div className="space-y-3">
          <div className="flex items-center gap-2 px-4 py-1.5 bg-indigo-50 text-indigo-700 rounded-full text-[10px] font-black uppercase tracking-[0.2em] border border-indigo-100">
            <Zap size={14} className="fill-indigo-600" /> Executive Pulse
          </div>
          <h1 className="text-5xl font-[1000] tracking-tight text-slate-900">
            {getGreeting()}, <span className="gradient-text">{currentUser?.name?.split(' ')[0] || 'Member'}!</span>
          </h1>
          <p className="text-slate-500 font-medium text-lg">Your workspace is optimized and ready for impact.</p>
        </div>

        <div className="bg-white p-6 rounded-[32px] border border-slate-100 shadow-xl shadow-slate-200/50 flex items-center gap-6">
          <div className="w-14 h-14 bg-slate-900 rounded-2xl flex items-center justify-center text-white shadow-lg">
            <CalendarIcon size={24} />
          </div>
          <div>
            <p className="text-[10px] font-black text-slate-400 uppercase tracking-widest mb-1">Chronos</p>
            <h2 className="text-xl font-[900] text-slate-900 leading-none">
              {new Date().toLocaleDateString(undefined, { weekday: 'long', month: 'short', day: 'numeric' })}
            </h2>
          </div>
        </div>
      </motion.div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
        <StatCard
          icon={Shield}
          label="Operational Tasks"
          value={stats.tasksPending}
          subLabel="Pending Review"
          color="bg-rose-500"
          gradient="from-rose-500 to-orange-500"
          onClick={() => navigate('/tasks')}
        />
        <StatCard
          icon={Activity}
          label="Today's Syncs"
          value={stats.meetingsToday}
          subLabel="Confirmed Events"
          color="bg-indigo-500"
          gradient="from-indigo-500 to-violet-600"
          onClick={() => navigate('/meetings')}
        />
        <StatCard
          icon={MessageSquare}
          label="Communications"
          value={stats.unreadMessages}
          subLabel="Awaiting Action"
          color="bg-sky-500"
          gradient="from-sky-500 to-blue-600"
          onClick={() => navigate('/messages')}
        />
        <StatCard
          icon={Star}
          label="Velocity Point"
          value={stats.tasksCompleted}
          subLabel="Completed Session"
          color="bg-emerald-500"
          gradient="from-emerald-500 to-teal-600"
        />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-12 gap-12">
        {/* Left: Schedule Timeline */}
        <motion.div variants={itemVariants} className="lg:col-span-7 space-y-10">
          <div className="flex items-center justify-between">
            <h3 className="text-xs font-black uppercase tracking-[0.2em] text-slate-400 flex items-center gap-3">
              <span className="w-8 h-[2px] bg-slate-200"></span>
              Flow Dynamics
            </h3>
            <button
              onClick={() => navigate('/calendar')}
              className="text-[10px] font-black uppercase tracking-widest text-indigo-600 hover:scale-105 transition-transform"
            >
              Deep Dive Calendar
            </button>
          </div>

          <div className="bg-white rounded-[40px] border border-slate-100 shadow-2xl shadow-slate-200/40 p-10">
            {todaysEvents.length > 0 ? (
              <div className="space-y-8">
                {todaysEvents.map((event, idx) => (
                  <motion.div
                    whileHover={{ x: 10 }}
                    key={idx}
                    className="flex items-start gap-8 group"
                  >
                    <div className="pt-1 w-20 shrink-0">
                      <p className="text-[10px] font-black text-slate-400 group-hover:text-indigo-600 transition-colors uppercase">
                        {event.time === "Due Today" ? "End Day" : event.time}
                      </p>
                    </div>
                    <div className="relative flex-1 bg-slate-50/50 rounded-3xl p-6 border border-slate-100 group-hover:bg-white group-hover:border-indigo-100 group-hover:shadow-xl group-hover:shadow-indigo-500/5 transition-all duration-500">
                      <div className="flex justify-between items-center">
                        <div className="space-y-2">
                          <div className="flex items-center gap-2">
                            <span className={`w-2 h-2 rounded-full ${event.type === 'meeting' ? 'bg-indigo-500 shadow-lg shadow-indigo-300' : 'bg-emerald-500 shadow-lg shadow-emerald-300'}`} />
                            <h4 className="text-lg font-black text-slate-900 group-hover:text-indigo-600 transition-colors">{event.title}</h4>
                          </div>
                          <p className="text-xs text-slate-400 font-bold uppercase tracking-tight ml-4">{event.details}</p>
                        </div>
                        {event.type === 'meeting' && (
                          <button
                            onClick={() => navigate('/meetings')}
                            className="p-3 bg-white border border-slate-200 rounded-2xl text-slate-900 hover:bg-slate-900 hover:text-white transition-all shadow-sm"
                          >
                            <ArrowRight size={18} />
                          </button>
                        )}
                      </div>
                    </div>
                  </motion.div>
                ))}
              </div>
            ) : (
              <div className="py-20 text-center flex flex-col items-center">
                <div className="w-20 h-20 bg-slate-50 rounded-full flex items-center justify-center mb-6">
                  <Layers size={32} className="text-slate-200" />
                </div>
                <h3 className="text-xl font-bold text-slate-400 italic">Static Channel</h3>
                <p className="text-slate-300 text-sm mt-1">No scheduled interruptions today.</p>
              </div>
            )}
          </div>
        </motion.div>

        {/* Right: Operational List */}
        <motion.div variants={itemVariants} className="lg:col-span-5 space-y-10">
          <div className="flex items-center justify-between">
            <h3 className="text-xs font-black uppercase tracking-[0.2em] text-slate-400 flex items-center gap-3">
              <span className="w-8 h-[2px] bg-slate-200"></span>
              Recent Ops
            </h3>
            <button
              onClick={() => navigate('/tasks')}
              className="text-[10px] font-black uppercase tracking-widest text-indigo-600"
            >
              All Operations
            </button>
          </div>

          <div className="bg-slate-900 rounded-[40px] p-8 shadow-2xl space-y-4">
            {recentTasks.length > 0 ? (
              recentTasks.map(task => (
                <motion.div
                  whileHover={{ scale: 1.02 }}
                  key={task.id}
                  className="bg-white/5 border border-white/5 rounded-3xl p-5 hover:bg-white/10 transition-all cursor-pointer flex items-center justify-between group"
                  onClick={() => navigate('/tasks')}
                >
                  <div className="flex items-center gap-4">
                    <div className={`w-3 h-3 rounded-full ${task.status === 'Completed' ? 'bg-emerald-400 shadow-lg shadow-emerald-400/20' : 'bg-white/20'}`} />
                    <div>
                      <p className={`text-sm font-bold text-white transition-all ${task.status === 'Completed' ? 'opacity-40 italic line-through' : ''}`}>{task.title}</p>
                      <div className="flex items-center gap-3 mt-1">
                        <span className={`text-[10px] font-black uppercase tracking-tight ${task.priority === 'High' ? 'text-rose-400' : 'text-indigo-400'}`}>
                          {task.priority}
                        </span>
                        <span className="text-[10px] font-black text-white/30 uppercase tracking-tight">System Req</span>
                      </div>
                    </div>
                  </div>
                  <div className="p-2.5 rounded-xl bg-white/5 text-white/40 group-hover:text-white group-hover:bg-white/10 transition-all">
                    <ChevronRight size={16} />
                  </div>
                </motion.div>
              ))
            ) : (
              <div className="py-20 text-center opacity-30">
                <p className="text-sm font-bold text-white uppercase tracking-widest">Awaiting Ops</p>
              </div>
            )}
          </div>

          {/* Productivity Tip Card */}
          <div className="relative group bg-gradient-to-br from-indigo-600 to-violet-700 rounded-[40px] p-10 overflow-hidden shadow-2xl shadow-indigo-200">
            <div className="absolute top-0 right-0 p-8 opacity-20 group-hover:scale-125 transition-transform duration-500">
              <Zap size={100} strokeWidth={1} className="fill-white" />
            </div>
            <div className="relative z-10 space-y-4">
              <h4 className="text-white font-[900] text-2xl max-w-[200px] leading-tight">Maximized Workflow Active.</h4>
              <p className="text-white/70 text-sm font-medium leading-relaxed max-w-[250px]">
                Focus on High Priority tasks first to maintain 98% team alignment this week.
              </p>
              <button className="px-6 py-2.5 bg-white text-indigo-700 rounded-2xl font-black uppercase tracking-widest text-[10px] shadow-lg shadow-white/5 hover:scale-105 transition-transform active:scale-95">
                Optimization Settings
              </button>
            </div>
          </div>
        </motion.div>
      </div>
    </motion.div>
  );
};

// Helper Components
const StatCard = ({ icon: Icon, label, value, subLabel, gradient, color, onClick }: any) => (
  <motion.div
    whileHover={{ y: -8, scale: 1.02 }}
    onClick={onClick}
    className="relative group cursor-pointer bg-white p-8 rounded-[40px] border border-slate-100 premium-shadow-hover overflow-hidden"
  >
    {/* Decorative Gradient Background */}
    <div className={`absolute top-0 right-0 w-32 h-32 bg-gradient-to-br ${gradient} rounded-full blur-[60px] opacity-10 -mr-16 -mt-16 group-hover:opacity-20 transition-opacity`} />

    <div className="relative z-10 space-y-6">
      <div className={`w-14 h-14 ${color} rounded-2xl flex items-center justify-center text-white shadow-2xl shadow-slate-200 transition-transform group-hover:rotate-6`}>
        <Icon size={24} strokeWidth={2.5} />
      </div>

      <div className="space-y-1">
        <h3 className="text-4xl font-[1000] text-slate-900 tracking-tight leading-none">{value}</h3>
        <p className="text-sm font-black text-slate-400 uppercase tracking-widest">{label}</p>
      </div>

      <div className="flex items-center gap-2">
        <div className="w-4 h-[2.5px] bg-slate-200 rounded-full" />
        <span className="text-[10px] font-black uppercase tracking-widest text-slate-300">{subLabel}</span>
      </div>
    </div>
  </motion.div>
);

const ChevronRight = ({ size }: { size: number }) => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke="currentColor"
    strokeWidth="3"
    strokeLinecap="round"
    strokeLinejoin="round"
  >
    <path d="m9 18 6-6-6-6" />
  </svg>
);

export default DashboardPage;
