import React, { useState } from "react";
import { Eye, EyeOff, Shield, Zap, Activity, Star, ChevronRight, Mail, Lock, Loader2 } from "lucide-react";
import { Link, useNavigate } from "react-router-dom";
import { authService } from "../../services/authService";
import { motion, AnimatePresence } from "framer-motion";

const AuthPage: React.FC = () => {
  const [showPassword, setShowPassword] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      await authService.login(email, password);
      navigate("/dashboard");
    } catch (error: any) {
      console.error("Login failed:", error);
      alert(error.response?.data?.detail || "Login failed. Please check your credentials.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-[#fcfdfe] flex items-center justify-center p-6 font-sans overflow-hidden relative">
      <div className="w-full max-w-[1100px] h-[750px] bg-white rounded-[40px] shadow-2xl flex relative overflow-hidden border border-slate-100/50">

        {/* Left: Branding & Visuals */}
        <div className="hidden lg:flex lg:w-1/2 bg-slate-900 p-16 flex-col justify-between relative overflow-hidden">
          {/* Simple accent patterns */}
          <div className="absolute top-0 left-0 w-full h-full opacity-5 pointer-events-none">
            <div className="absolute -top-20 -left-20 w-80 h-80 border border-white rounded-full" />
            <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-[500px] h-[500px] border border-white rounded-[100px] rotate-45" />
          </div>

          <div className="relative z-10">
            <div className="w-12 h-12 bg-indigo-600 rounded-2xl flex items-center justify-center shadow-lg shadow-indigo-500/20 mb-10">
              <Activity size={24} className="text-white" />
            </div>
            <h1 className="text-5xl font-bold text-white tracking-tight leading-tight mb-6">
              Professional <br />
              <span className="text-indigo-400">Workspace Control.</span>
            </h1>
            <p className="text-slate-400 text-lg font-medium leading-relaxed max-w-[340px]">
              Access your team's centralized intelligence hub for task management and real-time collaboration.
            </p>
          </div>

          <div className="relative z-10 flex items-center gap-3 text-slate-500">
            <Shield size={16} />
            <span className="text-[10px] font-bold uppercase tracking-[0.2em]">Secure Authentication Protocol</span>
          </div>
        </div>

        {/* Right: Login Form */}
        <div className="w-full lg:w-1/2 p-10 md:p-20 flex flex-col justify-center">
          <motion.div
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            className="max-w-sm w-full mx-auto"
          >
            <div className="mb-12">
              <h2 className="text-3xl font-bold text-slate-900 tracking-tight mb-2">Login Access</h2>
              <p className="text-sm font-semibold text-slate-400">Initialize your secure operative session.</p>
            </div>

            <form onSubmit={handleLogin} className="space-y-6">
              <div className="space-y-2">
                <label className="text-[11px] font-bold text-slate-400 uppercase tracking-wider ml-1">Work Email</label>
                <div className="relative group">
                  <Mail className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-300 group-focus-within:text-indigo-600 transition-colors" size={18} />
                  <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    className="w-full bg-slate-50 border border-slate-100 rounded-2xl py-3.5 pl-11 pr-4 text-sm font-semibold text-slate-900 focus:bg-white focus:border-indigo-500 outline-none transition-all placeholder:text-slate-300"
                    placeholder="name@company.com"
                    required
                  />
                </div>
              </div>

              <div className="space-y-2">
                <div className="flex items-center justify-between px-1">
                  <label className="text-[11px] font-bold text-slate-400 uppercase tracking-wider">Access Key</label>
                  <Link to="/forgot-password" size={18} className="text-[11px] font-bold text-indigo-600 hover:underline transition-all">Recover Key</Link>
                </div>
                <div className="relative group">
                  <Lock className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-300 group-focus-within:text-indigo-600 transition-colors" size={18} />
                  <input
                    type={showPassword ? "text" : "password"}
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className="w-full bg-slate-50 border border-slate-100 rounded-2xl py-3.5 pl-11 pr-12 text-sm font-semibold text-slate-900 focus:bg-white focus:border-indigo-500 outline-none transition-all placeholder:text-slate-300"
                    placeholder="••••••••"
                    required
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    className="absolute right-4 top-1/2 -translate-y-1/2 text-slate-300 hover:text-indigo-600 transition-colors"
                  >
                    {showPassword ? <EyeOff size={18} /> : <Eye size={18} />}
                  </button>
                </div>
              </div>

              <div className="flex items-center gap-3 px-1 pb-2">
                <input
                  type="checkbox"
                  className="w-4 h-4 rounded-lg border-slate-200 text-indigo-600 focus:ring-indigo-500"
                />
                <span className="text-[11px] font-bold text-slate-500 uppercase tracking-wider">Persistent Login</span>
              </div>

              <button
                disabled={isLoading}
                type="submit"
                className="w-full bg-indigo-600 hover:bg-indigo-700 text-white rounded-2xl py-4 font-bold text-sm shadow-xl shadow-indigo-100 transition-all flex items-center justify-center gap-3 active:scale-[0.98]"
              >
                {isLoading ? <Loader2 className="animate-spin" size={20} /> : (
                  <>
                    Establish Session
                    <ChevronRight size={18} />
                  </>
                )}
              </button>
            </form>

            <div className="mt-12 pt-8 border-t border-slate-50 text-center">
              <p className="text-xs font-bold text-slate-400">
                New Operative?{" "}
                <Link to="/register" className="text-indigo-600 hover:underline font-bold ml-1">
                  Request Induction
                </Link>
              </p>
            </div>
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
