import React from "react";
import {
  LayoutDashboard,
  MessageSquare,
  CalendarDays,
  FileUp,
  ClipboardList,
  LogOut,
  Users,
  Settings,
  Video,
  ChevronRight,
} from "lucide-react";
import { useNavigate, useLocation } from "react-router-dom";
import { AnimatePresence, motion } from "framer-motion";
import { authService } from "../services/authService";

interface SidebarProps {
  isOpen: boolean;
  onClose: () => void;
}

const Sidebar: React.FC<SidebarProps> = ({ isOpen, onClose }) => {
  const navigate = useNavigate();
  const location = useLocation();

  const navItems = [
    { label: "Dashboard", icon: LayoutDashboard, path: "/dashboard", color: "text-indigo-400" },
    { label: "Messages", icon: MessageSquare, path: "/messages", color: "text-sky-400" },
    { label: "Tasks", icon: ClipboardList, path: "/tasks", color: "text-rose-400" },
    { label: "Meetings", icon: Video, path: "/meetings", color: "text-emerald-400" },
    { label: "Calendar", icon: CalendarDays, path: "/calendar", color: "text-amber-400" },
    { label: "Files", icon: FileUp, path: "/files", color: "text-violet-400" },
    { label: "Team", icon: Users, path: "/team", color: "text-pink-400" },
    { label: "Settings", icon: Settings, path: "/settings", color: "text-slate-400" },
  ];

  return (
    <>
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black/80 backdrop-blur-sm z-40 lg:hidden"
            onClick={onClose}
          />
        )}
      </AnimatePresence>

      <aside
        className={`fixed top-0 left-0 z-50 h-screen w-72 bg-[#0f172a] border-r border-slate-800 transform transition-transform duration-500 cubic-bezier(0.4, 0, 0.2, 1) ${isOpen ? "translate-x-0" : "-translate-x-full"
          } lg:translate-x-0 flex flex-col`}
      >
        {/* Brand Section */}
        <div
          className="p-8 pb-10 flex items-center gap-4 cursor-pointer group"
          onClick={() => navigate("/dashboard")}
        >
          <div className="w-12 h-12 bg-gradient-to-tr from-indigo-600 to-violet-600 rounded-2xl flex items-center justify-center shadow-lg shadow-indigo-500/20 group-hover:scale-110 transition-transform duration-300">
            <img src="/logo1.png" alt="Logo" className="w-8 h-8 object-contain brightness-0 invert" />
          </div>
          <div>
            <h1 className="text-2xl font-[900] text-white tracking-tight leading-none mb-1">Collabryta</h1>
            <p className="text-[10px] font-black uppercase tracking-[0.2em] text-indigo-400/80">Pro Workspace</p>
          </div>
        </div>

        {/* Navigation */}
        <nav className="flex-1 px-4 space-y-2 overflow-y-auto custom-scrollbar">
          {navItems.map(({ label, icon: Icon, path, color }) => {
            const active = location.pathname.startsWith(path);
            return (
              <button
                key={path}
                onClick={() => {
                  navigate(path);
                  onClose();
                }}
                className={`w-full group flex items-center justify-between px-4 py-3.5 rounded-2xl text-[13px] font-black uppercase tracking-wider transition-all duration-300 ${active
                    ? "bg-gradient-to-r from-indigo-600 to-violet-600 text-white shadow-xl shadow-indigo-500/20 active:scale-95"
                    : "text-slate-400 hover:bg-slate-800/50 hover:text-white"
                  }`}
              >
                <div className="flex items-center gap-4">
                  <div className={`p-2 rounded-xl transition-colors ${active ? "bg-white/20" : "bg-slate-800 group-hover:bg-slate-700"}`}>
                    <Icon size={20} className={active ? "text-white" : color} />
                  </div>
                  <span>{label}</span>
                </div>
                {active && (
                  <motion.div layoutId="activeDot" className="w-1.5 h-1.5 bg-white rounded-full shadow-lg shadow-white" />
                )}
              </button>
            );
          })}
        </nav>

        {/* Footer / Logout */}
        <div className="p-6 border-t border-slate-800 bg-[#0f172a]/50 backdrop-blur-md">
          <button
            onClick={() => {
              authService.logout();
              navigate("/");
            }}
            className="w-full group flex items-center gap-4 px-4 py-4 rounded-2xl text-[13px] font-black uppercase tracking-wider text-rose-500 hover:bg-rose-500/10 transition-all duration-300 active:scale-95"
          >
            <div className="p-2 bg-rose-500/10 rounded-xl group-hover:bg-rose-500 group-hover:text-white transition-all">
              <LogOut size={20} />
            </div>
            Sign Out
          </button>
        </div>
      </aside>
    </>
  );
};

export default Sidebar;
