import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import {
    ChevronLeft,
    Save,
    Loader2,
    Calendar,
    User,
    AlignLeft,
    Type,
    AlertCircle,
    Activity,
    Shield,
    Zap,
    Sparkles,
    CheckCircle2,
    Target
} from "lucide-react";
import { taskService } from "../../services/taskService";
import { userService } from "../../services/userService";
import { motion, AnimatePresence } from "framer-motion";

const AddTaskPage: React.FC = () => {
    const navigate = useNavigate();
    const { id } = useParams<{ id: string }>();
    const isEditing = Boolean(id);
    const today = new Date().toISOString().split('T')[0];

    const [users, setUsers] = useState<any[]>([]);
    const [submitting, setSubmitting] = useState(false);
    const [loading, setLoading] = useState(isEditing);
    const [error, setError] = useState<string | null>(null);

    const [formData, setFormData] = useState({
        title: "",
        description: "",
        status: "Upcoming",
        priority: "Normal",
        assigned_to: "",
        due_date: today
    });

    useEffect(() => {
        const loadData = async () => {
            try {
                const [usersData, taskData] = await Promise.all([
                    userService.getAllUsers(),
                    isEditing && id ? taskService.getTaskById(Number(id)) : Promise.resolve(null)
                ]);
                setUsers(usersData);
                if (taskData) {
                    setFormData({
                        title: taskData.title,
                        description: taskData.description || "",
                        status: taskData.status === "todo" ? "Upcoming" : taskData.status === "in_progress" ? "In Progress" : taskData.status === "done" ? "Completed" : taskData.status,
                        priority: taskData.priority || "Normal",
                        assigned_to: taskData.assignee_id ? String(taskData.assignee_id) : "",
                        due_date: taskData.due_date ? taskData.due_date.split("T")[0] : today
                    });
                }
            } catch (e) {
                console.error("Failed to load data", e);
            } finally {
                setLoading(false);
            }
        };
        loadData();
    }, [id, isEditing, today]);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setError(null);
        if (formData.due_date < today) {
            setError("Temporal breach: Due date cannot be in the past.");
            return;
        }

        setSubmitting(true);
        try {
            const payload = {
                ...formData,
                status: (formData.status === "Upcoming" ? "todo" : formData.status === "In Progress" ? "in_progress" : "done") as any,
                priority: formData.priority as any,
                assigned_to: formData.assigned_to ? Number(formData.assigned_to) : undefined
            };

            if (isEditing && id) {
                await taskService.updateTask(Number(id), payload);
            } else {
                await taskService.createTask(payload);
            }
            navigate("/tasks");
        } catch (err: any) {
            console.error(err);
            setError(err.response?.data?.detail?.[0]?.msg || err.response?.data?.detail || "Sequence failure. Please retry protocol.");
            setSubmitting(false);
        }
    };

    if (loading) {
        return (
            <div className="min-h-screen flex items-center justify-center bg-[#fcfdfe]">
                <Loader2 className="w-10 h-10 text-indigo-600 animate-spin" />
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-[#fcfdfe] text-slate-900 font-sans">
            <div className="max-w-[800px] mx-auto px-6 py-10 lg:py-12 relative z-10">

                {/* Header */}
                <div className="flex items-center justify-between mb-12">
                    <button
                        onClick={() => navigate(-1)}
                        className="flex items-center gap-2 text-slate-400 hover:text-slate-900 font-bold transition-all text-sm"
                    >
                        <ChevronLeft size={20} />
                        Back to Tasks
                    </button>
                    <div className="flex items-center gap-2 px-4 py-1.5 bg-indigo-50 text-indigo-600 rounded-full text-[11px] font-bold uppercase tracking-wider border border-indigo-100 shadow-sm">
                        <Target size={14} />
                        Operational Protocol
                    </div>
                </div>

                <div className="grid grid-cols-1 lg:grid-cols-12 gap-12 items-start">
                    {/* Left: Branding & Tips */}
                    <div className="lg:col-span-4 space-y-8">
                        <div>
                            <h1 className="text-4xl font-bold tracking-tight text-slate-900 mb-2">
                                {isEditing ? 'Modify' : 'Create'} <br />
                                <span className="text-indigo-600">Objective.</span>
                            </h1>
                            <p className="text-slate-500 font-medium leading-relaxed italic">
                                "{isEditing ? 'Recalibrate the parameters of this operational unit.' : 'Define a new mission segment and integrate it into the squad stream.'}"
                            </p>
                        </div>

                        <div className="space-y-4 pt-6">
                            <div className="flex items-start gap-4 p-5 bg-white rounded-2xl border border-slate-100 shadow-sm">
                                <div className="p-2 bg-indigo-50 text-indigo-600 rounded-lg shrink-0"><Zap size={16} /></div>
                                <div>
                                    <p className="text-xs font-bold text-slate-900 mb-0.5">Instant Sync</p>
                                    <p className="text-[11px] font-medium text-slate-400 leading-tight">Assignees will receive immediate alerts.</p>
                                </div>
                            </div>
                            <div className="flex items-start gap-4 p-5 bg-white rounded-2xl border border-slate-100 shadow-sm">
                                <div className="p-2 bg-emerald-50 text-emerald-600 rounded-lg shrink-0"><CheckCircle2 size={16} /></div>
                                <div>
                                    <p className="text-xs font-bold text-slate-900 mb-0.5">Status Tracking</p>
                                    <p className="text-[11px] font-medium text-slate-400 leading-tight">Monitor progress across the timeline.</p>
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Right: Form */}
                    <div className="lg:col-span-8">
                        <AnimatePresence>
                            {error && (
                                <motion.div
                                    initial={{ opacity: 0, scale: 0.95 }}
                                    animate={{ opacity: 1, scale: 1 }}
                                    exit={{ opacity: 0, scale: 0.95 }}
                                    className="mb-8 p-4 bg-rose-50 border border-rose-100 rounded-2xl flex items-center gap-3 text-rose-600"
                                >
                                    <AlertCircle size={18} />
                                    <p className="text-xs font-bold uppercase tracking-tight">{error}</p>
                                </motion.div>
                            )}
                        </AnimatePresence>

                        <div className="bg-white rounded-[32px] border border-slate-100 p-10 shadow-sm">
                            <form onSubmit={handleSubmit} className="space-y-8">
                                <div className="space-y-2">
                                    <label className="text-[11px] font-bold text-slate-400 uppercase tracking-wider ml-1">Task Title</label>
                                    <div className="relative">
                                        <input
                                            type="text"
                                            value={formData.title}
                                            onChange={e => setFormData({ ...formData, title: e.target.value })}
                                            placeholder="Deployment identifier..."
                                            required
                                            className="w-full px-5 py-3.5 bg-slate-50 border border-slate-100 rounded-2xl text-slate-900 font-semibold text-sm focus:bg-white focus:border-indigo-500 outline-none transition-all placeholder:text-slate-300"
                                        />
                                        <Type size={16} className="absolute right-5 top-1/2 -translate-y-1/2 text-slate-200" />
                                    </div>
                                </div>

                                <div className="space-y-2">
                                    <label className="text-[11px] font-bold text-slate-400 uppercase tracking-wider ml-1">Tactical Briefing</label>
                                    <div className="relative">
                                        <textarea
                                            value={formData.description}
                                            onChange={e => setFormData({ ...formData, description: e.target.value })}
                                            placeholder="Outline the mission requirements..."
                                            rows={4}
                                            className="w-full px-5 py-3.5 bg-slate-50 border border-slate-100 rounded-2xl text-slate-900 font-semibold text-sm focus:bg-white focus:border-indigo-500 outline-none transition-all placeholder:text-slate-300 resize-none"
                                        />
                                        <AlignLeft size={16} className="absolute right-5 top-5 text-slate-200" />
                                    </div>
                                </div>

                                <div className="grid grid-cols-2 gap-6">
                                    <div className="space-y-2">
                                        <label className="text-[11px] font-bold text-slate-400 uppercase tracking-wider ml-1">Assign Personnel</label>
                                        <div className="relative">
                                            <select
                                                value={formData.assigned_to}
                                                onChange={e => setFormData({ ...formData, assigned_to: e.target.value })}
                                                className="w-full px-5 py-3.5 bg-slate-50 border border-slate-100 rounded-2xl text-slate-900 font-semibold text-sm focus:bg-white focus:border-indigo-500 outline-none transition-all appearance-none cursor-pointer"
                                            >
                                                <option value="">Select Member</option>
                                                {users.map(u => (
                                                    <option key={u.id} value={u.id}>{u.name || u.email}</option>
                                                ))}
                                            </select>
                                            <User size={16} className="absolute right-5 top-1/2 -translate-y-1/2 text-slate-200 pointer-events-none" />
                                        </div>
                                    </div>

                                    <div className="space-y-2">
                                        <label className="text-[11px] font-bold text-slate-400 uppercase tracking-wider ml-1">Temporal Deadline</label>
                                        <div className="relative">
                                            <input
                                                type="date"
                                                min={today}
                                                value={formData.due_date}
                                                onChange={e => setFormData({ ...formData, due_date: e.target.value })}
                                                className="w-full px-5 py-3.5 bg-slate-50 border border-slate-100 rounded-2xl text-slate-900 font-semibold text-sm focus:bg-white focus:border-indigo-500 outline-none transition-all"
                                            />
                                            <Calendar size={16} className="absolute right-5 top-1/2 -translate-y-1/2 text-slate-200 pointer-events-none" />
                                        </div>
                                    </div>
                                </div>

                                <div className="space-y-4">
                                    <label className="text-[11px] font-bold text-slate-400 uppercase tracking-wider ml-1">Phase Status</label>
                                    <div className="flex flex-wrap gap-2">
                                        {["Upcoming", "In Progress", "Completed"].map(s => (
                                            <button
                                                key={s}
                                                type="button"
                                                onClick={() => setFormData({ ...formData, status: s })}
                                                className={`px-6 py-2.5 rounded-xl text-[11px] font-bold uppercase tracking-wider border transition-all ${formData.status === s
                                                        ? 'bg-slate-900 border-slate-900 text-white shadow-lg'
                                                        : 'bg-white border-slate-100 text-slate-400 hover:border-indigo-100 hover:text-indigo-600'
                                                    }`}
                                            >
                                                {s}
                                            </button>
                                        ))}
                                    </div>
                                </div>

                                <div className="pt-6 flex items-center justify-end gap-8 border-t border-slate-50">
                                    <button
                                        type="button"
                                        onClick={() => navigate(-1)}
                                        className="text-xs font-bold text-slate-400 hover:text-slate-600 transition-colors uppercase tracking-widest"
                                    >
                                        Abort
                                    </button>
                                    <button
                                        type="submit"
                                        disabled={submitting}
                                        className="flex items-center gap-2 px-8 py-3.5 bg-indigo-600 hover:bg-indigo-700 text-white rounded-2xl font-bold text-sm shadow-xl shadow-indigo-100 transition-all active:scale-[0.98]"
                                    >
                                        {submitting ? <Loader2 size={18} className="animate-spin" /> : <Save size={18} />}
                                        {isEditing ? 'Confirm Updates' : 'Commit Objective'}
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AddTaskPage;
