import React, { useState, useEffect } from "react";
import {
    User,
    Bell,
    ShieldCheck,
    Save,
    Loader2,
    CheckCircle2,
    Eye,
    Smartphone,
    Globe,
    Lock,
    Activity,
    Shield,
    Zap,
    Cpu,
    Star
} from "lucide-react";
import { authService } from "../../services/authService";
import { motion, AnimatePresence } from "framer-motion";

const SettingsPage: React.FC = () => {
    const [loading, setLoading] = useState(true);
    const [saving, setSaving] = useState(false);
    const [success, setSuccess] = useState(false);
    const [settings, setSettings] = useState<any>(null);
    const [show2FAModal, setShow2FAModal] = useState(false);
    const [otpCode, setOtpCode] = useState("");
    const [is2FALoading, setIs2FALoading] = useState(false);
    const [testNotifStatus, setTestNotifStatus] = useState<string | null>(null);

    useEffect(() => {
        const fetchSettings = async () => {
            try {
                const data = await authService.getCurrentUser();
                setSettings(data);
            } catch (error) {
                console.error("Failed to load settings", error);
            } finally {
                setLoading(false);
            }
        };
        fetchSettings();
    }, []);

    const handleUpdate = (field: string, value: any) => {
        setSettings((prev: any) => ({ ...prev, [field]: value }));
    };

    const handleSave = async () => {
        try {
            setSaving(true);
            setSuccess(false);
            const { id, email, hashed_password, last_seen, status, ...updateData } = settings;
            await authService.updateProfile(updateData);
            setSuccess(true);
            setTimeout(() => setSuccess(false), 3000);
        } catch (error) {
            console.error("Failed to save settings", error);
        } finally {
            setSaving(false);
        }
    };

    const handleSendTestNotif = () => {
        setTestNotifStatus("Transmitting...");
        setTimeout(() => {
            setTestNotifStatus("Delivered!");
            setTimeout(() => setTestNotifStatus(null), 3000);
        }, 1500);
    };

    const handleEnable2FA = () => {
        if (settings.two_factor_enabled) {
            handleUpdate('two_factor_enabled', false);
        } else {
            setShow2FAModal(true);
        }
    };

    const handleVerifyOTP = () => {
        setIs2FALoading(true);
        setTimeout(() => {
            if (otpCode === "123456") {
                handleUpdate('two_factor_enabled', true);
                setShow2FAModal(false);
                setOtpCode("");
            } else {
                alert("Invalid OTP! Try 123456");
            }
            setIs2FALoading(false);
        }, 1000);
    };

    if (loading || !settings) {
        return (
            <div className="min-h-screen flex items-center justify-center bg-[#fcfdfe]">
                <Loader2 className="w-10 h-10 text-indigo-600 animate-spin" />
            </div>
        );
    }

    const Section = ({ icon: Icon, title, desc, children }: any) => (
        <section className="bg-white border border-slate-100 rounded-3xl overflow-hidden shadow-sm">
            <div className="px-8 py-6 bg-slate-50/50 border-b border-slate-50 flex items-center gap-4">
                <div className="p-2.5 bg-white border border-slate-100 rounded-xl text-indigo-600 shadow-sm">
                    <Icon size={20} />
                </div>
                <div>
                    <h2 className="text-lg font-bold text-slate-900 leading-tight">{title}</h2>
                    <p className="text-xs font-medium text-slate-400 mt-0.5">{desc}</p>
                </div>
            </div>
            <div className="p-8">{children}</div>
        </section>
    );

    const Toggle = ({ active, label, subtitle, field, disabled }: any) => (
        <div className={`flex items-center justify-between py-6 first:pt-0 border-b border-slate-50 last:border-0 last:pb-0 ${disabled ? 'opacity-50' : ''}`}>
            <div>
                <p className="text-sm font-bold text-slate-700">{label}</p>
                <p className="text-xs font-medium text-slate-400 mt-1">{subtitle}</p>
            </div>
            <button
                onClick={() => !disabled && handleUpdate(field, !active)}
                disabled={disabled}
                className={`relative inline-flex h-6 w-11 items-center rounded-full transition-all duration-200 focus:outline-none ${active ? 'bg-indigo-600' : 'bg-slate-200'}`}
            >
                <span className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform duration-200 ${active ? 'translate-x-6' : 'translate-x-1'}`} />
            </button>
        </div>
    );

    return (
        <div className="min-h-screen bg-[#fcfdfe] text-slate-900 font-sans">
            <div className="max-w-[800px] mx-auto px-6 py-10 lg:py-12 relative z-10 space-y-10">

                {/* Header */}
                <div className="flex flex-col md:flex-row md:items-center justify-between gap-6 mb-12">
                    <div>
                        <h1 className="text-3xl font-bold tracking-tight text-slate-900 mb-1">Settings</h1>
                        <p className="text-slate-500 font-medium">Fine-tune your workspace and account preferences.</p>
                    </div>

                    <button
                        onClick={handleSave}
                        disabled={saving}
                        className={`flex items-center justify-center gap-2 px-6 py-3 rounded-xl font-bold text-sm transition-all shadow-lg ${success ? 'bg-emerald-600 text-white shadow-emerald-100' : 'bg-indigo-600 text-white shadow-indigo-100 hover:bg-indigo-700'
                            }`}
                    >
                        {saving ? <Loader2 size={18} className="animate-spin" /> : success ? <CheckCircle2 size={18} /> : <Save size={18} />}
                        {saving ? "Saving..." : success ? "Changes Saved" : "Save Changes"}
                    </button>
                </div>

                {/* Profile Section */}
                <Section icon={User} title="Profile Information" desc="Manage your identity and public records within the team.">
                    <div className="space-y-8">
                        <div className="flex items-center gap-6 pb-6 border-b border-slate-50">
                            <div className="relative">
                                <div className="w-20 h-20 rounded-2xl bg-slate-100 border border-slate-200 overflow-hidden flex items-center justify-center">
                                    {settings.avatar ? (
                                        <img src={settings.avatar} alt="Avatar" className="w-full h-full object-cover" />
                                    ) : (
                                        <span className="text-2xl font-bold text-slate-400">{settings.name?.charAt(0)}</span>
                                    )}
                                </div>
                                <div className="absolute -bottom-1 -right-1 w-7 h-7 bg-indigo-600 text-white rounded-lg flex items-center justify-center border-2 border-white shadow-sm">
                                    <Star size={12} fill="white" />
                                </div>
                            </div>
                            <div>
                                <h3 className="text-base font-bold text-slate-900 mb-1">Avatar Protocol</h3>
                                <p className="text-xs font-medium text-slate-400">Profile image is managed through global identification records.</p>
                            </div>
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                            <div className="space-y-2">
                                <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Full Name</label>
                                <div className="relative">
                                    <input
                                        type="text"
                                        value={settings.name || ''}
                                        disabled
                                        className="w-full px-4 py-3 bg-slate-50 border border-slate-100 rounded-xl text-slate-500 font-semibold text-sm cursor-not-allowed"
                                    />
                                    <Lock size={14} className="absolute right-4 top-1/2 -translate-y-1/2 text-slate-300" />
                                </div>
                            </div>
                            <div className="space-y-2">
                                <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Job Title</label>
                                <div className="relative">
                                    <input
                                        type="text"
                                        value={settings.job_title || ''}
                                        disabled
                                        className="w-full px-4 py-3 bg-slate-50 border border-slate-100 rounded-xl text-slate-500 font-semibold text-sm cursor-not-allowed"
                                    />
                                    <Shield size={14} className="absolute right-4 top-1/2 -translate-y-1/2 text-slate-300" />
                                </div>
                            </div>
                            <div className="md:col-span-2 space-y-2">
                                <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Email Address</label>
                                <div className="relative">
                                    <input
                                        type="text"
                                        value={settings.email || ''}
                                        disabled
                                        className="w-full px-4 py-3 bg-slate-50 border border-slate-100 rounded-xl text-slate-500 font-semibold text-sm cursor-not-allowed"
                                    />
                                    <Globe size={14} className="absolute right-4 top-1/2 -translate-y-1/2 text-slate-300" />
                                </div>
                            </div>
                        </div>
                    </div>
                </Section>

                {/* Regional Section */}
                <Section icon={Globe} title="Regional Settings" desc="Configure your language and timezone preferences.">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div className="space-y-2">
                            <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Language</label>
                            <select
                                value={settings.language}
                                onChange={(e) => handleUpdate('language', e.target.value)}
                                className="w-full px-4 py-3 bg-white border border-slate-200 rounded-xl focus:border-indigo-500 focus:outline-none text-slate-700 font-semibold text-sm transition-all"
                            >
                                <option>English (US)</option>
                                <option>Hindi (HI)</option>
                                <option>Spanish (ES)</option>
                            </select>
                        </div>
                        <div className="space-y-2">
                            <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Timezone</label>
                            <select
                                value={settings.timezone}
                                onChange={(e) => handleUpdate('timezone', e.target.value)}
                                className="w-full px-4 py-3 bg-white border border-slate-200 rounded-xl focus:border-indigo-500 focus:outline-none text-slate-700 font-semibold text-sm transition-all"
                            >
                                <option value="(GMT+05:30) Mumbai, New Delhi">India (GMT+5:30)</option>
                                <option value="(GMT-08:00) Pacific Time">Pacific Time (GMT-8:00)</option>
                                <option value="(GMT+00:00) London, UTC">London (GMT+0:00)</option>
                            </select>
                        </div>
                    </div>
                </Section>

                {/* Privacy Section */}
                <Section icon={Eye} title="Privacy & Visibility" desc="Control how your presence is shared within the workspace.">
                    <div className="space-y-2">
                        <Toggle active={settings.show_online_status} label="Show Online Status" subtitle="Allow other team members to see when you are active" field="show_online_status" />
                        <Toggle active={settings.share_local_time} label="Share Local Time" subtitle="Help team members know your current working hours" field="share_local_time" />
                    </div>
                </Section>

                {/* Notifications Section */}
                <Section icon={Bell} title="Notifications" desc="Manage how and when you receive updates.">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-10 mb-8">
                        <div>
                            <h4 className="text-[10px] font-bold text-slate-400 uppercase tracking-[0.2em] mb-4">Email Notifications</h4>
                            <div className="space-y-1">
                                <Toggle active={settings.email_tasks} label="Task Updates" subtitle="Immediate email for task assignments" field="email_tasks" />
                                <Toggle active={settings.email_summary} label="Weekly Summary" subtitle="Performance digest every Monday" field="email_summary" />
                            </div>
                        </div>
                        <div>
                            <h4 className="text-[10px] font-bold text-slate-400 uppercase tracking-[0.2em] mb-4">Push Notifications</h4>
                            <div className="space-y-1">
                                <Toggle active={settings.push_meetings} label="Meeting Alerts" subtitle="Reminders for upcoming sessions" field="push_meetings" />
                                <Toggle active={settings.push_chat} label="Direct Messages" subtitle="Alerts for new chat communications" field="push_chat" />
                            </div>
                        </div>
                    </div>
                    <div className="pt-6 border-t border-slate-50">
                        <button
                            onClick={handleSendTestNotif}
                            disabled={!!testNotifStatus}
                            className="flex items-center gap-2 px-5 py-2.5 bg-slate-50 text-indigo-600 rounded-xl text-xs font-bold border border-slate-100 hover:bg-indigo-50 transition-all"
                        >
                            {testNotifStatus === "Delivered!" ? <CheckCircle2 size={16} /> : <Zap size={16} />}
                            {testNotifStatus || "Send Test Notification"}
                        </button>
                    </div>
                </Section>

                {/* Security Section */}
                <Section icon={ShieldCheck} title="Security & Access" desc="Manage your account protection and authentication.">
                    <div className="space-y-6">
                        <div className={`p-6 rounded-2xl border transition-all flex flex-col md:flex-row items-center justify-between gap-6 ${settings.two_factor_enabled ? 'bg-emerald-50 border-emerald-100' : 'bg-slate-50 border-slate-100'
                            }`}>
                            <div className="flex items-center gap-4">
                                <div className={`w-12 h-12 rounded-xl flex items-center justify-center ${settings.two_factor_enabled ? 'bg-emerald-600 text-white' : 'bg-slate-200 text-slate-500'
                                    }`}>
                                    <ShieldCheck size={24} />
                                </div>
                                <div className="text-center md:text-left">
                                    <p className="text-base font-bold text-slate-900 leading-tight">Two-Factor Authentication</p>
                                    <p className={`text-xs font-medium mt-1 ${settings.two_factor_enabled ? 'text-emerald-600' : 'text-slate-400'}`}>
                                        {settings.two_factor_enabled ? 'Your account is protected by 2FA.' : 'Add an extra layer of security to your account.'}
                                    </p>
                                </div>
                            </div>
                            <button
                                onClick={handleEnable2FA}
                                className={`px-5 py-2.5 rounded-xl text-xs font-bold transition-all shadow-sm ${settings.two_factor_enabled ? 'bg-white text-slate-600 border border-slate-200' : 'bg-indigo-600 text-white hover:bg-indigo-700'
                                    }`}
                            >
                                {settings.two_factor_enabled ? 'Disable 2FA' : 'Enable 2FA'}
                            </button>
                        </div>

                        <AnimatePresence>
                            {show2FAModal && (
                                <motion.div
                                    initial={{ opacity: 0, y: -10 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    exit={{ opacity: 0, y: -10 }}
                                    className="p-8 rounded-2xl border border-indigo-100 bg-indigo-50/50 space-y-6"
                                >
                                    <div className="flex items-center gap-3 text-indigo-700">
                                        <Smartphone size={20} />
                                        <h4 className="text-sm font-bold uppercase tracking-wider">Verification Required</h4>
                                    </div>
                                    <p className="text-xs font-medium text-slate-500 leading-relaxed">
                                        Enter the 6-digit verification code sent to your device. (Use <span className="text-indigo-600 font-bold">123456</span> for demo)
                                    </p>
                                    <div className="flex flex-wrap items-center gap-4">
                                        <input
                                            type="text"
                                            maxLength={6}
                                            placeholder="000000"
                                            value={otpCode}
                                            onChange={(e) => setOtpCode(e.target.value)}
                                            className="w-36 px-4 py-3 bg-white border border-indigo-100 rounded-xl text-center text-lg font-bold tracking-widest text-indigo-900 focus:outline-none shadow-sm"
                                        />
                                        <button
                                            onClick={handleVerifyOTP}
                                            disabled={otpCode.length !== 6 || is2FALoading}
                                            className="px-6 py-3 bg-indigo-600 text-white rounded-xl text-xs font-bold hover:bg-indigo-700 transition-all shadow-md disabled:bg-indigo-200"
                                        >
                                            {is2FALoading ? <Loader2 size={18} className="animate-spin" /> : "Verify Code"}
                                        </button>
                                        <button
                                            onClick={() => setShow2FAModal(false)}
                                            className="text-xs font-bold text-slate-400 hover:text-slate-600 transition-colors"
                                        >
                                            Cancel
                                        </button>
                                    </div>
                                </motion.div>
                            )}
                        </AnimatePresence>
                    </div>
                </Section>

            </div>
        </div>
    );
};

export default SettingsPage;
