import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {
  Calendar,
  CalendarDays,
  Clock,
  PlusCircle,
  Trash2,
  ExternalLink,
  Users,
  MapPin,
  PlayCircle,
  History,
  AlertCircle
} from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import { meetingService, Meeting } from "../../services/meetingService";

const MeetingsPage: React.FC = () => {
  const navigate = useNavigate();
  const [meetings, setMeetings] = useState<Meeting[]>([]);
  const [loading, setLoading] = useState(true);
  const [currentTime, setCurrentTime] = useState(new Date());

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000);
    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    const fetchMeetings = async () => {
      try {
        const data = await meetingService.getAllMeetings();
        setMeetings(data);
      } catch (error) {
        console.error("Failed to fetch meetings", error);
      } finally {
        setLoading(false);
      }
    };
    fetchMeetings();
  }, []);

  const handleDelete = async (id: number) => {
    if (window.confirm("Are you sure you want to delete this meeting?")) {
      try {
        await meetingService.deleteMeeting(id);
        setMeetings(meetings.filter(m => m.id !== id));
      } catch (error) {
        console.error("Failed to delete meeting", error);
      }
    }
  };

  const getMeetingStatus = (start: string, end: string) => {
    const now = currentTime;
    const startTime = new Date(start);
    const endTime = new Date(end);

    if (now >= startTime && now <= endTime) return 'running';
    if (now > endTime) return 'past';
    return 'upcoming';
  };

  const formatTime = (dateString: string) => {
    return new Date(dateString).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString([], { weekday: 'long', month: 'long', day: 'numeric' });
  };

  const isToday = (dateString: string) => {
    const today = new Date();
    const date = new Date(dateString);
    return date.toDateString() === today.toDateString();
  };

  // Categorize meetings
  const runningMeetings = meetings.filter(m => getMeetingStatus(m.start_time, m.end_time) === 'running');
  const upcomingMeetings = meetings.filter(m => getMeetingStatus(m.start_time, m.end_time) === 'upcoming').sort((a, b) => new Date(a.start_time).getTime() - new Date(b.start_time).getTime());
  const pastMeetings = meetings.filter(m => getMeetingStatus(m.start_time, m.end_time) === 'past').sort((a, b) => new Date(b.start_time).getTime() - new Date(a.start_time).getTime());

  const MeetingCard = ({ meeting, type }: { meeting: Meeting, type: 'running' | 'upcoming' | 'past' }) => {
    const isPast = type === 'past';
    const isLive = type === 'running';

    return (
      <motion.div
        layout
        initial={{ opacity: 0, scale: 0.95 }}
        animate={{ opacity: 1, scale: 1 }}
        exit={{ opacity: 0, scale: 0.95 }}
        className={`group relative overflow-hidden rounded-3xl border transition-all duration-300 ${isLive
            ? 'bg-gradient-to-br from-indigo-50 via-white to-blue-50 border-indigo-200 shadow-lg shadow-indigo-100/50 scale-[1.02]'
            : isPast
              ? 'bg-gray-50/50 border-gray-100 opacity-80 grayscale-[0.3]'
              : 'bg-white border-slate-100 hover:border-indigo-100 hover:shadow-xl hover:shadow-slate-200/50'
          }`}
      >
        {/* Progress Bar for Running Meetings */}
        {isLive && (
          <div className="absolute top-0 left-0 w-full h-1 bg-indigo-100">
            <motion.div
              className="h-full bg-indigo-500"
              initial={{ width: "0%" }}
              animate={{ width: "100%" }}
              transition={{ duration: 60, repeat: Infinity, ease: "linear" }}
            />
          </div>
        )}

        <div className="p-6">
          <div className="flex justify-between items-start mb-4">
            <div className="flex items-center gap-3">
              <div className={`p-3 rounded-2xl ${isLive ? 'bg-indigo-500 text-white shadow-lg shadow-indigo-200' :
                  isPast ? 'bg-gray-200 text-gray-500' : 'bg-slate-50 text-slate-400 group-hover:bg-indigo-50 group-hover:text-indigo-500 transition-colors'
                }`}>
                {isLive ? <PlayCircle size={24} className="animate-pulse" /> : isPast ? <History size={24} /> : <Calendar size={24} />}
              </div>
              <div>
                <h3 className={`font-bold text-lg tracking-tight ${isPast ? 'text-gray-600' : 'text-slate-900'}`}>{meeting.title}</h3>
                <div className="flex items-center gap-2 text-xs font-semibold uppercase tracking-wider">
                  {isLive ? (
                    <span className="flex items-center gap-1.5 text-indigo-600">
                      <span className="flex h-1.5 w-1.5 rounded-full bg-indigo-600 animate-ping"></span>
                      Now Live
                    </span>
                  ) : (
                    <span className={isPast ? 'text-gray-400' : 'text-slate-400'}>
                      {isToday(meeting.start_time) ? 'Today' : formatDate(meeting.start_time)}
                    </span>
                  )}
                </div>
              </div>
            </div>

            <button
              onClick={() => handleDelete(meeting.id)}
              className="p-2 text-gray-300 hover:text-red-500 hover:bg-red-50 rounded-xl transition-all opacity-0 group-hover:opacity-100"
            >
              <Trash2 size={18} />
            </button>
          </div>

          <div className="space-y-3 mb-6">
            <div className="flex items-center gap-3 text-sm text-slate-500 font-medium">
              <div className="w-8 h-8 rounded-lg bg-slate-50 flex items-center justify-center text-slate-400 group-hover:bg-white group-hover:text-indigo-400 transition-colors">
                <Clock size={16} />
              </div>
              <span>{formatTime(meeting.start_time)} â€” {formatTime(meeting.end_time)}</span>
            </div>
            {meeting.location && (
              <div className="flex items-center gap-3 text-sm text-slate-500 font-medium">
                <div className="w-8 h-8 rounded-lg bg-slate-50 flex items-center justify-center text-slate-400 group-hover:bg-white group-hover:text-indigo-400 transition-colors">
                  <MapPin size={16} />
                </div>
                <span className="truncate">{meeting.location}</span>
              </div>
            )}
            <div className="flex items-center gap-3 text-sm text-slate-500 font-medium">
              <div className="w-8 h-8 rounded-lg bg-slate-50 flex items-center justify-center text-slate-400 group-hover:bg-white group-hover:text-indigo-400 transition-colors">
                <Users size={16} />
              </div>
              <span>{meeting.participants?.length || 0} Attendees</span>
            </div>
          </div>

          <div className="pt-4 border-t border-slate-50 flex gap-3">
            {meeting.meeting_link && !isPast && (
              <button
                onClick={() => window.open(meeting.meeting_link, '_blank')}
                className={`flex-1 flex items-center justify-center gap-2 py-3 rounded-2xl text-sm font-bold transition-all ${isLive
                    ? 'bg-indigo-600 text-white hover:bg-indigo-700 shadow-lg shadow-indigo-100'
                    : 'bg-slate-100 text-slate-700 hover:bg-slate-200'
                  }`}
              >
                {isLive ? 'Join Meeting' : 'Meeting Details'} <ExternalLink size={16} />
              </button>
            )}
            {isPast && (
              <button className="flex-1 py-3 px-4 bg-gray-100 text-gray-500 rounded-2xl text-sm font-bold cursor-not-allowed">
                Meeting Ended
              </button>
            )}
          </div>
        </div>
      </motion.div>
    );
  };

  return (
    <div className="min-h-screen bg-slate-50/50 p-6 md:p-10">
      {/* Header */}
      <header className="max-w-7xl mx-auto flex flex-col md:flex-row md:items-center justify-between gap-8 mb-16">
        <div>
          <h1 className="text-4xl font-black text-slate-900 tracking-tight mb-2 uppercase">Meetings</h1>
          <p className="text-slate-500 text-sm font-medium">Connect with team members and host dynamic sessions.</p>
        </div>

        <div className="flex items-center gap-4">
          <button
            onClick={() => navigate("/calendar")}
            className="flex items-center gap-2.5 px-6 py-3 border border-slate-200 rounded-2xl bg-white hover:bg-slate-50 transition-all text-slate-700 text-sm font-bold shadow-sm"
          >
            <Calendar size={18} /> Calendar
          </button>
          <button
            onClick={() => navigate("/meetings/schedule")}
            className="flex items-center gap-2.5 px-6 py-3 rounded-2xl bg-indigo-600 hover:bg-indigo-700 transition-all text-white text-sm font-bold shadow-lg shadow-indigo-100 active:scale-95"
          >
            <PlusCircle size={18} /> Schedule Session
          </button>
        </div>
      </header>

      {loading ? (
        <div className="flex flex-col items-center justify-center py-32 space-y-4">
          <div className="w-12 h-12 border-4 border-indigo-100 border-t-indigo-600 rounded-full animate-spin"></div>
          <p className="text-slate-400 font-bold text-sm uppercase tracking-widest">Synchronizing Workspace...</p>
        </div>
      ) : (
        <div className="max-w-7xl mx-auto space-y-20">

          {/* ðŸ”´ ACTIVE SESSIONS */}
          {runningMeetings.length > 0 && (
            <section className="animate-in fade-in slide-in-from-bottom-4">
              <div className="flex items-center gap-3 mb-8">
                <div className="h-2 w-2 rounded-full bg-indigo-500 animate-ping"></div>
                <h2 className="text-[10px] font-black text-indigo-500 uppercase tracking-[0.2em]">Active Sessions</h2>
              </div>
              <div className="grid md:grid-cols-2 lg:grid-cols-2 gap-8">
                {runningMeetings.map(m => <MeetingCard key={m.id} meeting={m} type="running" />)}
              </div>
            </section>
          )}

          {/* ðŸ“… UPCOMING SESSIONS */}
          <section>
            <div className="flex items-center gap-3 mb-8">
              <div className="h-6 w-1 bg-slate-900 rounded-full"></div>
              <h2 className="text-[10px] font-black text-slate-900 uppercase tracking-[0.2em]">Upcoming Schedule</h2>
            </div>

            {upcomingMeetings.length > 0 ? (
              <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
                {upcomingMeetings.map(m => <MeetingCard key={m.id} meeting={m} type="upcoming" />)}
              </div>
            ) : (
              <div className="bg-white border-2 border-dashed border-slate-200 rounded-3xl p-12 text-center text-slate-400">
                <AlertCircle size={40} className="mx-auto mb-4 opacity-20" />
                <p className="font-bold text-sm uppercase tracking-wider">No meetings scheduled for now</p>
                <p className="text-xs font-medium mt-1">Take some time to focus on your deep work sessions.</p>
              </div>
            )}
          </section>

          {/* ðŸ•’ ARCHIVE / PAST SESSIONS */}
          {pastMeetings.length > 0 && (
            <section className="opacity-75 hover:opacity-100 transition-opacity">
              <div className="flex items-center gap-3 mb-8">
                <div className="h-6 w-1 bg-gray-400 rounded-full"></div>
                <h2 className="text-[10px] font-black text-gray-500 uppercase tracking-[0.2em]">Archived Sessions</h2>
              </div>
              <div className="grid md:grid-cols-3 lg:grid-cols-4 gap-6">
                {pastMeetings.map(m => <MeetingCard key={m.id} meeting={m} type="past" />)}
              </div>
            </section>
          )}

        </div>
      )}
    </div>
  );
};

export default MeetingsPage;
