import React, { useState, useEffect } from "react";
import { taskService } from "../../services/taskService";
import { meetingService } from "../../services/meetingService";
import {
  Calendar as CalendarIcon, Clock, MapPin,
  Video, ChevronLeft, ChevronRight, Zap,
  Activity, Sparkles, Loader2, ArrowUpRight, Plus, ExternalLink
} from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";

interface CalendarEvent {
  id: string | number;
  title: string;
  time?: string;
  date: string;
  type: "meeting" | "task";
  status?: string;
  details?: string;
  isOverdue?: boolean;
}

const CalendarPage: React.FC = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [events, setEvents] = useState<CalendarEvent[]>([]);
  const [loading, setLoading] = useState<boolean>(true);

  const monthNames = [
    "January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"
  ];

  const daysOfWeek = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];

  const year = currentDate.getFullYear();
  const month = currentDate.getMonth();

  const firstDay = new Date(year, month, 1);
  const lastDay = new Date(year, month + 1, 0);
  const startingDay = firstDay.getDay();
  const totalDays = lastDay.getDate();

  const daysArray: number[] = [];
  for (let i = 1; i <= totalDays; i++) daysArray.push(i);

  const today = new Date();
  const todayStr = today.toLocaleDateString("en-CA");

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [tasks, meetings] = await Promise.all([
          taskService.getTasks(),
          meetingService.getAllMeetings()
        ]);

        const taskEvents: CalendarEvent[] = tasks
          .filter(t => t.due_date)
          .map(t => ({
            id: `task-${t.id}`,
            title: t.title,
            date: t.due_date ? new Date(t.due_date).toLocaleDateString("en-CA") : "",
            type: "task",
            status: t.status,
            details: t.priority,
            isOverdue: t.is_overdue
          }));

        const meetingEvents: CalendarEvent[] = meetings.map(m => {
          const start = new Date(m.start_time);
          const end = new Date(m.end_time);
          const timeStr = `${start.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })} – ${end.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}`;

          return {
            id: `meeting-${m.id}`,
            title: m.title,
            time: timeStr,
            date: start.toLocaleDateString("en-CA"),
            type: "meeting",
            details: m.location || "Online"
          };
        });

        setEvents([...taskEvents, ...meetingEvents]);
      } catch (error) {
        console.error("Failed to fetch calendar data", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const selectedDateStr = selectedDate.toLocaleDateString("en-CA");
  const dayEvents = events.filter((e) => e.date === selectedDateStr);

  return (
    <div className="min-h-screen text-slate-900 pb-20 animate-fade-in relative">
      {/* Background Decor */}
      <div className="fixed top-0 left-0 w-full h-[300px] bg-gradient-to-b from-indigo-50/50 to-transparent pointer-events-none" />

      <div className="max-w-7xl mx-auto relative z-10">

        {/* Header Area */}
        <div className="flex flex-col md:flex-row md:items-end justify-between gap-6 mb-12">
          <div>
            <h1 className="text-5xl font-black tracking-tight text-slate-900 mb-2">Calendar</h1>
            <p className="text-slate-500 font-medium text-lg">Keep track of your meetings and task deadlines.</p>
          </div>

          <div className="flex items-center gap-4">
            <div className="flex items-center bg-white border border-slate-200 rounded-[20px] shadow-sm overflow-hidden p-1.5 hover:shadow-md transition-shadow">
              <button
                onClick={() => setCurrentDate(new Date(year, month - 1, 1))}
                className="p-3 text-slate-400 hover:text-indigo-600 hover:bg-slate-50 rounded-[14px] transition-all"
              >
                <ChevronLeft size={20} strokeWidth={2.5} />
              </button>
              <div className="px-6 min-w-[180px] text-center">
                <span className="text-sm font-black text-slate-900 uppercase tracking-widest">{monthNames[month]} {year}</span>
              </div>
              <button
                onClick={() => setCurrentDate(new Date(year, month + 1, 1))}
                className="p-3 text-slate-400 hover:text-indigo-600 hover:bg-slate-50 rounded-[14px] transition-all"
              >
                <ChevronRight size={20} strokeWidth={2.5} />
              </button>
            </div>
            <button
              onClick={() => {
                setCurrentDate(new Date());
                setSelectedDate(new Date());
              }}
              className="px-6 py-4 bg-white text-slate-900 font-bold rounded-[16px] shadow-sm border border-slate-200 hover:bg-slate-50 transition-all whitespace-nowrap hidden sm:block"
            >
              Today
            </button>
            <button className="btn-premium shadow-xl shadow-indigo-200 hover:shadow-indigo-300 hover:scale-[1.02]">
              <Plus size={20} strokeWidth={2.5} className="mr-2" />
              <span className="font-bold">Add Event</span>
            </button>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-10">

          {/* Calendar Grid */}
          <div className="lg:col-span-2 bg-white/80 backdrop-blur-xl rounded-[40px] border border-slate-200/50 shadow-xl shadow-slate-200/50 overflow-hidden">
            <div className="grid grid-cols-7 text-center font-black text-slate-400 text-[10px] uppercase tracking-[0.2em] py-8 border-b border-slate-100/50">
              {daysOfWeek.map((d) => (
                <div key={d}>{d}</div>
              ))}
            </div>

            <div className="grid grid-cols-7 bg-slate-50/50 gap-px border-l border-t border-slate-100/50">
              {Array(startingDay).fill(null).map((_, i) => (
                <div key={`empty-${i}`} className="bg-white/40 aspect-square" />
              ))}
              {daysArray.map((day) => {
                const d = new Date(year, month, day);
                const dateStr = d.toLocaleDateString("en-CA");
                const isTodayStr = dateStr === todayStr;
                const dateEvents = events.filter((e) => e.date === dateStr);
                const isSelected = dateStr === selectedDateStr;

                return (
                  <div
                    key={day}
                    onClick={() => setSelectedDate(new Date(year, month, day))}
                    className={`relative aspect-square p-3 lg:p-4 cursor-pointer transition-all bg-white hover:z-10 group ${isSelected ? 'ring-2 ring-inset ring-indigo-500 z-10 shadow-lg' : 'hover:bg-indigo-50/30 hover:shadow-inner'
                      }`}
                  >
                    <div className="flex justify-between items-start mb-2">
                      <span className={`text-sm font-bold flex items-center justify-center w-8 h-8 rounded-xl transition-all ${isSelected
                        ? 'text-indigo-600 bg-indigo-50'
                        : isTodayStr
                          ? 'text-white bg-indigo-600 shadow-lg shadow-indigo-200 scale-110'
                          : 'text-slate-400 group-hover:text-slate-900 group-hover:bg-white group-hover:shadow-sm'
                        }`}>{day}</span>
                      {dateEvents.length > 0 && !isSelected && !isTodayStr && (
                        <div className="flex gap-0.5">
                          {dateEvents.slice(0, 3).map((_, i) => (
                            <div key={i} className={`w-1.5 h-1.5 rounded-full ${i === 0 ? 'bg-indigo-500' : 'bg-indigo-200'}`} />
                          ))}
                        </div>
                      )}
                    </div>
                    <div className="space-y-1.5 overflow-hidden hidden sm:block">
                      {dateEvents.slice(0, 2).map((e, idx) => (
                        <div
                          key={idx}
                          className={`text-[9px] font-bold truncate px-2 py-1.5 rounded-[8px] flex items-center gap-1 ${e.type === 'meeting'
                            ? 'bg-indigo-50 text-indigo-700 border border-indigo-100/50'
                            : 'bg-slate-50 text-slate-600 border border-slate-100'
                            }`}
                        >
                          <div className={`w-1 h-1 rounded-full ${e.type === 'meeting' ? 'bg-indigo-500' : 'bg-slate-400'}`} />
                          {e.title}
                        </div>
                      ))}
                      {dateEvents.length > 2 && (
                        <div className="text-[9px] font-bold text-slate-300 pl-2">
                          + {dateEvents.length - 2} more
                        </div>
                      )}
                    </div>
                  </div>
                );
              })}
              {/* Fill remaining empty cells */}
              {Array(42 - startingDay - totalDays).fill(null).map((_, i) => (
                <div key={`empty-end-${i}`} className="bg-white/40 aspect-square" />
              ))}
            </div>
          </div>

          {/* Side panel */}
          <div className="space-y-10">
            <div className="flex flex-col gap-6">
              <div className="px-2">
                <h3 className="text-4xl font-black text-slate-900 tracking-tight">
                  {selectedDate.toLocaleDateString('en-US', { day: 'numeric' })}
                </h3>
                <div className="flex items-center gap-3 mt-1">
                  <span className="text-xl font-medium text-slate-400">{selectedDate.toLocaleDateString('en-US', { month: 'long' })}</span>
                  <span className="w-1 h-1 bg-slate-200 rounded-full" />
                  <span className="text-sm font-bold text-indigo-500 uppercase tracking-widest bg-indigo-50 px-3 py-1 rounded-lg">
                    {selectedDate.toLocaleDateString('en-US', { weekday: 'long' })}
                  </span>
                </div>
              </div>

              <div className="space-y-4 max-h-[500px] overflow-y-auto custom-scrollbar pr-2">
                {loading ? (
                  <div className="py-16 text-center glass-card rounded-[32px]">
                    <Loader2 className="w-10 h-10 text-indigo-600 animate-spin mx-auto mb-4" />
                    <p className="text-xs font-bold text-slate-400 uppercase tracking-widest">Loading events...</p>
                  </div>
                ) : dayEvents.length > 0 ? (
                  <div className="space-y-4">
                    {dayEvents.map((event) => (
                      <motion.div
                        layout
                        initial={{ opacity: 0, y: 10 }}
                        animate={{ opacity: 1, y: 0 }}
                        key={event.id}
                        className={`p-6 rounded-[32px] border transition-all cursor-pointer group hover:scale-[1.02] ${event.type === 'meeting'
                          ? 'bg-gradient-to-br from-indigo-600 to-violet-700 text-white border-transparent shadow-xl shadow-indigo-200'
                          : 'bg-white text-slate-900 border-slate-200 shadow-sm hover:border-indigo-200 hover:shadow-md'
                          }`}
                      >
                        <div className="flex items-start gap-5">
                          <div className={`p-3.5 rounded-[20px] shadow-sm ${event.type === 'meeting' ? 'bg-white/20 text-white backdrop-blur-sm' : 'bg-slate-50 text-indigo-600'
                            }`}>
                            {event.type === 'meeting' ? <Video size={20} strokeWidth={2.5} /> : <Zap size={20} strokeWidth={2.5} />}
                          </div>
                          <div className="flex-1 min-w-0">
                            <div className="flex items-center justify-between mb-2">
                              <p className={`text-[10px] font-black uppercase tracking-widest ${event.type === 'meeting' ? 'text-indigo-200' : 'text-slate-400'
                                }`}>{event.type}</p>
                              {event.isOverdue && <span className="bg-rose-500 text-white text-[9px] font-bold px-2 py-0.5 rounded-full">OVERDUE</span>}
                            </div>

                            <h4 className="text-lg font-black leading-tight mb-3 line-clamp-2 tracking-tight">{event.title}</h4>
                            <div className="flex flex-col gap-2.5">
                              <div className={`flex items-center gap-2 text-xs font-bold ${event.type === 'meeting' ? 'text-indigo-100' : 'text-slate-500'
                                }`}>
                                <Clock size={14} className={event.type === 'meeting' ? 'text-indigo-300' : 'text-slate-400'} />
                                {event.time || `${event.details} Priority`}
                              </div>
                              {event.type === 'meeting' && event.details && (
                                <div className="flex items-center gap-2 text-xs font-bold text-indigo-200">
                                  <MapPin size={14} className="text-indigo-300" />
                                  {event.details}
                                </div>
                              )}
                            </div>
                          </div>
                        </div>
                      </motion.div>
                    ))}
                  </div>
                ) : (
                  <div className="py-20 text-center glass-card rounded-[32px] border-dashed border-slate-300/50">
                    <div className="w-16 h-16 bg-slate-50 rounded-[24px] flex items-center justify-center mx-auto mb-4 border border-slate-100">
                      <CalendarIcon className="text-slate-300" size={32} />
                    </div>
                    <h4 className="text-lg font-bold text-slate-900">Quiet day...</h4>
                    <p className="text-xs text-slate-400 font-bold uppercase tracking-wider mt-2">No scheduled events found.</p>
                  </div>
                )}
              </div>
            </div>

            {/* AI Insights Card */}
            <div className="bg-slate-900 rounded-[40px] p-10 text-white relative overflow-hidden shadow-2xl shadow-slate-200 group hover:scale-[1.02] transition-transform duration-500">
              <div className="absolute top-0 right-0 p-8 opacity-10 group-hover:opacity-20 transition-opacity">
                <Sparkles size={120} />
              </div>
              <div className="absolute -bottom-20 -left-20 w-60 h-60 bg-indigo-500/20 rounded-full blur-[80px]" />

              <div className="relative z-10">
                <div className="flex items-center gap-4 mb-8">
                  <div className="w-12 h-12 bg-gradient-to-tr from-indigo-500 to-violet-500 rounded-[18px] flex items-center justify-center shadow-lg shadow-indigo-500/30">
                    <Activity size={24} className="text-white" />
                  </div>
                  <div>
                    <p className="text-[10px] font-bold uppercase tracking-[0.2em] text-indigo-400 mb-1">AI Assistant</p>
                    <p className="text-lg font-black tracking-tight">Weekly Forecast</p>
                  </div>
                </div>
                <p className="text-sm font-medium leading-relaxed mb-8 text-slate-300">
                  You have a dense schedule on Wednesday. Consider moving some tasks to Tuesday to maintain optimal productivity.
                </p>
                <button className="w-full py-4 bg-white text-slate-900 rounded-[20px] font-black text-xs hover:bg-indigo-50 transition-all flex items-center justify-center gap-3 shadow-lg hover:shadow-xl group/btn">
                  View Full Report
                  <ArrowUpRight size={16} className="text-indigo-600 group-hover/btn:translate-x-0.5 group-hover/btn:-translate-y-0.5 transition-transform" />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CalendarPage;
