import api from './api';

export interface User {
    id: number;
    full_name: string;
    email: string;
    // Initialize with optional fields if backend returns them
    avatar?: string;
    is_active?: boolean;
}

export interface ConversationUser extends User {
    last_message?: string;
    last_message_time?: string;
    unread_count?: number;
}

export interface Message {
    id: number;
    sender_id: number;
    receiver_id: number;
    content: string;
    timestamp: string;
    is_read: boolean;
}

export const messageService = {
    getConversations: async (): Promise<ConversationUser[]> => {
        const response = await api.get('/messages/conversations');
        return response.data;
    },

    getMessages: async (otherUserId: number, skip = 0, limit = 50): Promise<Message[]> => {
        const response = await api.get(`/messages/${otherUserId}`, {
            params: { skip, limit }
        });
        return response.data;
    },

    sendMessage: async (receiverId: number, content: string): Promise<Message> => {
        const response = await api.post('/messages/', {
            receiver_id: receiverId,
            content: content
        });
        return response.data;
    }
};
