import React, { useState, useEffect } from "react";
import {
  Plus,
  Search,
  Loader2,
  Trash2,
  Edit3,
  Calendar,
  User as UserIcon,
  Filter,
  AlertCircle,
  X,
  Clock,
  CheckCircle2,
  Zap,
  ArrowRight,
  Shield,
  Layers,
  ChevronRight,
  ArrowUpRight,
  MoreVertical,
  ClipboardList
} from "lucide-react";
import { useNavigate } from "react-router-dom";
import { taskService, Task } from "../../services/taskService";
import { motion, AnimatePresence } from "framer-motion";


const TasksPage: React.FC = () => {
  const navigate = useNavigate();
  const [tasks, setTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState<string>("All");
  const [selectedTask, setSelectedTask] = useState<Task | null>(null);

  useEffect(() => {
    fetchTasks();
    const pollInterval = setInterval(() => {
      fetchTasks(false);
    }, 30000);

    return () => {
      clearInterval(pollInterval);
    };
  }, []);

  const fetchTasks = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const data = await taskService.getTasks();
      setTasks(data);
    } catch (error) {
      console.error("Failed to fetch tasks", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  const normalizeStatus = (status: string) => {
    const s = status ? status.toLowerCase() : "";
    if (s === "todo" || s === "upcoming") return "Upcoming";
    if (s === "in_progress" || s === "in progress" || s === "inprogress") return "In Progress";
    if (s === "completed" || s === "done") return "Completed";
    return "Upcoming";
  };

  const getTimeStatus = (dueDate: string | null | undefined, status: string) => {
    const normStatus = normalizeStatus(status);
    if (normStatus === "Completed") return { label: "Completed", style: "bg-emerald-50 text-emerald-600 border-emerald-100" };
    if (!dueDate) return { label: "Planned", style: "bg-slate-50 text-slate-500 border-slate-100" };

    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const due = new Date(dueDate);
    due.setHours(0, 0, 0, 0);

    if (due < today) return { label: "Overdue", style: "bg-rose-50 text-rose-600 border-rose-100" };
    if (due.getTime() === today.getTime()) return { label: "Due Today", style: "bg-amber-50 text-amber-600 border-amber-100" };

    return { label: "Active", style: "bg-indigo-50 text-indigo-600 border-indigo-100" };
  };

  const handleDelete = async (e: React.MouseEvent, id: number) => {
    e.stopPropagation();
    if (!window.confirm("Are you sure you want to delete this task?")) return;
    try {
      await taskService.deleteTask(id);
      setTasks(prev => prev.filter(t => t.id !== id));
      if (selectedTask?.id === id) setSelectedTask(null);
    } catch (error) {
      console.error("Failed to delete task");
    }
  };

  const filteredTasks = tasks.filter(task => {
    const matchesSearch = task.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (task.description && task.description.toLowerCase().includes(searchQuery.toLowerCase()));
    const matchesStatus = statusFilter === "All" || normalizeStatus(task.status) === statusFilter;
    return matchesSearch && matchesStatus;
  });

  return (
    <div className="min-h-screen text-slate-900 pb-12 animate-fade-in relative">

      {/* Background Decor */}
      {/* Background Decor */}
      <div className="fixed top-0 left-0 w-full h-[300px] bg-gradient-to-b from-indigo-50/50 to-transparent pointer-events-none" />

      {/* Main Content Area */}
      <div className={`transition-all duration-500 ${selectedTask ? 'lg:mr-[480px]' : ''}`}>

        {/* Header Section */}
        <div className="flex flex-col md:flex-row md:items-end justify-between gap-6 mb-12 relative z-10">
          <div>
            <h1 className="text-5xl font-black tracking-tight text-slate-900 mb-2">My Tasks</h1>
            <p className="text-slate-500 font-medium text-lg">Track progress and manage team objectives.</p>
          </div>
          <button
            onClick={() => navigate("/tasks/new")}
            className="btn-premium shadow-xl shadow-indigo-200 hover:shadow-indigo-300 hover:scale-[1.02]"
          >
            <Plus size={20} strokeWidth={2.5} />
            <span className="font-bold">Create Task</span>
          </button>
        </div>

        {/* Toolbar */}
        <div className="glass-card p-2 rounded-[24px] mb-8 flex flex-col lg:flex-row items-center gap-2 shadow-lg shadow-slate-200/50 relative z-10">
          <div className="relative flex-1 w-full group">
            <Search className="absolute left-5 top-1/2 -translate-y-1/2 text-slate-400 group-focus-within:text-indigo-600 transition-colors" size={20} />
            <input
              type="text"
              placeholder="Search by keyword..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-14 pr-4 py-4 bg-transparent border-none focus:ring-0 text-slate-900 font-semibold placeholder:text-slate-400 text-sm"
            />
          </div>
          <div className="h-8 w-px bg-slate-200 mx-2 hidden lg:block" />
          <div className="flex items-center gap-1 p-1 bg-slate-50 rounded-[20px] w-full lg:w-auto overflow-x-auto">
            {["All", "Upcoming", "In Progress", "Completed"].map((status) => (
              <button
                key={status}
                onClick={() => setStatusFilter(status)}
                className={`px-6 py-3 text-xs font-black uppercase tracking-wider rounded-[16px] transition-all whitespace-nowrap ${statusFilter === status
                  ? "bg-white text-indigo-600 shadow-md shadow-indigo-100 ring-1 ring-black/5"
                  : "text-slate-500 hover:text-slate-900 hover:bg-white/50"
                  }`}
              >
                {status}
              </button>
            ))}
          </div>
        </div>

        {/* Task List Rendering */}
        <div className="space-y-4 relative z-10">
          {loading ? (
            <div className="py-32 text-center glass-card rounded-[32px]">
              <div className="w-16 h-16 border-4 border-indigo-50 border-t-indigo-600 rounded-full animate-spin mx-auto mb-6" />
              <p className="text-sm font-bold text-slate-400 uppercase tracking-widest animate-pulse">Syncing tasks...</p>
            </div>
          ) : filteredTasks.length === 0 ? (
            <div className="py-32 text-center glass-card rounded-[32px]">
              <div className="w-20 h-20 bg-slate-50 rounded-[24px] flex items-center justify-center mx-auto mb-6 shadow-inner">
                <ClipboardList className="text-slate-300" size={32} />
              </div>
              <h4 className="text-xl font-black text-slate-900">No tasks found</h4>
              <p className="text-slate-400 font-medium mt-2">Time to create a new one!</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 gap-4">
              {filteredTasks.map((task) => {
                const timeStatus = getTimeStatus(task.due_date, task.status);
                const isSelected = selectedTask?.id === task.id;
                return (
                  <motion.div
                    layout
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    key={task.id}
                    onClick={() => setSelectedTask(task)}
                    className={`group relative p-6 bg-white border rounded-[32px] transition-all duration-300 cursor-pointer flex flex-col md:flex-row md:items-center gap-6 ${isSelected
                      ? 'border-indigo-500 ring-4 ring-indigo-500/10 shadow-2xl shadow-indigo-500/10 z-20 transform scale-[1.01]'
                      : 'border-slate-100/80 hover:border-indigo-200 hover:shadow-xl hover:shadow-indigo-100/50 hover:-translate-y-1'
                      }`}
                  >
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center gap-3 mb-3">
                        <span className={`px-3 py-1 text-[10px] font-black uppercase tracking-wider rounded-lg border ${timeStatus.style}`}>
                          {timeStatus.label}
                        </span>
                        {task.priority === 'High' && (
                          <span className="flex items-center gap-1.5 px-3 py-1 text-[10px] font-black bg-rose-50 text-rose-600 border border-rose-100 rounded-lg uppercase tracking-wider">
                            <AlertCircle size={10} strokeWidth={3} /> Priority
                          </span>
                        )}
                      </div>
                      <h4 className={`text-lg font-bold text-slate-900 mb-2 leading-tight group-hover:text-indigo-600 transition-colors ${task.status === 'Completed' ? 'line-through text-slate-400' : ''}`}>
                        {task.title}
                      </h4>
                      <p className="text-sm text-slate-500 font-medium line-clamp-1 opacity-80">{task.description || "No description provided."}</p>
                    </div>

                    <div className="flex items-center justify-between md:justify-end gap-10 shrink-0 mt-4 md:mt-0 pt-4 md:pt-0 border-t md:border-t-0 border-slate-50">
                      <div className="flex flex-col md:items-end">
                        <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest mb-1">Due Date</p>
                        <div className="flex items-center gap-2 text-slate-900 bg-slate-50 px-3 py-1.5 rounded-xl group-hover:bg-white group-hover:shadow-sm transition-all">
                          <Calendar size={14} className="text-slate-400 group-hover:text-indigo-500" />
                          <p className="text-xs font-bold">
                            {task.due_date ? new Date(task.due_date).toLocaleDateString(undefined, { month: 'short', day: 'numeric', year: 'numeric' }) : "No Date"}
                          </p>
                        </div>
                      </div>

                      <div className="flex items-center gap-2 opacity-0 group-hover:opacity-100 lg:opacity-100 transition-all duration-300">
                        <div className="w-10 h-10 rounded-full border border-slate-100 flex items-center justify-center text-slate-300 group-hover:bg-indigo-600 group-hover:text-white group-hover:border-indigo-600 group-hover:shadow-lg group-hover:shadow-indigo-200 transition-all">
                          <ChevronRight size={20} />
                        </div>
                      </div>
                    </div>
                  </motion.div>
                );
              })}
            </div>
          )}
        </div>
      </div>

      {/* Slide-over Panel */}
      <AnimatePresence>
        {selectedTask && (
          <motion.div
            initial={{ x: "100%", opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            exit={{ x: "100%", opacity: 0 }}
            transition={{ type: "spring", damping: 30, stiffness: 300 }}
            className="fixed top-4 right-4 bottom-4 w-full lg:w-[460px] glass-card rounded-[40px] shadow-2xl z-50 flex flex-col overflow-hidden border border-white/40"
          >
            <div className="p-8 pb-6 flex items-center justify-between border-b border-slate-100/50 bg-white/50 backdrop-blur-md sticky top-0 z-10">
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 bg-indigo-50 rounded-2xl flex items-center justify-center text-indigo-600">
                  <Layers size={20} />
                </div>
                <div>
                  <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest">Task Details</p>
                  <p className="text-xs font-bold text-slate-900">ID: #{selectedTask.id}</p>
                </div>
              </div>
              <button
                onClick={() => setSelectedTask(null)}
                className="w-10 h-10 flex items-center justify-center bg-white border border-slate-100 text-slate-400 hover:bg-slate-50 hover:text-slate-900 rounded-xl transition-all shadow-sm"
              >
                <X size={20} />
              </button>
            </div>

            <div className="flex-1 overflow-y-auto p-8 space-y-8 custom-scrollbar bg-white/30">
              <div className="space-y-5">
                <div className="flex gap-2">
                  <span className={`px-4 py-2 text-[10px] font-black rounded-xl border ${getTimeStatus(selectedTask.due_date, selectedTask.status).style}`}>
                    {normalizeStatus(selectedTask.status).toUpperCase()}
                  </span>
                  {selectedTask.priority === 'High' && (
                    <span className="px-4 py-2 text-[10px] font-black bg-rose-50 text-rose-600 border border-rose-100 rounded-xl uppercase tracking-wider flex items-center gap-2">
                      <AlertCircle size={12} strokeWidth={3} /> High Priority
                    </span>
                  )}
                </div>
                <h3 className="text-3xl font-black text-slate-900 tracking-tight leading-snug">{selectedTask.title}</h3>
                <div className="bg-white/50 p-6 rounded-[24px] border border-slate-100/50">
                  <p className="text-sm text-slate-600 font-medium leading-relaxed">
                    {selectedTask.description || "No description provided for this task."}
                  </p>
                </div>
              </div>

              <div className="space-y-4">
                <h4 className="text-xs font-bold text-slate-900 uppercase tracking-wider flex items-center gap-2">
                  <MoreVertical size={14} className="text-indigo-500" /> Meta Data
                </h4>

                <div className="grid grid-cols-2 gap-4">
                  <div className="p-5 bg-white rounded-[24px] border border-slate-100 shadow-sm hover:shadow-md transition-shadow">
                    <div className="w-10 h-10 bg-indigo-50 rounded-xl flex items-center justify-center text-indigo-500 mb-3">
                      <Calendar size={20} />
                    </div>
                    <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest mb-1">Due Date</p>
                    <p className="text-sm font-bold text-slate-900">
                      {selectedTask.due_date ? new Date(selectedTask.due_date).toLocaleDateString(undefined, { month: 'short', day: 'numeric' }) : "No Date"}
                    </p>
                  </div>

                  <div className="p-5 bg-white rounded-[24px] border border-slate-100 shadow-sm hover:shadow-md transition-shadow">
                    <div className="w-10 h-10 bg-emerald-50 rounded-xl flex items-center justify-center text-emerald-500 mb-3">
                      <UserIcon size={20} />
                    </div>
                    <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest mb-1">Assignee</p>
                    <p className="text-sm font-bold text-slate-900 truncate">{selectedTask.assigned_to_user?.name || "Unassigned"}</p>
                  </div>
                </div>
              </div>
            </div>

            <div className="p-6 border-t border-slate-100 bg-white/80 backdrop-blur-md mt-auto flex gap-3">
              <button
                onClick={() => navigate(`/tasks/edit/${selectedTask.id}`)}
                className="flex-1 py-4 bg-slate-900 text-white rounded-2xl font-bold text-sm hover:bg-slate-800 transition-all flex items-center justify-center gap-2 shadow-lg shadow-slate-200"
              >
                <Edit3 size={18} />
                Edit
              </button>
              <button
                onClick={(e) => handleDelete(e, selectedTask.id)}
                className="w-14 h-14 bg-white text-rose-500 border border-slate-200 rounded-2xl font-bold text-sm hover:bg-rose-50 hover:border-rose-200 transition-all flex items-center justify-center shadow-lg shadow-slate-100"
              >
                <Trash2 size={20} />
              </button>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default TasksPage;
