import React, { useState } from "react";
import { Calendar, Clock, FileText, ArrowLeft, CheckCircle2, Link as LinkIcon, Sparkles } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { meetingService } from "../../services/meetingService";

const ScheduleMeetingPage: React.FC = () => {
  const navigate = useNavigate();
  const [meeting, setMeeting] = useState({
    title: "",
    date: "",
    time: "",
    end_time: "",
    notes: "",
    location: "",
    meeting_link: "",
    is_recurring_mon_fri: false
  });
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!meeting.title.trim() || !meeting.date || !meeting.time || !meeting.end_time) {
      alert("⚠️ Please fill in all required fields.");
      return;
    }

    setLoading(true);
    try {
      const createMeetingData = (dateStr: string) => ({
        title: meeting.title,
        description: meeting.notes,
        start_time: new Date(`${dateStr}T${meeting.time}:00`).toISOString(),
        end_time: new Date(`${dateStr}T${meeting.end_time}:00`).toISOString(),
        meeting_link: meeting.meeting_link,
        location: meeting.location
      });

      if (meeting.is_recurring_mon_fri) {
        const baseDate = new Date(meeting.date);
        const datesToCreate: string[] = [];

        const day = baseDate.getDay();
        const diff = baseDate.getDate() - day + (day === 0 ? -6 : 1);
        const monday = new Date(baseDate.setDate(diff));

        for (let i = 0; i < 5; i++) {
          const d = new Date(monday);
          d.setDate(monday.getDate() + i);
          datesToCreate.push(d.toISOString().split('T')[0]);
        }

        await Promise.all(datesToCreate.map(d => meetingService.createMeeting(createMeetingData(d))));
        alert(`✅ Recurring Meetings "${meeting.title}" (Mon-Fri) Scheduled!`);
      } else {
        await meetingService.createMeeting(createMeetingData(meeting.date));
        alert(`✅ Meeting "${meeting.title}" Scheduled!`);
      }

      navigate("/meetings");
    } catch (error) {
      console.error("Failed to schedule meeting", error);
      alert("Failed to schedule meeting. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-[#f8fafc] p-6 md:p-12 lg:p-20">
      <motion.main
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="max-w-3xl mx-auto bg-white rounded-[32px] shadow-2xl shadow-slate-200/50 border border-slate-100 overflow-hidden"
      >
        <div className="p-8 md:p-12">
          {/* Header */}
          <div className="flex items-center justify-between mb-12">
            <button
              onClick={() => navigate("/meetings")}
              className="group flex items-center text-slate-500 hover:text-indigo-600 font-bold transition-all"
            >
              <div className="p-2 bg-slate-50 rounded-xl group-hover:bg-indigo-50 mr-3 transition-colors">
                <ArrowLeft className="w-5 h-5" />
              </div>
              Return to Schedule
            </button>
            <div className="hidden md:flex items-center gap-2 px-4 py-2 bg-indigo-50 text-indigo-700 rounded-full text-xs font-black uppercase tracking-widest">
              <Sparkles className="w-4 h-4" />
              <span>Premium Planner</span>
            </div>
          </div>

          <h1 className="text-4xl font-[900] text-slate-900 mb-2 tracking-tight">
            Design your <span className="text-indigo-600">Collaboration</span>
          </h1>
          <p className="text-slate-500 font-medium mb-12">Set up a new sync and keep your team aligned.</p>

          {/* Form */}
          <form onSubmit={handleSubmit} className="space-y-8">
            <div className="space-y-2">
              <label className="text-sm font-black text-slate-800 uppercase tracking-widest">
                Session Title <span className="text-rose-500">*</span>
              </label>
              <input
                type="text"
                value={meeting.title}
                onChange={(e) => setMeeting({ ...meeting, title: e.target.value })}
                placeholder="e.g., Weekly Strategic Review"
                className="w-full px-6 py-4 bg-slate-50 border-2 border-transparent rounded-2xl focus:bg-white focus:border-indigo-600 focus:outline-none transition-all placeholder:text-slate-400 font-bold text-slate-900 shadow-inner"
                required
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              <div className="space-y-2">
                <label className="text-sm font-black text-slate-800 uppercase tracking-widest flex items-center gap-2">
                  <Calendar className="w-4 h-4 text-indigo-600" /> Event Date
                </label>
                <input
                  type="date"
                  value={meeting.date}
                  onChange={(e) => setMeeting({ ...meeting, date: e.target.value })}
                  className="w-full px-6 py-4 bg-slate-50 border-2 border-transparent rounded-2xl focus:bg-white focus:border-indigo-600 focus:outline-none transition-all font-bold text-slate-900 shadow-inner"
                  required
                />
              </div>

              <div className="flex gap-4">
                <div className="flex-1 space-y-2">
                  <label className="text-sm font-black text-slate-800 uppercase tracking-widest flex items-center gap-2">
                    <Clock className="w-4 h-4 text-indigo-600" /> Start
                  </label>
                  <input
                    type="time"
                    value={meeting.time}
                    onChange={(e) => setMeeting({ ...meeting, time: e.target.value })}
                    className="w-full px-4 py-4 bg-slate-50 border-2 border-transparent rounded-2xl focus:bg-white focus:border-indigo-600 focus:outline-none transition-all font-bold text-slate-900 shadow-inner"
                    required
                  />
                </div>
                <div className="flex-1 space-y-2">
                  <label className="text-sm font-black text-slate-800 uppercase tracking-widest flex items-center gap-2">
                    <Clock className="w-4 h-4 text-indigo-600" /> End
                  </label>
                  <input
                    type="time"
                    value={meeting.end_time}
                    onChange={(e) => setMeeting({ ...meeting, end_time: e.target.value })}
                    className="w-full px-4 py-4 bg-slate-50 border-2 border-transparent rounded-2xl focus:bg-white focus:border-indigo-600 focus:outline-none transition-all font-bold text-slate-900 shadow-inner"
                    required
                  />
                </div>
              </div>
            </div>

            <motion.div
              whileHover={{ scale: 1.01 }}
              className="flex items-center gap-4 p-6 bg-gradient-to-r from-indigo-50 to-white rounded-3xl border-2 border-indigo-100 shadow-lg shadow-indigo-100/20"
            >
              <div className="relative inline-flex items-center cursor-pointer">
                <input
                  type="checkbox"
                  id="recurring"
                  checked={meeting.is_recurring_mon_fri}
                  onChange={(e) => setMeeting({ ...meeting, is_recurring_mon_fri: e.target.checked })}
                  className="sr-only peer"
                />
                <div className="w-11 h-6 bg-slate-200 peer-focus:outline-none rounded-full peer peer-checked:after:translate-x-full after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-indigo-600"></div>
              </div>
              <label htmlFor="recurring" className="text-sm font-black text-indigo-900 cursor-pointer uppercase tracking-tight">
                Repeat for the active week (Monday to Friday)
              </label>
            </motion.div>

            <div className="space-y-2">
              <label className="text-sm font-black text-slate-800 uppercase tracking-widest flex items-center gap-2">
                <LinkIcon className="w-4 h-4 text-indigo-600" /> Virtual Access Link
              </label>
              <input
                type="url"
                value={meeting.meeting_link}
                onChange={(e) => setMeeting({ ...meeting, meeting_link: e.target.value })}
                placeholder="https://zoom.us/j/unique-meeting-id"
                className="w-full px-6 py-4 bg-slate-50 border-2 border-transparent rounded-2xl focus:bg-white focus:border-indigo-600 focus:outline-none transition-all placeholder:text-slate-400 font-bold text-slate-900 shadow-inner"
              />
            </div>

            <div className="space-y-2">
              <label className="text-sm font-black text-slate-800 uppercase tracking-widest flex items-center gap-2">
                <FileText className="w-4 h-4 text-indigo-600" /> Discussion Notes
              </label>
              <textarea
                value={meeting.notes}
                onChange={(e) => setMeeting({ ...meeting, notes: e.target.value })}
                rows={4}
                placeholder="What's the agenda? Add participants and key goals."
                className="w-full px-6 py-4 bg-slate-50 border-2 border-transparent rounded-2xl focus:bg-white focus:border-indigo-600 focus:outline-none transition-all placeholder:text-slate-400 font-bold text-slate-900 shadow-inner resize-none"
              />
            </div>

            <div className="flex justify-end items-center gap-6 pt-8">
              <button
                type="button"
                onClick={() => navigate("/meetings")}
                className="text-slate-400 hover:text-slate-900 font-black uppercase tracking-widest transition-colors"
                disabled={loading}
              >
                Cancel
              </button>
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                type="submit"
                disabled={loading}
                className={`bg-slate-900 text-white px-10 py-5 rounded-2xl font-black uppercase tracking-widest shadow-2xl shadow-slate-900/40 hover:bg-slate-800 transition-all ${loading ? 'opacity-70 cursor-not-allowed' : ''}`}
              >
                {loading ? "Syncing..." : "Finalize Schedule"}
              </motion.button>
            </div>
          </form>
        </div>
      </motion.main>
    </div>
  );
};

export default ScheduleMeetingPage;
