import React, { useState, useEffect } from "react";
import {
  Plus,
  Calendar,
  User as UserIcon,
  Search,
  Loader2,
  Trash2,
  X,
  Edit3,
  ArrowRight,
  Layers,
  CheckCircle,
  Clock,
  Layout,
  ExternalLink,
  Flag,
  MoreHorizontal,
  Clock3,
  AlignLeft
} from "lucide-react";
import { useNavigate } from "react-router-dom";
import { taskService, Task } from "../../services/taskService";
import { motion, AnimatePresence } from "framer-motion";

const TasksPage: React.FC = () => {
  const navigate = useNavigate();
  const [tasks, setTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [activeTab, setActiveTab] = useState<string>("All");
  const [selectedTask, setSelectedTask] = useState<Task | null>(null);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    setLoading(true);
    try {
      const data = await taskService.getTasks();
      setTasks(data);
    } catch (error) {
      console.error("Failed to fetch tasks", error);
    } finally {
      setLoading(false);
    }
  };

  const normalizeStatus = (status: string) => {
    const s = status ? status.toLowerCase() : "";
    if (s === "todo" || s === "upcoming") return "Upcoming";
    if (s === "in progress" || s === "inprogress") return "In Progress";
    if (s === "completed" || s === "done") return "Completed";
    return "Upcoming";
  };

  const getFilteredTasks = () => {
    return tasks.filter(t => {
      const matchesSearch = t.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        (t.description && t.description.toLowerCase().includes(searchQuery.toLowerCase()));
      const matchesTab = activeTab === "All" || normalizeStatus(t.status) === activeTab;
      return matchesSearch && matchesTab;
    });
  };

  const handleStatusUpdate = async (e: React.MouseEvent, task: Task, newStatus: string) => {
    e.stopPropagation();
    setTasks(prev => prev.map(t => t.id === task.id ? { ...t, status: newStatus } : t));
    if (selectedTask?.id === task.id) setSelectedTask({ ...selectedTask, status: newStatus });
    try {
      await taskService.updateTask(task.id, { status: newStatus });
    } catch (error) {
      console.error("Failed to update status");
      fetchTasks();
    }
  };

  const onTaskDelete = async (e: React.MouseEvent, id: number) => {
    e.stopPropagation();
    if (!window.confirm("Delete this task?")) return;
    setTasks(prev => prev.filter(t => t.id !== id));
    if (selectedTask?.id === id) setSelectedTask(null);
    try {
      await taskService.deleteTask(id);
    } catch (error) {
      console.error("Failed to delete task");
    }
  };

  const stats = {
    total: tasks.length,
    upcoming: tasks.filter(t => normalizeStatus(t.status) === "Upcoming").length,
    progress: tasks.filter(t => normalizeStatus(t.status) === "In Progress").length,
    done: tasks.filter(t => normalizeStatus(t.status) === "Completed").length,
  };

  return (
    <div className="min-h-screen bg-[#F4F7FB] flex">
      {/* ðŸ“‹ Main List Area */}
      <div className={`flex-1 p-6 md:p-10 lg:p-12 transition-all duration-500 ease-in-out ${selectedTask ? 'mr-0 lg:mr-[450px]' : ''}`}>
        <div className="max-w-6xl mx-auto space-y-10">

          {/* Header */}
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-4xl font-black text-slate-900 tracking-tighter flex items-center gap-2">
                <span className="bg-indigo-600 w-2 h-8 rounded-full"></span>
                Board.
              </h1>
              <p className="text-slate-400 font-medium ml-4">Advanced project node monitoring.</p>
            </div>
            <button
              onClick={() => navigate("/tasks/new")}
              className="bg-slate-900 hover:bg-black text-white px-6 py-3 rounded-2xl font-bold flex items-center gap-2 transition-all active:scale-95 shadow-xl shadow-slate-200"
            >
              <Plus size={20} /> New Task
            </button>
          </div>

          {/* Quick Stats Grid */}
          <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
            <StatCard label="Total" count={stats.total} icon={<Layout size={18} />} color="bg-slate-900" />
            <StatCard label="Upcoming" count={stats.upcoming} icon={<Clock size={18} />} color="bg-indigo-600" />
            <StatCard label="In Progress" count={stats.progress} icon={<Loader2 size={18} />} color="bg-orange-500" />
            <StatCard label="Completed" count={stats.done} icon={<CheckCircle size={18} />} color="bg-green-500" />
          </div>

          {/* Filter Bar */}
          <div className="bg-white p-2 rounded-[24px] shadow-sm border border-slate-100 flex items-center gap-2">
            <div className="relative flex-1">
              <Search className="absolute left-5 top-1/2 -translate-y-1/2 text-slate-300" size={18} />
              <input
                type="text"
                placeholder="Search workspace..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="w-full pl-12 pr-4 py-4 bg-transparent text-slate-700 font-bold outline-none placeholder:text-slate-300"
              />
            </div>
            <div className="hidden md:flex bg-slate-50 p-1 rounded-2xl gap-1">
              {["All", "Upcoming", "In Progress", "Completed"].map(tab => (
                <button
                  key={tab}
                  onClick={() => setActiveTab(tab)}
                  className={`px-4 py-2 rounded-xl text-xs font-black uppercase tracking-widest transition-all ${activeTab === tab ? 'bg-white text-indigo-600 shadow-sm' : 'text-slate-400 hover:text-slate-600'}`}
                >
                  {tab}
                </button>
              ))}
            </div>
          </div>

          {/* Task Bar List */}
          <div className="space-y-3">
            {loading ? (
              <div className="py-20 flex flex-col items-center justify-center gap-4">
                <Loader2 className="animate-spin text-indigo-600" size={32} />
                <p className="text-xs font-black text-slate-300 uppercase">Indexing Data...</p>
              </div>
            ) : (
              <AnimatePresence>
                {getFilteredTasks().map((task, idx) => (
                  <TaskRow
                    key={task.id}
                    task={task}
                    index={idx}
                    isActive={selectedTask?.id === task.id}
                    onClick={() => setSelectedTask(task)}
                    onDelete={onTaskDelete}
                    normalizeStatus={normalizeStatus}
                  />
                ))}
              </AnimatePresence>
            )}
          </div>
        </div>
      </div>

      {/* ðŸ“‘ Detail Slide-over Panel */}
      <AnimatePresence>
        {selectedTask && (
          <DetailPanel
            task={selectedTask}
            onClose={() => setSelectedTask(null)}
            onUpdateStatus={handleStatusUpdate}
            onEdit={() => navigate(`/tasks/edit/${selectedTask.id}`)}
            normalizeStatus={normalizeStatus}
          />
        )}
      </AnimatePresence>
    </div>
  );
};

/* ðŸ§© Component: StatCard */
const StatCard = ({ label, count, icon, color }: any) => (
  <div className="bg-white p-5 rounded-[24px] border border-slate-100 shadow-sm flex items-center gap-4 hover:shadow-md transition-all">
    <div className={`w-12 h-12 rounded-2xl ${color} text-white flex items-center justify-center shadow-lg shadow-current/10`}>
      {icon}
    </div>
    <div>
      <p className="text-[10px] font-black text-slate-400 uppercase tracking-widest">{label}</p>
      <p className="text-2xl font-black text-slate-900">{count}</p>
    </div>
  </div>
);

/* ðŸ§© Component: TaskRow */
const TaskRow = ({ task, index, isActive, onClick, onDelete, normalizeStatus }: any) => {
  const status = normalizeStatus(task.status);
  return (
    <motion.div
      layout
      initial={{ opacity: 0, x: -20 }}
      animate={{ opacity: 1, x: 0 }}
      exit={{ opacity: 0, scale: 0.95 }}
      transition={{ delay: index * 0.05 }}
      onClick={onClick}
      className={`group flex items-center gap-6 p-4 rounded-[28px] border transition-all duration-300 cursor-pointer ${isActive ? 'bg-indigo-600 border-indigo-600 shadow-2xl shadow-indigo-200' : 'bg-white border-slate-50 hover:border-indigo-100 hover:shadow-xl hover:shadow-slate-200/50'}`}
    >
      <div className={`w-3 h-10 rounded-full ${isActive ? 'bg-white/40' : (status === 'Completed' ? 'bg-green-500' : status === 'In Progress' ? 'bg-orange-500' : 'bg-indigo-500')}`}></div>

      <div className="flex-1 min-w-0">
        <h4 className={`font-black text-lg truncate uppercase tracking-tight ${isActive ? 'text-white' : 'text-slate-800'}`}>{task.title}</h4>
        <div className={`flex items-center gap-3 text-[10px] font-bold uppercase tracking-wider ${isActive ? 'text-white/60' : 'text-slate-400'}`}>
          <span>#{task.id}</span>
          <span>â€¢</span>
          <span>{status}</span>
        </div>
      </div>

      <div className="hidden lg:flex items-center gap-8 px-6">
        <div className="flex items-center gap-2">
          <Calendar size={14} className={isActive ? 'text-white/60' : 'text-slate-300'} />
          <span className={`text-xs font-bold ${isActive ? 'text-white' : 'text-slate-600'}`}>{task.due_date ? new Date(task.due_date).toLocaleDateString() : 'N/A'}</span>
        </div>
        {task.assigned_to_user && (
          <div className={`w-8 h-8 rounded-full flex items-center justify-center text-[10px] font-black border-2 ${isActive ? 'bg-white/20 border-white/20 text-white' : 'bg-indigo-50 border-white text-indigo-600 shadow-sm'}`}>
            {task.assigned_to_user.name.charAt(0)}
          </div>
        )}
      </div>

      <div className="flex items-center gap-2">
        <button
          onClick={(e) => onDelete(e, task.id)}
          className={`p-3 rounded-2xl transition-all ${isActive ? 'hover:bg-white/10 text-white/40 hover:text-white' : 'hover:bg-red-50 text-slate-200 hover:text-red-500'}`}
        >
          <Trash2 size={18} />
        </button>
        <div className={`w-10 h-10 rounded-2xl flex items-center justify-center transition-all ${isActive ? 'bg-white text-indigo-600' : 'bg-slate-50 text-slate-300 group-hover:bg-indigo-600 group-hover:text-white'}`}>
          <ChevronRight size={20} />
        </div>
      </div>
    </motion.div>
  );
};

/* ðŸ§© Component: DetailPanel */
const DetailPanel = ({ task, onClose, onUpdateStatus, onEdit, normalizeStatus }: any) => {
  const status = normalizeStatus(task.status);
  return (
    <motion.div
      initial={{ x: "100%" }}
      animate={{ x: 0 }}
      exit={{ x: "100%" }}
      transition={{ type: "spring", damping: 30, stiffness: 300 }}
      className="fixed top-0 right-0 w-full lg:w-[450px] h-screen bg-white shadow-[-20px_0_60px_rgba(0,0,0,0.05)] z-50 flex flex-col border-l border-slate-100"
    >
      {/* Panel Header */}
      <div className="p-8 pb-4 flex items-center justify-between border-b border-slate-50">
        <div className="flex items-center gap-2">
          <button onClick={onClose} className="p-2 -ml-2 rounded-full hover:bg-slate-50 text-slate-400 transition-all">
            <X size={20} />
          </button>
          <span className="text-[10px] font-black text-slate-300 uppercase tracking-widest leading-none">Node Details</span>
        </div>
        <div className="flex items-center gap-2">
          <button onClick={onEdit} className="p-2 rounded-xl bg-slate-50 text-slate-400 hover:text-indigo-600 hover:bg-indigo-50 transition-all">
            <Edit3 size={18} />
          </button>
          <button className="p-2 rounded-xl bg-slate-50 text-slate-400 hover:text-slate-800 transition-all">
            <MoreHorizontal size={18} />
          </button>
        </div>
      </div>

      {/* Panel Content */}
      <div className="flex-1 overflow-y-auto p-8 space-y-10 custom-scrollbar">

        {/* Title & Badge */}
        <div className="space-y-4">
          <div className={`inline-flex items-center gap-1.5 px-3 py-1 rounded-full text-[10px] font-black uppercase tracking-wider ${status === 'Completed' ? 'bg-green-50 text-green-600' : status === 'In Progress' ? 'bg-orange-50 text-orange-600' : 'bg-indigo-50 text-indigo-600'}`}>
            <div className={`w-1.5 h-1.5 rounded-full ${status === 'Completed' ? 'bg-green-500' : status === 'In Progress' ? 'bg-orange-500' : 'bg-indigo-500'}`}></div>
            {status}
          </div>
          <h2 className="text-3xl font-black text-slate-900 leading-tight tracking-tighter uppercase">{task.title}</h2>
        </div>

        {/* Status Quick Select */}
        <div className="space-y-4">
          <p className="text-[10px] font-black text-slate-400 uppercase tracking-widest flex items-center gap-2">
            <ArrowRight size={12} /> Update Status
          </p>
          <div className="grid grid-cols-3 gap-2">
            {["Upcoming", "In Progress", "Completed"].map(s => (
              <button
                key={s}
                onClick={(e) => onUpdateStatus(e, task, s)}
                className={`py-3 rounded-2xl text-[9px] font-black uppercase border-2 transition-all ${status === s ? 'bg-indigo-600 border-indigo-600 text-white shadow-lg shadow-indigo-100' : 'bg-white border-slate-100 text-slate-400 hover:border-indigo-100 hover:text-indigo-600'}`}
              >
                {s}
              </button>
            ))}
          </div>
        </div>

        {/* Metadata Grid */}
        <div className="grid grid-cols-2 gap-4">
          <div className="p-5 rounded-3xl bg-slate-50 border border-slate-100">
            <p className="text-[9px] font-black text-slate-400 uppercase mb-2 flex items-center gap-2"><UserIcon size={12} /> Assignee</p>
            <p className="text-sm font-black text-slate-800 truncate">{task.assigned_to_user?.name || "System"}</p>
          </div>
          <div className="p-5 rounded-3xl bg-slate-50 border border-slate-100">
            <p className="text-[9px] font-black text-slate-400 uppercase mb-2 flex items-center gap-2"><Calendar size={12} /> Date</p>
            <p className="text-sm font-black text-slate-800">{task.due_date ? new Date(task.due_date).toLocaleDateString() : "Unset"}</p>
          </div>
        </div>

        {/* Description Textarea Area */}
        <div className="space-y-4">
          <p className="text-[10px] font-black text-slate-400 uppercase tracking-widest flex items-center gap-2">
            <AlignLeft size={14} /> Technical Documentation
          </p>
          <div className="p-6 bg-slate-50/50 rounded-[32px] border border-slate-100 text-slate-600 leading-relaxed min-h-[250px] whitespace-pre-wrap text-sm font-medium italic">
            {task.description || "The project node lacks descriptive metadata. Please update to include technical specifications."}
          </div>
        </div>

      </div>

      {/* Bottom Actions */}
      <div className="p-8 bg-slate-50/50 border-t border-slate-100">
        <button
          onClick={onEdit}
          className="w-full bg-slate-900 hover:bg-black text-white py-5 rounded-2xl font-black uppercase tracking-widest text-xs flex items-center justify-center gap-2 shadow-xl shadow-slate-200 transition-all hover:-translate-y-1 active:scale-95"
        >
          <ExternalLink size={16} /> Open Full Workspace
        </button>
      </div>
    </motion.div>
  );
};

export default TasksPage;
