import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {
  Calendar,
  CalendarDays,
  Clock,
  PlusCircle,
  Trash2,
  ExternalLink
} from "lucide-react";
import { meetingService, Meeting } from "../../services/meetingService";

const MeetingsPage: React.FC = () => {
  const navigate = useNavigate();
  const [meetings, setMeetings] = useState<Meeting[]>([]);
  const [loading, setLoading] = useState(true);
  const [currentTime, setCurrentTime] = useState(new Date());

  useEffect(() => {
    // Update time every minute to refresh meeting status
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000); // Check every minute

    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    const fetchMeetings = async () => {
      try {
        const data = await meetingService.getAllMeetings();
        setMeetings(data);
      } catch (error) {
        console.error("Failed to fetch meetings", error);
      } finally {
        setLoading(false);
      }
    };
    fetchMeetings();
  }, []);

  const handleDelete = async (id: number) => {
    if (window.confirm("Are you sure you want to delete this meeting?")) {
      try {
        await meetingService.deleteMeeting(id);
        setMeetings(meetings.filter(m => m.id !== id));
      } catch (error) {
        console.error("Failed to delete meeting", error);
        alert("Failed to delete meeting.");
      }
    }
  };

  const isToday = (dateString: string) => {
    const today = new Date();
    const date = new Date(dateString);
    return date.getDate() === today.getDate() &&
      date.getMonth() === today.getMonth() &&
      date.getFullYear() === today.getFullYear();
  };

  const formatTime = (dateString: string) => {
    return new Date(dateString).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString([], { weekday: 'short', month: 'short', day: 'numeric' });
  };

  const todayMeetings = meetings.filter((m) => isToday(m.start_time));
  const upcomingMeetings = meetings.filter((m) => !isToday(m.start_time));

  const getMeetingStatus = (start: string, end: string) => {
    const now = currentTime;
    const startTime = new Date(start);
    const endTime = new Date(end);

    if (now >= startTime && now <= endTime) return 'running';
    if (now > endTime) return 'past';
    return 'upcoming';
  };

  const getStatusStyles = (status: string) => {
    switch (status) {
      case 'running':
        return 'bg-emerald-50 border-emerald-200 ring-1 ring-emerald-500';
      case 'past':
        return 'bg-red-50 border-red-200 opacity-75 grayscale-[0.5]';
      default:
        return 'bg-white border-gray-200';
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-gray-100 p-8">
      {/* Header */}
      <header className="flex flex-col md:flex-row md:items-center justify-between gap-6 mb-10">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Meetings Overview</h1>
          <p className="text-gray-500 text-sm mt-1">
            Stay on top of your team’s schedule and upcoming sessions
          </p>
        </div>

        <div className="flex items-center space-x-3">
          <button
            onClick={() => navigate("/calendar")}
            className="flex items-center gap-2 px-4 py-2 border border-gray-300 rounded-lg bg-white hover:bg-gray-50 transition text-gray-700 font-medium"
          >
            <Calendar className="w-4 h-4" /> Calendar
          </button>

          <button
            onClick={() => navigate("/meetings/schedule")}
            className="flex items-center gap-2 px-4 py-2 rounded-lg bg-blue-600 hover:bg-blue-700 transition text-white font-medium shadow-sm"
          >
            <PlusCircle className="w-4 h-4" /> Schedule
          </button>

        </div>
      </header>

      {loading ? (
        <div className="text-center py-20 text-gray-500">Loading meetings...</div>
      ) : (
        <>
          {/* Today’s Meetings */}
          <section className="mb-12">
            <h2 className="text-xl font-semibold text-gray-800 flex items-center gap-2 mb-5">
              <CalendarDays className="w-5 h-5 text-blue-600" /> Today’s Meetings
            </h2>

            {todayMeetings.length > 0 ? (
              <div className="grid md:grid-cols-2 gap-5">
                {todayMeetings.map((meeting) => {
                  const status = getMeetingStatus(meeting.start_time, meeting.end_time);
                  return (
                    <div
                      key={meeting.id}
                      className={`border rounded-2xl p-5 shadow-sm hover:shadow-md transition flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4 ${getStatusStyles(status)}`}
                    >
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-1">
                          <h3 className="text-gray-900 font-semibold">{meeting.title}</h3>
                          {status === 'running' && (
                            <span className="bg-emerald-100 text-emerald-700 text-[10px] font-bold px-2 py-0.5 rounded-full animate-pulse">LIVE</span>
                          )}
                          {status === 'past' && (
                            <span className="bg-red-100 text-red-700 text-[10px] font-bold px-2 py-0.5 rounded-full">ENDED</span>
                          )}
                        </div>
                        <p className="text-gray-500 flex items-center gap-2 mt-1 text-sm">
                          <Clock className="w-4 h-4 text-gray-400" /> {formatTime(meeting.start_time)} - {formatTime(meeting.end_time)}
                        </p>
                        {meeting.location && <p className="text-xs text-gray-400 mt-1">{meeting.location}</p>}
                      </div>
                      <div className="flex items-center gap-3">
                        {meeting.meeting_link && status === 'running' && (
                          <button
                            onClick={() => window.open(meeting.meeting_link, '_blank')}
                            className="flex items-center gap-2 bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-xl text-sm font-bold transition shadow-sm"
                          >
                            Join <ExternalLink size={14} />
                          </button>
                        )}
                        <button onClick={() => handleDelete(meeting.id)} className="text-gray-400 hover:text-red-600 transition">
                          <Trash2 size={16} />
                        </button>
                      </div>
                    </div>
                  )
                })}
              </div>
            ) : (
              <p className="text-gray-500 italic">No meetings scheduled for today.</p>
            )}
          </section>

          {/* Upcoming Meetings */}
          <section>
            <h2 className="text-xl font-semibold text-gray-800 mb-5">Meetings</h2>

            {upcomingMeetings.length > 0 ? (
              <div className="grid md:grid-cols-3 gap-5">
                {upcomingMeetings.map((meeting) => {
                  const status = getMeetingStatus(meeting.start_time, meeting.end_time);
                  return (
                    <div
                      key={meeting.id}
                      className={`border rounded-2xl p-5 hover:shadow-md transition flex flex-col justify-between ${getStatusStyles(status)}`}
                    >
                      <div>
                        <div className="flex items-center gap-2 mb-1">
                          <h3 className="font-semibold text-gray-900">{meeting.title}</h3>
                          {status === 'past' && <span className="text-xs bg-red-100 text-red-600 px-1.5 py-0.5 rounded">Past</span>}
                        </div>
                        <p className="text-gray-600 flex items-center gap-2 mt-2 text-sm">
                          <Clock className="w-4 h-4 text-blue-500" /> {formatTime(meeting.start_time)}
                        </p>
                      </div>
                      <div className="flex flex-col gap-3 mt-4 border-t border-gray-100 pt-3">
                        <div className="flex justify-between items-center">
                          <span className="text-sm text-gray-500 font-medium">{formatDate(meeting.start_time)}</span>
                          <div className="flex gap-2">
                            <button onClick={() => handleDelete(meeting.id)} className="text-gray-400 hover:text-red-600 transition">
                              <Trash2 size={16} />
                            </button>
                          </div>
                        </div>
                        {meeting.meeting_link && status === 'running' && (
                          <button
                            onClick={() => window.open(meeting.meeting_link, '_blank')}
                            className="w-full flex items-center justify-center gap-2 bg-slate-900 hover:bg-black text-white px-4 py-2.5 rounded-xl text-sm font-bold transition shadow-sm"
                          >
                            Join Session <ExternalLink size={14} />
                          </button>
                        )}
                      </div>
                    </div>
                  )
                })}
              </div>
            ) : (
              <p className="text-gray-500 italic">No upcoming meetings found.</p>
            )}
          </section>
        </>
      )}
    </div>
  );
};

export default MeetingsPage;
