import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Plus,
  Calendar as CalendarIcon,
  FileUp,
  MessageSquare,
  CheckCircle,
  Clock,
  Bell,
  ArrowRight,
  User,
  Activity
} from "lucide-react";
import { taskService, Task } from "../../services/taskService";
import { meetingService } from "../../services/meetingService";
import { messageService, Chat } from "../../services/messageService";
import { userService } from "../../services/userService";
import { notificationService, Notification } from "../../services/notificationService";
import { authService } from "../../services/authService";

interface DashboardEvent {
  id: string;
  title: string;
  time: string;
  type: "meeting" | "task";
  status?: string;
  details?: string;
  timestamp: Date;
}

const DashboardPage: React.FC = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState<any>(null);
  const [stats, setStats] = useState({
    tasksPending: 0,
    meetingsToday: 0,
    unreadMessages: 0,
    teamMembers: 0,
    tasksCompleted: 0
  });
  const [todaysEvents, setTodaysEvents] = useState<DashboardEvent[]>([]);
  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [recentTasks, setRecentTasks] = useState<Task[]>([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [user, tasks, meetings, chats, users, notifs] = await Promise.all([
          authService.getCurrentUser(),
          taskService.getTasks(),
          meetingService.getAllMeetings(),
          messageService.getChats(),
          userService.getAllUsers(),
          notificationService.getNotifications(0, 5)
        ]);

        setCurrentUser(user);

        // Stats Calculation
        const pendingTasks = tasks.filter(t => t.status !== "Completed");
        const completedTasks = tasks.filter(t => t.status === "Completed");

        const unreadCount = chats.reduce((acc: number, curr: Chat) => acc + (curr.unread_count || 0), 0);

        const todayStr = new Date().toLocaleDateString("en-CA");
        const todayMeetings = meetings.filter(m => new Date(m.start_time).toLocaleDateString("en-CA") === todayStr);

        setStats({
          tasksPending: pendingTasks.length,
          meetingsToday: todayMeetings.length,
          unreadMessages: unreadCount,
          teamMembers: users.length,
          tasksCompleted: completedTasks.length
        });

        // Today's Schedule Processing
        const taskEvents: DashboardEvent[] = tasks
          .filter(t => t.due_date && new Date(t.due_date).toLocaleDateString("en-CA") === todayStr && t.status !== "Completed")
          .map(t => ({
            id: `task-${t.id}`,
            title: t.title,
            time: "Due Today",
            type: "task",
            status: t.status,
            details: t.priority,
            timestamp: new Date(t.created_at || new Date()) // Fallback for sort if no time specific due date
          }));

        const meetingEvents: DashboardEvent[] = todayMeetings.map(m => {
          const startDate = new Date(m.start_time);
          return {
            id: `meeting-${m.id}`,
            title: m.title,
            time: startDate.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
            type: "meeting",
            details: m.location || "Online",
            timestamp: startDate
          };
        });

        const allEvents = [...taskEvents, ...meetingEvents].sort((a, b) => a.timestamp.getTime() - b.timestamp.getTime());
        setTodaysEvents(allEvents);

        setRecentTasks(tasks.slice(0, 4));
        setNotifications(notifs);

      } catch (error) {
        console.error("Failed to load dashboard data", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return "Good morning";
    if (hour < 18) return "Good afternoon";
    return "Good evening";
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-slate-50">
        <div className="animate-pulse flex flex-col items-center">
          <div className="h-12 w-12 bg-slate-200 rounded-full mb-4"></div>
          <div className="h-4 w-32 bg-slate-200 rounded"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-slate-50 text-slate-900 p-6 md:p-10 space-y-8 fade-in">

      {/* Header */}
      <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
        <div>
          <h1 className="text-3xl font-bold text-slate-900 tracking-tight">{getGreeting()}, {currentUser?.name?.split(' ')[0] || 'User'}!</h1>
          <p className="text-slate-500 mt-1">Here's what's happening in your workspace today.</p>
        </div>
        <div className="flex items-center gap-3 bg-white p-2 rounded-xl border border-slate-200 shadow-sm">
          <div className="bg-blue-50 p-2 rounded-lg text-blue-600">
            <CalendarIcon size={20} />
          </div>
          <div className="pr-2">
            <p className="text-xs text-slate-500 font-semibold uppercase tracking-wider">Today</p>
            <p className="text-sm font-bold text-slate-800">{new Date().toLocaleDateString(undefined, { weekday: 'short', month: 'short', day: 'numeric' })}</p>
          </div>
        </div>
      </div>

      {/* Stats Overview */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          icon={CheckCircle}
          label="Pending Tasks"
          value={stats.tasksPending}
          trend={stats.tasksPending > 0 ? "Needs attention" : "All clear"}
          trendColor={stats.tasksPending > 0 ? "text-orange-600" : "text-green-600"}
          onClick={() => navigate('/tasks')}
        />
        <StatCard
          icon={Clock}
          label="Meetings Today"
          value={stats.meetingsToday}
          trend={stats.meetingsToday > 0 ? "Upcoming" : "No meetings"}
          trendColor="text-blue-600"
          onClick={() => navigate('/meetings')}
        />
        <StatCard
          icon={MessageSquare}
          label="Unread Messages"
          value={stats.unreadMessages}
          trend="New messages"
          trendColor={stats.unreadMessages > 0 ? "text-purple-600" : "text-slate-400"}
          onClick={() => navigate('/messages')}
        />
        <StatCard
          icon={Activity}
          label="Productivity"
          value={`${stats.tasksCompleted}`}
          subValue="Tasks Done"
          trend="Keep it up!"
          trendColor="text-green-600"
        />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">

        {/* Main Feed: Today's Schedule & Recent Tasks */}
        <div className="lg:col-span-2 space-y-8">

          {/* Today's Schedule */}
          <div className="bg-white rounded-2xl border border-slate-200 shadow-sm overflow-hidden">
            <div className="p-6 border-b border-slate-100 flex justify-between items-center">
              <h3 className="font-bold text-lg text-slate-800 flex items-center gap-2">
                <CalendarIcon size={20} className="text-indigo-500" /> Today's Schedule
              </h3>
              <button onClick={() => navigate('/calendar')} className="text-sm font-medium text-indigo-600 hover:text-indigo-700">View Calendar</button>
            </div>
            <div className="p-6">
              {todaysEvents.length > 0 ? (
                <div className="space-y-4 relative before:absolute before:inset-y-0 before:left-4 before:w-0.5 before:bg-slate-100">
                  {todaysEvents.map((event, idx) => (
                    <div key={idx} className="relative flex items-start gap-4 ml-2">
                      <div className={`absolute -left-[1.35rem] mt-1.5 w-3 h-3 rounded-full border-2 border-white ring-1 ${event.type === 'meeting' ? 'bg-indigo-500 ring-indigo-100' : 'bg-green-500 ring-green-100'}`}></div>
                      <div className="flex-1 bg-slate-50 rounded-xl p-4 border border-slate-100 hover:border-slate-300 transition-colors">
                        <div className="flex justify-between items-start">
                          <div>
                            <h4 className="font-semibold text-slate-800">{event.title}</h4>
                            <div className="flex items-center gap-2 mt-1">
                              <span className={`text-xs px-2 py-0.5 rounded font-medium ${event.type === 'meeting' ? 'bg-indigo-100 text-indigo-700' : 'bg-green-100 text-green-700'}`}>
                                {event.type === 'meeting' ? 'Meeting' : 'Task'}
                              </span>
                              <span className="text-xs text-slate-500 flex items-center gap-1">
                                <Clock size={12} /> {event.time}
                              </span>
                            </div>
                          </div>
                          {event.type === 'meeting' && <button className="text-xs bg-indigo-600 text-white px-3 py-1.5 rounded-lg hover:bg-indigo-700 transition">Join</button>}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-10 text-slate-500">
                  <div className="w-12 h-12 bg-slate-100 rounded-full flex items-center justify-center mx-auto mb-3">
                    <CalendarIcon className="text-slate-400" />
                  </div>
                  <p>No events scheduled for today.</p>
                </div>
              )}
            </div>
          </div>

          {/* Recent Tasks */}
          <div className="bg-white rounded-2xl border border-slate-200 shadow-sm">
            <div className="p-6 border-b border-slate-100 flex justify-between items-center">
              <h3 className="font-bold text-lg text-slate-800 flex items-center gap-2">
                <CheckCircle size={20} className="text-blue-500" /> Recent Tasks
              </h3>
              <button onClick={() => navigate('/tasks')} className="text-sm font-medium text-blue-600 hover:text-blue-700">View All Tasks</button>
            </div>
            <div className="divide-y divide-slate-100">
              {recentTasks.length > 0 ? (
                recentTasks.map(task => (
                  <div key={task.id} className="p-4 hover:bg-slate-50 transition flex items-center justify-between group cursor-pointer" onClick={() => navigate('/tasks')}>
                    <div className="flex items-center gap-3">
                      <div className={`w-2 h-2 rounded-full ${task.status === 'Completed' ? 'bg-green-500' : 'bg-slate-300'}`}></div>
                      <div>
                        <p className={`text-sm font-medium text-slate-800 ${task.status === 'Completed' ? 'line-through text-slate-400' : ''}`}>{task.title}</p>
                        <p className="text-xs text-slate-500">{task.priority} Priority â€¢ {task.status}</p>
                      </div>
                    </div>
                    <ArrowRight size={16} className="text-slate-300 group-hover:text-blue-500 transition-colors opacity-0 group-hover:opacity-100" />
                  </div>
                ))
              ) : (
                <div className="p-6 text-center text-slate-500 text-sm">No tasks found. Create one to get started!</div>
              )}
            </div>
          </div>

        </div>

        {/* Sidebar */}
        <div className="space-y-6">

          {/* Quick Actions */}
          <div className="bg-white rounded-2xl p-6 border border-slate-200 shadow-sm">
            <h3 className="font-bold text-lg text-slate-800 mb-4">Quick Actions</h3>
            <div className="grid grid-cols-2 gap-3">
              <ActionButton icon={Plus} label="New Task" onClick={() => navigate('/tasks')} />
              <ActionButton icon={CalendarIcon} label="Schedule" onClick={() => navigate('/meetings/schedule')} />
              <ActionButton icon={MessageSquare} label="Message" onClick={() => navigate('/messages')} />
              <ActionButton icon={FileUp} label="Upload" onClick={() => navigate('/files/upload')} />
            </div>
          </div>

          {/* Notifications */}
          <div className="bg-white rounded-2xl border border-slate-200 shadow-sm p-6">
            <h3 className="font-bold text-lg text-slate-800 mb-4 flex items-center gap-2">
              <Bell size={18} className="text-yellow-500" /> Notifications
            </h3>
            <div className="space-y-4">
              {notifications.length > 0 ? notifications.map(n => (
                <div key={n.id} className="flex gap-3">
                  <div className="mt-1 flex-shrink-0 w-2 h-2 rounded-full bg-blue-500"></div>
                  <div>
                    <p className="text-sm font-medium text-slate-800 line-clamp-2">{n.title}</p>
                    <p className="text-xs text-slate-400 mt-0.5">{new Date(n.created_at).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</p>
                  </div>
                </div>
              )) : (
                <p className="text-sm text-slate-500 italic">No new notifications.</p>
              )}
            </div>
          </div>



        </div>

      </div>
    </div>
  );
};

// Helper Components

const StatCard = ({ icon: Icon, label, value, subValue, trend, trendColor, onClick }: any) => (
  <div onClick={onClick} className="bg-white p-5 rounded-xl border border-slate-200 shadow-sm hover:shadow-md transition cursor-pointer">
    <div className="flex justify-between items-start mb-4">
      <div>
        <p className="text-sm font-medium text-slate-500">{label}</p>
        <div className="flex items-baseline gap-2 mt-1">
          <h3 className="text-2xl font-bold text-slate-900">{value}</h3>
          {subValue && <span className="text-sm text-slate-400">{subValue}</span>}
        </div>
      </div>
      <div className="p-2 bg-slate-50 rounded-lg">
        <Icon size={18} className="text-slate-600" />
      </div>
    </div>
    <div className="flex items-center gap-2">
      <span className={`text-xs font-medium ${trendColor}`}>{trend}</span>
    </div>
  </div>
);

const ActionButton = ({ icon: Icon, label, onClick }: any) => (
  <button onClick={onClick} className="flex flex-col items-center justify-center p-4 bg-white hover:bg-slate-50 rounded-xl transition text-center border border-slate-200 shadow-sm group">
    <Icon size={20} className="mb-2 text-slate-600 group-hover:text-blue-600 transition-colors" />
    <span className="text-sm font-medium text-slate-700 group-hover:text-slate-900">{label}</span>
  </button>
);

export default DashboardPage;
