import React, { useEffect, useState, useRef } from "react";
import { Menu, Search, Bell, Sparkles } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { authService } from "../services/authService";
import { User } from "../services/userService";
import { useNotifications } from "../context/NotificationContext";
import { AnimatePresence, motion } from "framer-motion";

interface TopbarProps {
  onMenuClick: () => void;
  isSidebarOpen: boolean;
}

const Topbar: React.FC<TopbarProps> = ({ onMenuClick, isSidebarOpen }) => {
  const navigate = useNavigate();
  const [user, setUser] = useState<User | null>(null);
  const { notifications, unreadCount, markAsRead, markAllAsRead } = useNotifications();
  const [showNotifications, setShowNotifications] = useState(false);
  const dropdownRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const currentUser = await authService.getCurrentUser();
        setUser(currentUser);
      } catch (error) {
        console.error("Failed to fetch user", error);
      }
    };
    fetchUser();
  }, []);

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setShowNotifications(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const getInitials = (name: string) => {
    return name
      .split(" ")
      .map((n) => n[0])
      .join("")
      .toUpperCase()
      .substring(0, 2);
  };

  return (
    <header className="sticky top-0 z-30 w-full bg-white/80 backdrop-blur-xl border-b border-slate-100 flex items-center justify-between px-6 py-4">
      {/* Search Section */}
      <div className="flex items-center gap-4 flex-1">
        <button
          onClick={onMenuClick}
          className="lg:hidden p-2.5 text-slate-500 hover:bg-slate-50 rounded-xl transition-all"
        >
          <Menu size={20} />
        </button>

        <div className="hidden sm:flex items-center w-full max-w-md bg-slate-50 border border-slate-100 rounded-xl px-4 py-2 focus-within:bg-white focus-within:border-indigo-200 focus-within:shadow-sm transition-all duration-200 group">
          <Search size={16} className="text-slate-400 mr-3 group-focus-within:text-indigo-500 transition-colors" />
          <input
            type="text"
            placeholder="Search resources..."
            className="bg-transparent border-none outline-none text-sm font-semibold w-full text-slate-900 placeholder:text-slate-400"
          />
        </div>
      </div>

      {/* Actions Section */}
      <div className="flex items-center gap-4">
        {/* Pro Badge */}
        <div className="hidden md:flex items-center gap-2 px-3 py-1.5 bg-indigo-50 text-indigo-600 rounded-lg text-[11px] font-bold uppercase tracking-wider border border-indigo-100">
          <Sparkles size={12} />
          Pro
        </div>

        {/* Notifications */}
        <div className="relative" ref={dropdownRef}>
          <button
            onClick={() => setShowNotifications(!showNotifications)}
            className={`relative p-2.5 rounded-xl transition-all duration-200 ${showNotifications ? "bg-indigo-600 text-white shadow-lg shadow-indigo-100" : "text-slate-500 hover:bg-slate-50"}`}
          >
            < Bell size={20} />
            {unreadCount > 0 && (
              <span className="absolute top-2 right-2 w-4 h-4 bg-rose-500 text-white text-[9px] font-bold flex items-center justify-center rounded-full border-2 border-white">
                {unreadCount > 9 ? '9+' : unreadCount}
              </span>
            )}
          </button>

          <AnimatePresence>
            {showNotifications && (
              <motion.div
                initial={{ opacity: 0, y: 10, scale: 0.98 }}
                animate={{ opacity: 1, y: 0, scale: 1 }}
                exit={{ opacity: 0, y: 10, scale: 0.98 }}
                className="absolute right-0 mt-4 w-80 max-w-[calc(100vw-2rem)] bg-white border border-slate-100 rounded-2xl shadow-xl shadow-slate-200/50 overflow-hidden z-50"
              >
                <div className="p-5 border-b border-slate-50 flex justify-between items-center bg-slate-50/30">
                  <h3 className="text-sm font-bold text-slate-900">Notifications</h3>
                  {unreadCount > 0 && (
                    <button
                      onClick={() => markAllAsRead()}
                      className="text-[10px] font-bold uppercase tracking-wider text-indigo-600 hover:text-indigo-700"
                    >
                      Mark all as read
                    </button>
                  )}
                </div>
                <div className="max-h-[350px] overflow-y-auto p-2">
                  {notifications.length > 0 ? (
                    notifications.map((notif) => (
                      <div
                        key={notif.id}
                        onClick={() => markAsRead(notif.id)}
                        className={`p-4 rounded-xl mb-1 cursor-pointer transition-colors flex gap-3 items-start ${!notif.is_read ? 'bg-indigo-50/50 hover:bg-indigo-50' : 'hover:bg-slate-50'}`}
                      >
                        <div className={`w-2 h-2 mt-1.5 rounded-full shrink-0 ${!notif.is_read ? 'bg-indigo-600' : 'bg-transparent'}`} />
                        <div className="flex-1 min-w-0">
                          <p className="text-xs font-bold text-slate-900 leading-tight mb-0.5 truncate">{notif.title}</p>
                          <p className="text-[11px] text-slate-500 font-medium leading-relaxed line-clamp-2">{notif.description}</p>
                          <p className="text-[9px] font-bold text-slate-400 mt-2 uppercase tracking-tight">{new Date(notif.created_at).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</p>
                        </div>
                      </div>
                    ))
                  ) : (
                    <div className="py-16 text-center">
                      <div className="w-12 h-12 bg-slate-50 rounded-2xl flex items-center justify-center mx-auto mb-3 border border-slate-100">
                        <Bell size={18} className="text-slate-300" />
                      </div>
                      <h4 className="text-xs font-bold text-slate-900">Quiet for now</h4>
                      <p className="text-[11px] text-slate-400 font-medium mt-1">We'll notify you of any updates.</p>
                    </div>
                  )}
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>

        {/* Profile */}
        <button
          onClick={() => navigate("/profile")}
          className="flex items-center gap-3 bg-white hover:bg-slate-50 p-1 rounded-xl border border-slate-100 transition-all duration-200 group"
        >
          <div className="w-8 h-8 rounded-lg bg-indigo-600 flex items-center justify-center text-white font-bold text-[11px] shadow-sm">
            {user?.name ? getInitials(user.name) : "U"}
          </div>
          <div className="hidden sm:block text-left mr-2">
            <p className="text-xs font-bold text-slate-900 leading-none mb-0.5 truncate max-w-[80px]">
              {user?.name || "User"}
            </p>
            <p className="text-[9px] font-bold text-slate-400 uppercase tracking-wider">{user?.role || "Member"}</p>
          </div>
        </button>
      </div>
    </header>
  );
};

export default Topbar;
