import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {
  Calendar,
  Clock,
  Plus,
  Trash2,
  ExternalLink,
  Users,
  Video,
  History,
  AlertCircle,
  MapPin,
  ChevronRight,
  ArrowRight,
  Layers
} from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import { meetingService, Meeting } from "../../services/meetingService";
import { webSocketService, WebSocketMessage } from "../../services/websocketService";

const MeetingsPage: React.FC = () => {
  const navigate = useNavigate();
  const [meetings, setMeetings] = useState<Meeting[]>([]);
  const [loading, setLoading] = useState(true);
  const [currentTime, setCurrentTime] = useState(new Date());

  const fetchMeetings = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const data = await meetingService.getAllMeetings();
      setMeetings(data);
    } catch (error) {
      console.error("Failed to fetch meetings", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000);
    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    fetchMeetings();

    const unsubscribe = webSocketService.subscribe((message: WebSocketMessage) => {
      if (message.type === 'meeting') {
        fetchMeetings(false);
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);

  const handleDelete = async (id: number) => {
    if (window.confirm("Delete this meeting?")) {
      try {
        await meetingService.deleteMeeting(id);
        setMeetings(prev => prev.filter(m => m.id !== id));
      } catch (error) {
        console.error("Failed to delete meeting", error);
      }
    }
  };

  const getStatus = (start: string, end: string) => {
    const now = currentTime;
    const startTime = new Date(start);
    const endTime = new Date(end);
    if (now >= startTime && now <= endTime) return 'present';
    if (now > endTime) return 'past';
    return 'upcoming';
  };

  const formatTime = (dateString: string) => {
    return new Date(dateString).toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true
    }).toUpperCase();
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString([], { weekday: 'long', month: 'long', day: 'numeric' });
  };

  const isToday = (dateString: string) => {
    const d = new Date(dateString);
    const today = new Date();
    return d.getFullYear() === today.getFullYear() &&
      d.getMonth() === today.getMonth() &&
      d.getDate() === today.getDate();
  };

  const presentMeetings = meetings.filter(m => getStatus(m.start_time, m.end_time) === 'present');

  const upcomingMeetings = meetings.filter(m => {
    return getStatus(m.start_time, m.end_time) === 'upcoming' && isToday(m.start_time);
  }).sort((a, b) => new Date(a.start_time).getTime() - new Date(b.start_time).getTime());

  const pastMeetings = meetings.filter(m => {
    if (getStatus(m.start_time, m.end_time) !== 'past') return false;
    const endTime = new Date(m.end_time);
    const fortyEightHoursAgo = new Date(currentTime.getTime() - (48 * 60 * 60 * 1000));
    return endTime > fortyEightHoursAgo;
  }).sort((a, b) => new Date(b.start_time).getTime() - new Date(a.start_time).getTime());

  const currentOrNext = presentMeetings.length > 0 ? presentMeetings[0] : (upcomingMeetings.length > 0 ? upcomingMeetings[0] : null);

  const TimelineNode = ({ meeting, type }: { meeting: Meeting, type: 'upcoming' | 'past' }) => (
    <motion.div
      initial={{ opacity: 0, x: -20 }}
      animate={{ opacity: 1, x: 0 }}
      className="relative pl-12 pb-12 last:pb-0"
    >
      {/* Connection Line */}
      <div className="absolute left-[11px] top-6 bottom-0 w-[2px] bg-slate-100 last:hidden" />

      {/* Node Dot */}
      <div className={`absolute left-0 top-1 w-6 h-6 rounded-full border-4 border-white shadow-md z-10 ${type === 'upcoming' ? 'bg-indigo-600' : 'bg-slate-300'
        }`} />

      <div className={`group bg-white p-6 rounded-[24px] border border-slate-100 shadow-sm transition-all hover:shadow-xl hover:border-indigo-100 ${type === 'past' ? 'opacity-60' : ''}`}>
        <div className="flex flex-col md:flex-row md:items-center justify-between gap-6">
          <div className="space-y-3">
            <div className="flex items-center gap-3">
              <span className={`text-[10px] font-black uppercase tracking-widest px-2 py-1 rounded-md ${type === 'upcoming' ? 'bg-indigo-50 text-indigo-700' : 'bg-slate-50 text-slate-500'
                }`}>
                {formatTime(meeting.start_time)}
              </span>
              <h3 className="text-lg font-bold text-slate-900 group-hover:text-indigo-600 transition-colors">{meeting.title}</h3>
            </div>

            <div className="flex flex-wrap items-center gap-4 text-sm text-slate-500 font-medium">
              <span className="flex items-center gap-1.5"><Users size={14} /> {meeting.participants?.length || 0} Joined</span>
              {meeting.location && <span className="flex items-center gap-1.5"><MapPin size={14} /> {meeting.location}</span>}
              <span className="flex items-center gap-1.5"><Clock size={14} /> 60m</span>
            </div>
          </div>

          <div className="flex items-center gap-3">
            {meeting.meeting_link && (
              <motion.button
                whileTap={{ scale: 0.95 }}
                onClick={() => window.open(meeting.meeting_link, '_blank')}
                className="flex items-center gap-2 px-5 py-2.5 bg-slate-900 text-white rounded-xl text-sm font-bold hover:bg-indigo-600 transition-all shadow-lg shadow-slate-200"
              >
                Connect <ExternalLink size={14} />
              </motion.button>
            )}
            <button
              onClick={() => handleDelete(meeting.id)}
              className="p-2 text-slate-300 hover:text-rose-600 hover:bg-rose-50 rounded-xl transition-all"
            >
              <Trash2 size={18} />
            </button>
          </div>
        </div>
      </div>
    </motion.div>
  );

  return (
    <div className="min-h-screen bg-[#fafbfc] text-slate-900 overflow-hidden">
      {/* Background Orbs */}
      <div className="fixed top-0 right-0 w-[500px] h-[500px] bg-indigo-100/30 rounded-full blur-[120px] -mr-64 -mt-64" />
      <div className="fixed bottom-0 left-0 w-[400px] h-[400px] bg-sky-100/30 rounded-full blur-[100px] -ml-48 -mb-48" />

      <div className="max-w-7xl mx-auto px-6 py-12 lg:px-12 relative z-10">
        {/* New Header Approach */}
        <div className="flex flex-col lg:flex-row lg:items-end justify-between gap-8 mb-16">
          <div className="space-y-4">
            <motion.div
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              className="inline-flex items-center gap-2 px-4 py-1.5 bg-white border border-slate-100 rounded-full shadow-sm text-[10px] font-black uppercase tracking-[0.2em] text-indigo-600"
            >
              <Layers size={12} strokeWidth={3} /> Workspace Intelligence
            </motion.div>
            <h1 className="text-6xl font-[1000] tracking-tight leading-[1.1]">
              Focus on your <br />
              <span className="text-transparent bg-clip-text bg-gradient-to-r from-indigo-600 via-violet-600 to-sky-600">Daily Syncs.</span>
            </h1>
          </div>

          <div className="flex items-center gap-4">
            <button
              onClick={() => navigate("/calendar")}
              className="h-14 px-8 rounded-2xl bg-white border border-slate-200 font-bold text-slate-600 hover:border-indigo-600 hover:text-indigo-600 transition-all shadow-sm"
            >
              Calendar view
            </button>
            <motion.button
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              onClick={() => navigate("/meetings/schedule")}
              className="h-14 px-10 bg-indigo-600 text-white rounded-2xl font-[900] uppercase tracking-widest text-xs shadow-xl shadow-indigo-100 hover:bg-indigo-700 transition-all flex items-center gap-3"
            >
              <Plus size={20} strokeWidth={3} /> Schedule
            </motion.button>
          </div>
        </div>

        {loading ? (
          <div className="py-20 flex flex-col items-center">
            <div className="w-12 h-12 border-4 border-slate-200 border-t-indigo-600 rounded-full animate-spin mb-6" />
            <p className="text-xs font-black uppercase tracking-widest text-slate-400">Aggregating schedule</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 lg:grid-cols-12 gap-16">

            {/* Left Column: Timeline & Feed */}
            <div className="lg:col-span-7 space-y-12">
              {/* Hero: Current / Next Meeting */}
              {currentOrNext && (
                <motion.section
                  initial={{ opacity: 0, scale: 0.95 }}
                  animate={{ opacity: 1, scale: 1 }}
                  className="relative overflow-hidden group"
                >
                  <div className="absolute inset-0 bg-gradient-to-br from-indigo-600 to-violet-700 rounded-[40px] shadow-2xl shadow-indigo-200" />
                  <div className="absolute top-0 right-0 w-64 h-64 bg-white/10 rounded-full -mr-32 -mt-32 transition-transform group-hover:scale-125" />

                  <div className="relative p-10 md:p-12 text-white flex flex-col md:flex-row justify-between gap-10">
                    <div className="space-y-6 flex-1">
                      <div className="flex items-center gap-3">
                        <div className="px-3 py-1 bg-white/20 backdrop-blur-md rounded-lg text-[10px] font-black uppercase tracking-widest">
                          {presentMeetings.length > 0 ? 'Live Now' : 'Up Next'}
                        </div>
                        <span className="text-white/70 text-sm font-bold">{formatTime(currentOrNext.start_time)}</span>
                      </div>
                      <h2 className="text-4xl font-black leading-tight max-w-sm">{currentOrNext.title}</h2>
                      <div className="flex flex-wrap items-center gap-6 text-white/80">
                        <div className="flex items-center gap-2 px-4 py-2 bg-white/10 rounded-xl">
                          <Users size={16} /> <span className="text-sm font-bold">{currentOrNext.participants?.length || 0} Joined</span>
                        </div>
                        {currentOrNext.meeting_link && (
                          <button
                            onClick={() => window.open(currentOrNext.meeting_link, '_blank')}
                            className="flex items-center gap-2 text-sm font-black underline underline-offset-4 hover:text-white transition-colors"
                          >
                            Open Meeting <ArrowRight size={16} />
                          </button>
                        )}
                      </div>
                    </div>

                    <div className="hidden md:flex flex-col items-center justify-center space-y-4">
                      <div className="w-24 h-24 rounded-full border-2 border-white/20 flex items-center justify-center backdrop-blur-md">
                        <Video size={32} className="text-white animate-pulse" />
                      </div>
                      <button
                        onClick={() => window.open(currentOrNext.meeting_link, '_blank')}
                        className="px-6 py-3 bg-white text-indigo-700 rounded-2xl font-black uppercase tracking-widest text-[10px] shadow-xl hover:scale-105 transition-all"
                      >
                        Join now
                      </button>
                    </div>
                  </div>
                </motion.section>
              )}

              {/* Today's Timeline */}
              <section className="space-y-10">
                <div className="flex items-center justify-between">
                  <h2 className="text-xs font-black uppercase tracking-[0.2em] text-slate-400">Timeline Today</h2>
                  <div className="text-[10px] font-bold text-indigo-600 bg-indigo-50 px-2 py-1 rounded">
                    {upcomingMeetings.length} Pending
                  </div>
                </div>

                <div className="relative">
                  {upcomingMeetings.length > 0 ? (
                    <div>
                      {upcomingMeetings.map(m => (
                        <TimelineNode key={m.id} meeting={m} type="upcoming" />
                      ))}
                    </div>
                  ) : (
                    <div className="p-16 border-2 border-dashed border-slate-100 rounded-[40px] text-center">
                      <div className="w-16 h-16 bg-slate-50 rounded-full flex items-center justify-center mx-auto mb-4">
                        <AlertCircle size={32} className="text-slate-200" />
                      </div>
                      <h3 className="text-xl font-bold text-slate-400">End of schedule</h3>
                      <p className="text-slate-300 text-sm mt-1 font-medium">No more meetings for today.</p>
                    </div>
                  )}
                </div>
              </section>
            </div>

            {/* Right Column: History & Stats */}
            <aside className="lg:col-span-5 space-y-16">
              <div className="sticky top-12">
                {/* Summary Deck */}
                <div className="bg-slate-900 rounded-[40px] p-10 text-white shadow-2xl space-y-10 relative overflow-hidden">
                  <div className="absolute top-0 right-0 p-10 opacity-10">
                    <Plus size={150} strokeWidth={1} />
                  </div>

                  <div className="space-y-2">
                    <h3 className="text-xs font-black uppercase tracking-[0.2em] text-white/40">Weekly Summary</h3>
                    <p className="text-3xl font-black">Performance is <span className="text-indigo-400">92%</span></p>
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div className="p-6 bg-white/5 rounded-3xl border border-white/10 hover:bg-white/10 transition-colors">
                      <p className="text-xs font-bold text-white/40 mb-2 uppercase tracking-tight">Syncs</p>
                      <p className="text-3xl font-black">{meetings.length}</p>
                    </div>
                    <div className="p-6 bg-white/5 rounded-3xl border border-white/10 hover:bg-white/10 transition-colors">
                      <p className="text-xs font-bold text-white/40 mb-2 uppercase tracking-tight">Attendees</p>
                      <p className="text-3xl font-black">12k+</p>
                    </div>
                  </div>

                  <div className="space-y-4 pt-4">
                    <div className="flex justify-between items-center text-sm">
                      <span className="font-bold text-white/60">Monday-Friday Load</span>
                      <span className="font-black text-indigo-400">High</span>
                    </div>
                    <div className="w-full h-2 bg-white/5 rounded-full overflow-hidden">
                      <div className="w-[85%] h-full bg-gradient-to-r from-indigo-500 to-sky-400" />
                    </div>
                  </div>
                </div>

                {/* Recent Timeline */}
                <div className="mt-16 space-y-8">
                  <h2 className="text-xs font-black uppercase tracking-[0.2em] text-slate-400 flex items-center gap-4">
                    <span className="w-8 h-[2px] bg-slate-200"></span>
                    History Explorer
                  </h2>
                  <div className="space-y-4">
                    {pastMeetings.slice(0, 5).map(m => (
                      <motion.div
                        key={m.id}
                        whileHover={{ y: -2 }}
                        className="p-5 bg-white border border-slate-100 rounded-3xl flex items-center justify-between group"
                      >
                        <div className="flex items-center gap-4">
                          <div className="w-10 h-10 rounded-2xl bg-slate-50 flex items-center justify-center text-slate-400 group-hover:bg-slate-900 group-hover:text-white transition-all">
                            <History size={18} />
                          </div>
                          <div>
                            <h4 className="text-sm font-black text-slate-800 truncate max-w-[150px]">{m.title}</h4>
                            <p className="text-[10px] font-black text-slate-400 uppercase tracking-tighter">
                              {formatDate(m.start_time).split(',')[0]}  Completed
                            </p>
                          </div>
                        </div>
                        <button
                          onClick={() => handleDelete(m.id)}
                          className="p-2 text-slate-200 hover:text-rose-500 transition-colors"
                        >
                          <Trash2 size={16} />
                        </button>
                      </motion.div>
                    ))}
                  </div>
                </div>
              </div>
            </aside>
          </div>
        )}
      </div>
    </div>
  );
};

export default MeetingsPage;
