import React from "react";
import { Users, Rocket, Target } from "lucide-react";
import { motion } from "framer-motion";

const AboutPage: React.FC = () => {
  const features = [
    {
      icon: <Users className="w-10 h-10 text-blue-600" />,
      title: "Team First",
      desc: "Empower your team to collaborate effortlessly and stay connected across projects.",
    },
    {
      icon: <Rocket className="w-10 h-10 text-orange-500" />,
      title: "Fast & Scalable",
      desc: "Built for performance and scalability, ensuring speed and reliability for every team size.",
    },
    {
      icon: <Target className="w-10 h-10 text-green-500" />,
      title: "Goal Oriented",
      desc: "Stay on track and hit your targets with smart insights and collaborative planning.",
    },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-b from-slate-50 via-white to-slate-100 px-6 md:px-12 py-16">
      {/* Header Section */}
      <motion.div
        className="text-center max-w-3xl mx-auto mb-14"
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        <h1 className="text-5xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent mb-6">
          About Collabryta
        </h1>
        <p className="text-gray-600 text-lg leading-relaxed">
          Collabryta is an AI-powered collaboration hub that helps teams plan, communicate,
          and execute with clarity. We bring together productivity, creativity, and automation —
          all in one beautifully unified platform.
        </p>
      </motion.div>

      {/* Features Grid */}
      <div className="grid md:grid-cols-3 gap-10 max-w-6xl mx-auto">
        {features.map((item, index) => (
          <motion.div
            key={index}
            className="bg-white hover:shadow-xl shadow-md rounded-2xl p-8 text-center transition-all duration-300 border border-slate-100 hover:border-blue-100"
            initial={{ opacity: 0, y: 60 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.2, duration: 0.5 }}
            viewport={{ once: true }}
          >
            <div className="flex justify-center mb-4">{item.icon}</div>
            <h3 className="text-xl font-semibold text-gray-800 mb-3">
              {item.title}
            </h3>
            <p className="text-gray-500 text-sm leading-relaxed">{item.desc}</p>
          </motion.div>
        ))}
      </div>

      {/* Mission Section */}
      <motion.div
        className="max-w-4xl mx-auto mt-20 text-center"
        initial={{ opacity: 0, y: 60 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.7 }}
        viewport={{ once: true }}
      >
        <h2 className="text-3xl font-semibold text-gray-800 mb-4">
          Our Mission
        </h2>
        <p className="text-gray-600 text-lg leading-relaxed">
          To revolutionize how teams collaborate by integrating artificial intelligence,
          creativity, and simplicity — empowering every organization to achieve more
          with less friction.
        </p>
      </motion.div>

      {/* Gradient Footer Accent */}
      <div className="mt-24 flex justify-center">
        <div className="h-1 w-40 bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500 rounded-full" />
      </div>
    </div>
  );
};

export default AboutPage;
