import React, { useState, useEffect } from "react";
import { taskService } from "../../services/taskService";
import { meetingService } from "../../services/meetingService";
import {
  Calendar as CalendarIcon, CheckCircle2, Clock, MapPin,
  Video, AlertCircle, ChevronLeft, ChevronRight, Zap,
  Layers, Activity, Shield, Sparkles, Loader2
} from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";

interface CalendarEvent {
  id: string | number;
  title: string;
  time?: string;
  date: string;
  type: "meeting" | "task";
  status?: string;
  details?: string;
  isOverdue?: boolean;
}

const CalendarPage: React.FC = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [events, setEvents] = useState<CalendarEvent[]>([]);
  const [loading, setLoading] = useState<boolean>(true);

  const monthNames = [
    "January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"
  ];

  const daysOfWeek = ["SUN", "MON", "TUE", "WED", "THU", "FRI", "SAT"];

  const year = currentDate.getFullYear();
  const month = currentDate.getMonth();

  const firstDay = new Date(year, month, 1);
  const lastDay = new Date(year, month + 1, 0);
  const startingDay = firstDay.getDay();
  const totalDays = lastDay.getDate();

  const daysArray: number[] = [];
  for (let i = 1; i <= totalDays; i++) daysArray.push(i);

  const today = new Date();
  const todayStr = today.toLocaleDateString("en-CA");

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [tasks, meetings] = await Promise.all([
          taskService.getTasks(),
          meetingService.getAllMeetings()
        ]);

        const taskEvents: CalendarEvent[] = tasks
          .filter(t => t.due_date)
          .map(t => ({
            id: `task-${t.id}`,
            title: t.title,
            date: t.due_date ? new Date(t.due_date).toLocaleDateString("en-CA") : "",
            type: "task",
            status: t.status,
            details: t.priority,
            isOverdue: t.is_overdue
          }));

        const meetingEvents: CalendarEvent[] = meetings.map(m => {
          const start = new Date(m.start_time);
          const end = new Date(m.end_time);
          const timeStr = `${start.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })} – ${end.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}`;

          return {
            id: `meeting-${m.id}`,
            title: m.title,
            time: timeStr,
            date: start.toLocaleDateString("en-CA"),
            type: "meeting",
            details: m.location || "Online"
          };
        });

        setEvents([...taskEvents, ...meetingEvents]);
      } catch (error) {
        console.error("Failed to fetch calendar data", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const selectedDateStr = selectedDate.toLocaleDateString("en-CA");
  const dayEvents = events.filter((e) => e.date === selectedDateStr);

  return (
    <div className="min-h-screen bg-[#fcfdfe] text-slate-900 font-sans">
      <div className="max-w-[1200px] mx-auto px-6 py-10 lg:py-12 relative z-10">

        {/* Header */}
        <div className="flex flex-col md:flex-row md:items-center justify-between gap-6 mb-12">
          <div>
            <h1 className="text-3xl font-bold tracking-tight text-slate-900 mb-1">Calendar</h1>
            <p className="text-slate-500 font-medium">Keep track of all your upcoming sessions and milestones.</p>
          </div>

          <div className="flex items-center gap-4">
            <div className="flex items-center bg-white p-1 rounded-xl border border-slate-100 shadow-sm">
              <button
                onClick={() => setCurrentDate(new Date(year, month - 1, 1))}
                className="p-2 text-slate-400 hover:text-indigo-600 hover:bg-indigo-50 rounded-lg transition-all"
              >
                <ChevronLeft size={20} />
              </button>
              <div className="px-4 min-w-[140px] text-center">
                <span className="text-sm font-bold text-slate-700">{monthNames[month]} {year}</span>
              </div>
              <button
                onClick={() => setCurrentDate(new Date(year, month + 1, 1))}
                className="p-2 text-slate-400 hover:text-indigo-600 hover:bg-indigo-50 rounded-lg transition-all"
              >
                <ChevronRight size={20} />
              </button>
            </div>
            <button
              onClick={() => {
                setCurrentDate(new Date());
                setSelectedDate(new Date());
              }}
              className="px-5 py-2.5 bg-indigo-600 text-white rounded-xl font-bold text-sm shadow-lg shadow-indigo-100 hover:bg-indigo-700 transition-all"
            >
              Today
            </button>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-10">

          {/* Calendar Grid */}
          <div className="lg:col-span-2 bg-white rounded-3xl border border-slate-100 p-8 shadow-sm">
            <div className="grid grid-cols-7 text-center font-bold text-slate-400 text-[11px] tracking-wider mb-8 uppercase px-2">
              {daysOfWeek.map((d) => (
                <div key={d}>{d}</div>
              ))}
            </div>

            <div className="grid grid-cols-7 gap-2">
              {Array(startingDay).fill(null).map((_, i) => (
                <div key={`empty-${i}`} />
              ))}
              {daysArray.map((day) => {
                const d = new Date(year, month, day);
                const dateStr = d.toLocaleDateString("en-CA");
                const isTodayStr = dateStr === todayStr;
                const dateEvents = events.filter((e) => e.date === dateStr);
                const isSelected = dateStr === selectedDateStr;

                return (
                  <motion.div
                    key={day}
                    onClick={() => setSelectedDate(new Date(year, month, day))}
                    className={`relative aspect-square sm:aspect-auto sm:h-24 p-3 rounded-2xl cursor-pointer transition-all border ${isSelected
                        ? "bg-indigo-600 border-indigo-600 text-white shadow-lg shadow-indigo-100"
                        : isTodayStr
                          ? "bg-indigo-50 border-indigo-100 text-indigo-700"
                          : "bg-white border-transparent hover:bg-slate-50 hover:border-slate-100"
                      }`}
                  >
                    <span className="text-sm font-bold">{day}</span>
                    <div className="mt-2 space-y-1 overflow-hidden hidden sm:block">
                      {dateEvents.slice(0, 2).map((e, idx) => (
                        <div key={idx} className={`text-[10px] font-bold truncate px-1.5 py-0.5 rounded ${isSelected ? 'bg-white/20 text-white' : e.type === 'meeting' ? 'bg-indigo-100/50 text-indigo-700' : 'bg-emerald-100/50 text-emerald-700'
                          }`}>
                          {e.title}
                        </div>
                      ))}
                      {dateEvents.length > 2 && (
                        <div className={`text-[9px] font-bold ${isSelected ? 'text-white/70' : 'text-slate-400'} px-1`}>+ {dateEvents.length - 2} more</div>
                      )}
                    </div>
                    {dateEvents.length > 0 && (
                      <div className="sm:hidden flex justify-center mt-2">
                        <div className={`w-1 h-1 rounded-full ${isSelected ? 'bg-white' : 'bg-indigo-600'}`} />
                      </div>
                    )}
                  </motion.div>
                );
              })}
            </div>
          </div>

          {/* Activity Panel */}
          <div className="space-y-8">
            <div className="flex items-center justify-between px-2">
              <div>
                <h3 className="text-lg font-bold text-slate-900">{selectedDate.toLocaleDateString('en-US', { day: 'numeric', month: 'short' })}</h3>
                <p className="text-xs font-semibold text-slate-400">{selectedDate.toLocaleDateString('en-US', { weekday: 'long' })}</p>
              </div>
            </div>

            <div className="space-y-4">
              {loading ? (
                <div className="py-12 flex flex-col items-center">
                  <Loader2 className="w-8 h-8 text-indigo-600 animate-spin mb-3" />
                  <p className="text-xs font-bold text-slate-400">Loading schedule...</p>
                </div>
              ) : dayEvents.length > 0 ? (
                dayEvents.map((event) => (
                  <div
                    key={event.id}
                    className={`p-5 rounded-2xl border transition-all ${event.type === 'meeting'
                        ? 'bg-slate-900 text-white border-slate-900 shadow-lg shadow-slate-200'
                        : 'bg-white text-slate-900 border-slate-100 shadow-sm'
                      }`}
                  >
                    <div className="flex items-center gap-4 mb-3">
                      <div className={`p-2 rounded-lg ${event.type === 'meeting' ? 'bg-white/10' : 'bg-indigo-50 text-indigo-600'}`}>
                        {event.type === 'meeting' ? <Video size={16} /> : <Zap size={16} />}
                      </div>
                      <div>
                        <p className="text-sm font-bold">{event.title}</p>
                        <p className={`text-[10px] font-bold ${event.type === 'meeting' ? 'text-slate-400' : 'text-slate-500'} uppercase tracking-wider`}>
                          {event.time || `${event.details} Priority`}
                        </p>
                      </div>
                    </div>
                    {event.type === 'meeting' && event.details && (
                      <div className="flex items-center gap-2 text-[11px] text-indigo-200 font-medium">
                        <MapPin size={12} />
                        <span>{event.details}</span>
                      </div>
                    )}
                  </div>
                ))
              ) : (
                <div className="py-20 bg-slate-50/50 rounded-3xl border border-dashed border-slate-100 text-center">
                  <Layers size={32} className="text-slate-200 mx-auto mb-4" />
                  <p className="text-sm font-semibold text-slate-400">No events today.</p>
                </div>
              )}
            </div>

            <div className="bg-indigo-600 rounded-3xl p-8 text-white shadow-xl shadow-indigo-100">
              <Sparkles size={24} className="text-indigo-200 mb-6" />
              <h4 className="text-xl font-bold mb-2">Workspace Insight</h4>
              <p className="text-sm font-medium text-indigo-100 leading-relaxed mb-6">
                Looking ahead: You have a busy Wednesday next week. Consider blocking out some "deep work" time now.
              </p>
              <button className="w-full py-3 bg-white/10 hover:bg-white/20 border border-white/20 rounded-xl text-xs font-bold transition-all">
                Optimize Schedule
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CalendarPage;
