import React, { useState, useEffect } from "react";
import {
  Plus,
  Calendar,
  User as UserIcon,
  Search,
  Loader2,
  Trash2,
  ChevronRight,
  Filter,
  Layers,
  CheckCircle,
  Clock,
  Layout,
  ChevronDown,
  Edit3,
  ExternalLink,
  Clock3
} from "lucide-react";
import { useNavigate } from "react-router-dom";
import { taskService, Task } from "../../services/taskService";
import { motion, AnimatePresence } from "framer-motion";

const TasksPage: React.FC = () => {
  const navigate = useNavigate();
  const [tasks, setTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [activeTab, setActiveTab] = useState<string>("All");
  const [expandedTaskId, setExpandedTaskId] = useState<number | null>(null);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    setLoading(true);
    try {
      const data = await taskService.getTasks();
      setTasks(data);
    } catch (error) {
      console.error("Failed to fetch tasks", error);
    } finally {
      setLoading(false);
    }
  };

  const normalizeStatus = (status: string) => {
    const s = status ? status.toLowerCase() : "";
    if (s === "todo" || s === "upcoming") return "Upcoming";
    if (s === "in progress" || s === "inprogress") return "In Progress";
    if (s === "completed" || s === "done") return "Completed";
    return "Upcoming";
  };

  const getFilteredTasks = () => {
    return tasks.filter(t => {
      const matchesSearch = t.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        (t.description && t.description.toLowerCase().includes(searchQuery.toLowerCase()));
      const matchesTab = activeTab === "All" || normalizeStatus(t.status) === activeTab;
      return matchesSearch && matchesTab;
    });
  };

  const handleStatusUpdate = async (e: React.MouseEvent, task: Task, newStatus: string) => {
    e.stopPropagation();
    setTasks(prev => prev.map(t => t.id === task.id ? { ...t, status: newStatus } : t));
    try {
      await taskService.updateTask(task.id, { status: newStatus });
    } catch (error) {
      console.error("Failed to update status");
      fetchTasks();
    }
  };

  const onTaskDelete = async (e: React.MouseEvent, id: number) => {
    e.stopPropagation();
    if (!window.confirm("Delete this task?")) return;
    setTasks(prev => prev.filter(t => t.id !== id));
    try {
      await taskService.deleteTask(id);
    } catch (error) {
      console.error("Failed to delete task");
    }
  };

  const stats = {
    total: tasks.length,
    upcoming: tasks.filter(t => normalizeStatus(t.status) === "Upcoming").length,
    progress: tasks.filter(t => normalizeStatus(t.status) === "In Progress").length,
    done: tasks.filter(t => normalizeStatus(t.status) === "Completed").length,
  };

  return (
    <div className="min-h-screen bg-[#F0F2F5] p-4 md:p-8 lg:p-12 animate-in fade-in duration-500 font-sans">
      <div className="max-w-7xl mx-auto space-y-8">

        {/* üèóÔ∏è Modern Page Header */}
        <div className="flex flex-col md:flex-row md:items-end justify-between gap-6 border-b border-slate-200 pb-8">
          <div className="space-y-1">
            <div className="flex items-center gap-3">
              <div className="w-12 h-12 bg-indigo-600 rounded-2xl flex items-center justify-center text-white shadow-lg shadow-indigo-200">
                <Layers size={24} />
              </div>
              <h1 className="text-4xl font-black text-slate-900 tracking-tight">Project Hub</h1>
            </div>
            <p className="text-slate-500 font-medium pl-1">Intelligent task management & monitoring dashboard.</p>
          </div>
          <button
            onClick={() => navigate("/tasks/new")}
            className="bg-slate-900 hover:bg-black text-white px-8 py-4 rounded-2xl font-bold shadow-xl shadow-slate-300 transition-all active:scale-95 flex items-center gap-2 group"
          >
            <Plus size={20} className="group-hover:rotate-180 transition-transform duration-500" />
            Init New Task
          </button>
        </div>

        {/* üìä Metrics Bar */}
        <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
          <SimpleStatBox label="Archived" value={stats.total} icon={<Layout size={16} />} sub="Live Projects" />
          <SimpleStatBox label="Planned" value={stats.upcoming} icon={<Clock size={16} />} sub="Ready to start" />
          <SimpleStatBox label="Active" value={stats.progress} icon={<Loader2 size={16} className="animate-spin-slow" />} sub="Under development" />
          <SimpleStatBox label="Verified" value={stats.done} icon={<CheckCircle size={16} />} sub="Quality assured" />
        </div>

        {/* üîç Dynamic Filters */}
        <div className="flex flex-col lg:flex-row items-center gap-4">
          <div className="relative flex-1 w-full">
            <Search className="absolute left-6 top-1/2 -translate-y-1/2 text-slate-400" size={20} />
            <input
              type="text"
              placeholder="Query tasks by title or metadata..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-16 pr-6 py-5 bg-white rounded-[24px] border-none shadow-sm text-slate-700 font-bold outline-none placeholder:text-slate-300 focus:ring-2 focus:ring-indigo-100 transition-all"
            />
          </div>
          <div className="flex bg-white p-2 rounded-[24px] gap-2 shadow-sm border border-slate-100/50">
            {["All", "Upcoming", "In Progress", "Completed"].map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`px-6 py-3 rounded-xl text-xs font-black uppercase tracking-widest transition-all ${activeTab === tab ? 'bg-indigo-600 text-white shadow-lg shadow-indigo-200' : 'text-slate-400 hover:text-slate-600'}`}
              >
                {tab}
              </button>
            ))}
          </div>
        </div>

        {/* üìã Expandable Task List */}
        <div className="space-y-6">
          {loading ? (
            <div className="py-32 flex flex-col items-center justify-center space-y-4">
              <Loader2 className="w-10 h-10 animate-spin text-indigo-600" />
              <span className="font-black text-slate-400 uppercase tracking-widest text-xs">Syncing Core...</span>
            </div>
          ) : (
            <AnimatePresence mode="popLayout">
              {getFilteredTasks().length === 0 ? (
                <motion.div
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  className="py-32 text-center bg-white rounded-[40px] border-2 border-dashed border-slate-200"
                >
                  <h3 className="text-2xl font-black text-slate-200">NO ACTIVE NODES</h3>
                </motion.div>
              ) : (
                getFilteredTasks().map((task, idx) => (
                  <ExpandableTaskBar
                    key={task.id}
                    task={task}
                    index={idx}
                    isExpanded={expandedTaskId === task.id}
                    onToggle={() => setExpandedTaskId(expandedTaskId === task.id ? null : task.id)}
                    onDelete={onTaskDelete}
                    onUpdateStatus={handleStatusUpdate}
                    navigate={navigate}
                    normalizeStatus={normalizeStatus}
                  />
                ))
              )}
            </AnimatePresence>
          )}
        </div>
      </div>
    </div>
  );
};

/* üß© Component: SimpleStatBox */
const SimpleStatBox = ({ label, value, icon, sub }: any) => (
  <div className="bg-white p-6 rounded-3xl border border-slate-200/50 shadow-sm flex items-center gap-5 hover:border-indigo-100 transition-colors group">
    <div className="w-12 h-12 rounded-2xl bg-slate-50 flex items-center justify-center text-slate-400 group-hover:bg-indigo-50 group-hover:text-indigo-600 transition-all">
      {icon}
    </div>
    <div>
      <p className="text-[10px] font-black text-slate-400 uppercase tracking-widest">{label}</p>
      <h4 className="text-2xl font-black text-slate-900 leading-none my-1">{value}</h4>
      <p className="text-[10px] font-medium text-slate-400 italic">{sub}</p>
    </div>
  </div>
);

/* üß© Component: ExpandableTaskBar (Redesigned with Click-to-Expand) */
const ExpandableTaskBar = ({ task, index, isExpanded, onToggle, onDelete, onUpdateStatus, navigate, normalizeStatus }: any) => {
  const status = normalizeStatus(task.status);

  return (
    <motion.div
      layout
      initial={{ opacity: 0, y: 15 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, scale: 0.95 }}
      transition={{ delay: index * 0.04 }}
      className={`group bg-white rounded-[32px] overflow-hidden border ${isExpanded ? 'border-indigo-400 shadow-2xl shadow-indigo-100' : 'border-slate-100 shadow-sm'} transition-all duration-300`}
    >
      {/* Header / Bar Section */}
      <div
        onClick={onToggle}
        className="p-6 md:p-8 flex flex-col lg:flex-row items-center gap-6 cursor-pointer relative"
      >
        {/* Status indicator strip */}
        <div className={`absolute left-0 top-0 bottom-0 w-1.5 ${status === 'Completed' ? 'bg-green-500' : status === 'In Progress' ? 'bg-orange-500' : 'bg-indigo-500'}`}></div>

        {/* Left Side: Basic Info */}
        <div className="flex-1 min-w-0 flex items-center gap-4">
          <div className={`w-12 h-12 rounded-2xl flex items-center justify-center ${status === 'Completed' ? 'bg-green-50 text-green-600' : status === 'In Progress' ? 'bg-orange-50 text-orange-600' : 'bg-indigo-50 text-indigo-600'}`}>
            {status === 'Completed' ? <CheckCircle size={20} /> : status === 'In Progress' ? <Loader2 size={20} className="animate-spin" /> : <Clock3 size={20} />}
          </div>
          <div className="overflow-hidden">
            <h3 className="text-xl font-black text-slate-800 uppercase tracking-tighter truncate group-hover:text-indigo-600 transition-colors">
              {task.title}
            </h3>
            <p className="text-[10px] font-bold text-slate-400 uppercase tracking-[0.2em]">{status} ‚Ä¢ Task #{task.id}</p>
          </div>
        </div>

        {/* Middle: Compact Meta (Visible when collapsed) */}
        <div className="flex items-center gap-10">
          <div className="flex -space-x-2">
            {task.assigned_to_user ? (
              <div className="w-10 h-10 rounded-full bg-slate-100 border-4 border-white flex items-center justify-center font-black text-xs text-indigo-600">
                {task.assigned_to_user.name.charAt(0)}
              </div>
            ) : (
              <div className="w-10 h-10 rounded-full bg-slate-50 border-4 border-white flex items-center justify-center text-slate-300">
                <UserIcon size={16} />
              </div>
            )}
          </div>

          <div className="hidden sm:block text-right">
            <p className="text-[10px] font-black text-slate-400 uppercase tracking-tighter">Deadline</p>
            <p className="text-xs font-bold text-slate-700">{task.due_date ? new Date(task.due_date).toLocaleDateString() : 'N/A'}</p>
          </div>
        </div>

        {/* Right Side: Quick Actions & Toggle */}
        <div className="flex items-center gap-4">
          <div className="hidden lg:flex bg-slate-50 p-1.5 rounded-2xl gap-1">
            {["Upcoming", "In Progress", "Completed"].map((s) => (
              <button
                key={s}
                onClick={(e) => onUpdateStatus(e, task, s)}
                className={`px-3 py-1.5 rounded-xl text-[8px] font-black uppercase transition-all ${status === s ? (s === 'Upcoming' ? 'bg-indigo-600' : s === 'In Progress' ? 'bg-orange-600' : 'bg-green-600') + ' text-white shadow-md' : 'text-slate-300 hover:text-slate-500'}`}
              >
                {s}
              </button>
            ))}
          </div>

          <button
            onClick={(e) => onDelete(e, task.id)}
            className="w-12 h-12 rounded-2xl flex items-center justify-center text-slate-300 hover:text-red-500 hover:bg-red-50 transition-all"
          >
            <Trash2 size={18} />
          </button>

          <div className={`w-12 h-12 rounded-2xl bg-slate-50 flex items-center justify-center text-slate-400 transition-transform duration-300 ${isExpanded ? 'rotate-180 bg-indigo-50 text-indigo-600' : ''}`}>
            <ChevronDown size={20} />
          </div>
        </div>
      </div>

      {/* Expanded Content Section */}
      <AnimatePresence>
        {isExpanded && (
          <motion.div
            initial={{ height: 0, opacity: 0 }}
            animate={{ height: "auto", opacity: 1 }}
            exit={{ height: 0, opacity: 0 }}
            transition={{ duration: 0.3, ease: "easeInOut" }}
            className="border-t border-slate-100 bg-slate-50/50 overflow-hidden"
          >
            <div className="p-8 grid grid-cols-1 lg:grid-cols-12 gap-10">

              {/* Description Panel */}
              <div className="lg:col-span-8 space-y-4">
                <div className="flex items-center gap-2 text-[10px] font-black text-indigo-500 uppercase tracking-[0.2em] mb-4">
                  <AlignLeft size={16} /> Node Description
                </div>
                <div className="bg-white p-8 rounded-[32px] border border-slate-200 shadow-sm text-slate-600 leading-relaxed min-h-[160px] whitespace-pre-wrap">
                  {task.description || "No technical documentation or description was provided for this task node."}
                </div>
              </div>

              {/* Details & Actions Panel */}
              <div className="lg:col-span-4 flex flex-col gap-6">
                <div className="space-y-4">
                  <div className="flex items-center gap-2 text-[10px] font-black text-slate-400 uppercase tracking-[0.2em]">
                    Node Analytics
                  </div>
                  <div className="bg-white p-6 rounded-[28px] border border-slate-200 space-y-4 shadow-sm">
                    <div className="flex justify-between items-center text-sm">
                      <span className="text-slate-400 font-bold uppercase text-[9px]">Assignee</span>
                      <span className="text-slate-800 font-black">{task.assigned_to_user?.name || "System Allocated"}</span>
                    </div>
                    <div className="flex justify-between items-center text-sm">
                      <span className="text-slate-400 font-bold uppercase text-[9px]">Target</span>
                      <span className="text-slate-800 font-black">{task.due_date ? new Date(task.due_date).toDateString() : "Unscheduled"}</span>
                    </div>
                    <div className="flex justify-between items-center text-sm pt-4 border-t border-dashed border-slate-100">
                      <span className="text-slate-400 font-bold uppercase text-[9px]">Priority</span>
                      <span className="px-3 py-1 bg-slate-100 rounded-full text-[10px] font-black text-slate-600">{task.priority || 'NORMAL'}</span>
                    </div>
                  </div>
                </div>

                {/* Main Action */}
                <button
                  onClick={() => navigate(`/tasks/edit/${task.id}`)}
                  className="w-full bg-indigo-600 hover:bg-indigo-700 text-white rounded-3xl py-5 font-black uppercase tracking-widest text-xs flex items-center justify-center gap-3 shadow-xl shadow-indigo-100 transition-all hover:scale-[1.02] active:scale-95 group"
                >
                  <Edit3 size={18} />
                  Edit Task Node
                  <ExternalLink size={14} className="opacity-40 group-hover:translate-x-1 group-hover:-translate-y-1 transition-transform" />
                </button>
              </div>

            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.div>
  );
};

export default TasksPage;
