import React, { useState, useEffect, useRef } from "react";
import { motion, AnimatePresence } from "framer-motion";
import {
  User,
  Mail,
  Phone,
  MapPin,
  Briefcase,
  Camera,
  Save,
  Loader2,
  ShieldCheck,
  Lock,
  Smartphone,
  Globe,
  CheckCircle2,
  XCircle,
  AlertCircle
} from "lucide-react";
import { authService } from "../../services/authService";

const ProfilePage: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [success, setSuccess] = useState(false);
  const [profile, setProfile] = useState<any>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const fetchUser = async () => {
    try {
      setLoading(true);
      const data = await authService.getCurrentUser();
      setProfile(data);
    } catch (error) {
      console.error("Failed to load profile", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUser();
  }, []);

  const handleUpdate = async (field: string, value: any) => {
    setProfile((prev: any) => ({ ...prev, [field]: value }));
  };

  const handleSave = async () => {
    try {
      setSaving(true);
      setSuccess(false);
      // We don't want to send sensitive or internal fields back usually
      const { id, email, hashed_password, last_seen, status, ...updateData } = profile;
      await authService.updateProfile(updateData);
      setSuccess(true);
      setTimeout(() => setSuccess(false), 3000);
    } catch (error) {
      console.error("Update failed", error);
    } finally {
      setSaving(false);
    }
  };

  const handleImageUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        handleUpdate('avatar', reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  if (loading || !profile) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-slate-50/50">
        <div className="flex flex-col items-center gap-4">
          <Loader2 className="w-10 h-10 text-indigo-600 animate-spin" />
          <p className="text-sm font-medium text-slate-500">Retrieving your dossier...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 max-w-5xl mx-auto space-y-8 pb-24">
      {/* Profile Header */}
      <motion.div
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-white rounded-[2rem] p-8 border border-slate-100 shadow-sm flex flex-col md:flex-row items-center gap-8 relative overflow-hidden"
      >
        <div className="absolute top-0 right-0 w-64 h-64 bg-indigo-50/30 rounded-full blur-3xl -mr-32 -mt-32" />

        {/* Avatar Section */}
        <div className="relative group z-10 flex-shrink-0">
          <div className="w-40 h-40 rounded-[2.5rem] bg-indigo-100 flex items-center justify-center text-indigo-700 text-5xl font-black border-4 border-white shadow-2xl overflow-hidden ring-1 ring-slate-100 transition-transform duration-500 group-hover:scale-[1.02]">
            {profile.avatar ? (
              <img src={profile.avatar} alt={profile.name} className="w-full h-full object-cover" />
            ) : (
              profile.name?.charAt(0) || 'U'
            )}
          </div>
          <button
            onClick={() => fileInputRef.current?.click()}
            className="absolute -bottom-2 -right-2 p-3.5 bg-indigo-600 text-white rounded-2xl shadow-xl border-4 border-white hover:bg-black transition-all hover:scale-110 active:scale-95"
          >
            <Camera size={20} />
          </button>
          <input type="file" ref={fileInputRef} onChange={handleImageUpload} className="hidden" accept="image/*" />
        </div>

        {/* User Info */}
        <div className="flex-1 text-center md:text-left z-10">
          <div className="flex items-center justify-center md:justify-start gap-3 mb-2">
            <h1 className="text-4xl font-black text-slate-900 tracking-tight">{profile.name}</h1>
            <span className="px-2.5 py-1 bg-green-50 text-green-600 text-[10px] font-black uppercase tracking-widest rounded-lg border border-green-100">
              Live Account
            </span>
          </div>
          <p className="text-lg text-slate-500 font-medium mb-4 flex items-center justify-center md:justify-start gap-2">
            <Briefcase size={18} className="text-indigo-400" />
            {profile.job_title || profile.role || "Workspace Member"}
          </p>
          <div className="flex flex-wrap items-center justify-center md:justify-start gap-4">
            <div className="flex items-center gap-2 px-3 py-1.5 bg-slate-50 rounded-xl text-xs font-bold text-slate-600 border border-slate-100">
              <Mail size={14} className="text-indigo-500" /> {profile.email}
            </div>
            <div className="flex items-center gap-2 px-3 py-1.5 bg-slate-50 rounded-xl text-xs font-bold text-slate-600 border border-slate-100">
              <Globe size={14} className="text-indigo-500" /> {profile.timezone}
            </div>
          </div>
        </div>

        {/* Global Save Button */}
        <div className="pt-4 md:pt-0 z-10 w-full md:w-auto">
          <button
            onClick={handleSave}
            disabled={saving}
            className={`w-full md:w-auto flex items-center justify-center gap-2 px-8 py-3.5 rounded-2xl font-black text-sm transition-all shadow-xl active:scale-95 ${success
                ? 'bg-green-500 text-white shadow-green-500/20'
                : 'bg-black text-white hover:bg-indigo-600 shadow-indigo-500/10'
              }`}
          >
            {saving ? <Loader2 size={18} className="animate-spin" /> : success ? <CheckCircle2 size={18} /> : <Save size={18} />}
            {saving ? 'Synchronizing...' : success ? 'Profile Saved' : 'Commit Changes'}
          </button>
        </div>
      </motion.div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Left Column - Detailed Profile Info */}
        <div className="lg:col-span-2 space-y-8">
          <section className="bg-white rounded-3xl p-8 border border-slate-100 shadow-sm space-y-6">
            <div className="flex items-center gap-4 mb-2">
              <div className="p-2.5 bg-indigo-50 text-indigo-600 rounded-xl"><User size={20} /></div>
              <h3 className="text-xl font-black text-slate-900 tracking-tight">Identity & Role</h3>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="space-y-2">
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-[0.15em] ml-1">Legal Name</label>
                <input
                  type="text"
                  value={profile.name}
                  onChange={(e) => handleUpdate('name', e.target.value)}
                  className="w-full px-5 py-3.5 bg-slate-50 border border-slate-100 rounded-2xl focus:outline-none focus:ring-2 focus:ring-indigo-500/10 focus:border-indigo-500 transition-all font-bold text-slate-800"
                />
              </div>
              <div className="space-y-2">
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-[0.15em] ml-1">Current Job Title</label>
                <input
                  type="text"
                  value={profile.job_title || ''}
                  onChange={(e) => handleUpdate('job_title', e.target.value)}
                  placeholder="e.g. Senior Product Designer"
                  className="w-full px-5 py-3.5 bg-slate-50 border border-slate-100 rounded-2xl focus:outline-none focus:ring-2 focus:ring-indigo-500/10 focus:border-indigo-500 transition-all font-bold text-slate-800"
                />
              </div>
              <div className="md:col-span-2 space-y-2">
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-[0.15em] ml-1">About & Biography</label>
                <textarea
                  rows={4}
                  value={profile.bio || ''}
                  onChange={(e) => handleUpdate('bio', e.target.value)}
                  className="w-full px-5 py-3.5 bg-slate-50 border border-slate-100 rounded-2xl focus:outline-none focus:ring-2 focus:ring-indigo-500/10 focus:border-indigo-500 transition-all font-medium text-slate-700 resize-none"
                />
              </div>
            </div>
          </section>

          <section className="bg-white rounded-3xl p-8 border border-slate-100 shadow-sm space-y-6">
            <div className="flex items-center gap-4 mb-2">
              <div className="p-2.5 bg-indigo-50 text-indigo-600 rounded-xl"><MapPin size={20} /></div>
              <h3 className="text-xl font-black text-slate-900 tracking-tight">Connectivity</h3>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="space-y-2">
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-[0.15em] ml-1">Work Email (Locked)</label>
                <div className="px-5 py-3.5 bg-slate-100 border border-slate-200 rounded-2xl font-bold text-slate-500 flex items-center justify-between">
                  {profile.email}
                  <Lock size={14} className="text-slate-400" />
                </div>
              </div>
              <div className="space-y-2">
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-[0.15em] ml-1">Mobile Phone</label>
                <input
                  type="tel"
                  value={profile.phone || ''}
                  onChange={(e) => handleUpdate('phone', e.target.value)}
                  placeholder="+1 (000) 000-0000"
                  className="w-full px-5 py-3.5 bg-slate-50 border border-slate-100 rounded-2xl focus:outline-none focus:ring-2 focus:ring-indigo-500/10 focus:border-indigo-500 transition-all font-bold text-slate-800"
                />
              </div>
            </div>
          </section>
        </div>

        {/* Right Column - Workable Security & Settings */}
        <div className="space-y-8">
          {/* Security & 2FA - Workable */}
          <section className="bg-white rounded-3xl p-8 border border-slate-100 shadow-sm space-y-6">
            <div className="flex items-center gap-4">
              <div className="p-2.5 bg-indigo-50 text-indigo-600 rounded-xl"><ShieldCheck size={20} /></div>
              <h3 className="text-xl font-black text-slate-900 tracking-tight">Security</h3>
            </div>

            <div className={`p-4 rounded-2xl border transition-all ${profile.two_factor_enabled ? 'bg-green-50 border-green-100' : 'bg-amber-50 border-amber-100'}`}>
              <div className="flex items-center justify-between mb-2">
                <h4 className={`text-xs font-black uppercase tracking-widest ${profile.two_factor_enabled ? 'text-green-700' : 'text-amber-700'}`}>
                  Two-Factor Auth
                </h4>
                <div className={`w-2 h-2 rounded-full ${profile.two_factor_enabled ? 'bg-green-500' : 'bg-amber-500'}`} />
              </div>
              <p className={`text-xs font-bold leading-relaxed mb-4 ${profile.two_factor_enabled ? 'text-green-600' : 'text-amber-600'}`}>
                {profile.two_factor_enabled
                  ? "Your account is currently protected by 2FA. We'll ask for a code on new logins."
                  : "Your account is vulnerable! Enable 2FA to prevent unauthorized access."}
              </p>
              <button
                onClick={() => handleUpdate('two_factor_enabled', !profile.two_factor_enabled)}
                className={`w-full py-2.5 rounded-xl text-xs font-black transition-all ${profile.two_factor_enabled
                    ? 'bg-white text-green-700 border border-green-200 hover:bg-green-100'
                    : 'bg-amber-600 text-white shadow-lg shadow-amber-600/20 hover:bg-amber-700'
                  }`}
              >
                {profile.two_factor_enabled ? "Disable 2FA Protection" : "Enable 2FA Protection"}
              </button>
            </div>

            <div className="space-y-4 pt-2">
              <button className="w-full flex items-center justify-between px-4 py-3 bg-slate-50 hover:bg-slate-100 border border-slate-100 rounded-2xl transition-colors group">
                <div className="flex items-center gap-3">
                  <Smartphone className="text-slate-400 group-hover:text-indigo-600 transition-colors" size={18} />
                  <span className="text-xs font-bold text-slate-700">Trusted Devices</span>
                </div>
                <span className="text-[10px] font-black text-slate-400">03 ACTIVE</span>
              </button>
              <button className="w-full flex items-center justify-between px-4 py-3 bg-slate-50 hover:bg-slate-100 border border-slate-100 rounded-2xl transition-colors group">
                <div className="flex items-center gap-3">
                  <Lock className="text-slate-400 group-hover:text-indigo-600 transition-colors" size={18} />
                  <span className="text-xs font-bold text-slate-700">Login Activity</span>
                </div>
                <AlertCircle className="text-slate-400" size={14} />
              </button>
            </div>
          </section>

          {/* Quick Settings - Workable */}
          <section className="bg-white rounded-3xl p-8 border border-slate-100 shadow-sm space-y-6">
            <div className="flex items-center gap-4">
              <div className="p-2.5 bg-indigo-50 text-indigo-600 rounded-xl"><Globe size={20} /></div>
              <h3 className="text-xl font-black text-slate-900 tracking-tight">Preferences</h3>
            </div>
            <div className="space-y-4">
              <div className="flex items-center justify-between py-2">
                <span className="text-xs font-bold text-slate-600">Online Status</span>
                <button
                  onClick={() => handleUpdate('show_online_status', !profile.show_online_status)}
                  className={`relative inline-flex h-5 w-10 items-center rounded-full transition-colors ${profile.show_online_status ? 'bg-indigo-600' : 'bg-slate-200'}`}
                >
                  <span className={`inline-block h-3 w-3 transform rounded-full bg-white transition-transform ${profile.show_online_status ? 'translate-x-6' : 'translate-x-1'}`} />
                </button>
              </div>
              <div className="flex items-center justify-between py-2">
                <span className="text-xs font-bold text-slate-600">Sync Timezone</span>
                <button
                  onClick={() => handleUpdate('share_local_time', !profile.share_local_time)}
                  className={`relative inline-flex h-5 w-10 items-center rounded-full transition-colors ${profile.share_local_time ? 'bg-indigo-600' : 'bg-slate-200'}`}
                >
                  <span className={`inline-block h-3 w-3 transform rounded-full bg-white transition-transform ${profile.share_local_time ? 'translate-x-6' : 'translate-x-1'}`} />
                </button>
              </div>
            </div>
          </section>
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
