import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { authService } from "../../services/authService";
import { Mail, Phone, MapPin, Briefcase, Camera, Save, Loader2, Lock } from "lucide-react";

const ProfilePage: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [profile, setProfile] = useState<any>({
    name: "",
    email: "",
    role: "Team Member",
    job_title: "",
    avatar: "https://i.pravatar.cc/150?u=a042581f4e29026704d",
    phone: "",
    address: "",
    bio: "",
    two_factor_enabled: false
  });

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const userData = await authService.getCurrentUser();
        setProfile({
          ...userData,
          avatar: userData.avatar || "https://i.pravatar.cc/150?u=a042581f4e29026704d"
        });
      } catch (error) {
        console.error("Failed to load profile", error);
      } finally {
        setLoading(false);
      }
    };
    fetchUser();
  }, []);

  const handleSave = async () => {
    try {
      setSaving(true);
      const { id, email, hashed_password, last_seen, status, ...updateData } = profile;
      await authService.updateProfile(updateData);
      setIsEditing(false);
    } catch (error) {
      console.error("Failed to update profile", error);
      alert("Failed to save profile changes.");
    } finally {
      setSaving(false);
    }
  };

  const fileInputRef = React.useRef<HTMLInputElement>(null);

  const handleImageClick = () => {
    if (isEditing) {
      fileInputRef.current?.click();
    }
  };

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setProfile((prev: any) => ({ ...prev, avatar: reader.result as string }));
      };
      reader.readAsDataURL(file);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <Loader2 className="w-8 h-8 text-indigo-600 animate-spin" />
      </div>
    );
  }

  return (
    <div className="max-w-6xl mx-auto space-y-6 p-6">
      <motion.div
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        className="flex items-center justify-between"
      >
        <div>
          <h1 className="text-3xl font-bold text-slate-800">My Profile</h1>
          <p className="text-slate-500 mt-1">Manage your account settings and preferences.</p>
        </div>
        <div className="flex items-center gap-3">
          {isEditing && (
            <button
              onClick={() => setIsEditing(false)}
              className="px-4 py-2 text-sm font-semibold text-slate-500 hover:text-slate-800"
            >
              Cancel
            </button>
          )}
          <button
            onClick={() => isEditing ? handleSave() : setIsEditing(true)}
            disabled={saving}
            className={`px-6 py-2 rounded-xl font-bold flex items-center gap-2 transition-all shadow-sm ${isEditing
              ? "bg-indigo-600 text-white hover:bg-indigo-700 active:scale-95"
              : "bg-white border border-slate-200 text-slate-700 hover:bg-slate-50"
              }`}
          >
            {saving ? <Loader2 size={18} className="animate-spin" /> : isEditing ? <Save size={18} /> : null}
            {saving ? "Saving..." : isEditing ? "Save Changes" : "Edit Profile"}
          </button>
        </div>
      </motion.div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 text-left">
        {/* Left Column: Profile Card */}
        <motion.div
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ delay: 0.1 }}
          className="lg:col-span-1"
        >
          <div className="bg-white rounded-2xl shadow-sm border border-slate-200 p-8 flex flex-col items-center text-center">
            <input
              type="file"
              ref={fileInputRef}
              onChange={handleFileChange}
              accept="image/*"
              className="hidden"
            />
            <div
              className={`relative group mb-6 ${isEditing ? 'cursor-pointer' : ''}`}
              onClick={handleImageClick}
            >
              <div className="w-36 h-36 rounded-[2rem] overflow-hidden border-4 border-slate-50 shadow-inner bg-slate-100">
                <img
                  src={profile.avatar}
                  alt={profile.name}
                  className="w-full h-full object-cover group-hover:opacity-90 transition-opacity"
                />
              </div>
              {isEditing && (
                <div className="absolute inset-0 flex items-center justify-center bg-black/40 rounded-[2rem] opacity-0 group-hover:opacity-100 transition-opacity">
                  <Camera className="text-white w-8 h-8" />
                </div>
              )}
            </div>

            <h2 className="text-2xl font-black text-slate-900 leading-tight">{profile.name}</h2>
            <p className="text-indigo-600 font-bold text-sm mt-1">{profile.job_title || profile.role}</p>

            <div className="w-full mt-8 space-y-3">
              <div className="flex items-center gap-3 text-slate-600 text-sm p-3.5 bg-slate-50 rounded-xl border border-slate-100">
                <Mail size={16} className="text-indigo-500" />
                <span className="truncate font-semibold">{profile.email}</span>
              </div>
              <div className="flex items-center gap-3 text-slate-600 text-sm p-3.5 bg-slate-50 rounded-xl border border-slate-100">
                <Briefcase size={16} className="text-indigo-500" />
                <span className="truncate font-semibold">{profile.role}</span>
              </div>
            </div>
          </div>
        </motion.div>

        {/* Right Column: Details Form */}
        <motion.div
          initial={{ opacity: 0, x: 20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ delay: 0.2 }}
          className="lg:col-span-2 space-y-6"
        >
          <div className="bg-white rounded-2xl shadow-sm border border-slate-200 overflow-hidden">
            <div className="px-8 py-5 border-b border-slate-100 bg-slate-50/50">
              <h3 className="font-bold text-slate-800">Personal Information</h3>
            </div>

            <div className="p-8 grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="space-y-1.5">
                <label className="text-xs font-black text-slate-400 uppercase tracking-widest ml-1">Full Name</label>
                <input
                  type="text"
                  disabled={!isEditing}
                  value={profile.name}
                  onChange={(e) => setProfile({ ...profile, name: e.target.value })}
                  className="w-full px-4 py-2.5 rounded-xl border border-slate-200 focus:outline-none focus:ring-2 focus:ring-indigo-500/20 focus:border-indigo-500 disabled:bg-slate-50 disabled:text-slate-500 transition-all font-semibold"
                />
              </div>

              <div className="space-y-1.5">
                <label className="text-xs font-black text-slate-400 uppercase tracking-widest ml-1">Job Title</label>
                <input
                  type="text"
                  disabled={!isEditing}
                  placeholder="e.g. Project Manager"
                  value={profile.job_title || ''}
                  onChange={(e) => setProfile({ ...profile, job_title: e.target.value })}
                  className="w-full px-4 py-2.5 rounded-xl border border-slate-200 focus:outline-none focus:ring-2 focus:ring-indigo-500/20 focus:border-indigo-500 disabled:bg-slate-50 disabled:text-slate-500 transition-all font-semibold"
                />
              </div>

              <div className="space-y-1.5">
                <label className="text-xs font-black text-slate-400 uppercase tracking-widest ml-1">Work Email</label>
                <div className="relative">
                  <input
                    type="email"
                    disabled={true}
                    value={profile.email}
                    className="w-full px-4 py-2.5 rounded-xl border border-slate-200 bg-slate-100 text-slate-500 cursor-not-allowed font-semibold"
                  />
                  <Lock size={14} className="absolute right-4 top-1/2 -translate-y-1/2 text-slate-400" />
                </div>
              </div>

              <div className="space-y-1.5">
                <label className="text-xs font-black text-slate-400 uppercase tracking-widest ml-1">Phone Number</label>
                <div className="relative">
                  <Phone className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400" size={16} />
                  <input
                    type="text"
                    disabled={!isEditing}
                    placeholder="+1 (555) 000-0000"
                    value={profile.phone || ''}
                    onChange={(e) => setProfile({ ...profile, phone: e.target.value })}
                    className="w-full pl-10 pr-4 py-2.5 rounded-xl border border-slate-200 focus:outline-none focus:ring-2 focus:ring-indigo-500/20 focus:border-indigo-500 disabled:bg-slate-50 disabled:text-slate-500 transition-all font-semibold"
                  />
                </div>
              </div>

              <div className="space-y-1.5">
                <label className="text-xs font-black text-slate-400 uppercase tracking-widest ml-1">Location</label>
                <div className="relative">
                  <MapPin className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400" size={16} />
                  <input
                    type="text"
                    disabled={!isEditing}
                    placeholder="San Francisco, CA"
                    value={profile.address || ''}
                    onChange={(e) => setProfile({ ...profile, address: e.target.value })}
                    className="w-full pl-10 pr-4 py-2.5 rounded-xl border border-slate-200 focus:outline-none focus:ring-2 focus:ring-indigo-500/20 focus:border-indigo-500 disabled:bg-slate-50 disabled:text-slate-500 transition-all font-semibold"
                  />
                </div>
              </div>

              <div className="col-span-1 md:col-span-2 space-y-1.5">
                <label className="text-xs font-black text-slate-400 uppercase tracking-widest ml-1">Bio</label>
                <textarea
                  rows={4}
                  disabled={!isEditing}
                  value={profile.bio || ''}
                  onChange={(e) => setProfile({ ...profile, bio: e.target.value })}
                  placeholder="Tell us about yourself..."
                  className="w-full px-4 py-3 rounded-xl border border-slate-200 focus:outline-none focus:ring-2 focus:ring-indigo-500/20 focus:border-indigo-500 disabled:bg-slate-50 disabled:text-slate-500 transition-all resize-none font-medium text-slate-700"
                />
              </div>
            </div>
          </div>


        </motion.div>
      </div>
    </div>
  );
};

export default ProfilePage;
