import React, { useState } from "react";
import {
  Github,
  Linkedin,
  Twitter,
  Hexagon,
  Heart,
  Mail,
  Globe,
  ArrowRight,
  CheckCircle2
} from "lucide-react";
import { Link } from "react-router-dom";

const Footer: React.FC = () => {
  const year = new Date().getFullYear();
  const [email, setEmail] = useState("");
  const [subscribed, setSubscribed] = useState(false);

  const handleSubscribe = (e: React.FormEvent) => {
    e.preventDefault();
    if (email) {
      setSubscribed(true);
      setTimeout(() => {
        setSubscribed(false);
        setEmail("");
      }, 3000);
    }
  };

  return (
    <footer className="w-full bg-slate-900 text-slate-300 mt-auto border-t border-slate-800 font-sans">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">

        {/* Top Section: CTA & Newsletter */}
        <div className="py-12 border-b border-slate-800 flex flex-col lg:flex-row justify-between items-start lg:items-center gap-8">
          <div className="max-w-xl">
            <h3 className="text-2xl font-bold text-white mb-2">Ready to boost your productivity?</h3>
            <p className="text-slate-400">Join 10,000+ teams using Collabryta to ship faster. Subscribe for updates, tips, and exclusive offers.</p>
          </div>

          <form onSubmit={handleSubscribe} className="w-full lg:w-auto flex flex-col sm:flex-row gap-3">
            <div className="relative">
              <Mail className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-500" size={18} />
              <input
                type="email"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="w-full sm:w-72 pl-10 pr-4 py-2.5 bg-slate-800 border border-slate-700 rounded-lg text-white placeholder:text-slate-500 focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all"
                required
              />
            </div>
            <button
              type="submit"
              disabled={subscribed}
              className={`px-5 py-2.5 rounded-lg font-semibold transition-all flex items-center justify-center gap-2 ${subscribed ? 'bg-green-600 text-white cursor-default' : 'bg-blue-600 hover:bg-blue-500 text-white shadow-lg hover:shadow-blue-500/25'}`}
            >
              {subscribed ? (
                <>
                  <CheckCircle2 size={18} /> Subscribed
                </>
              ) : (
                <>
                  Subscribe <ArrowRight size={18} />
                </>
              )}
            </button>
          </form>
        </div>

        {/* Middle Section: Links Grid */}
        <div className="py-16 grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-8 lg:gap-12">
          <div className="col-span-2 md:col-span-3 lg:col-span-2">
            <div className="flex items-center gap-2 mb-6">
              <div className="p-1.5 bg-gradient-to-br from-blue-600 to-indigo-600 rounded-lg shadow-inner">
                <Hexagon size={24} className="text-white fill-white/20" />
              </div>
              <span className="text-2xl font-bold text-white tracking-tight">Collabryta</span>
            </div>
            <p className="text-slate-400 text-sm leading-relaxed mb-6 max-w-sm">
              The all-in-one workspace that brings your team's tasks, docs, and conversations together. Designed for speed, built for scale.
            </p>
            <div className="flex gap-4">
              <SocialLink href="#" icon={<Twitter size={18} />} label="Twitter" />
              <SocialLink href="#" icon={<Github size={18} />} label="GitHub" />
              <SocialLink href="#" icon={<Linkedin size={18} />} label="LinkedIn" />
            </div>
          </div>

          <FooterColumn
            title="Platform"
            links={[
              { label: "Task Management", to: "/tasks" },
              { label: "Real-time Chat", to: "/chat" },
              { label: "File Storage", to: "/files" },
              { label: "Workflows", to: "/workflows" },
              { label: "Analytics", to: "/analytics" }
            ]}
          />

          <FooterColumn
            title="Company"
            links={[
              { label: "About Us", to: "/about" },
              { label: "Careers", to: "/careers" },
              { label: "Blog", to: "/blog" },
              { label: "Press Kit", to: "/press" },
              { label: "Contact", to: "/contact" }
            ]}
          />

          <FooterColumn
            title="Support"
            links={[
              { label: "Help Center", to: "/help" },
              { label: "Community", to: "/community" },
              { label: "API Status", to: "/status" },
              { label: "Documentation", to: "/docs" },
              { label: "Report a Bug", to: "/bugs" }
            ]}
          />
        </div>

        {/* Bottom Section */}
        <div className="py-8 border-t border-slate-800 flex flex-col md:flex-row justify-between items-center gap-6">
          <div className="flex flex-col md:flex-row items-center gap-4 text-sm text-slate-500">
            <span>&copy; {year} Collabryta Inc.</span>
            <div className="hidden md:block w-1 h-1 bg-slate-700 rounded-full"></div>
            <Link to="/privacy" className="hover:text-white transition-colors">Privacy Policy</Link>
            <div className="hidden md:block w-1 h-1 bg-slate-700 rounded-full"></div>
            <Link to="/terms" className="hover:text-white transition-colors">Terms of Service</Link>
          </div>

          <div className="flex items-center gap-6">
            <button className="flex items-center gap-2 text-sm text-slate-400 hover:text-white transition-colors">
              <Globe size={16} />
              <span>English (US)</span>
            </button>

            <div className="h-4 w-px bg-slate-700"></div>

            <div className="flex items-center gap-2 cursor-help group">
              <div className="relative flex h-2.5 w-2.5">
                <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-emerald-500 opacity-75"></span>
                <span className="relative inline-flex rounded-full h-2.5 w-2.5 bg-emerald-500"></span>
              </div>
              <span className="text-xs font-medium text-emerald-500 group-hover:text-emerald-400 transition-colors">Systems Normal</span>
            </div>
          </div>
        </div>
      </div>
    </footer>
  );
};

// Sub-components
const FooterColumn: React.FC<{ title: string; links: { label: string; to: string }[] }> = ({ title, links }) => (
  <div>
    <h3 className="font-semibold text-white text-sm tracking-wider uppercase mb-5">{title}</h3>
    <ul className="space-y-3">
      {links.map((link) => (
        <li key={link.label}>
          <Link
            to={link.to}
            className="text-slate-400 text-sm hover:text-blue-400 hover:pl-1 transition-all inline-block"
          >
            {link.label}
          </Link>
        </li>
      ))}
    </ul>
  </div>
);

const SocialLink: React.FC<{ href: string; icon: React.ReactNode; label: string }> = ({ href, icon, label }) => (
  <a
    href={href}
    className="w-10 h-10 flex items-center justify-center rounded-lg bg-slate-800 text-slate-400 hover:bg-blue-600 hover:text-white transition-all shadow-sm border border-slate-700 hover:border-blue-500 transform hover:-translate-y-1"
    aria-label={label}
  >
    {icon}
  </a>
);

export default Footer;
