import React, { useState } from "react";
import Sidebar from "../components/Sidebar";
import Topbar from "../components/Topbar";
import Footer from "../components/Footer";
import { Outlet, useLocation } from "react-router-dom";
import { motion } from "framer-motion";
import { userService } from "../services/userService";

const MainLayout: React.FC = () => {
  const [isSidebarOpen, setSidebarOpen] = useState(false);
  const location = useLocation();
  const isMessagesPage = location.pathname.startsWith("/messages");

  React.useEffect(() => {
    // Initial heartbeat
    userService.updateStatus("Online").catch(console.error);

    // Periodic heartbeat every 30 seconds
    const interval = setInterval(() => {
      userService.updateStatus("Online").catch(console.error);
    }, 30000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="flex min-h-screen bg-white font-sans text-slate-900">
      {/* Sidebar */}
      <Sidebar isOpen={isSidebarOpen} onClose={() => setSidebarOpen(false)} />

      {/* Main Content Area */}
      <div className="flex-1 flex flex-col lg:ml-64 transition-all duration-300 min-h-screen">
        <Topbar
          isSidebarOpen={isSidebarOpen}
          onMenuClick={() => setSidebarOpen((p) => !p)}
        />

        <main className={`flex-1 w-full ${isMessagesPage ? 'p-0' : 'max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8'}`}>
          <motion.div
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
            className={isMessagesPage ? "h-full" : ""}
          >
            <Outlet />
          </motion.div>
        </main>

        <Footer />
      </div>
    </div>
  );
};

export default MainLayout;
