import React, { useState, useEffect } from "react";
import {
  Plus,
  Search,
  Loader2,
  Trash2,
  Edit3,
  Calendar,
  User as UserIcon,
  Filter,
  AlertCircle,
  X,
  Clock,
  CheckCircle2,
  Zap,
  ArrowRight,
  Shield,
  Layers
} from "lucide-react";
import { useNavigate } from "react-router-dom";
import { taskService, Task } from "../../services/taskService";
import { motion, AnimatePresence } from "framer-motion";
import { webSocketService, WebSocketMessage } from "../../services/websocketService";

const TasksPage: React.FC = () => {
  const navigate = useNavigate();
  const [tasks, setTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState<string>("All");
  const [selectedTask, setSelectedTask] = useState<Task | null>(null);

  useEffect(() => {
    fetchTasks();

    const unsubscribe = webSocketService.subscribe((message: WebSocketMessage) => {
      if (message.type === 'task') {
        fetchTasks(false);
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);

  const fetchTasks = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const data = await taskService.getTasks();
      setTasks(data);
    } catch (error) {
      console.error("Failed to fetch tasks", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  const normalizeStatus = (status: string) => {
    const s = status ? status.toLowerCase() : "";
    if (s === "todo" || s === "upcoming") return "Upcoming";
    if (s === "in_progress" || s === "in progress" || s === "inprogress") return "In Progress";
    if (s === "completed" || s === "done") return "Completed";
    return "Upcoming";
  };

  const getTimeStatus = (dueDate: string | null | undefined, status: string) => {
    const normStatus = normalizeStatus(status);
    if (normStatus === "Completed") return { label: "Success", color: "text-emerald-500 bg-emerald-500/10 border-emerald-500/20 shadow-emerald-500/10" };
    if (!dueDate) return { label: "Drafted", color: "text-slate-400 bg-slate-100 border-slate-200" };

    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const due = new Date(dueDate);
    due.setHours(0, 0, 0, 0);

    if (due < today) return { label: "Breached", color: "text-rose-500 bg-rose-500/10 border-rose-500/20 shadow-rose-500/10" };
    if (due.getTime() === today.getTime()) return { label: "Priority", color: "text-orange-500 bg-orange-500/10 border-orange-500/20 shadow-orange-500/10" };

    return { label: "On Track", color: "text-indigo-500 bg-indigo-500/10 border-indigo-500/20 shadow-indigo-500/10" };
  };

  const handleDelete = async (e: React.MouseEvent, id: number) => {
    e.stopPropagation();
    if (!window.confirm("Archive this task?")) return;
    try {
      await taskService.deleteTask(id);
      setTasks(prev => prev.filter(t => t.id !== id));
      if (selectedTask?.id === id) setSelectedTask(null);
    } catch (error) {
      console.error("Failed to delete task");
    }
  };

  const filteredTasks = tasks.filter(task => {
    const matchesSearch = task.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (task.description && task.description.toLowerCase().includes(searchQuery.toLowerCase()));
    const matchesStatus = statusFilter === "All" || normalizeStatus(task.status) === statusFilter;
    return matchesSearch && matchesStatus;
  });

  return (
    <div className="min-h-screen bg-[#f8fafc] relative font-sans overflow-hidden">

      {/* Background Orbs */}
      <div className="absolute top-0 right-0 w-[500px] h-[500px] bg-indigo-100/30 blur-[100px] -z-10 animate-pulse" />
      <div className="absolute bottom-0 left-0 w-[500px] h-[500px] bg-sky-100/30 blur-[100px] -z-10" />

      {/* ðŸ“‹ Main Content Area */}
      <div className={`p-6 lg:p-12 max-w-[1400px] mx-auto space-y-12 transition-all duration-500 ${selectedTask ? 'lg:mr-[450px]' : ''}`}>

        {/* Hero Section */}
        <div className="flex flex-col lg:flex-row lg:items-end justify-between gap-8">
          <div className="space-y-4">
            <div className="flex items-center gap-2 px-4 py-1.5 bg-indigo-50 text-indigo-700 rounded-full text-[10px] font-[900] uppercase tracking-[0.2em] border border-indigo-100 w-fit">
              <Shield size={14} className="fill-indigo-600" /> Operational Matrix
            </div>
            <h1 className="text-5xl font-[1000] text-slate-900 tracking-tight leading-none">Task <span className="gradient-text">Console</span></h1>
            <p className="text-slate-500 font-medium text-lg max-w-xl leading-relaxed">
              Orchestrate your workflow with precision. Manage mission-critical tasks and track velocity in real-time.
            </p>
          </div>
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => navigate("/tasks/new")}
            className="flex items-center gap-3 bg-slate-900 text-white px-8 py-5 rounded-[24px] font-black uppercase tracking-widest text-xs shadow-2xl shadow-slate-900/20 group transition-all"
          >
            <Plus size={20} className="group-hover:rotate-90 transition-transform" />
            Initialize Task
          </motion.button>
        </div>

        {/* Sophisticated Toolbar */}
        <div className="bg-white/80 backdrop-blur-xl p-4 rounded-[32px] border border-slate-100 shadow-xl shadow-slate-200/40 flex flex-col md:flex-row items-center gap-4">
          <div className="relative flex-1 w-full group">
            <Search className="absolute left-5 top-1/2 -translate-y-1/2 text-slate-400 group-focus-within:text-indigo-600 transition-colors" size={20} />
            <input
              type="text"
              placeholder="Query task titles, descriptions, or assignees..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-14 pr-6 py-4 bg-slate-50/50 border-2 border-transparent rounded-[24px] text-sm font-bold text-slate-900 focus:bg-white focus:border-indigo-600/20 focus:outline-none transition-all placeholder:text-slate-400"
            />
          </div>
          <div className="flex items-center gap-3 w-full md:w-auto">
            <div className="h-10 w-[2px] bg-slate-100 hidden md:block mx-2" />
            <Filter size={18} className="text-slate-400 ml-2" />
            <select
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
              className="flex-1 md:flex-none bg-slate-50/50 border-2 border-transparent rounded-[20px] px-6 py-4 text-xs font-black uppercase tracking-widest text-slate-700 outline-none cursor-pointer hover:bg-slate-100 transition-all"
            >
              <option value="All">All Operations</option>
              <option value="Upcoming">Upcoming</option>
              <option value="In Progress">In Progress</option>
              <option value="Completed">Completed</option>
            </select>
          </div>
        </div>

        {/* Task Grid Rendering */}
        <div className="grid grid-cols-1 gap-4">
          {loading ? (
            <div className="py-32 flex flex-col items-center justify-center gap-6">
              <div className="w-16 h-16 bg-indigo-600 rounded-3xl flex items-center justify-center animate-bounce shadow-xl shadow-indigo-200">
                <Zap className="text-white fill-white" size={32} />
              </div>
              <p className="text-[10px] font-[1000] uppercase tracking-[0.4em] text-slate-400">Syncing Intelligence</p>
            </div>
          ) : (
            <AnimatePresence mode="popLayout">
              {filteredTasks.length === 0 ? (
                <motion.div
                  initial={{ opacity: 0, scale: 0.95 }}
                  animate={{ opacity: 1, scale: 1 }}
                  className="py-24 bg-white/50 backdrop-blur-md rounded-[40px] border-2 border-slate-100 border-dashed text-center flex flex-col items-center group"
                >
                  <div className="w-20 h-20 bg-slate-100 rounded-[30px] flex items-center justify-center mb-6 group-hover:scale-110 transition-transform duration-500">
                    <Layers size={32} className="text-slate-300" />
                  </div>
                  <h3 className="text-2xl font-[1000] text-slate-900 tracking-tight">Zero Activity Detected</h3>
                  <p className="text-slate-400 font-bold uppercase tracking-widest text-[10px] mt-2">Adjust your parameters or initialize a new sequence.</p>
                </motion.div>
              ) : (
                <div className="grid grid-cols-1 gap-6">
                  {filteredTasks.map((task, idx) => {
                    const timeStatus = getTimeStatus(task.due_date, task.status);
                    const isSelected = selectedTask?.id === task.id;
                    return (
                      <motion.div
                        key={task.id}
                        layout
                        initial={{ opacity: 0, y: 30 }}
                        animate={{ opacity: 1, y: 0 }}
                        exit={{ opacity: 0, scale: 0.95 }}
                        transition={{ delay: idx * 0.04, type: "spring", damping: 20 }}
                        onClick={() => setSelectedTask(task)}
                        className={`group relative p-8 rounded-[32px] border-2 transition-all duration-500 cursor-pointer overflow-hidden ${isSelected ? 'bg-white border-indigo-600 shadow-2xl shadow-indigo-500/10' : 'bg-white/80 border-slate-50 hover:border-indigo-100 hover:shadow-2xl hover:shadow-slate-200/50'
                          }`}
                      >
                        {/* Interactive Shine Effect */}
                        <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/40 to-transparent -translate-x-full group-hover:translate-x-full transition-transform duration-1000 ease-in-out pointer-events-none" />

                        <div className="flex flex-col md:flex-row items-center gap-10">
                          {/* Label Section */}
                          <div className="w-full md:w-40 flex-shrink-0">
                            <span className={`px-4 py-2 rounded-2xl text-[10px] font-black uppercase tracking-[0.2em] border shadow-sm block text-center transition-all ${timeStatus.color}`}>
                              {timeStatus.label}
                            </span>
                          </div>

                          {/* Core Content */}
                          <div className="flex-1 min-w-0 w-full space-y-2">
                            <div className="flex items-center gap-3">
                              <h3 className={`text-xl font-[1000] tracking-tight transition-colors leading-none ${isSelected ? 'text-indigo-600' : 'text-slate-900 group-hover:text-indigo-600'}`}>
                                {task.title}
                              </h3>
                              {task.priority === 'High' && (
                                <div className="w-2 h-2 bg-rose-500 rounded-full animate-ping" />
                              )}
                            </div>
                            <p className="text-slate-500 font-medium text-sm line-clamp-1 leading-relaxed max-w-2xl">
                              {task.description || "No tactical briefing provided for this operational unit."}
                            </p>
                          </div>

                          {/* Metadata/Assignee */}
                          <div className="flex items-center gap-8 w-full md:w-auto shrink-0 justify-between md:justify-end border-t md:border-t-0 pt-6 md:pt-0 border-slate-50">
                            <div className="flex items-center gap-3 bg-slate-50 px-4 py-2 rounded-2xl border border-slate-100">
                              <div className="w-8 h-8 rounded-xl bg-gradient-to-tr from-indigo-600 to-violet-600 flex items-center justify-center text-white font-black text-xs shadow-lg">
                                {task.assigned_to_user ? task.assigned_to_user.name.charAt(0).toUpperCase() : <UserIcon size={14} />}
                              </div>
                              <div className="text-left">
                                <p className="text-[10px] font-black text-slate-400 capitalize">Commander</p>
                                <p className="text-xs font-black text-slate-800 uppercase tracking-tighter truncate w-24">
                                  {task.assigned_to_user?.name.split(' ')[0] || "Unbound"}
                                </p>
                              </div>
                            </div>

                            <div className="flex items-center gap-2 px-4 py-2 bg-indigo-50/30 rounded-2xl text-indigo-600 border border-indigo-100/50 group-hover:bg-indigo-600 group-hover:text-white transition-all duration-500">
                              <Calendar size={16} />
                              <span className="text-[10px] font-black tracking-widest">{task.due_date ? new Date(task.due_date).toLocaleDateString(undefined, { month: 'short', day: 'numeric' }).toUpperCase() : "N/A"}</span>
                            </div>

                            {/* Actions Overlay */}
                            <div className="flex items-center gap-2 opacity-0 group-hover:opacity-100 translate-x-4 group-hover:translate-x-0 transition-all duration-500">
                              <button
                                onClick={(e) => { e.stopPropagation(); navigate(`/tasks/edit/${task.id}`); }}
                                className="p-3 bg-slate-900 text-white rounded-2xl hover:scale-110 active:scale-95 transition-all shadow-xl shadow-slate-900/10"
                              >
                                <Edit3 size={16} />
                              </button>
                              <button
                                onClick={(e) => handleDelete(e, task.id)}
                                className="p-3 bg-rose-500 text-white rounded-2xl hover:scale-110 active:scale-95 transition-all shadow-xl shadow-rose-900/10"
                              >
                                <Trash2 size={16} />
                              </button>
                            </div>
                          </div>
                        </div>
                      </motion.div>
                    );
                  })}
                </div>
              )}
            </AnimatePresence>
          )}
        </div>
      </div>

      {/* ðŸ“‘ Fixed High-End Slide-Over Panel */}
      <AnimatePresence>
        {selectedTask && (
          <motion.div
            initial={{ x: "100%" }}
            animate={{ x: 0 }}
            exit={{ x: "100%" }}
            transition={{ type: "spring", damping: 30, stiffness: 200 }}
            className="fixed top-0 right-0 w-full lg:w-[450px] h-screen bg-white border-l border-slate-100 shadow-[0_0_100px_rgba(0,0,0,0.1)] z-50 flex flex-col p-8"
          >
            {/* Background Texture for Panel */}
            <div className="absolute top-0 left-0 w-full h-[300px] bg-gradient-to-b from-slate-50 to-white -z-10 opacity-50" />

            {/* Header */}
            <div className="flex items-center justify-between mb-12">
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 bg-slate-900 rounded-xl flex items-center justify-center text-white">
                  <Shield size={20} />
                </div>
                <div>
                  <h2 className="text-lg font-[1000] text-slate-900 tracking-tight leading-none uppercase">Unit Analytics</h2>
                  <p className="text-[10px] font-black text-slate-400 uppercase tracking-widest mt-1">Ref: {selectedTask.id}</p>
                </div>
              </div>
              <button
                onClick={() => setSelectedTask(null)}
                className="p-3 text-slate-400 hover:text-slate-900 hover:bg-slate-100 rounded-2xl transition-all"
              >
                <X size={24} />
              </button>
            </div>

            {/* Content Scroll Area */}
            <div className="flex-1 overflow-y-auto custom-scrollbar pr-2 space-y-12">

              {/* Identity Block */}
              <div className="space-y-4">
                <span className={`inline-block px-5 py-2 rounded-full text-[10px] font-[1000] uppercase tracking-[0.2em] border shadow-lg ${getTimeStatus(selectedTask.due_date, selectedTask.status).color}`}>
                  {getTimeStatus(selectedTask.due_date, selectedTask.status).label} Sequence
                </span>
                <h2 className="text-4xl font-[1000] text-slate-900 leading-[1.1] tracking-tight">{selectedTask.title}</h2>
              </div>

              {/* Dynamic Metric Grid */}
              <div className="grid grid-cols-2 gap-4">
                <div className="p-6 bg-slate-900 rounded-[32px] text-white space-y-2 group overflow-hidden relative">
                  <Calendar className="text-indigo-400 mb-2 opacity-50 group-hover:scale-125 transition-transform" size={20} />
                  <p className="text-[10px] font-black text-white/40 uppercase tracking-widest">Temporal Deadline</p>
                  <p className="text-lg font-black">{selectedTask.due_date ? new Date(selectedTask.due_date).toLocaleDateString(undefined, { month: 'long', day: 'numeric', year: 'numeric' }) : "Indefinite"}</p>
                </div>
                <div className="p-6 bg-indigo-600 rounded-[32px] text-white space-y-2 group overflow-hidden relative">
                  <Clock className="text-white/50 mb-2 group-hover:rotate-12 transition-transform" size={20} />
                  <p className="text-[10px] font-black text-white/40 uppercase tracking-widest">Operational Status</p>
                  <p className="text-lg font-black">{normalizeStatus(selectedTask.status).toUpperCase()}</p>
                </div>
              </div>

              {/* Intelligence Log */}
              <div className="space-y-4">
                <div className="flex items-center gap-4 text-[10px] font-black uppercase tracking-[0.5em] text-slate-300">
                  Tactical briefing
                  <div className="flex-1 h-[2px] bg-slate-100" />
                </div>
                <div className="text-lg text-slate-600 leading-relaxed font-bold font-sans selection:bg-indigo-100">
                  {selectedTask.description || "Intelligence missing. No briefing data found for this unit."}
                </div>
              </div>

              {/* Personnel Block */}
              <div className="p-8 bg-slate-50 rounded-[40px] border border-slate-100 flex items-center justify-between group cursor-pointer hover:bg-white hover:border-indigo-100 transition-all">
                <div className="flex items-center gap-5">
                  <div className="w-14 h-14 rounded-2xl bg-gradient-to-tr from-indigo-600 to-violet-700 flex items-center justify-center text-white font-black text-xl shadow-xl shadow-indigo-200">
                    {selectedTask.assigned_to_user?.name.charAt(0) || "U"}
                  </div>
                  <div>
                    <p className="text-[10px] font-black text-slate-400 uppercase tracking-widest leading-none mb-1">Assigned Executive</p>
                    <p className="text-xl font-black text-slate-900 tracking-tighter">{selectedTask.assigned_to_user?.name || "System Automated"}</p>
                  </div>
                </div>
                <div className="w-10 h-10 rounded-xl bg-white flex items-center justify-center text-slate-400 group-hover:text-indigo-600 group-hover:bg-indigo-50 transition-all">
                  <ArrowRight size={20} />
                </div>
              </div>
            </div>

            {/* Action Matrix */}
            <div className="pt-8 mt-auto border-t border-slate-100 grid grid-cols-2 gap-4">
              <button
                onClick={() => navigate(`/tasks/edit/${selectedTask.id}`)}
                className="py-5 bg-slate-900 text-white font-black text-[10px] uppercase tracking-widest rounded-3xl hover:bg-indigo-600 hover:shadow-2xl hover:shadow-indigo-600/20 transition-all duration-300 active:scale-95"
              >
                Modify Sequence
              </button>
              <button
                onClick={(e) => handleDelete(e, selectedTask.id)}
                className="py-5 bg-rose-50 text-rose-600 border-2 border-transparent hover:border-rose-200 font-black text-[10px] uppercase tracking-widest rounded-3xl hover:bg-rose-100 transition-all duration-300 active:scale-95"
              >
                Zero Protocol
              </button>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default TasksPage;
