import React, { useState, useEffect, useRef, useMemo } from "react";
import {
    Search,
    Phone,
    Video,
    Info,
    MoreVertical,
    Paperclip,
    Smile,
    Mic,
    Send,
    Check,
    CheckCheck,
    Play,
    Pause,
    ArrowLeft
} from "lucide-react";

// --- Types ---
interface Contact {
    id: number;
    name: string;
    avatar?: string;
    initials: string;
    status: "online" | "offline" | "busy" | "away";
    lastMessage: string;
    time: string;
    unread: number;
    color: string;
    type: "personal" | "group";
}

interface Message {
    id: number;
    senderId: number | "me";
    text: string;
    time: string;
    status: "sent" | "delivered" | "read";
    audioUrl?: string;
    duration?: number;
}

// --- Mock Data ---
const CONTACTS: Contact[] = [
    { id: 1, name: "Team Collabryta", initials: "TC", color: "bg-indigo-500", type: "group", status: "online", lastMessage: "Let's sync up later today.", time: "10:30 AM", unread: 2 },
    { id: 2, name: "Alice Johnson", initials: "AJ", color: "bg-pink-500", type: "personal", status: "online", lastMessage: "Can you review the PR?", time: "09:15 AM", unread: 0 },
    { id: 3, name: "Bob Reynolds", initials: "BR", color: "bg-teal-500", type: "personal", status: "offline", lastMessage: "Thanks for the update!", time: "Yesterday", unread: 0 },
    { id: 4, name: "Project Alpha", initials: "PA", color: "bg-blue-600", type: "group", status: "busy", lastMessage: "Deadline pushed to Friday", time: "Mon", unread: 5 },
    { id: 5, name: "Sophia Lee", initials: "SL", color: "bg-purple-500", type: "personal", status: "away", lastMessage: "I'll get back to you soon.", time: "Sun", unread: 0 },
];

const MOCK_MESSAGES: Record<number, Message[]> = {
    1: [
        { id: 1, senderId: 2, text: "Hey team, are we on track for the release?", time: "10:00 AM", status: "read" },
        { id: 2, senderId: "me", text: "Yes, frontend is mostly done. Just need to fix some styling.", time: "10:05 AM", status: "read" },
        { id: 3, senderId: 1, text: "Great! Let's do a quick sync at 2 PM.", time: "10:15 AM", status: "read" },
        { id: 4, senderId: "me", text: "Sounds good.", time: "10:30 AM", status: "read" },
    ],
    2: [
        { id: 101, senderId: 2, text: "Hey! Did you check the latest design updates?", time: "09:00 AM", status: "read" },
        { id: 102, senderId: "me", text: "Not yet, sending it over now.", time: "09:05 AM", status: "read" },
        { id: 103, senderId: 2, text: "Can you review the PR?", time: "09:15 AM", status: "read" },
    ]
};

// --- Components ---

const MessageBubble: React.FC<{ message: Message; isMe: boolean }> = ({ message, isMe }) => {
    const [isPlaying, setIsPlaying] = useState(false);
    const audioRef = useRef<HTMLAudioElement>(null);

    const toggleAudio = () => {
        if (!audioRef.current) return;
        if (isPlaying) {
            audioRef.current.pause();
        } else {
            audioRef.current.play();
        }
    };

    useEffect(() => {
        const audio = audioRef.current;
        if (!audio) return;
        const handledEnded = () => setIsPlaying(false);
        audio.addEventListener('ended', handledEnded);
        return () => audio.removeEventListener('ended', handledEnded);
    }, []);

    return (
        <div className={`flex w-full mb-4 ${isMe ? 'justify-end' : 'justify-start'}`}>
            <div className={`flex flex-col max-w-[70%] ${isMe ? 'items-end' : 'items-start'}`}>
                <div
                    className={`px-4 py-3 rounded-2xl shadow-sm text-sm ${isMe
                            ? 'bg-blue-600 text-white rounded-tr-sm'
                            : 'bg-white border border-gray-100 text-slate-800 rounded-tl-sm'
                        }`}
                >
                    {message.audioUrl ? (
                        <div className="flex items-center gap-3 min-w-[200px]">
                            <button onClick={toggleAudio} className={`p-2 rounded-full ${isMe ? 'bg-white/20' : 'bg-gray-100 hover:bg-gray-200'} transition`}>
                                {isPlaying ? <Pause size={16} /> : <Play size={16} />}
                            </button>
                            <div className="flex-1 h-1 bg-current opacity-30 rounded-full overflow-hidden">
                                <div className="h-full bg-current w-1/2" /> {/* Mock progress */}
                            </div>
                            <span className="text-xs opacity-80">{message.duration}s</span>
                            <audio ref={audioRef} src={message.audioUrl} onPlay={() => setIsPlaying(true)} onPause={() => setIsPlaying(false)} />
                        </div>
                    ) : (
                        <p className="leading-relaxed">{message.text}</p>
                    )}
                </div>
                <div className="flex items-center gap-1 mt-1 px-1">
                    <span className="text-[10px] text-gray-400 font-medium">{message.time}</span>
                    {isMe && (
                        <span className="text-blue-600">
                            {message.status === 'read' ? <CheckCheck size={12} /> : <Check size={12} />}
                        </span>
                    )}
                </div>
            </div>
        </div>
    );
};

const MessagesPage: React.FC = () => {
    const [activeContactId, setActiveContactId] = useState<number | null>(null);
    const [messages, setMessages] = useState(MOCK_MESSAGES);
    const [inputText, setInputText] = useState("");
    const [searchQuery, setSearchQuery] = useState("");
    const messagesEndRef = useRef<HTMLDivElement>(null);
    const [isMobileChatOpen, setIsMobileChatOpen] = useState(false);

    // Recording states
    const [isRecording, setIsRecording] = useState(false);
    const [recTime, setRecTime] = useState(0);
    const mediaRecorderRef = useRef<MediaRecorder | null>(null);
    const audioChunks = useRef<Blob[]>([]);

    const activeContact = CONTACTS.find(c => c.id === activeContactId);
    const currentMessages = activeContactId ? (messages[activeContactId] || []) : [];

    const filteredContacts = CONTACTS.filter(c =>
        c.name.toLowerCase().includes(searchQuery.toLowerCase())
    );

    // Scroll to bottom
    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [currentMessages, activeContactId]);

    // Handle send text
    const handleSendMessage = () => {
        if (!inputText.trim() || !activeContactId) return;

        const newMessage: Message = {
            id: Date.now(),
            senderId: "me",
            text: inputText,
            time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
            status: "sent"
        };

        setMessages(prev => ({
            ...prev,
            [activeContactId]: [...(prev[activeContactId] || []), newMessage]
        }));
        setInputText("");
    };

    const handleKeyDown = (e: React.KeyboardEvent) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            handleSendMessage();
        }
    };

    // Recording logic
    useEffect(() => {
        let interval: NodeJS.Timeout;
        if (isRecording) {
            interval = setInterval(() => setRecTime(t => t + 1), 1000);
        }
        return () => clearInterval(interval);
    }, [isRecording]);

    const startRecording = async () => {
        try {
            const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
            const recorder = new MediaRecorder(stream);
            mediaRecorderRef.current = recorder;
            audioChunks.current = [];

            recorder.ondataavailable = (e) => audioChunks.current.push(e.data);
            recorder.onstop = () => {
                if (!activeContactId) return;
                const blob = new Blob(audioChunks.current, { type: 'audio/webm' });
                const url = URL.createObjectURL(blob);
                const newMessage: Message = {
                    id: Date.now(),
                    senderId: "me",
                    text: "",
                    audioUrl: url,
                    duration: recTime,
                    time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
                    status: "sent"
                };
                setMessages(prev => ({
                    ...prev,
                    [activeContactId]: [...(prev[activeContactId] || []), newMessage]
                }));
                stream.getTracks().forEach(track => track.stop());
                setRecTime(0);
            };

            recorder.start();
            setIsRecording(true);
        } catch (err) {
            console.error("Mic error", err);
            alert("Could not access microphone.");
        }
    };

    const stopRecording = () => {
        if (mediaRecorderRef.current && isRecording) {
            mediaRecorderRef.current.stop();
            setIsRecording(false);
        }
    };


    return (
        <div className="flex h-[calc(100vh-64px)] bg-white overflow-hidden border-t border-gray-100">

            {/* --- Sidebar (Contact List) --- */}
            <div className={`w-full md:w-80 lg:w-96 border-r border-gray-100 flex flex-col bg-white ${isMobileChatOpen ? 'hidden md:flex' : 'flex'}`}>

                {/* Header */}
                <div className="p-4 border-b border-white">
                    <div className="flex justify-between items-center mb-4">
                        <h2 className="text-xl font-bold text-slate-800">Messages</h2>
                        <button className="p-2 text-slate-400 hover:text-blue-600 hover:bg-blue-50 rounded-full transition">
                            <MoreVertical size={20} />
                        </button>
                    </div>
                    {/* Search */}
                    <div className="relative">
                        <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={18} />
                        <input
                            type="text"
                            placeholder="Search chats..."
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                            className="w-full pl-10 pr-4 py-2 bg-slate-50 border-none rounded-xl text-sm focus:ring-2 focus:ring-blue-100 transition-all placeholder:text-slate-400"
                        />
                    </div>
                </div>

                {/* List */}
                <div className="flex-1 overflow-y-auto p-3 space-y-2">
                    {filteredContacts.map(contact => (
                        <div
                            key={contact.id}
                            onClick={() => {
                                setActiveContactId(contact.id);
                                setIsMobileChatOpen(true);
                            }}
                            className={`p-3 rounded-xl flex items-center gap-3 cursor-pointer transition-all ${activeContactId === contact.id
                                    ? 'bg-blue-50/80 border border-blue-100/50'
                                    : 'hover:bg-gray-50 border border-transparent'
                                }`}
                        >
                            <div className="relative">
                                <div className={`w-12 h-12 rounded-full flex items-center justify-center text-white font-semibold text-sm shadow-sm ${contact.color}`}>
                                    {contact.initials}
                                </div>
                                {contact.status === 'online' && (
                                    <div className="absolute bottom-0 right-0 w-3.5 h-3.5 bg-green-500 border-2 border-white rounded-full"></div>
                                )}
                            </div>
                            <div className="flex-1 min-w-0">
                                <div className="flex justify-between items-baseline mb-0.5">
                                    <h3 className={`text-sm font-semibold truncate ${activeContactId === contact.id ? 'text-blue-900' : 'text-slate-700'}`}>
                                        {contact.name}
                                    </h3>
                                    <span className={`text-[11px] font-medium ${activeContactId === contact.id ? 'text-blue-400' : 'text-slate-400'}`}>
                                        {contact.time}
                                    </span>
                                </div>
                                <div className="flex justify-between items-center">
                                    <p className={`text-xs truncate max-w-[85%] ${activeContactId === contact.id ? 'text-blue-700/80' : 'text-slate-500'}`}>
                                        {contact.lastMessage}
                                    </p>
                                    {contact.unread > 0 && (
                                        <span className="bg-blue-600 text-white text-[10px] font-bold px-1.5 py-0.5 rounded-full min-w-[1.25rem] text-center">
                                            {contact.unread}
                                        </span>
                                    )}
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>

            {/* --- Main Chat Area --- */}
            {activeContact ? (
                <div className={`flex-1 flex flex-col bg-slate-50/30 ${isMobileChatOpen ? 'flex' : 'hidden md:flex'}`}>

                    {/* Chat Header */}
                    <div className="h-20 bg-white border-b border-gray-100 px-6 flex items-center justify-between shadow-sm z-10">
                        <div className="flex items-center gap-4">
                            <button
                                onClick={() => setIsMobileChatOpen(false)}
                                className="md:hidden p-2 -ml-2 text-slate-500 hover:bg-gray-100 rounded-full"
                            >
                                <ArrowLeft size={20} />
                            </button>
                            <div className={`w-10 h-10 rounded-full flex items-center justify-center text-white font-bold text-sm shadow-sm ${activeContact.color}`}>
                                {activeContact.initials}
                            </div>
                            <div>
                                <h2 className="font-bold text-slate-800 text-base">{activeContact.name}</h2>
                                <div className="flex items-center gap-2">
                                    <span className={`w-2 h-2 rounded-full ${activeContact.status === 'online' ? 'bg-green-500' : 'bg-gray-300'}`}></span>
                                    <p className="text-xs text-slate-500 font-medium capitalize">{activeContact.status}</p>
                                </div>
                            </div>
                        </div>

                        <div className="flex items-center gap-1 text-slate-400">
                            <button className="p-2.5 hover:bg-gray-50 hover:text-blue-600 rounded-full transition"><Phone size={20} /></button>
                            <button className="p-2.5 hover:bg-gray-50 hover:text-blue-600 rounded-full transition"><Video size={20} /></button>
                            <div className="w-px h-6 bg-gray-200 mx-2"></div>
                            <button className="p-2.5 hover:bg-gray-50 hover:text-blue-600 rounded-full transition"><Info size={20} /></button>
                        </div>
                    </div>

                    {/* Messages List */}
                    <div className="flex-1 overflow-y-auto p-6 bg-slate-50/50">
                        {currentMessages.length === 0 ? (
                            <div className="h-full flex flex-col items-center justify-center text-slate-400 opacity-60">
                                <div className="w-20 h-20 bg-slate-100 rounded-full flex items-center justify-center mb-4">
                                    <Search size={32} />
                                </div>
                                <p>No messages yet. Say hello! </p>
                            </div>
                        ) : (
                            currentMessages.map(msg => (
                                <MessageBubble key={msg.id} message={msg} isMe={msg.senderId === "me"} />
                            ))
                        )}
                        <div ref={messagesEndRef} />
                    </div>

                    {/* Input Area */}
                    <div className="p-4 bg-white border-t border-gray-100">
                        <div className={`flex items-center gap-2 bg-gray-50 border border-gray-200 rounded-2xl p-2 px-3 transition-all ${isRecording ? 'border-red-200 bg-red-50' : 'focus-within:ring-2 focus-within:ring-blue-100 focus-within:border-blue-300'}`}>

                            {isRecording ? (
                                <div className="flex-1 flex items-center gap-3 px-2">
                                    <div className="w-3 h-3 bg-red-500 rounded-full animate-pulse"></div>
                                    <span className="text-red-500 font-medium text-sm">Recording {recTime}s...</span>
                                </div>
                            ) : (
                                <>
                                    <button className="p-2 text-slate-400 hover:text-blue-600 transition"><Paperclip size={20} /></button>
                                    <input
                                        type="text"
                                        value={inputText}
                                        onChange={(e) => setInputText(e.target.value)}
                                        onKeyDown={handleKeyDown}
                                        placeholder="Type your message..."
                                        className="flex-1 bg-transparent border-none focus:ring-0 text-slate-700 placeholder:text-slate-400 text-sm"
                                    />
                                    <button className="p-2 text-slate-400 hover:text-blue-600 transition"><Smile size={20} /></button>
                                </>
                            )}

                            {inputText.trim() ? (
                                <button
                                    onClick={handleSendMessage}
                                    className="p-2.5 bg-blue-600 text-white rounded-xl hover:bg-blue-700 shadow-md transition-all hover:scale-105 active:scale-95"
                                >
                                    <Send size={18} fill="currentColor" />
                                </button>
                            ) : (
                                <button
                                    onClick={isRecording ? stopRecording : startRecording}
                                    className={`p-2.5 rounded-xl transition-all shadow-sm ${isRecording
                                            ? 'bg-red-500 text-white hover:bg-red-600 animate-pulse'
                                            : 'bg-slate-200 text-slate-600 hover:bg-blue-600 hover:text-white'
                                        }`}
                                >
                                    {isRecording ? <div className="w-4 h-4 bg-white rounded-sm" /> : <Mic size={20} />}
                                </button>
                            )}
                        </div>
                        <p className="text-center text-[10px] text-gray-300 mt-2">Press Enter to send</p>
                    </div>

                </div>
            ) : (
                /* Empty State */
                <div className="hidden md:flex flex-1 flex-col items-center justify-center bg-slate-50/30 text-slate-400">
                    <div className="w-24 h-24 bg-blue-50 rounded-full flex items-center justify-center mb-6">
                        <div className="w-12 h-12 bg-blue-100 rounded-full animate-ping absolute opacity-20"></div>
                        <Search size={40} className="text-blue-300 relative z-10" />
                    </div>
                    <h3 className="text-lg font-semibold text-slate-600 mb-2">Select a Conversation</h3>
                    <p className="max-w-xs text-center text-sm">Choose a contact from the sidebar to start messaging or collaborating.</p>
                </div>
            )}

        </div>
    );
};

export default MessagesPage;
