import React from "react";
import { Bell, Menu, X, Search, Settings, Grid2X2 } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";

interface TopbarProps {
  onMenuClick: () => void;
  isSidebarOpen: boolean;
}

const Topbar: React.FC<TopbarProps> = ({ onMenuClick, isSidebarOpen }) => {
  const navigate = useNavigate();

  return (
    <header className="sticky top-0 z-30 w-full backdrop-blur-xl bg-white/40 border-b border-white/60 px-4 sm:px-8 py-3.5 flex justify-between items-center transition-all duration-300 shadow-sm">
      <div className="flex items-center gap-4 flex-1">
        <motion.button
          whileTap={{ scale: 0.95 }}
          onClick={onMenuClick}
          className="lg:hidden p-2.5 bg-white/60 hover:bg-white text-slate-600 rounded-2xl transition-all border border-white/80 shadow-sm"
        >
          {isSidebarOpen ? <X size={20} /> : <Menu size={20} />}
        </motion.button>

        {/* ðŸ§ª Glass Search */}
        <div className="hidden md:flex items-center bg-white/30 border border-white/80 focus-within:border-blue-500/40 focus-within:bg-white/60 px-4 py-2 rounded-[1.25rem] w-80 transition-all shadow-inner shadow-slate-100">
          <Search size={16} className="text-slate-400 mr-2" />
          <input
            type="text"
            placeholder="Search resources..."
            className="bg-transparent border-none outline-none text-sm w-full text-slate-700 placeholder:text-slate-500 font-medium"
          />
        </div>
      </div>

      <div className="flex items-center gap-3 sm:gap-6">
        <div className="hidden lg:flex items-center gap-2 bg-blue-500/10 px-3 py-1.5 rounded-xl border border-blue-500/20">
          <Grid2X2 size={14} className="text-blue-600" />
          <span className="text-[11px] font-black text-blue-700 uppercase tracking-tighter">Workspace V.4</span>
        </div>

        <div className="flex items-center gap-2">
          <motion.button
            whileHover={{ scale: 1.1, rotate: -5 }}
            whileTap={{ scale: 0.9 }}
            className="p-2.5 bg-white/60 backdrop-blur-md text-slate-500 hover:text-blue-600 rounded-2xl transition-all relative border border-white shadow-sm"
          >
            <Bell size={20} />
            <span className="absolute top-2.5 right-2.5 w-2 h-2 bg-blue-500 rounded-full border-2 border-white" />
          </motion.button>

          <motion.button
            whileHover={{ scale: 1.1, rotate: 5 }}
            whileTap={{ scale: 0.9 }}
            className="p-2.5 bg-white/60 backdrop-blur-md text-slate-500 hover:text-blue-600 rounded-2xl transition-all border border-white shadow-sm"
          >
            <Settings size={20} />
          </motion.button>
        </div>

        <div className="h-8 w-[1px] bg-slate-200/60 mx-1 hidden sm:block" />

        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={() => navigate("/profile")}
          className="flex items-center gap-3 pl-2 group"
        >
          <div className="hidden sm:block text-right">
            <p className="text-sm font-black text-slate-900 leading-none">Ashok Sharma</p>
            <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest mt-0.5">Administrator</p>
          </div>
          <div className="w-11 h-11 rounded-2xl bg-gradient-to-tr from-blue-600 via-indigo-500 to-sky-400 p-[2px] shadow-lg shadow-blue-200 transition-transform group-hover:scale-110">
            <div className="w-full h-full bg-white rounded-[0.9rem] flex items-center justify-center text-blue-600 font-black text-lg">
              A
            </div>
          </div>
        </motion.button>
      </div>
    </header>
  );
};

export default Topbar;
