import React from "react";
import { useNavigate } from "react-router-dom";
import { ArrowLeft, CheckCircle2, RefreshCw, Clock } from "lucide-react";

const StatusUpdatesPage: React.FC = () => {
  const navigate = useNavigate();

  const updates = [
    { id: 1, title: "Design Homepage", status: "Completed", date: "Today, 11:00 AM" },
    { id: 2, title: "Integrate API", status: "In Progress", date: "Today, 09:15 AM" },
    { id: 3, title: "Fix Login Bug", status: "Pending", date: "Yesterday, 04:45 PM" },
  ];

  const getStatusStyle = (status: string) => {
    switch (status) {
      case "Completed":
        return "bg-green-50 text-green-700 border-green-200";
      case "In Progress":
        return "bg-blue-50 text-blue-700 border-blue-200";
      case "Pending":
        return "bg-yellow-50 text-yellow-700 border-yellow-200";
      default:
        return "bg-gray-50 text-gray-600 border-gray-200";
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 via-white to-gray-100 p-10">
      {/* Header */}
      <div className="flex items-center mb-10">
        <button
          onClick={() => navigate("/workflows")}
          className="flex items-center text-sm text-gray-600 hover:text-blue-600 transition"
        >
          <ArrowLeft className="w-4 h-4 mr-1" /> Back to Workflows
        </button>
      </div>

      <h2 className="text-3xl font-bold text-gray-800 mb-2">Status Updates</h2>
      <p className="text-gray-500 mb-8">
        Real-time overview of all automated task and project status changes.
      </p>

      {/* Updates List */}
      <div className="bg-white rounded-2xl shadow-md border border-gray-100 divide-y divide-gray-100">
        {updates.map((item) => (
          <div key={item.id} className="flex justify-between items-center p-5">
            <div>
              <h3 className="font-semibold text-gray-800">{item.title}</h3>
              <p className="text-sm text-gray-500">{item.date}</p>
            </div>
            <div
              className={`flex items-center gap-2 px-3 py-1.5 rounded-full text-sm border font-medium ${getStatusStyle(
                item.status
              )}`}
            >
              {item.status === "Completed" && <CheckCircle2 className="w-4 h-4" />}
              {item.status === "In Progress" && (
                <RefreshCw className="w-4 h-4 animate-spin" />
              )}
              {item.status === "Pending" && <Clock className="w-4 h-4" />}
              {item.status}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default StatusUpdatesPage;
