import React from "react";
import { MessageSquare, Shield, Cloud, Brain } from "lucide-react";
import { motion } from "framer-motion";

const features = [
  {
    icon: <MessageSquare className="w-10 h-10 text-blue-600" />,
    title: "Real-time Chat",
    desc: "Instant communication with live syncing, typing indicators, and media sharing for effortless collaboration.",
  },
  {
    icon: <Shield className="w-10 h-10 text-green-600" />,
    title: "Secure Authentication",
    desc: "Enterprise-grade protection with Keycloak-powered SSO and role-based access control.",
  },
  {
    icon: <Cloud className="w-10 h-10 text-purple-600" />,
    title: "Cloud Sync",
    desc: "Work anywhere, anytime. Your data is always backed up and accessible with our reliable cloud infrastructure.",
  },
  {
    icon: <Brain className="w-10 h-10 text-orange-600" />,
    title: "AI Assistance",
    desc: "Powered by AWS Bedrock AI — get automated insights, intelligent suggestions, and smart task prioritization.",
  },
];

const FeaturesPage: React.FC = () => {
  return (
    <div className="min-h-screen bg-gradient-to-b from-white via-slate-50 to-slate-100 text-gray-800">
      {/* Hero Section */}
      <motion.div
        className="text-center py-20 px-6"
        initial={{ opacity: 0, y: -40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        <h1 className="text-5xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent mb-4">
          Powerful Features
        </h1>
        <p className="text-gray-600 text-lg max-w-2xl mx-auto leading-relaxed">
          Discover how <span className="font-semibold text-gray-800">Collabryta</span> helps teams
          connect, create, and collaborate smarter — with the speed, security, and intelligence you need.
        </p>
      </motion.div>

      {/* Features Grid */}
      <div className="max-w-6xl mx-auto grid gap-10 sm:grid-cols-2 lg:grid-cols-4 px-6 pb-24">
        {features.map((feature, i) => (
          <motion.div
            key={i}
            className="group bg-white rounded-2xl shadow-sm border border-slate-100 hover:border-blue-200 hover:shadow-lg transition-all duration-300 p-8 text-center"
            initial={{ opacity: 0, y: 40 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ delay: i * 0.15, duration: 0.5 }}
            viewport={{ once: true }}
          >
            <div className="flex justify-center mb-5">{feature.icon}</div>
            <h3 className="text-xl font-semibold text-gray-800 mb-3 group-hover:text-blue-600 transition-colors duration-300">
              {feature.title}
            </h3>
            <p className="text-gray-500 text-sm leading-relaxed">{feature.desc}</p>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default FeaturesPage;
