import React from "react";
import {
  LayoutDashboard,
  MessageSquare,
  CalendarDays,
  FileUp,
  Workflow,
  ClipboardList,
  LogOut,
  UsersRound,
  X,
  ChevronRight,
  Zap,
} from "lucide-react";
import { useNavigate, useLocation } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";

interface SidebarProps {
  isOpen: boolean;
  onClose: () => void;
}

const Sidebar: React.FC<SidebarProps> = ({ isOpen, onClose }) => {
  const navigate = useNavigate();
  const location = useLocation();

  const navItems = [
    { label: "Dashboard", icon: LayoutDashboard, path: "/dashboard" },
    { label: "Chat", icon: MessageSquare, path: "/chat" },
    { label: "Tasks", icon: ClipboardList, path: "/tasks" },
    { label: "Meetings", icon: UsersRound, path: "/meetings" },
    { label: "Calendar", icon: CalendarDays, path: "/calendar" },
    { label: "Files", icon: FileUp, path: "/files" },
    { label: "Workflows", icon: Workflow, path: "/workflows" },
  ];

  return (
    <>
      {/* ðŸŒ‘ Deep Overlay */}
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-slate-950/60 backdrop-blur-md z-40 lg:hidden"
            onClick={onClose}
          />
        )}
      </AnimatePresence>

      <aside
        className={`
          fixed top-0 left-0 z-50 h-screen w-72 bg-[#0a0f1d] border-r border-slate-800/50
          transform transition-all duration-500 ease-[cubic-bezier(0.23,1,0.32,1)]
          ${isOpen ? "translate-x-0" : "-translate-x-full"}
          lg:translate-x-0 shadow-2xl shadow-blue-900/20
        `}
      >
        {/* âœ¨ Background Glow */}
        <div className="absolute top-0 left-0 w-full h-full overflow-hidden pointer-events-none">
          <div className="absolute -top-[10%] -left-[20%] w-[150%] h-[40%] bg-blue-600/10 blur-[120px] rounded-full" />
          <div className="absolute bottom-[20%] -right-[10%] w-[80%] h-[30%] bg-indigo-600/5 blur-[100px] rounded-full" />
        </div>

        {/* ðŸ“± Mobile Close */}
        <div className="lg:hidden flex justify-end p-6 relative z-10">
          <motion.button
            whileHover={{ rotate: 90 }}
            whileTap={{ scale: 0.9 }}
            onClick={onClose}
            className="p-2.5 bg-slate-800/50 hover:bg-slate-700/50 text-slate-400 rounded-xl border border-slate-700/50 transition-colors"
          >
            <X size={18} />
          </motion.button>
        </div>

        {/* ðŸš€ Brand Section */}
        <div
          className="flex flex-col items-center py-12 px-6 cursor-pointer group relative z-10"
          onClick={() => {
            navigate("/dashboard");
            onClose();
          }}
        >
          <div className="relative p-1 rounded-2xl bg-gradient-to-tr from-blue-600 via-indigo-500 to-sky-400 transition-all duration-500 group-hover:shadow-[0_0_30px_rgba(37,99,235,0.4)]">
            <div className="bg-[#0a0f1d] p-3 rounded-xl">
              <img
                src="/logo1.png"
                alt="Collabryta"
                className="w-12 h-12 transition-transform duration-700 group-hover:scale-110 group-hover:rotate-[5deg]"
              />
            </div>
          </div>
          <h1 className="mt-5 text-2xl font-black tracking-tight text-white transition-all group-hover:text-blue-400">
            Collabryta
          </h1>
          <div className="mt-2 flex items-center gap-2 px-3 py-1 rounded-full bg-blue-500/10 border border-blue-500/20">
            <Zap size={10} className="text-blue-400 fill-blue-400" />
            <span className="text-[10px] uppercase tracking-[0.2em] font-bold text-blue-400/80">Pro Version</span>
          </div>
        </div>

        {/* ðŸ§­ Navigation */}
        <nav className="px-6 mt-4 space-y-2 overflow-y-auto max-h-[calc(100vh-320px)] custom-scrollbar relative z-10">
          {navItems.map(({ label, icon: Icon, path }) => {
            const active = location.pathname === path;
            return (
              <motion.button
                key={path}
                whileHover={{ x: 6 }}
                whileTap={{ scale: 0.98 }}
                onClick={() => {
                  navigate(path);
                  onClose();
                }}
                className={`w-full flex items-center justify-between px-4 py-3.5 rounded-2xl text-sm font-semibold transition-all group border
                  ${active
                    ? "bg-gradient-to-r from-blue-600 to-indigo-600 text-white border-transparent shadow-[0_8px_20px_rgba(37,99,235,0.3)]"
                    : "text-slate-400 border-transparent hover:bg-slate-800/40 hover:text-slate-200 hover:border-slate-800"
                  }`}
              >
                <div className="flex items-center gap-4">
                  <div className={`p-1.5 rounded-lg transition-colors ${active ? "bg-white/20" : "bg-slate-800/0 group-hover:bg-slate-800"}`}>
                    <Icon size={18} className={`${active ? "text-white" : "text-slate-500 group-hover:text-blue-400"}`} />
                  </div>
                  {label}
                </div>
                {active ? (
                  <motion.div layoutId="navActive" className="w-1.5 h-6 rounded-full bg-white opacity-80" />
                ) : (
                  <ChevronRight size={14} className="opacity-0 group-hover:opacity-100 group-hover:translate-x-1 transition-all text-slate-600" />
                )}
              </motion.button>
            );
          })}
        </nav>

        {/* ðŸšª User & Logout Section */}
        <div className="absolute bottom-10 w-full px-6 z-10">
          <div className="p-1 rounded-2xl bg-gradient-to-b from-slate-800/50 to-transparent">
            <motion.button
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              onClick={() => navigate("/login")}
              className="w-full flex items-center justify-center gap-3 py-4 bg-[#0d1326] text-slate-300 rounded-xl text-sm font-bold border border-slate-800/50 hover:bg-rose-500/10 hover:text-rose-400 hover:border-rose-500/20 transition-all shadow-lg"
            >
              <LogOut size={18} />
              Terminte Session
            </motion.button>
          </div>
        </div>

        {/* ðŸŒ‘ Subtle Gradient Fade */}
        <div className="absolute bottom-0 left-0 w-full h-40 bg-gradient-to-t from-[#0a0f1d] to-transparent pointer-events-none" />
      </aside>
    </>
  );
};

export default Sidebar;
