import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import {
    ChevronLeft,
    Save,
    Loader2,
    Calendar,
    User as UserIcon, // Renamed to avoid conflict with User component if any
    Type,
    AlertCircle,
    Zap,
    Sparkles,
    CheckCircle2,
    Target,
    ArrowUpRight,
    Flag,
    ChevronDown
} from "lucide-react";
import { taskService } from "../../services/taskService";
import { userService } from "../../services/userService";
import { motion, AnimatePresence } from "framer-motion";

const AddTaskPage: React.FC = () => {
    const navigate = useNavigate();
    const { id } = useParams<{ id: string }>();
    const isEditing = Boolean(id);
    const today = new Date().toISOString().split('T')[0];

    const [users, setUsers] = useState<any[]>([]);
    const [submitting, setSubmitting] = useState(false);
    const [loading, setLoading] = useState(isEditing);
    const [error, setError] = useState<string | null>(null);

    const [formData, setFormData] = useState({
        title: "",
        description: "",
        status: "Upcoming",
        priority: "Normal",
        assigned_to: "",
        due_date: today
    });

    useEffect(() => {
        const loadData = async () => {
            try {
                const [usersData, taskData] = await Promise.all([
                    userService.getAllUsers(),
                    isEditing && id ? taskService.getTaskById(Number(id)) : Promise.resolve(null)
                ]);
                setUsers(usersData);
                if (taskData) {
                    setFormData({
                        title: taskData.title,
                        description: taskData.description || "",
                        status: taskData.status === "todo" ? "Upcoming" : taskData.status === "in_progress" ? "In Progress" : taskData.status === "done" ? "Completed" : "Upcoming",
                        priority: taskData.priority || "Normal",
                        assigned_to: taskData.assignee_id ? String(taskData.assignee_id) : "",
                        due_date: taskData.due_date ? taskData.due_date.split("T")[0] : today
                    });
                }
            } catch (e) {
                console.error("Failed to load data", e);
                setError("Failed to load task data. Please try again.");
            } finally {
                setLoading(false);
            }
        };
        loadData();
    }, [id, isEditing, today]);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setError(null);
        if (formData.due_date < today) {
            setError("Due date cannot be in the past.");
            return;
        }

        setSubmitting(true);
        try {
            const payload = {
                ...formData,
                status: (formData.status === "Upcoming" ? "todo" : formData.status === "In Progress" ? "in_progress" : "done") as any,
                priority: formData.priority as any,
                assigned_to: formData.assigned_to ? Number(formData.assigned_to) : undefined
            };

            if (isEditing && id) {
                await taskService.updateTask(Number(id), payload);
            } else {
                await taskService.createTask(payload);
            }
            navigate("/tasks");
        } catch (err: any) {
            console.error(err);
            setError(err.response?.data?.detail?.[0]?.msg || err.response?.data?.detail || "Something went wrong. Please try again.");
            setSubmitting(false);
        }
    };

    const updateField = (field: string, value: any) => {
        setFormData(prev => ({ ...prev, [field]: value }));
    };

    if (loading) {
        return (
            <div className="min-h-screen flex flex-col items-center justify-center bg-[#fcfdfe]">
                <div className="w-12 h-12 border-4 border-slate-100 border-t-blue-600 rounded-full animate-spin mb-6" />
                <p className="text-sm font-semibold text-slate-400">Loading task data...</p>
            </div>
        );
    }

    return (
        <div className="min-h-screen text-slate-900 pb-20 animate-fade-in relative">
            {/* Background Decor */}
            {/* Background Decor Removed */}

            <div className="max-w-6xl mx-auto relative z-10 px-6 py-8">

                {/* Header Navigation */}
                <div className="flex items-center justify-between mb-12">
                    <button
                        onClick={() => navigate(-1)}
                        className="flex items-center gap-3 text-slate-500 hover:text-blue-600 transition-all font-bold text-sm group"
                    >
                        <div className="w-10 h-10 rounded-xl bg-white border border-slate-200 flex items-center justify-center shadow-sm group-hover:bg-blue-50 group-hover:border-blue-100 transition-all hover:scale-110">
                            <ChevronLeft size={18} className="group-hover:-translate-x-0.5 transition-transform" />
                        </div>
                        Back to Tasks
                    </button>

                    <div className="flex items-center gap-3 px-4 py-2 bg-blue-50 text-blue-600 rounded-xl font-bold text-[11px] uppercase tracking-widest border border-blue-100 shadow-sm">
                        <Target size={14} />
                        Task Management
                    </div>
                </div>

                <div className="grid grid-cols-1 lg:grid-cols-12 gap-12 items-start">
                    {/* Left: Branding & Info */}
                    <div className="lg:col-span-4 space-y-8">
                        <div>
                            <h1 className="text-4xl font-extrabold tracking-tight text-slate-900 mb-4 leading-tight">
                                {isEditing ? 'Edit Task' : 'New Task'}
                            </h1>
                            <p className="text-sm font-medium text-slate-500 leading-relaxed">
                                {isEditing
                                    ? 'Update the task details and parameters.'
                                    : 'Create a new task to track progress and deliverables.'}
                            </p>
                        </div>

                        <div className="bg-white rounded-[24px] border border-slate-200 p-8 space-y-8 shadow-sm">
                            <div className="flex items-start gap-4">
                                <div className="w-12 h-12 rounded-2xl bg-amber-50 text-amber-600 flex items-center justify-center shrink-0 border border-amber-100/50 shadow-sm">
                                    <Zap size={20} />
                                </div>
                                <div>
                                    <h4 className="text-sm font-bold text-slate-900 mb-1">Quick Action</h4>
                                    <p className="text-xs font-semibold text-slate-400 leading-relaxed">Tasks are immediately available to the team.</p>
                                </div>
                            </div>
                            <div className="flex items-start gap-4">
                                <div className="w-12 h-12 rounded-2xl bg-emerald-50 text-emerald-600 flex items-center justify-center shrink-0 border border-emerald-100/50 shadow-sm">
                                    <CheckCircle2 size={20} />
                                </div>
                                <div>
                                    <h4 className="text-sm font-black text-slate-900 mb-1">Track Progress</h4>
                                    <p className="text-xs font-semibold text-slate-400 leading-relaxed">Monitor status updates in real-time.</p>
                                </div>
                            </div>
                        </div>

                        <div className="bg-slate-900 rounded-[24px] p-8 text-white relative overflow-hidden shadow-xl shadow-slate-200 group hover:scale-[1.02] transition-transform duration-500">
                            <div className="absolute top-0 right-0 p-6 opacity-10 group-hover:opacity-20 transition-opacity">
                                <Sparkles size={80} />
                            </div>
                            <h4 className="text-md font-bold mb-3 flex items-center gap-2"><Flag size={16} className="text-blue-400" /> Pro Tip</h4>
                            <p className="text-xs font-medium text-slate-300 leading-relaxed">
                                Assigning a clear priority helps the team focus on what matters most.
                            </p>
                        </div>
                    </div>

                    {/* Right: Form */}
                    <div className="lg:col-span-8">
                        <AnimatePresence>
                            {error && (
                                <motion.div
                                    initial={{ opacity: 0, y: -10 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    exit={{ opacity: 0, y: -10 }}
                                    className="mb-8 p-6 bg-rose-50 border border-rose-100 rounded-[24px] text-rose-600 flex items-center gap-4 shadow-sm"
                                >
                                    <AlertCircle size={20} />
                                    <p className="text-sm font-bold">{error}</p>
                                </motion.div>
                            )}
                        </AnimatePresence>

                        <div className="bg-white rounded-[40px] border border-slate-200 p-8 md:p-12 shadow-xl shadow-slate-200/50 relative overflow-hidden">
                            <div>
                                <h1 className="text-3xl font-bold tracking-tight text-slate-900">{isEditing ? 'Edit Task' : 'Create New Task'}</h1>
                                <p className="text-slate-500 font-medium">{isEditing ? 'Update task details below' : 'Define your new task objectives'}</p>
                            </div>
                        </div>

                        {/* The previous error block was duplicated, removing one */}

                        <div className="bg-white rounded-[24px] border border-slate-200 shadow-sm overflow-hidden mt-8">
                            <form onSubmit={handleSubmit} className="p-8">
                                <div className="space-y-8">
                                    {/* Title Input */}
                                    <div className="space-y-2">
                                        <label htmlFor="task-title" className="text-sm font-bold text-slate-700">Task Title</label>
                                        <div className="relative">
                                            <Type className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={18} />
                                            <input
                                                id="task-title"
                                                className="w-full pl-11 pr-4 py-3 bg-white border border-slate-300 rounded-lg focus:border-blue-500 focus:ring-2 focus:ring-blue-100 outline-none transition-all font-medium"
                                                value={formData.title}
                                                onChange={e => updateField('title', e.target.value)}
                                                placeholder="Enter task title..."
                                                required
                                            />
                                        </div>
                                    </div>

                                    {/* Description Input */}
                                    <div className="space-y-2">
                                        <label htmlFor="task-description" className="text-sm font-bold text-slate-700">Description</label>
                                        <textarea
                                            id="task-description"
                                            className="w-full p-4 bg-white border border-slate-300 rounded-lg focus:border-blue-500 focus:ring-2 focus:ring-blue-100 outline-none transition-all font-medium h-32 resize-none"
                                            value={formData.description}
                                            onChange={e => updateField('description', e.target.value)}
                                            placeholder="Enter task description..."
                                        />
                                    </div>

                                    <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                                        <div className="space-y-6">
                                            <div className="space-y-4">
                                                <label className="text-sm font-bold text-slate-700">Status</label>
                                                <div className="flex flex-wrap gap-3">
                                                    {['Upcoming', 'In Progress', 'Completed'].map((s) => (
                                                        <button
                                                            key={s}
                                                            type="button"
                                                            onClick={() => updateField('status', s)}
                                                            className={`px-4 py-2 rounded-lg text-sm font-semibold border transition-all ${formData.status === s
                                                                ? 'bg-blue-600 text-white border-blue-600 shadow-sm'
                                                                : 'bg-white text-slate-600 border-slate-200 hover:bg-slate-50'
                                                                }`}
                                                        >
                                                            {s}
                                                        </button>
                                                    ))}
                                                </div>
                                            </div>

                                            <div className="space-y-4">
                                                <label className="text-sm font-bold text-slate-700">Priority Level</label>
                                                <div className="grid grid-cols-3 gap-3">
                                                    {[
                                                        { label: 'High', color: 'bg-rose-500', border: 'border-rose-200', text: 'text-rose-700', bg: 'bg-rose-50' },
                                                        { label: 'Medium', color: 'bg-amber-500', border: 'border-amber-200', text: 'text-amber-700', bg: 'bg-amber-50' },
                                                        { label: 'Low', color: 'bg-emerald-500', border: 'border-emerald-200', text: 'text-emerald-700', bg: 'bg-emerald-50' }
                                                    ].map((p) => (
                                                        <button
                                                            key={p.label}
                                                            type="button"
                                                            onClick={() => updateField('priority', p.label)}
                                                            className={`p-3 rounded-lg border flex flex-col items-center justify-center gap-2 transition-all ${formData.priority === p.label
                                                                ? `${p.bg} ${p.border} ${p.text} ring-2 ring-offset-1 ring-${p.label === 'High' ? 'rose' : p.label === 'Medium' ? 'amber' : 'emerald'}-400`
                                                                : 'bg-white border-slate-200 text-slate-500 hover:bg-slate-50'
                                                                }`}
                                                        >
                                                            <div className={`w-2 h-2 rounded-full ${p.color}`} />
                                                            <span className="text-xs font-bold uppercase tracking-wider">{p.label}</span>
                                                        </button>
                                                    ))}
                                                </div>
                                            </div>
                                        </div>

                                        <div className="space-y-6">
                                            <div className="space-y-2">
                                                <label htmlFor="assigned-to" className="text-sm font-bold text-slate-700">Assign To</label>
                                                <div className="relative">
                                                    <UserIcon className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={18} />
                                                    <select
                                                        id="assigned-to"
                                                        className="w-full pl-11 pr-4 py-3 bg-white border border-slate-300 rounded-lg focus:border-blue-500 focus:ring-2 focus:ring-blue-100 outline-none appearance-none font-medium text-slate-700"
                                                        value={formData.assigned_to || ''}
                                                        onChange={e => updateField('assigned_to', e.target.value)}
                                                    >
                                                        <option value="">Select Team Member...</option>
                                                        {users.map(u => (
                                                            <option key={u.id} value={u.id}>{u.name || u.email}</option>
                                                        ))}
                                                    </select>
                                                    <ChevronDown className="absolute right-4 top-1/2 -translate-y-1/2 text-slate-400 pointer-events-none" size={16} />
                                                </div>
                                            </div>

                                            <div className="space-y-2">
                                                <label htmlFor="due-date" className="text-sm font-bold text-slate-700">Due Date</label>
                                                <div className="relative">
                                                    <Calendar className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={18} />
                                                    <input
                                                        id="due-date"
                                                        type="date"
                                                        min={today}
                                                        className="w-full pl-11 pr-4 py-3 bg-white border border-slate-300 rounded-lg focus:border-blue-500 focus:ring-2 focus:ring-blue-100 outline-none font-medium text-slate-700"
                                                        value={formData.due_date}
                                                        onChange={e => updateField('due_date', e.target.value)}
                                                    />
                                                </div>
                                            </div>
                                        </div>
                                    </div>

                                    <div className="pt-6 border-t border-slate-100 flex justify-end gap-3">
                                        <button
                                            type="button"
                                            onClick={() => navigate('/tasks')}
                                            className="px-6 py-3 bg-white border border-slate-200 text-slate-600 rounded-lg font-bold hover:bg-slate-50 transition-all"
                                        >
                                            Cancel
                                        </button>
                                        <button
                                            type="submit"
                                            disabled={submitting}
                                            className="btn-premium min-w-[150px] px-6 py-3 bg-blue-600 text-white rounded-lg font-bold hover:bg-blue-700 transition-all flex items-center justify-center gap-2"
                                        >
                                            {submitting ? (
                                                <Loader2 size={20} className="animate-spin" />
                                            ) : (
                                                <>
                                                    <Save size={18} />
                                                    {isEditing ? 'Update Task' : 'Create Task'}
                                                </>
                                            )}
                                        </button>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AddTaskPage;
