import React, { useState, useEffect } from "react";
import { taskService } from "../../services/taskService";
import { meetingService } from "../../services/meetingService";
import {
  Calendar as CalendarIcon, Clock, MapPin,
  Video, ChevronLeft, ChevronRight, Zap,
  Activity, Sparkles, Loader2, ArrowUpRight, Plus, ExternalLink
} from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";

interface CalendarEvent {
  id: string | number;
  title: string;
  time?: string;
  date: string;
  type: "meeting" | "task";
  status?: string;
  details?: string;
  isOverdue?: boolean;
}

const CalendarPage: React.FC = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [events, setEvents] = useState<CalendarEvent[]>([]);
  const [loading, setLoading] = useState<boolean>(true);

  const monthNames = [
    "January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"
  ];

  const daysOfWeek = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];

  const year = currentDate.getFullYear();
  const month = currentDate.getMonth();

  const firstDay = new Date(year, month, 1);
  const lastDay = new Date(year, month + 1, 0);
  const startingDay = firstDay.getDay();
  const totalDays = lastDay.getDate();

  const daysArray: number[] = [];
  for (let i = 1; i <= totalDays; i++) daysArray.push(i);

  const today = new Date();
  const todayStr = today.toLocaleDateString("en-CA");

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [tasks, meetings] = await Promise.all([
          taskService.getTasks(),
          meetingService.getAllMeetings()
        ]);

        const taskEvents: CalendarEvent[] = tasks
          .filter(t => t.due_date)
          .map(t => ({
            id: `task-${t.id}`,
            title: t.title,
            date: t.due_date ? new Date(t.due_date).toLocaleDateString("en-CA") : "",
            type: "task",
            status: t.status,
            details: t.priority,
            isOverdue: t.is_overdue
          }));

        const meetingEvents: CalendarEvent[] = meetings.map(m => {
          const start = new Date(m.start_time);
          const end = new Date(m.end_time);
          const timeStr = `${start.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })} – ${end.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}`;

          return {
            id: `meeting-${m.id}`,
            title: m.title,
            time: timeStr,
            date: start.toLocaleDateString("en-CA"),
            type: "meeting",
            details: m.location || "Online"
          };
        });

        setEvents([...taskEvents, ...meetingEvents]);
      } catch (error) {
        console.error("Failed to fetch calendar data", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const selectedDateStr = selectedDate.toLocaleDateString("en-CA");
  const dayEvents = events.filter((e) => e.date === selectedDateStr);

  return (
    <div className="min-h-screen bg-[#fcfdfe] text-slate-900 px-8 py-10 lg:px-12 animate-fade-in relative">
      <div className="max-w-7xl mx-auto">

        {/* Header Area */}
        <div className="flex flex-col md:flex-row md:items-center justify-between gap-6 mb-12">
          <div>
            <h1 className="text-4xl font-bold tracking-tight text-slate-900">Calendar</h1>
            <p className="text-slate-500 font-medium mt-1">Keep track of your meetings and task deadlines.</p>
          </div>

          <div className="flex items-center gap-4">
            <div className="flex items-center bg-white border border-slate-100 rounded-2xl shadow-sm overflow-hidden p-1">
              <button
                onClick={() => setCurrentDate(new Date(year, month - 1, 1))}
                className="p-3 text-slate-400 hover:text-indigo-600 hover:bg-slate-50 rounded-xl transition-all"
              >
                <ChevronLeft size={20} />
              </button>
              <div className="px-6 min-w-[180px] text-center">
                <span className="text-sm font-bold text-slate-900">{monthNames[month]} {year}</span>
              </div>
              <button
                onClick={() => setCurrentDate(new Date(year, month + 1, 1))}
                className="p-3 text-slate-400 hover:text-indigo-600 hover:bg-slate-50 rounded-xl transition-all"
              >
                <ChevronRight size={20} />
              </button>
            </div>
            <button
              onClick={() => {
                setCurrentDate(new Date());
                setSelectedDate(new Date());
              }}
              className="btn-secondary whitespace-nowrap"
            >
              Today
            </button>
            <button className="btn-primary">
              <Plus size={18} />
              Add Event
            </button>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-10">

          {/* Calendar Grid */}
          <div className="lg:col-span-2 bg-white rounded-[32px] border border-slate-100 shadow-sm overflow-hidden">
            <div className="grid grid-cols-7 text-center font-bold text-slate-400 text-xs uppercase tracking-widest py-6 border-b border-slate-50">
              {daysOfWeek.map((d) => (
                <div key={d}>{d}</div>
              ))}
            </div>

            <div className="grid grid-cols-7 bg-slate-50/30 gap-px">
              {Array(startingDay).fill(null).map((_, i) => (
                <div key={`empty-${i}`} className="bg-white" />
              ))}
              {daysArray.map((day) => {
                const d = new Date(year, month, day);
                const dateStr = d.toLocaleDateString("en-CA");
                const isTodayStr = dateStr === todayStr;
                const dateEvents = events.filter((e) => e.date === dateStr);
                const isSelected = dateStr === selectedDateStr;

                return (
                  <div
                    key={day}
                    onClick={() => setSelectedDate(new Date(year, month, day))}
                    className={`relative aspect-square p-4 cursor-pointer transition-all bg-white group ${isSelected ? 'ring-2 ring-inset ring-indigo-500 z-10' : 'hover:bg-slate-50/50'
                      }`}
                  >
                    <div className="flex justify-between items-start mb-2">
                      <span className={`text-sm font-bold ${isSelected
                        ? 'text-indigo-600'
                        : isTodayStr
                          ? 'text-white bg-indigo-600 w-7 h-7 flex items-center justify-center rounded-lg shadow-lg shadow-indigo-100'
                          : 'text-slate-400 group-hover:text-slate-900'
                        }`}>{day}</span>
                      {dateEvents.length > 0 && !isSelected && !isTodayStr && (
                        <div className="w-1.5 h-1.5 bg-indigo-500 rounded-full" />
                      )}
                    </div>
                    <div className="space-y-1 overflow-hidden hidden sm:block">
                      {dateEvents.slice(0, 2).map((e, idx) => (
                        <div
                          key={idx}
                          className={`text-[9px] font-bold truncate px-2 py-1 rounded-md ${e.type === 'meeting'
                            ? 'bg-indigo-50 text-indigo-700 border border-indigo-100'
                            : 'bg-slate-100 text-slate-600 border border-slate-200'
                            }`}
                        >
                          {e.title}
                        </div>
                      ))}
                      {dateEvents.length > 2 && (
                        <div className="text-[9px] font-bold text-slate-300 pl-1">
                          + {dateEvents.length - 2} more
                        </div>
                      )}
                    </div>
                  </div>
                );
              })}
              {/* Fill remaining empty cells */}
              {Array(42 - startingDay - totalDays).fill(null).map((_, i) => (
                <div key={`empty-end-${i}`} className="bg-white" />
              ))}
            </div>
          </div>

          {/* Side panel */}
          <div className="space-y-8">
            <div className="flex flex-col gap-6">
              <div className="px-2">
                <h3 className="text-2xl font-bold text-slate-900">
                  {selectedDate.toLocaleDateString('en-US', { day: 'numeric', month: 'long' })}
                </h3>
                <p className="text-sm font-semibold text-indigo-600 uppercase tracking-wider mt-0.5">
                  {selectedDate.toLocaleDateString('en-US', { weekday: 'long' })}
                </p>
              </div>

              <div className="space-y-4 max-h-[500px] overflow-y-auto pr-2 custom-scrollbar">
                {loading ? (
                  <div className="py-12 text-center bg-white rounded-3xl border border-slate-100">
                    <Loader2 className="w-10 h-10 text-indigo-600 animate-spin mx-auto mb-4" />
                    <p className="text-sm font-semibold text-slate-400">Loading events...</p>
                  </div>
                ) : dayEvents.length > 0 ? (
                  <div className="space-y-4">
                    {dayEvents.map((event) => (
                      <div
                        key={event.id}
                        className={`p-6 rounded-[28px] border transition-all cursor-pointer group ${event.type === 'meeting'
                          ? 'bg-indigo-600 text-white border-indigo-600 shadow-lg shadow-indigo-100'
                          : 'bg-white text-slate-900 border-slate-100 shadow-sm hover:border-indigo-100'
                          }`}
                      >
                        <div className="flex items-start gap-4">
                          <div className={`p-3 rounded-2xl ${event.type === 'meeting' ? 'bg-white/20 text-white' : 'bg-indigo-50 text-indigo-600'
                            }`}>
                            {event.type === 'meeting' ? <Video size={20} /> : <Zap size={20} />}
                          </div>
                          <div className="flex-1 min-w-0">
                            <p className={`text-[10px] font-bold border-b w-fit mb-2 uppercase tracking-wider ${event.type === 'meeting' ? 'text-indigo-200 border-indigo-500' : 'text-slate-400 border-slate-100'
                              }`}>{event.type}</p>
                            <h4 className="text-base font-bold leading-tight mb-3 line-clamp-2">{event.title}</h4>
                            <div className="flex flex-col gap-2">
                              <div className={`flex items-center gap-2 text-[11px] font-bold ${event.type === 'meeting' ? 'text-indigo-100' : 'text-slate-500'
                                }`}>
                                <Clock size={12} />
                                {event.time || `${event.details} Priority`}
                              </div>
                              {event.type === 'meeting' && event.details && (
                                <div className="flex items-center gap-2 text-[11px] font-bold text-indigo-200">
                                  <MapPin size={12} />
                                  {event.details}
                                </div>
                              )}
                            </div>
                          </div>
                          <ArrowUpRight size={18} className={event.type === 'meeting' ? 'text-white/40 group-hover:text-white transition-colors' : 'text-slate-200 group-hover:text-indigo-600 transition-colors'} />
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="py-24 text-center bg-white rounded-[32px] border border-slate-100 shadow-sm">
                    <div className="w-16 h-16 bg-slate-50 rounded-2xl flex items-center justify-center mx-auto mb-4">
                      <CalendarIcon className="text-slate-300" size={32} />
                    </div>
                    <h4 className="text-md font-bold text-slate-900">Quiet day...</h4>
                    <p className="text-xs text-slate-400 font-medium mt-1">No scheduled events found.</p>
                  </div>
                )}
              </div>
            </div>

            {/* AI Insights Card */}
            <div className="bg-slate-900 rounded-[32px] p-8 text-white relative overflow-hidden shadow-xl shadow-slate-200">
              <div className="absolute top-0 right-0 p-8 opacity-10">
                <Sparkles size={100} />
              </div>
              <div className="relative z-10">
                <div className="flex items-center gap-3 mb-6">
                  <div className="w-10 h-10 bg-indigo-500 rounded-xl flex items-center justify-center shadow-lg shadow-indigo-500/20">
                    <Activity size={20} className="text-white" />
                  </div>
                  <p className="text-xs font-bold uppercase tracking-wider text-slate-400">Weekly Forecast</p>
                </div>
                <p className="text-sm font-medium leading-relaxed mb-8">
                  You have a dense schedule on Wednesday. Consider moving some tasks to Tuesday to maintain optimal productivity.
                </p>
                <button className="w-full py-4 bg-white text-slate-900 border border-white rounded-2xl font-bold text-xs hover:bg-slate-50 transition-all flex items-center justify-center gap-2">
                  Learn More
                  <ExternalLink size={14} />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CalendarPage;
