import React, { useState, useEffect } from "react";
import {
  Plus,
  Search,
  Loader2,
  Trash2,
  Edit3,
  Calendar,
  User as UserIcon,
  Filter,
  AlertCircle,
  X,
  Clock,
  CheckCircle2,
  Zap,
  ArrowRight,
  Shield,
  Layers,
  ChevronRight,
  ArrowUpRight
} from "lucide-react";
import { useNavigate } from "react-router-dom";
import { taskService, Task } from "../../services/taskService";
import { motion, AnimatePresence } from "framer-motion";


const TasksPage: React.FC = () => {
  const navigate = useNavigate();
  const [tasks, setTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState<string>("All");
  const [selectedTask, setSelectedTask] = useState<Task | null>(null);

  useEffect(() => {
    fetchTasks();
    const pollInterval = setInterval(() => {
      fetchTasks(false);
    }, 30000);

    return () => {
      clearInterval(pollInterval);
    };
  }, []);

  const fetchTasks = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const data = await taskService.getTasks();
      setTasks(data);
    } catch (error) {
      console.error("Failed to fetch tasks", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  const normalizeStatus = (status: string) => {
    const s = status ? status.toLowerCase() : "";
    if (s === "todo" || s === "upcoming") return "Upcoming";
    if (s === "in_progress" || s === "in progress" || s === "inprogress") return "In Progress";
    if (s === "completed" || s === "done") return "Completed";
    return "Upcoming";
  };

  const getTimeStatus = (dueDate: string | null | undefined, status: string) => {
    const normStatus = normalizeStatus(status);
    if (normStatus === "Completed") return { label: "Completed", style: "bg-black text-white" };
    if (!dueDate) return { label: "Planned", style: "bg-zinc-100 text-zinc-500" };

    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const due = new Date(dueDate);
    due.setHours(0, 0, 0, 0);

    if (due < today) return { label: "Overdue", style: "border border-black text-black" };
    if (due.getTime() === today.getTime()) return { label: "Due Today", style: "bg-zinc-800 text-white" };

    return { label: "Active", style: "bg-zinc-100 text-black" };
  };

  const handleDelete = async (e: React.MouseEvent, id: number) => {
    e.stopPropagation();
    if (!window.confirm("ARE YOU SURE?")) return;
    try {
      await taskService.deleteTask(id);
      setTasks(prev => prev.filter(t => t.id !== id));
      if (selectedTask?.id === id) setSelectedTask(null);
    } catch (error) {
      console.error("Failed to delete task");
    }
  };

  const filteredTasks = tasks.filter(task => {
    const matchesSearch = task.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (task.description && task.description.toLowerCase().includes(searchQuery.toLowerCase()));
    const matchesStatus = statusFilter === "All" || normalizeStatus(task.status) === statusFilter;
    return matchesSearch && matchesStatus;
  });

  return (
    <div className="min-h-screen bg-white text-black p-10 lg:p-16 animate-fade-in relative">

      {/* ðŸ“‹ Main Content Area */}
      <div className={`transition-all duration-500 ${selectedTask ? 'lg:mr-[450px]' : ''}`}>

        {/* Header Section */}
        <div className="flex flex-col md:flex-row md:items-end justify-between gap-10 mb-20 border-b border-zinc-100 pb-12">
          <div>
            <p className="text-[10px] font-black tracking-[0.3em] text-zinc-400 mb-2 uppercase">OPERATIONAL / OBJECTIVES</p>
            <h1 className="text-5xl font-bold tracking-tighter leading-none uppercase">TASKS</h1>
          </div>
          <button
            onClick={() => navigate("/tasks/add")}
            className="group flex items-center gap-4 px-10 py-4 bg-black text-white hover:bg-zinc-800 transition-all active:scale-95"
          >
            <span className="text-[11px] font-bold tracking-widest uppercase">NEW OBJECTIVE</span>
            <Plus size={16} />
          </button>
        </div>

        {/* Toolbar */}
        <div className="flex flex-col lg:flex-row items-center gap-10 mb-12">
          <div className="relative flex-1 w-full border-b border-zinc-200">
            <Search className="absolute left-0 top-1/2 -translate-y-1/2 text-zinc-300" size={14} />
            <input
              type="text"
              placeholder="SEARCH OBJECTIVES..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-8 pr-4 py-4 bg-transparent border-none focus:ring-0 text-[11px] font-bold tracking-widest text-black placeholder:text-zinc-300 uppercase"
            />
          </div>
          <div className="flex items-center gap-8 w-full lg:w-auto">
            {["All", "Upcoming", "In Progress", "Completed"].map((status) => (
              <button
                key={status}
                onClick={() => setStatusFilter(status)}
                className={`text-[10px] font-black tracking-widest uppercase transition-all pb-2 border-b-2 ${statusFilter === status
                  ? "border-black text-black"
                  : "border-transparent text-zinc-300 hover:text-black hover:border-zinc-200"
                  }`}
              >
                {status}
              </button>
            ))}
          </div>
        </div>

        {/* Task List Rendering */}
        <div className="space-y-px bg-zinc-100 border border-zinc-100">
          {loading ? (
            <div className="py-40 text-center bg-white">
              <Loader2 className="w-8 h-8 text-black animate-spin mx-auto mb-4" />
              <p className="text-[10px] font-bold tracking-widest text-zinc-400 uppercase">SYNCHRONIZING ARCHIVE</p>
            </div>
          ) : filteredTasks.length === 0 ? (
            <div className="py-40 text-center bg-white">
              <p className="text-[11px] font-black text-zinc-300 tracking-[0.3em] uppercase">NO OBJECTIVES FOUND IN CURRENT MATRIX</p>
            </div>
          ) : (
            <div className="divide-y divide-zinc-100">
              {filteredTasks.map((task, idx) => {
                const timeStatus = getTimeStatus(task.due_date, task.status);
                const isSelected = selectedTask?.id === task.id;
                return (
                  <div
                    key={task.id}
                    onClick={() => setSelectedTask(task)}
                    className={`group relative p-10 bg-white hover:bg-zinc-50 transition-all cursor-pointer flex flex-col md:flex-row md:items-center gap-10 ${isSelected ? 'bg-zinc-50' : ''}`}
                  >
                    <div className={`w-32 shrink-0 text-center`}>
                      <p className={`text-[9px] font-black tracking-widest uppercase px-3 py-1.5 inline-block ${timeStatus.style}`}>
                        {timeStatus.label}
                      </p>
                    </div>

                    <div className="flex-1 min-w-0">
                      <div className="flex items-center gap-4 mb-2">
                        <h4 className="text-xl font-bold text-black uppercase tracking-tight group-hover:underline underline-offset-4 decoration-1">
                          {task.title}
                        </h4>
                        {task.priority === 'High' && (
                          <span className="text-[9px] font-black tracking-widest text-black border border-black px-2 py-0.5">PRIORITY</span>
                        )}
                      </div>
                      <p className="text-[11px] text-zinc-400 font-medium tracking-tight uppercase line-clamp-1">{task.description || "NO ADDITIONAL BRIEFING DATA AVAILABLE."}</p>
                    </div>

                    <div className="flex items-center gap-12 shrink-0">
                      <div className="flex flex-col items-end">
                        <p className="text-[9px] font-black text-zinc-300 tracking-[0.2em] uppercase mb-1">DEADLINE</p>
                        <p className="text-[11px] font-bold text-black tracking-widest uppercase">
                          {task.due_date ? new Date(task.due_date).toLocaleDateString(undefined, { month: 'short', day: 'numeric', year: 'numeric' }) : "UNDEFINED"}
                        </p>
                      </div>

                      <div className="opacity-0 group-hover:opacity-100 transition-all flex items-center gap-4">
                        <button
                          onClick={(e) => { e.stopPropagation(); navigate(`/tasks/edit/${task.id}`); }}
                          className="p-3 text-zinc-300 hover:text-black transition-all"
                        >
                          <Edit3 size={16} />
                        </button>
                        <button
                          onClick={(e) => handleDelete(e, task.id)}
                          className="p-3 text-zinc-300 hover:text-black transition-all"
                        >
                          <Trash2 size={16} />
                        </button>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </div>

      {/* Slide-over Panel */}
      <AnimatePresence>
        {selectedTask && (
          <motion.div
            initial={{ x: "100%" }}
            animate={{ x: 0 }}
            exit={{ x: "100%" }}
            transition={{ type: "tween", duration: 0.4, ease: [0.16, 1, 0.3, 1] }}
            className="fixed top-0 right-0 w-full lg:w-[450px] h-screen bg-white border-l border-zinc-100 shadow-2xl z-50 flex flex-col p-12 overflow-hidden"
          >
            <div className="flex items-center justify-between mb-16">
              <p className="text-[10px] font-black tracking-[0.3em] text-zinc-400 uppercase">OBJECTIVE DETAIL</p>
              <button onClick={() => setSelectedTask(null)} className="p-2 text-zinc-300 hover:text-black transition-all">
                <X size={24} />
              </button>
            </div>

            <div className="flex-1 overflow-y-auto space-y-12 pr-4 custom-scrollbar">
              <div className="space-y-6">
                <div className={`w-fit px-4 py-1.5 text-[9px] font-black uppercase tracking-widest ${getTimeStatus(selectedTask.due_date, selectedTask.status).style}`}>
                  {normalizeStatus(selectedTask.status)}
                </div>
                <h3 className="text-4xl font-bold text-black tracking-tighter leading-tight uppercase">{selectedTask.title}</h3>
                <p className="text-[13px] text-zinc-500 font-medium leading-relaxed tracking-tight uppercase">
                  {selectedTask.description || "NO ADDITIONAL BRIEFING DATA PROVIDED FOR THIS OPERATIONAL UNIT."}
                </p>
              </div>

              <div className="space-y-10 pt-10 border-t border-zinc-100">
                <div className="flex items-start gap-6">
                  <Calendar size={18} className="text-zinc-200 mt-1" />
                  <div>
                    <p className="text-[9px] font-black text-zinc-300 uppercase tracking-widest mb-1">DUE DATE</p>
                    <p className="text-[11px] font-bold text-black tracking-widest uppercase">
                      {selectedTask.due_date ? new Date(selectedTask.due_date).toLocaleDateString(undefined, { month: 'long', day: 'numeric', year: 'numeric' }) : "NOT DEFINED"}
                    </p>
                  </div>
                </div>

                <div className="flex items-start gap-6">
                  <UserIcon size={18} className="text-zinc-200 mt-1" />
                  <div>
                    <p className="text-[9px] font-black text-zinc-300 uppercase tracking-widest mb-1">ASSIGNED PERSONNEL</p>
                    <p className="text-[11px] font-bold text-black tracking-widest uppercase">{selectedTask.assigned_to_user?.name || "UNASSIGNED"}</p>
                  </div>
                </div>
              </div>
            </div>

            <div className="pt-12 border-t border-zinc-100 grid grid-cols-2 gap-px bg-zinc-100 mt-auto">
              <button
                onClick={() => navigate(`/tasks/edit/${selectedTask.id}`)}
                className="py-6 bg-black text-white text-[10px] font-black tracking-widest uppercase hover:bg-zinc-800 transition-all"
              >
                EDIT UNIT
              </button>
              <button
                onClick={(e) => handleDelete(e, selectedTask.id)}
                className="py-6 bg-white text-black text-[10px] font-black tracking-widest uppercase hover:bg-zinc-50 transition-all"
              >
                DELETE ARCHIVE
              </button>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default TasksPage;
